-- Aiko Uef
Aiko_1 =        {{text = '[{i Aiko}]: I will not allow you to interfere with Black Sun, Aeon!', vid = 'A06_Aiko_M01_01047.sfd', bank = 'A06_VO', cue = 'A06_Aiko_M01_01047', faction = 'UEF'},}
Aiko_2 =        {{text = '[{i Aiko}]: I will deal with you first, Aeon!', vid = 'A06_Aiko_M01_01050.sfd', bank = 'A06_VO', cue = 'A06_Aiko_M01_01050', faction = 'UEF'},}
Aiko_3 =        {{text = '[{i Aiko}]: This is Major Aiko to any UEF forces in the area! An Aeon Commander is advancing on Black Sun. I need any and all help that can be provided. Aiko out.', vid = 'A06_Aiko_M02_01157.sfd', bank = 'A06_VO', cue = 'A06_Aiko_M02_01157', faction = 'UEF'},}
Aiko_4 =        {{text = '[{i Aiko}]: The Aeon Commander is still advancing. I can\'t stop him! It\'s like he knows everything I\'m going to do. I need help here!', vid = 'A06_Aiko_M02_01061.sfd', bank = 'A06_VO', cue = 'A06_Aiko_M02_01061', faction = 'UEF'},}
Aiko_5 =        {{text = '[{i Aiko}]: Somebody help me! I\'ve lost most of my forces...Black Sun is vulnerable. I need help!\n\n', vid = 'A06_Aiko_M02_01067.sfd', bank = 'A06_VO', cue = 'A06_Aiko_M02_01067', faction = 'UEF'},}
Aiko_6 =        {{text = '[{i Aiko}]: I can\'t...no! ', vid = 'A06_Aiko_M02_01158.sfd', bank = 'A06_VO', cue = 'A06_Aiko_M02_01158', faction = 'UEF'},}
Aiko_7 =        {{text = '[{i Aiko}]: Mark my words, Black Sun will fire!', vid = 'A06_Aiko_T01_01034.sfd', bank = 'A06_VO', cue = 'A06_Aiko_T01_01034', faction = 'UEF'},}
Aiko_8 =        {{text = '[{i Aiko}]: Your fanaticism will not protect you.', vid = 'A06_Aiko_T01_01035.sfd', bank = 'A06_VO', cue = 'A06_Aiko_T01_01035', faction = 'UEF'},}
Aiko_9 =        {{text = '[{i Aiko}]: The Aeon have splintered. You are weak.', vid = 'A06_Aiko_T01_01036.sfd', bank = 'A06_VO', cue = 'A06_Aiko_T01_01036', faction = 'UEF'},}
Aiko_10 =       {{text = '[{i Aiko}]: I will defend Black Sun with my life.', vid = 'A06_Aiko_T01_01037.sfd', bank = 'A06_VO', cue = 'A06_Aiko_T01_01037', faction = 'UEF'},}
Aiko_11 =       {{text = '[{i Aiko}]: On this day, the Aeon will be destroyed.', vid = 'A06_Aiko_T01_01038.sfd', bank = 'A06_VO', cue = 'A06_Aiko_T01_01038', faction = 'UEF'},}
Aiko_12 =       {{text = '[{i Aiko}]: The UEF will triumph!', vid = 'A06_Aiko_T01_01039.sfd', bank = 'A06_VO', cue = 'A06_Aiko_T01_01039', faction = 'UEF'},}
Aiko_13 =       {{text = '[{i Aiko}]: Surrender is your only option!', vid = 'A06_Aiko_T01_01040.sfd', bank = 'A06_VO', cue = 'A06_Aiko_T01_01040', faction = 'UEF'},}
Aiko_14 =       {{text = '[{i Aiko}]: Earth will not fall to the likes of you!', vid = 'A06_Aiko_T01_01041.sfd', bank = 'A06_VO', cue = 'A06_Aiko_T01_01041', faction = 'UEF'},}
Aiko_15 =       {{text = '[{i Aiko}]: The Aeon are on them! One of the transports is going down!', vid = 'E06_Aiko_M01_0399.sfd', bank = 'E06_VO', cue = 'E06_Aiko_M01_0399', faction = 'UEF'},}
Aiko_16 =       {{text = '[{i Aiko}]: Sir, one of the transports ferrying parts for Black Sun has been shot down. It crashed on a small island to the east. You must recover the component it was carrying!  Pick it up with a transport and deliver it to Black Sun.', vid = 'E06_Aiko_M01_0391.sfd', bank = 'E06_VO', cue = 'E06_Aiko_M01_0391', faction = 'UEF'},}
Aiko_17 =       {{text = '[{i Aiko}]: Sorry for the brash introduction, Major. I am Commander Aiko. The island\'s defenses are now under your control. ', vid = 'E06_Aiko_M01_0264.sfd', bank = 'E06_VO', cue = 'E06_Aiko_M01_0264', faction = 'UEF'},}
Aiko_18 =       {{text = '[{i Aiko}]: There it is, sir! We just need to get that component back to Black Sun!', vid = 'E06_Aiko_M01_0265.sfd', bank = 'E06_VO', cue = 'E06_Aiko_M01_0265', faction = 'UEF'},}
Aiko_19 =       {{text = '[{i Aiko}]: Save Earth-- ', vid = 'E06_Aiko_M01_0266.sfd', bank = 'E06_VO', cue = 'E06_Aiko_M01_0266', faction = 'UEF'},}
Aiko_20 =       {{text = '[{i Aiko}]: Black Sun is almost ready to fire. We just need to hold out a bit longer.', vid = 'E06_Aiko_M02_0267.sfd', bank = 'E06_VO', cue = 'E06_Aiko_M02_0267', faction = 'UEF'},}
Aiko_21 =       {{text = '[{i Aiko}]: Major, we have confirmed the presence of an Aeon Commander on an island to the southeast. We need to destroy all enemy forces at that position.', vid = 'E06_Aiko_M02_0268.sfd', bank = 'E06_VO', cue = 'E06_Aiko_M02_0268', faction = 'UEF'},}
Aiko_22 =       {{text = '[{i Aiko}]: You got her, sir. We won\'t have to...wait, I\'m getting something.', vid = 'E06_Aiko_M02_0269.sfd', bank = 'E06_VO', cue = 'E06_Aiko_M02_0269', faction = 'UEF'},}
Aiko_23 =       {{text = '[{i Aiko}]: Sir, we have incoming Cybran forces!', vid = 'E06_Aiko_M02_01122.sfd', bank = 'E06_VO', cue = 'E06_Aiko_M02_01122', faction = 'UEF'},}
Aiko_24 =       {{text = '[{i Aiko}]: Sir, some Siege Assault Bots and Mobile Missile Launchers have made it through. They\'re yours to command!', vid = 'E06_Aiko_M02_0271.sfd', bank = 'E06_VO', cue = 'E06_Aiko_M02_0271', faction = 'UEF'},}
Aiko_25 =       {{text = '[{i Aiko}]: Sir, a Cybran transport is carrying Engineers toward the Black Sun Control Center. They must be stopped!', vid = 'E06_Aiko_M02_0272.sfd', bank = 'E06_VO', cue = 'E06_Aiko_M02_0272', faction = 'UEF'},}
Aiko_26 =       {{text = '[{i Aiko}]: We\'ve retaken the Control Center. Our techs are reloading the software to ensure that the Cybrans didn\'t corrupt the system.', vid = 'E06_Aiko_M02_0273.sfd', bank = 'E06_VO', cue = 'E06_Aiko_M02_0273', faction = 'UEF'},}
Aiko_27 =       {{text = '[{i Aiko}]: Black Sun has been completed. I repeat, Black Sun has been completed. Beginning its charging cycle.', vid = 'E06_Aiko_M02_0275.sfd', bank = 'E06_VO', cue = 'E06_Aiko_M02_0275', faction = 'UEF'},}
Aiko_28 =       {{text = '[{i Aiko}]: We\'re almost there...Black Sun just needs to charge before it can be fired. Defend it a bit longer.', vid = 'E06_Aiko_M03_0277.sfd', bank = 'E06_VO', cue = 'E06_Aiko_M03_0277', faction = 'UEF'},}
Aiko_29 =       {{text = '[{i Aiko}]: Black Sun is 20 percent charged, sir.', vid = 'E06_Aiko_M03_0278.sfd', bank = 'E06_VO', cue = 'E06_Aiko_M03_0278', faction = 'UEF'},}
Aiko_30 =       {{text = '[{i Aiko}]: Black Sun is at 40 percent.', vid = 'E06_Aiko_M03_0279.sfd', bank = 'E06_VO', cue = 'E06_Aiko_M03_0279', faction = 'UEF'},}
Aiko_31 =       {{text = '[{i Aiko}]: Black Sun is 60 percent charged.', vid = 'E06_Aiko_M03_01114.sfd', bank = 'E06_VO', cue = 'E06_Aiko_M03_01114', faction = 'UEF'},}
Aiko_32 =       {{text = '[{i Aiko}]: 80 percent! Black Sun is at 80 percent, sir!', vid = 'E06_Aiko_M03_01538.sfd', bank = 'E06_VO', cue = 'E06_Aiko_M03_01538', faction = 'UEF'},}
Aiko_33 =       {{text = '[{i Aiko}]: Sir, we\'re picking up something coming in from the northwest. It\'s...huge.  What? Now Arnold is landing to the north. He\'s personally leading his forces!', vid = 'E06_Aiko_M03_0280.sfd', bank = 'E06_VO', cue = 'E06_Aiko_M03_0280', faction = 'UEF'},}
Aiko_34 =       {{text = '[{i Aiko}]: It\'s at 100 percent!', vid = 'E06_Aiko_M03_01116.sfd', bank = 'E06_VO', cue = 'E06_Aiko_M03_01116', faction = 'UEF'},}
Aiko_35 =       {{text = '[{i Aiko}]: It\'s down, sir! The Aeon unit is down!', vid = 'E06_Aiko_M03_0281.sfd', bank = 'E06_VO', cue = 'E06_Aiko_M03_0281', faction = 'UEF'},}
Aiko_36 =       {{text = '[{i Aiko}]: Dammit, I don\'t need any more complications. ', vid = 'C06_Aiko_M01_00563.sfd', bank = 'C06_VO', cue = 'C06_Aiko_M01_00563', faction = 'UEF'},}
Aiko_37 =       {{text = '[{i Aiko}]: This is Captain Aiko. Black Sun is under heavy attack by Cybran and Aeon forces. Requesting assistance. I repeat, Black Sun is under attack. We need all the help we can get! Aiko out.', vid = 'C06_Aiko_M02_00579.sfd', bank = 'C06_VO', cue = 'C06_Aiko_M02_00579', faction = 'UEF'},}
Aiko_38 =       {{text = '[{i Aiko}]: I will not let you have Black Sun!', vid = 'C06_Aiko_M03_00594.sfd', bank = 'C06_VO', cue = 'C06_Aiko_M03_00594', faction = 'UEF'},}
Aiko_39 =       {{text = '[{i Aiko}]: Aaaaaaaaagh! ', vid = 'C06_Aiko_M03_00596.sfd', bank = 'C06_VO', cue = 'C06_Aiko_M03_00596', faction = 'UEF'},}
Aiko_40 =       {{text = '[{i Aiko}]: The UEF will finally end the Infinite War!', vid = 'C06_Aiko_T01_00543.sfd', bank = 'C06_VO', cue = 'C06_Aiko_T01_00543', faction = 'UEF'},}
Aiko_41 =       {{text = '[{i Aiko}]: I have the might of the UEF behind me!', vid = 'C06_Aiko_T01_00544.sfd', bank = 'C06_VO', cue = 'C06_Aiko_T01_00544', faction = 'UEF'},}
Aiko_42 =       {{text = '[{i Aiko}]: I will defend this Facility with my life!', vid = 'C06_Aiko_T01_00545.sfd', bank = 'C06_VO', cue = 'C06_Aiko_T01_00545', faction = 'UEF'},}
Aiko_43 =       {{text = '[{i Aiko}]: Black Sun will destroy you and every other madman.', vid = 'C06_Aiko_T01_00546.sfd', bank = 'C06_VO', cue = 'C06_Aiko_T01_00546', faction = 'UEF'},}
Aiko_44 =       {{text = '[{i Aiko}]: This island shall be your tomb.', vid = 'C06_Aiko_T01_00547.sfd', bank = 'C06_VO', cue = 'C06_Aiko_T01_00547', faction = 'UEF'},}
Aiko_45 =       {{text = '[{i Aiko}]: The UEF will triumph!', vid = 'C06_Aiko_T01_00548.sfd', bank = 'C06_VO', cue = 'C06_Aiko_T01_00548', faction = 'UEF'},}
Aiko_46 =       {{text = '[{i Aiko}]: There is no chance of surrender. This is a fight to the end.', vid = 'C06_Aiko_T01_00549.sfd', bank = 'C06_VO', cue = 'C06_Aiko_T01_00549', faction = 'UEF'},}
Aiko_47 =       {{text = '[{i Aiko}]: The UEF will unite the galaxy...no matter the cost.', vid = 'C06_Aiko_T01_00550.sfd', bank = 'C06_VO', cue = 'C06_Aiko_T01_00550', faction = 'UEF'},}

---- Aeon Amalia
Amalia_1  =     {{text = '[{i Amalia}]: Before I was captured, I managed to place a Trojan Horse in QAI\'s sub-systems. Unless they find it, I may be able to use it to help you. Amalia out.', vid = 'X05_Amalia_M01_03821.sfd', bank = 'X05_VO', cue = 'X05_Amalia_M01_03821', faction = 'Aeon'},}
Amalia_2  =     {{text = '[{i Amalia}]: Thank you, Commander. I will remain here and assist you as I am able. Amalia out.', vid = 'X05_Amalia_M01_03822.sfd', bank = 'X05_VO', cue = 'X05_Amalia_M01_03822', faction = 'Aeon'},}
Amalia_3  =     {{text = '[{i Amalia}]: Commander, I am under fire. Please assist. Amalia out.', vid = 'X05_Amalia_M02_03848.sfd', bank = 'X05_VO', cue = 'X05_Amalia_M02_03848', faction = 'Aeon'},}
Amalia_4  =     {{text = '[{i Amalia}]: I need assistance, Commander! Amalia out.', vid = 'X05_Amalia_M02_03849.sfd', bank = 'X05_VO', cue = 'X05_Amalia_M02_03849', faction = 'Aeon'},} 
Amalia_5  =     {{text = '[{i Amalia}]: My ACU is seriously damaged, Commander!', vid = 'X05_Amalia_M02_03850.sfd', bank = 'X05_VO', cue = 'X05_Amalia_M02_03850', faction = 'Aeon'},}
Amalia_6  =     {{text = '[{i Amalia}]: I\'m in trouble! Requesting aid!', vid = 'X05_Amalia_M02_03851.sfd', bank = 'X05_VO', cue = 'X05_Amalia_M02_03851', faction = 'Aeon'},}
Amalia_7  =     {{text = '[{i Amalia}]: Remember that I fought honorably!', vid = 'X05_Amalia_M02_03852.sfd', bank = 'X05_VO', cue = 'X05_Amalia_M02_03852', faction = 'Aeon'},}
Amalia_8  =     {{text = '[{i Amalia}]: Champion, the Trojan Horse is still operational. I have marked a Science Facility for capture; if you seize it, the Colossus next to the Facility will be yours to command. Amalia out.', vid = 'X05_Amalia_M03_04055.sfd', bank = 'X05_VO', cue = 'X05_Amalia_M03_04055', faction = 'Aeon'},}
Amalia_9  =     {{text = '[{i Amalia}]: The Science Facility still needs to be captured, Champion. Amalia out.', vid = 'X05_Amalia_M03_03891.sfd', bank = 'X05_VO', cue = 'X05_Amalia_M03_03891', faction = 'Aeon'},}
Amalia_10 =     {{text = '[{i Amalia}]: A free Colossus awaits you if can capture the Science Facility, Champion. Amalia out.', vid = 'X05_Amalia_M03_03892.sfd', bank = 'X05_VO', cue = 'X05_Amalia_M03_03892', faction = 'Aeon'},}
Amalia_11 =     {{text = '[{i Amalia}]: The Colossus is yours, Champion! Use it to wreak havoc upon your enemies! Glory to the Princess! Amalia out.', vid = 'X05_Amalia_M03_03893.sfd', bank = 'X05_VO', cue = 'X05_Amalia_M03_03893', faction = 'Aeon'},}

---- Uef Arnold
Arnold_1 =      {{text = '[{i Arnold}]: Now you will pay for your crimes against humanity!', vid = 'A03_Arnold_M01_00917.sfd', bank = 'A03_VO', cue = 'A03_Arnold_M01_00917', faction = 'UEF'},}
Arnold_2 =      {{text = '[{i Arnold}]: The Aeon\'s days are numbered. The UEF will emerge victorious.', vid = 'A03_Arnold_M01_00859.sfd', bank = 'A03_VO', cue = 'A03_Arnold_M01_00859', faction = 'UEF'},}
Arnold_3 =      {{text = '[{i Arnold}]: Time to take off the gloves.', vid = 'A03_Arnold_M01_00861.sfd', bank = 'A03_VO', cue = 'A03_Arnold_M01_00861', faction = 'UEF'},}
Arnold_4 =      {{text = '[{i Arnold}]: I\'ll be damned. Maybe you ain\'t a pansy after all.', vid = 'A03_Arnold_M01_00863.sfd', bank = 'A03_VO', cue = 'A03_Arnold_M01_00863', faction = 'UEF'},}
Arnold_5 =      {{text = '[{i Arnold}]: You will never conquer Matar.', vid = 'A03_Arnold_M03_00879.sfd', bank = 'A03_VO', cue = 'A03_Arnold_M03_00879', faction = 'UEF'},}
Arnold_6 =      {{text = '[{i Arnold}]: Ah, blast it. You fried my logic array...I thought that was impossible. I can\'t move...trapped...never take me alive...', vid = 'A03_Arnold_M03_00885.sfd', bank = 'A03_VO', cue = 'A03_Arnold_M03_00885', faction = 'UEF'},}
Arnold_7 =      {{text = '[{i Arnold}]: Yes...now I understand...', vid = 'A03_Arnold_M03_00887.sfd', bank = 'A03_VO', cue = 'A03_Arnold_M03_00887', faction = 'UEF'},}
Arnold_8 =      {{text = '[{i Arnold}]: You will never conquer Matar.', vid = 'A03_Arnold_T01_00842.sfd', bank = 'A03_VO', cue = 'A03_Arnold_T01_00842', faction = 'UEF'},}
Arnold_9 =      {{text = '[{i Arnold}]: The UEF will never surrender to the Aeon.', vid = 'A03_Arnold_T01_00843.sfd', bank = 'A03_VO', cue = 'A03_Arnold_T01_00843', faction = 'UEF'},}
Arnold_10 =     {{text = '[{i Arnold}]: You are a fanatic.', vid = 'A03_Arnold_T01_00844.sfd', bank = 'A03_VO', cue = 'A03_Arnold_T01_00844', faction = 'UEF'},}
Arnold_11 =     {{text = '[{i Arnold}]: The end game is approaching. You will lose.', vid = 'A03_Arnold_T01_00845.sfd', bank = 'A03_VO', cue = 'A03_Arnold_T01_00845', faction = 'UEF'},}
Arnold_12 =     {{text = '[{i Arnold}]: The UEF will save the galaxy from you fanatics.', vid = 'A03_Arnold_T01_00846.sfd', bank = 'A03_VO', cue = 'A03_Arnold_T01_00846', faction = 'UEF'},}
Arnold_13 =     {{text = '[{i Arnold}]: I will restore order to the galaxy.', vid = 'A03_Arnold_T01_00847.sfd', bank = 'A03_VO', cue = 'A03_Arnold_T01_00847', faction = 'UEF'},}
Arnold_14 =     {{text = '[{i Arnold}]: You\'re not winning this fight.', vid = 'A03_Arnold_T01_00848.sfd', bank = 'A03_VO', cue = 'A03_Arnold_T01_00848', faction = 'UEF'},}
Arnold_15 =     {{text = '[{i Arnold}]: I\'m gonna add your head to my trophy case.', vid = 'A03_Arnold_T01_00849.sfd', bank = 'A03_VO', cue = 'A03_Arnold_T01_00849', faction = 'UEF'},}
Arnold_16 =     {{text = '[{i Arnold}]: Listen up, rookie. I know what they taught you at the academy regarding Cybran tactics, but you need to forget all that crap. The Cybrans are no longer running from a stand-up fight, and they\'re not afraid to come at you with guns blazing. Your predecessor forgot that and paid the price.', vid = 'E01_Arnold_M01_0004.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M01_0004', faction = 'UEF'},}
Arnold_17 =     {{text = '[{i Arnold}]: Start by building some Power Generators and Mass Extractors.', vid = 'E01_Arnold_M01_0005.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M01_0005', faction = 'UEF'},}
Arnold_18 =     {{text = '[{i Arnold}]: Get movin\' on those Power Generators, rookie.', vid = 'E01_Arnold_M01_00672.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M01_00672', faction = 'UEF'},}
Arnold_19 =     {{text = '[{i Arnold}]: Without any power, you\'re not gonna be able to fight back.', vid = 'E01_Arnold_M01_01130.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M01_01130', faction = 'UEF'},}
Arnold_20 =     {{text = '[{i Arnold}]: It\'s time to get into the fight! Build some Mass Extractors!', vid = 'E01_Arnold_M01_01552.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M01_01552', faction = 'UEF'},}
Arnold_21 =     {{text = '[{i Arnold}]: You\'re gonna need Mass if you want to survive, rookie.', vid = 'E01_Arnold_M01_01553.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M01_01553', faction = 'UEF'},}
Arnold_22 =     {{text = '[{i Arnold}]: Now then, build a Land Factory to take advantage of all that power and mass. Get it done.', vid = 'E01_Arnold_M02_0008.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M02_0008', faction = 'UEF'},}
Arnold_23 =     {{text = '[{i Arnold}]: Get that Land Factory built. We got a war to wage.', vid = 'E01_Arnold_M02_00675.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M02_00675', faction = 'UEF'},}
Arnold_24 =     {{text = '[{i Arnold}]: You gonna get that Land Factory built anytime soon?', vid = 'E01_Arnold_M02_01132.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M02_01132', faction = 'UEF'},}
Arnold_25 =     {{text = '[{i Arnold}]: Okay, now build a bunch of \'MA12 Striker\' Medium Tanks. Move \'em around and get used to the controls.', vid = 'E01_Arnold_M03_0009.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M03_0009', faction = 'UEF'},}
Arnold_26 =     {{text = '[{i Arnold}]: Get some tanks built. You don\'t want to be empty-handed if that Cybran comes by. ', vid = 'E01_Arnold_M03_00677.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M03_00677', faction = 'UEF'},}
Arnold_27 =     {{text = '[{i Arnold}]: I hear there\'s a war on. Why don\'t you help out? ', vid = 'E01_Arnold_M03_01133.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M03_01133', faction = 'UEF'},}
Arnold_28 =     {{text = '[{i Arnold}]: Good, now take your tanks and destroy that Cybran radar. There\'s a couple units defending it, but your tanks should make short work of them.', vid = 'E01_Arnold_M04_0010.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M04_0010', faction = 'UEF'},}
Arnold_29 =     {{text = '[{i Arnold}]: That Cybran radar isn\'t going to destroy itself, rookie. ', vid = 'E01_Arnold_M04_00679.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M04_00679', faction = 'UEF'},}
Arnold_30 =     {{text = '[{i Arnold}]: Get a move on, rookie! I need that radar down ASAP!', vid = 'E01_Arnold_M04_01134.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M04_01134', faction = 'UEF'},}
Arnold_31 =     {{text = '[{i Arnold}]: Nice work. Always build as quickly as possible; it\'ll save your backside if you gate into a hot LZ. Get some additional units online and destroy that air base to the southwest. There\'s some Cybran bombers prowling around, so take a few Mobile Anti-Air with you.', vid = 'E01_Arnold_M05_0011.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M05_0011', faction = 'UEF'},}
Arnold_32 =     {{text = '[{i Arnold}]: Not bad, rookie, but don\'t let it go to your head. Make one mistake, and the Cybrans will tear you apart.', vid = 'E01_Arnold_M05_0014.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M05_0014', faction = 'UEF'},}
Arnold_33 =     {{text = '[{i Arnold}]: Blast, there\'s another Cybran Commander. I\'m gonna deal with him. The rest of this OP is yours. I\'m leaving you with some reinforcements. They\'re a few steps above your pay grade, but that doesn\'t mean you can\'t play with \'em.', vid = 'E01_Arnold_M05_01533.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M05_01533', faction = 'UEF'},}
Arnold_34 =     {{text = '[{i Arnold}]: Ignore his psycho-babble.', vid = 'E01_Arnold_M05_01532.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M05_01532', faction = 'UEF'},}
Arnold_35 =     {{text = '[{i Arnold}]: You waitin\' for that Cybran air base to fall apart on its own? ', vid = 'E01_Arnold_M05_00681.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M05_00681', faction = 'UEF'},}
Arnold_36 =     {{text = '[{i Arnold}]: Did you forget why we\'re here? Flatten that air base, pronto!', vid = 'E01_Arnold_M05_01135.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M05_01135', faction = 'UEF'},}
Arnold_37 =     {{text = '[{i Arnold}]: What are you doing, takin\' a nap? Get down there and take out that defensive line!', vid = 'E01_Arnold_M06_00683.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M06_00683', faction = 'UEF'},}
Arnold_38 =     {{text = '[{i Arnold}]: I gave you an order to take out that defensive line, rookie. Jump to it!', vid = 'E01_Arnold_M06_01136.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M06_01136', faction = 'UEF'},}
Arnold_39 =     {{text = '[{i Arnold}]: Here\'s your next assignment. You need to take out the Cybran Commander. I don\'t care how you do it, just as long as it gets done.\n', vid = 'E01_Arnold_M07_0016.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M07_0016', faction = 'UEF'},}
Arnold_40 =     {{text = '[{i Arnold}]: I like it. Go capture one of those Tech 2 Power Generators.', vid = 'E01_Arnold_M07_01137.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M07_01137', faction = 'UEF'},}
Arnold_41 =     {{text = '[{i Arnold}]: That\'s what we needed to hear. Round up some ground forces and destroy those Anti-Air Towers, rookie. Once they\'re down, bomb the Cybrans back to the stone age. You should also look at getting a second base off the ground. There\'s a lot of Mass where the Cybran defensive line was positioned. Check it out.', vid = 'E01_Arnold_M07_0017.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M07_0017', faction = 'UEF'},}
Arnold_42 =     {{text = '[{i Arnold}]: Did you forget why we\'re here? You need to recapture the R&D Facility to the south.', vid = 'E01_Arnold_M07_0018.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M07_0018', faction = 'UEF'},}
Arnold_43 =     {{text = '[{i Arnold}]: Looks like the Cybran brass ain\'t too happy with you. Don\'t worry, I\'ll take care of \'em. You just worry about that other Commander.', vid = 'E01_Arnold_M07_01138.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M07_01138', faction = 'UEF'},}
Arnold_44 =     {{text = '[{i Arnold}]: Shut up, Thompson! You eggheads get back to what you were doing. We\'ll worry about Sweeney.', vid = 'E01_Arnold_M07_0019.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M07_0019', faction = 'UEF'},}
Arnold_45 =     {{text = '[{i Arnold}]: You actually took him down. Maybe you are cut out for this.', vid = 'E01_Arnold_M07_0020.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M07_0020', faction = 'UEF'},}
Arnold_46 =     {{text = '[{i Arnold}]: The R&D Facility is still under Cybran control. Get some forces down there ASAP. ', vid = 'E01_Arnold_M07_00685.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M07_00685', faction = 'UEF'},}
Arnold_47 =     {{text = '[{i Arnold}]: We don\'t want the Cybrans poking around that R&D Facility. Recapture it as soon as possible. ', vid = 'E01_Arnold_M07_01455.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M07_01455', faction = 'UEF'},}
Arnold_48 =     {{text = '[{i Arnold}]: I\'m not hearing any Cybran ACUs cooking off, rookie.', vid = 'E01_Arnold_M07_00687.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M07_00687', faction = 'UEF'},}
Arnold_49 =     {{text = '[{i Arnold}]: That Cybran Commander is still kicking. Fix that.', vid = 'E01_Arnold_M07_01456.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M07_01456', faction = 'UEF'},}
Arnold_50 =     {{text = '[{i Arnold}]: You gonna capture that Tech 2 Generator anytime soon, rookie?', vid = 'E01_Arnold_M07_00689.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M07_00689', faction = 'UEF'},}
Arnold_51 =     {{text = '[{i Arnold}]: Director Thompson is still waiting on you to capture that Tech 2 Generator. Let\'s not disappoint him.', vid = 'E01_Arnold_M07_01457.sfd', bank = 'E01_VO', cue = 'E01_Arnold_M07_01457', faction = 'UEF'},}
Arnold_52 =     {{text = '[{i Arnold}]: Use your transport to move either your Armored Command Unit or an Engineer to Lima. Scans are showing a lot of Aeon activity between you and the facility, so it\'s going to be hot. Clear out the enemy before you try and reach Lima. ', vid = 'E02_Arnold_M01_0045.sfd', bank = 'E02_VO', cue = 'E02_Arnold_M01_0045', faction = 'UEF'},}
Arnold_53 =     {{text = '[{i Arnold}]: You\'re on your own, rookie.', vid = 'E02_Arnold_M01_0046.sfd', bank = 'E02_VO', cue = 'E02_Arnold_M01_0046', faction = 'UEF'},}
Arnold_54 =     {{text = '[{i Arnold}]: Wait, what the...those eggheads dropped me on the wrong side of the continent! I\'m no help, EarthCom, it\'ll be hours before I can get there. Tell Clarke the rookie will have to do it on his own!', vid = 'E02_Arnold_M01_0047.sfd', bank = 'E02_VO', cue = 'E02_Arnold_M01_0047', faction = 'UEF'},}
Arnold_55 =     {{text = '[{i Arnold}]: Don\'t listen to her!', vid = 'E02_Arnold_M01_0048.sfd', bank = 'E02_VO', cue = 'E02_Arnold_M01_0048', faction = 'UEF'},}
Arnold_56 =     {{text = '[{i Arnold}]: What are you doing? Get over there and help them! We\'re going to lose that whole Facility!', vid = 'E02_Arnold_M01_0049.sfd', bank = 'E02_VO', cue = 'E02_Arnold_M01_0049', faction = 'UEF'},}
Arnold_57 =     {{text = '[{i Arnold}]: Looks like you got all the patrols. You actually impressed me with that one, rookie.', vid = 'E02_Arnold_M01_0050.sfd', bank = 'E02_VO', cue = 'E02_Arnold_M01_0050', faction = 'UEF'},}
Arnold_58 =     {{text = '[{i Arnold}]: Nice job defending the Station, rookie.', vid = 'E02_Arnold_M02_0051.sfd', bank = 'E02_VO', cue = 'E02_Arnold_M02_0051', faction = 'UEF'},}
Arnold_59 =     {{text = '[{i Arnold}]: After all this, we\'re abandoning the planet to those monsters?', vid = 'E02_Arnold_M03_0052.sfd', bank = 'E02_VO', cue = 'E02_Arnold_M03_0052', faction = 'UEF'},}
Arnold_60 =     {{text = '[{i Arnold}]: Satellite feeds of the Aeon base reveal a flaw: The eastern part of Min\'s base is defended with shields. Use ground units to take out her Power Generators and then hit her head-on once the shields are down.', vid = 'E02_Arnold_M04_0053.sfd', bank = 'E02_VO', cue = 'E02_Arnold_M04_0053', faction = 'UEF'},}
Arnold_61 =     {{text = '[{i Arnold}]: Thorough, ain\'t ya?', vid = 'E02_Arnold_M04_0054.sfd', bank = 'E02_VO', cue = 'E02_Arnold_M04_0054', faction = 'UEF'},}
Arnold_62 =     {{text = '[{i Arnold}]: Great job, Lieutenant. The first round is on me!', vid = 'E02_Arnold_M04_0055.sfd', bank = 'E02_VO', cue = 'E02_Arnold_M04_0055', faction = 'UEF'},}
Arnold_63 =     {{text = '[{i Arnold}]: Keep at it, you\'ll bring that Aeon down soon enough. Arnold out. ', vid = 'E02_Arnold_M04_00697.sfd', bank = 'E02_VO', cue = 'E02_Arnold_M04_00697', faction = 'UEF'},}
Arnold_64 =     {{text = '[{i Arnold}]: If you\'re having trouble with that Aeon, try a different unit combination. That might expose a flaw in her defenses. Arnold out.', vid = 'E02_Arnold_M04_01097.sfd', bank = 'E02_VO', cue = 'E02_Arnold_M04_01097', faction = 'UEF'},}
Arnold_65 =     {{text = '[{i Arnold}]: I thought they fixed the Gates! Get an operational base up and running. We\'re gonna have to wing this one.', vid = 'E03_Arnold_M01_0106.sfd', bank = 'E03_VO', cue = 'E03_Arnold_M01_0106', faction = 'UEF'},}
Arnold_66 =     {{text = '[{i Arnold}]: I\'ve been discovered by an Aeon scout. Things are gonna get really hot around here.', vid = 'E03_Arnold_M01_0107.sfd', bank = 'E03_VO', cue = 'E03_Arnold_M01_0107', faction = 'UEF'},}
Arnold_67 =     {{text = '[{i Arnold}]: Holy...EarthCom, I\'ve got incoming like I\'ve never seen before...it\'s a wall of units. They\'re on me...', vid = 'E03_Arnold_M01_0108.sfd', bank = 'E03_VO', cue = 'E03_Arnold_M01_0108', faction = 'UEF'},}
Arnold_68 =     {{text = '[{i Arnold}]: ...EarthCom, I\'ve suffered heavy losses. I\'m rebuilding and have scouts out, but if they hit me like that again, they\'ll roll right over me. Some of the units were marked with an unknown insignia. Transmitting image.', vid = 'E03_Arnold_M01_0109.sfd', bank = 'E03_VO', cue = 'E03_Arnold_M01_0109', faction = 'UEF'},}
Arnold_69 =     {{text = '[{i Arnold}]: Of course I can verify it! I just sent you a blasted picture!', vid = 'E03_Arnold_M01_0110.sfd', bank = 'E03_VO', cue = 'E03_Arnold_M01_0110', faction = 'UEF'},}
Arnold_70 =     {{text = '[{i Arnold}]: Fantastic.', vid = 'E03_Arnold_M01_0111.sfd', bank = 'E03_VO', cue = 'E03_Arnold_M01_0111', faction = 'UEF'},}
Arnold_71 =     {{text = '[{i Arnold}]: EarthCom, they\'re hitting me again! I don\'t think I can hold them off.', vid = 'E03_Arnold_M01_0112.sfd', bank = 'E03_VO', cue = 'E03_Arnold_M01_0112', faction = 'UEF'},}
Arnold_72 =     {{text = '[{i Arnold}]: Can\'t...too many of them...no where to--', vid = 'E03_Arnold_M01_0113.sfd', bank = 'E03_VO', cue = 'E03_Arnold_M01_0113', faction = 'UEF'},}
Arnold_73 =     {{text = '[{i Arnold}]: ...nothing he can do...finished...', vid = 'E03_Arnold_M01_01524.sfd', bank = 'E03_VO', cue = 'E03_Arnold_M01_01524', faction = 'UEF'},}
Arnold_74 =     {{text = '[{i Arnold}]: Aw, hell!', vid = 'E03_Arnold_M01_0114.sfd', bank = 'E03_VO', cue = 'E03_Arnold_M01_0114', faction = 'UEF'},}
--  Aeon from here
Arnold_101 =    {{text = '[{i Arnold}]: I was liberated on Matar. Shown the error of my ways.', vid = 'E05_Arnold_M01_0206.sfd', bank = 'E05_VO', cue = 'E05_Arnold_M01_0206', faction = 'Aeon'},}
Arnold_102 =    {{text = '[{i Arnold}]: The UEF Commander is dead. I have Black Sun, Princess. What are your orders?', vid = 'A06_Arnold_M03_01074.sfd', bank = 'A06_VO', cue = 'A06_Arnold_M03_01074', faction = 'Aeon'},}
Arnold_103 =    {{text = '[{i Arnold}]: I have never served you, Marxon. The Princess is the one who set me free, showed me the UEF lies. She has always commanded me.', vid = 'A06_Arnold_M03_01076.sfd', bank = 'A06_VO', cue = 'A06_Arnold_M03_01076', faction = 'Aeon'},}
Arnold_104 =    {{text = '[{i Arnold}]: You showed me the light, Princess. For that I am grateful-- ', vid = 'A06_Arnold_M03_01079.sfd', bank = 'A06_VO', cue = 'A06_Arnold_M03_01079', faction = 'Aeon'},}
Arnold_105 =    {{text = '[{i Arnold}]: You\'re only delaying the inevitable. Our strength is endless.', vid = 'E05_Arnold_M01_01143.sfd', bank = 'E05_VO', cue = 'E05_Arnold_M01_01143', faction = 'Aeon'},}
Arnold_106 =    {{text = '[{i Arnold}]: Your defenses will never stop us. The Aeon forces are marching across the galaxy.', vid = 'E05_Arnold_M01_0204.sfd', bank = 'E05_VO', cue = 'E05_Arnold_M01_0204', faction = 'Aeon'},}
Arnold_107 =    {{text = '[{i Arnold}]: Hello, General. ', vid = 'E05_Arnold_M01_0205.sfd', bank = 'E05_VO', cue = 'E05_Arnold_M01_0205', faction = 'Aeon'},}
Arnold_108 =    {{text = '[{i Arnold}]: It is you who\'s been brainwashed, believing every UEF lie that comes your way. You are the monster.', vid = 'E05_Arnold_M01_0207.sfd', bank = 'E05_VO', cue = 'E05_Arnold_M01_0207', faction = 'Aeon'},}
Arnold_109 =    {{text = '[{i Arnold}]: Your faith in Black Sun is misplaced. It will never fire. I will see to that.', vid = 'E05_Arnold_M01_0208.sfd', bank = 'E05_VO', cue = 'E05_Arnold_M01_0208', faction = 'Aeon'},}
Arnold_110 =    {{text = '[{i Arnold}]: I\'ll see you on Earth. This isn\'t finished.', vid = 'E05_Arnold_M03_0209.sfd', bank = 'E05_VO', cue = 'E05_Arnold_M03_0209', faction = 'Aeon'},}
Arnold_111 =    {{text = '[{i Arnold}]: I have seen The Way. Everything you believe is a lie.', vid = 'E05_Arnold_T01_0210.sfd', bank = 'E05_VO', cue = 'E05_Arnold_T01_0210', faction = 'Aeon'},}
Arnold_112 =    {{text = '[{i Arnold}]: Soon the Aeon will have purged the non-believers from the galaxy. There will be peace.', vid = 'E05_Arnold_T01_0211.sfd', bank = 'E05_VO', cue = 'E05_Arnold_T01_0211', faction = 'Aeon'},}
Arnold_113 =    {{text = '[{i Arnold}]: You\'re gonna have to do better than that, Captain.', vid = 'E05_Arnold_T01_0212.sfd', bank = 'E05_VO', cue = 'E05_Arnold_T01_0212', faction = 'Aeon'},}
Arnold_114 =    {{text = '[{i Arnold}]: The Aeon Illuminate is the truth.', vid = 'E05_Arnold_T01_0213.sfd', bank = 'E05_VO', cue = 'E05_Arnold_T01_0213', faction = 'Aeon'},}
Arnold_115 =    {{text = '[{i Arnold}]: Your fate is sealed.', vid = 'E05_Arnold_T01_0214.sfd', bank = 'E05_VO', cue = 'E05_Arnold_T01_0214', faction = 'Aeon'},}
Arnold_116 =    {{text = '[{i Arnold}]: Marxon will cleanse the galaxy.', vid = 'E05_Arnold_T01_0215.sfd', bank = 'E05_VO', cue = 'E05_Arnold_T01_0215', faction = 'Aeon'},}
Arnold_117 =    {{text = '[{i Arnold}]: General Clarke has betrayed you. She is nothing more than a warmonger and liar.', vid = 'E05_Arnold_T01_0216.sfd', bank = 'E05_VO', cue = 'E05_Arnold_T01_0216', faction = 'Aeon'},}
Arnold_118 =    {{text = '[{i Arnold}]: It is time to end this.', vid = 'E05_Arnold_T01_0217.sfd', bank = 'E05_VO', cue = 'E05_Arnold_T01_0217', faction = 'Aeon'},}
Arnold_119 =    {{text = '[{i Arnold}]: The Avatar-of-War demands vengeance against all who oppose the Aeon. I will grind your bones into dust and then turn my wrath upon all the people of Earth. They will be cleansed.', vid = 'E06_Arnold_M03_0282.sfd', bank = 'E06_VO', cue = 'E06_Arnold_M03_0282', faction = 'Aeon'},}
Arnold_120 =    {{text = '[{i Arnold}]: This changes nothing. We will rise again!', vid = 'E06_Arnold_M03_0284.sfd', bank = 'E06_VO', cue = 'E06_Arnold_M03_0284', faction = 'Aeon'},}
Arnold_121 =    {{text = '[{i Arnold}]: Black Sun will kill millions of innocents. Can you live with that blood on your hands?', vid = 'E06_Arnold_T01_0285.sfd', bank = 'E06_VO', cue = 'E06_Arnold_T01_0285', faction = 'Aeon'},}
Arnold_122 =    {{text = '[{i Arnold}]: You will never defeat the Aeon Illuminate.', vid = 'E06_Arnold_T01_0286.sfd', bank = 'E06_VO', cue = 'E06_Arnold_T01_0286', faction = 'Aeon'},}
Arnold_123 =    {{text = '[{i Arnold}]: The Avatar-of-War has seen your defeat.', vid = 'E06_Arnold_T01_0287.sfd', bank = 'E06_VO', cue = 'E06_Arnold_T01_0287', faction = 'Aeon'},}
Arnold_124 =    {{text = '[{i Arnold}]: Every soul on Earth shall be cleansed by my hand.', vid = 'E06_Arnold_T01_0288.sfd', bank = 'E06_VO', cue = 'E06_Arnold_T01_0288', faction = 'Aeon'},}
Arnold_125 =    {{text = '[{i Arnold}]: Your lies and secrets have been exposed for all to see.', vid = 'E06_Arnold_T01_0289.sfd', bank = 'E06_VO', cue = 'E06_Arnold_T01_0289', faction = 'Aeon'},}
Arnold_126 =    {{text = '[{i Arnold}]: Surrender now, and I shall offer you a merciful death.', vid = 'E06_Arnold_T01_0290.sfd', bank = 'E06_VO', cue = 'E06_Arnold_T01_0290', faction = 'Aeon'},}
Arnold_127 =    {{text = '[{i Arnold}]: Impressive.', vid = 'E06_Arnold_T01_0291.sfd', bank = 'E06_VO', cue = 'E06_Arnold_T01_0291', faction = 'Aeon'},}
Arnold_128 =    {{text = '[{i Arnold}]: Why do you continue to struggle?', vid = 'E06_Arnold_T01_0292.sfd', bank = 'E06_VO', cue = 'E06_Arnold_T01_0292', faction = 'Aeon'},}
Arnold_129 =    {{text = '[{i Arnold}]: So the Cybrans have decided to enter the fray. It matters not. Soon your kind will be destroyed.', vid = 'C06_Arnold_M01_00562.sfd', bank = 'C06_VO', cue = 'C06_Arnold_M01_00562', faction = 'Aeon'},}
Arnold_130 =    {{text = '[{i Arnold}]: That changes nothing. Our victory was determined long ago.', vid = 'C06_Arnold_M01_00571.sfd', bank = 'C06_VO', cue = 'C06_Arnold_M01_00571', faction = 'Aeon'},}
Arnold_131 =    {{text = '[{i Arnold}]: Princess!', vid = 'C06_Arnold_M03_01106.sfd', bank = 'C06_VO', cue = 'C06_Arnold_M03_01106', faction = 'Aeon'},}
Arnold_132 =    {{text = '[{i Arnold}]: There is no stopping the Aeon.', vid = 'C06_Arnold_T01_00551.sfd', bank = 'C06_VO', cue = 'C06_Arnold_T01_00551', faction = 'Aeon'},}
Arnold_133 =    {{text = '[{i Arnold}]: Once the UEF falls, the Cybrans are next.', vid = 'C06_Arnold_T01_00552.sfd', bank = 'C06_VO', cue = 'C06_Arnold_T01_00552', faction = 'Aeon'},}
Arnold_134 =    {{text = '[{i Arnold}]: You are an abomination.', vid = 'C06_Arnold_T01_00553.sfd', bank = 'C06_VO', cue = 'C06_Arnold_T01_00553', faction = 'Aeon'},}
Arnold_135 =    {{text = '[{i Arnold}]: The UEF will never fire that weapon.', vid = 'C06_Arnold_T01_00554.sfd', bank = 'C06_VO', cue = 'C06_Arnold_T01_00554', faction = 'Aeon'},}
Arnold_136 =    {{text = '[{i Arnold}]: I will exterminate the Cybrans myself.', vid = 'C06_Arnold_T01_00555.sfd', bank = 'C06_VO', cue = 'C06_Arnold_T01_00555', faction = 'Aeon'},}
Arnold_137 =    {{text = '[{i Arnold}]: The Avatar-of-War demands Earth be cleansed.', vid = 'C06_Arnold_T01_00556.sfd', bank = 'C06_VO', cue = 'C06_Arnold_T01_00556', faction = 'Aeon'},}
Arnold_138 =    {{text = '[{i Arnold}]: Earth is ours!', vid = 'C06_Arnold_T01_00557.sfd', bank = 'C06_VO', cue = 'C06_Arnold_T01_00557', faction = 'Aeon'},}
Arnold_139 =    {{text = '[{i Arnold}]: You will soon be extinct.', vid = 'C06_Arnold_T01_00558.sfd', bank = 'C06_VO', cue = 'C06_Arnold_T01_00558', faction = 'Aeon'},}

-- Aeon Ariel
Ariel_1 =        {{text = '[{i Ariel}]: The UEF stands on the brink of defeat. That is the Avatar\'s doing, Princess, not yours. I stand with Marxon and will destroy any who threaten him.', vid = 'A05_Ariel_M01_00957.sfd', bank = 'A05_VO', cue = 'A05_Ariel_M01_00957', faction = 'Aeon'},}
Ariel_2 =        {{text = '[{i Ariel}]: Let us see how well the Champion fights. ', vid = 'A05_Ariel_M01_00968.sfd', bank = 'A05_VO', cue = 'A05_Ariel_M01_00968', faction = 'Aeon'},}
Ariel_3 =        {{text = '[{i Ariel}]: Hmmm, well enough, I suppose. But how will she fare against strategic missiles? ', vid = 'A05_Ariel_M01_00971.sfd', bank = 'A05_VO', cue = 'A05_Ariel_M01_00971', faction = 'Aeon'},}
Ariel_4 =        {{text = '[{i Ariel}]: It is time to end this. ', vid = 'A05_Ariel_M01_00974.sfd', bank = 'A05_VO', cue = 'A05_Ariel_M01_00974', faction = 'Aeon'},}
Ariel_5 =        {{text = '[{i Ariel}]: I will have to do this the traditional way.', vid = 'A05_Ariel_M01_00976.sfd', bank = 'A05_VO', cue = 'A05_Ariel_M01_00976', faction = 'Aeon'},}
Ariel_6 =        {{text = '[{i Ariel}]: Avatar Marxon will win this war. Not you.', vid = 'A05_Ariel_M03_01005.sfd', bank = 'A05_VO', cue = 'A05_Ariel_M03_01005', faction = 'Aeon'},}
Ariel_7 =        {{text = '[{i Ariel}]: No. We will have conquered our enemies and shown them the error of their ways. The galaxy will finally be pure.', vid = 'A05_Ariel_M03_01007.sfd', bank = 'A05_VO', cue = 'A05_Ariel_M03_01007', faction = 'Aeon'},}
Ariel_8 =        {{text = '[{i Ariel}]: I will now kill your Champion, Princess. Your cause is lost.', vid = 'A05_Ariel_M03_01009.sfd', bank = 'A05_VO', cue = 'A05_Ariel_M03_01009', faction = 'Aeon'},}
Ariel_9 =        {{text = '[{i Ariel}]: The Avatar will reward me when he hears your Champion has fallen.', vid = 'A05_Ariel_M03_01011.sfd', bank = 'A05_VO', cue = 'A05_Ariel_M03_01011', faction = 'Aeon'},}
Ariel_10 =       {{text = '[{i Ariel}]: This isn\'t over, Princess.', vid = 'A05_Ariel_M03_01155.sfd', bank = 'A05_VO', cue = 'A05_Ariel_M03_01155', faction = 'Aeon'},}
Ariel_11 =       {{text = '[{i Ariel}]: Avatar! ', vid = 'A05_Ariel_M03_01013.sfd', bank = 'A05_VO', cue = 'A05_Ariel_M03_01013', faction = 'Aeon'},}
Ariel_12 =       {{text = '[{i Ariel}]: The Avatar will bring peace to the galaxy.', vid = 'A05_Ariel_T01_00938.sfd', bank = 'A05_VO', cue = 'A05_Ariel_T01_00938', faction = 'Aeon'},}
Ariel_13 =       {{text = '[{i Ariel}]: The UEF is close to defeat. Join us so that we can defeat them together!', vid = 'A05_Ariel_T01_00939.sfd', bank = 'A05_VO', cue = 'A05_Ariel_T01_00939', faction = 'Aeon'},}
Ariel_14 =       {{text = '[{i Ariel}]: The Princess is soft and weak. She knows nothing of waging war.', vid = 'A05_Ariel_T01_00940.sfd', bank = 'A05_VO', cue = 'A05_Ariel_T01_00940', faction = 'Aeon'},}
Ariel_15 =       {{text = '[{i Ariel}]: It saddens me to think of killing a fellow Aeon, but I will if I must.', vid = 'A05_Ariel_T01_00941.sfd', bank = 'A05_VO', cue = 'A05_Ariel_T01_00941', faction = 'Aeon'},}
Ariel_16 =       {{text = '[{i Ariel}]: The Avatar has promised glorious rewards to those that follow him.', vid = 'A05_Ariel_T01_00942.sfd', bank = 'A05_VO', cue = 'A05_Ariel_T01_00942', faction = 'Aeon'},}
Ariel_17 =       {{text = '[{i Ariel}]: The Avatar is the reason the UEF army is on the run.', vid = 'A05_Ariel_T01_00943.sfd', bank = 'A05_VO', cue = 'A05_Ariel_T01_00943', faction = 'Aeon'},}
Ariel_18 =       {{text = '[{i Ariel}]: Only Avatar Marxon can protect us from the fury of Black Sun.', vid = 'A05_Ariel_T01_00944.sfd', bank = 'A05_VO', cue = 'A05_Ariel_T01_00944', faction = 'Aeon'},}
Ariel_19 =       {{text = '[{i Ariel}]: You should be fighting beside me, not against me!', vid = 'A05_Ariel_T01_00945.sfd', bank = 'A05_VO', cue = 'A05_Ariel_T01_00945', faction = 'Aeon'},}
Ariel_20 =       {{text = '[{i Ariel}]: I will not allow you to defile another Artifact.', vid = 'C02_Ariel_M02_0404.sfd', bank = 'C02_VO', cue = 'C02_Ariel_M02_0404', faction = 'Aeon'},}
Ariel_21 =       {{text = '[{i Ariel}]: If those Artifacts are taken, I will hunt you for the rest of your days!', vid = 'C02_Ariel_M02_0405.sfd', bank = 'C02_VO', cue = 'C02_Ariel_M02_0405', faction = 'Aeon'},}
Ariel_22 =       {{text = '[{i Ariel}]: The time for warnings has passed. Now you will die.', vid = 'C02_Ariel_M02_0406.sfd', bank = 'C02_VO', cue = 'C02_Ariel_M02_0406', faction = 'Aeon'},}
Ariel_23 =       {{text = '[{i Ariel}]: I will live on in The Way! ', vid = 'C02_Ariel_M03_0415.sfd', bank = 'C02_VO', cue = 'C02_Ariel_M03_0415', faction = 'Aeon'},}
Ariel_24 =       {{text = '[{i Ariel}]: You dare to set foot on Orionis?', vid = 'C02_Ariel_T01_0366.sfd', bank = 'C02_VO', cue = 'C02_Ariel_T01_0366', faction = 'Aeon'},}
Ariel_25 =       {{text = '[{i Ariel}]: You will never stop us!', vid = 'C02_Ariel_T01_0367.sfd', bank = 'C02_VO', cue = 'C02_Ariel_T01_0367', faction = 'Aeon'},}
Ariel_26 =       {{text = '[{i Ariel}]: I will not rest until you are destroyed.', vid = 'C02_Ariel_T01_0368.sfd', bank = 'C02_VO', cue = 'C02_Ariel_T01_0368', faction = 'Aeon'},}
Ariel_27 =       {{text = '[{i Ariel}]: Cybrans are a blight that must be cleansed.', vid = 'C02_Ariel_T01_0369.sfd', bank = 'C02_VO', cue = 'C02_Ariel_T01_0369', faction = 'Aeon'},}
Ariel_28 =       {{text = '[{i Ariel}]: I am guided by The Way.', vid = 'C02_Ariel_T01_0370.sfd', bank = 'C02_VO', cue = 'C02_Ariel_T01_0370', faction = 'Aeon'},}
Ariel_29 =       {{text = '[{i Ariel}]: Your presence here defiles this holy place.', vid = 'C02_Ariel_T01_0371.sfd', bank = 'C02_VO', cue = 'C02_Ariel_T01_0371', faction = 'Aeon'},}
Ariel_30 =       {{text = '[{i Ariel}]: You cannot defeat me!', vid = 'C02_Ariel_T01_0372.sfd', bank = 'C02_VO', cue = 'C02_Ariel_T01_0372', faction = 'Aeon'},}
Ariel_31 =       {{text = '[{i Ariel}]: Your days are at an end.', vid = 'C02_Ariel_T01_0373.sfd', bank = 'C02_VO', cue = 'C02_Ariel_T01_0373', faction = 'Aeon'},}

-- Uef Berry
Berry_1 =        {{text = '[{i Berry}]: The Aeon\'s assault on the galaxy ends today.', vid = 'A01_Berry_M07_00774.sfd', bank = 'A01_VO', cue = 'A01_Berry_M07_00774', faction = 'UEF'},}
Berry_2 =        {{text = '[{i Berry}]: Colonel, I need help, Colonel! ', vid = 'A01_Berry_M07_00777.sfd', bank = 'A01_VO', cue = 'A01_Berry_M07_00777', faction = 'UEF'},}
Berry_3 =        {{text = '[{i Berry}]: You\'ve destroyed my Air Factory. I will respond in kind.', vid = 'A01_Berry_M07_00780.sfd', bank = 'A01_VO', cue = 'A01_Berry_M07_00780', faction = 'UEF'},}
Berry_4 =        {{text = '[{i Berry}]: You fanatics will never defeat the UEF!', vid = 'A01_Berry_T01_00738.sfd', bank = 'A01_VO', cue = 'A01_Berry_T01_00738', faction = 'UEF'},}
Berry_5 =        {{text = '[{i Berry}]: The UEF is unstoppable!', vid = 'A01_Berry_T01_00739.sfd', bank = 'A01_VO', cue = 'A01_Berry_T01_00739', faction = 'UEF'},}
Berry_6 =        {{text = '[{i Berry}]: I will not let you cleanse this planet.', vid = 'A01_Berry_T01_00740.sfd', bank = 'A01_VO', cue = 'A01_Berry_T01_00740', faction = 'UEF'},}
Berry_7 =        {{text = '[{i Berry}]: The Aeon army is comprised of murderers and madmen.', vid = 'A01_Berry_T01_00741.sfd', bank = 'A01_VO', cue = 'A01_Berry_T01_00741', faction = 'UEF'},}
Berry_8 =        {{text = '[{i Berry}]: So this is the mighty Aeon Illuminate. I\'m unimpressed.', vid = 'A01_Berry_T01_00742.sfd', bank = 'A01_VO', cue = 'A01_Berry_T01_00742', faction = 'UEF'},}
Berry_9 =        {{text = '[{i Berry}]: I think you underestimate me.', vid = 'A01_Berry_T01_00743.sfd', bank = 'A01_VO', cue = 'A01_Berry_T01_00743', faction = 'UEF'},}
Berry_10 =       {{text = '[{i Berry}]: I will never surrender to you.', vid = 'A01_Berry_T01_00744.sfd', bank = 'A01_VO', cue = 'A01_Berry_T01_00744', faction = 'UEF'},}
Berry_11 =       {{text = '[{i Berry}]: The UEF will restore order to the galaxy.', vid = 'A01_Berry_T01_00745.sfd', bank = 'A01_VO', cue = 'A01_Berry_T01_00745', faction = 'UEF'},}
Berry_12 =       {{text = '[{i Berry}]: I\'m going to take that town, Cybran, and there\'s nothing you can do about it.', vid = 'C03_Berry_M01_0426.sfd', bank = 'C03_VO', cue = 'C03_Berry_M01_0426', faction = 'UEF'},}
Berry_13 =       {{text = '[{i Berry}]: I\'m not done with you, Cybran.', vid = 'C03_Berry_M01_0428.sfd', bank = 'C03_VO', cue = 'C03_Berry_M01_0428', faction = 'UEF'},}
Berry_14 =       {{text = '[{i Berry}]: Now\'s my chance to kill Brackman!', vid = 'C03_Berry_M02_0444.sfd', bank = 'C03_VO', cue = 'C03_Berry_M02_0444', faction = 'UEF'},}
Berry_15 =       {{text = '[{i Berry}]: I may have missed Brackman, but you won\'t be so lucky.', vid = 'C03_Berry_M03_0454.sfd', bank = 'C03_VO', cue = 'C03_Berry_M03_0454', faction = 'UEF'},}
Berry_16 =       {{text = '[{i Berry}]: No, I can\'t aaaaaaaaaargh!', vid = 'C03_Berry_M03_0457.sfd', bank = 'C03_VO', cue = 'C03_Berry_M03_0457', faction = 'UEF'},}
Berry_17 =       {{text = '[{i Berry}]: You\'re nothing more than a crazed terrorist.', vid = 'C03_Berry_T01_0417.sfd', bank = 'C03_VO', cue = 'C03_Berry_T01_0417', faction = 'UEF'},}
Berry_18 =       {{text = '[{i Berry}]: It is time to pay for your crimes against humanity.', vid = 'C03_Berry_T01_0418.sfd', bank = 'C03_VO', cue = 'C03_Berry_T01_0418', faction = 'UEF'},}
Berry_19 =       {{text = '[{i Berry}]: You and your kind sicken me.', vid = 'C03_Berry_T01_0419.sfd', bank = 'C03_VO', cue = 'C03_Berry_T01_0419', faction = 'UEF'},}
Berry_20 =       {{text = '[{i Berry}]: You will die on this day.', vid = 'C03_Berry_T01_0420.sfd', bank = 'C03_VO', cue = 'C03_Berry_T01_0420', faction = 'UEF'},}
Berry_21 =       {{text = '[{i Berry}]: There is no stopping us. We will restore the Empire.', vid = 'C03_Berry_T01_0421.sfd', bank = 'C03_VO', cue = 'C03_Berry_T01_0421', faction = 'UEF'},}
Berry_22 =       {{text = '[{i Berry}]: I think you underestimate me.', vid = 'C03_Berry_T01_0422.sfd', bank = 'C03_VO', cue = 'C03_Berry_T01_0422', faction = 'UEF'},}
Berry_23 =       {{text = '[{i Berry}]: You cannot defeat me!', vid = 'C03_Berry_T01_0423.sfd', bank = 'C03_VO', cue = 'C03_Berry_T01_0423', faction = 'UEF'},}
Berry_24 =       {{text = '[{i Berry}]: The UEF will bring order to the galaxy.', vid = 'C03_Berry_T01_0424.sfd', bank = 'C03_VO', cue = 'C03_Berry_T01_0424', faction = 'UEF'},}

-- Uef Blake
Blake_1 =        {{text = '[{i Blake}]: Dammit, more Aeon!', vid = 'A05_Blake_M01_00962.sfd', bank = 'A05_VO', cue = 'A05_Blake_M01_00962', faction = 'UEF'},}
Blake_2 =        {{text = '[{i Blake}]: Like hell. I know how you freaks \"save\" colonies. ', vid = 'A05_Blake_M01_00964.sfd', bank = 'A05_VO', cue = 'A05_Blake_M01_00964', faction = 'UEF'},}
Blake_3 =        {{text = '[{i Blake}]: Don\'t try that mystic rubbish with me. It isn\'t going to work!', vid = 'A05_Blake_M01_00966.sfd', bank = 'A05_VO', cue = 'A05_Blake_M01_00966', faction = 'UEF'},}
Blake_4 =        {{text = '[{i Blake}]: Aaaaargh', vid = 'A05_Blake_M02_00998.sfd', bank = 'A05_VO', cue = 'A05_Blake_M02_00998', faction = 'UEF'},}
Blake_5 =        {{text = '[{i Blake}]: You\'ll pay for killing Colonel Arnold!', vid = 'A05_Blake_T01_00946.sfd', bank = 'A05_VO', cue = 'A05_Blake_T01_00946', faction = 'UEF'},}
Blake_6 =        {{text = '[{i Blake}]: The UEF will never stop waging war against the Aeon.', vid = 'A05_Blake_T01_00947.sfd', bank = 'A05_VO', cue = 'A05_Blake_T01_00947', faction = 'UEF'},}
Blake_7 =        {{text = '[{i Blake}]: Vengeance will be mine!', vid = 'A05_Blake_T01_00948.sfd', bank = 'A05_VO', cue = 'A05_Blake_T01_00948', faction = 'UEF'},}
Blake_8 =        {{text = '[{i Blake}]: I will never stop hunting you, Aeon!', vid = 'A05_Blake_T01_00949.sfd', bank = 'A05_VO', cue = 'A05_Blake_T01_00949', faction = 'UEF'},}
Blake_9 =        {{text = '[{i Blake}]: You abandoned your humanity a long time ago.', vid = 'A05_Blake_T01_00950.sfd', bank = 'A05_VO', cue = 'A05_Blake_T01_00950', faction = 'UEF'},}
Blake_10 =       {{text = '[{i Blake}]: Soon the Aeon will be extinct.', vid = 'A05_Blake_T01_00951.sfd', bank = 'A05_VO', cue = 'A05_Blake_T01_00951', faction = 'UEF'},}
Blake_11 =       {{text = '[{i Blake}]: You will answer for your crimes!', vid = 'A05_Blake_T01_00952.sfd', bank = 'A05_VO', cue = 'A05_Blake_T01_00952', faction = 'UEF'},}
Blake_12 =       {{text = '[{i Blake}]: I will not allow you to escape!', vid = 'A05_Blake_T01_00953.sfd', bank = 'A05_VO', cue = 'A05_Blake_T01_00953', faction = 'UEF'},}

-- Cybran Brackman 
Brackman_1  =    {{text = '[{i Brackman}]: Dostya has told me of her tracking bug. Oh yes. Smart girl, smart girl. Please do as she says, my boy. Oh yes.', vid = 'X02_Brackman_M02_03549.sfd', bank = 'X02_VO', cue = 'X02_Brackman_M02_03549', faction = 'Cybran'},}
Brackman_2  =    {{text = '[{i Brackman}]: This is a perfect opportunity for us, my child. Oh yes. I have been working on a translator, a device that will let us decode what the Seraphim are saying to each other.', vid = 'X02_Brackman_M03_04296.sfd', bank = 'X02_VO', cue = 'X02_Brackman_M03_04296', faction = 'Cybran'},}
Brackman_3  =    {{text = '[{i Brackman}]: If you capture the Quantum Gate that the Seraphim will use, I will upload a program that will kill the pilot as it exits the Gate. Oh yes. The Seraphim ACU will be yours to command, and I will be able to decipher its language matrix.', vid = 'X02_Brackman_M03_04297.sfd', bank = 'X02_VO', cue = 'X02_Brackman_M03_04297', faction = 'Cybran'},}
Brackman_4  =    {{text = '[{i Brackman}]: Capture the Gate, my child. Capture it.', vid = 'X02_Brackman_M03_04299.sfd', bank = 'X02_VO', cue = 'X02_Brackman_M03_04299', faction = 'Cybran'},}
Brackman_5  =    {{text = '[{i Brackman}]: Once you have destroyed QAI\'s ACU, Loyalist forces will arrive and secure the Seraphim commander. Oh yes. Then I will be able to decipher its language matrix.', vid = 'X02_Brackman_M03_04905.sfd', bank = 'X02_VO', cue = 'X02_Brackman_M03_04905', faction = 'Cybran'},}
Brackman_6  =    {{text = '[{i Brackman}]: Please destroy QAI, my child. We must get our hands on the Seraphim ACU. ', vid = 'X02_Brackman_M03_04906.sfd', bank = 'X02_VO', cue = 'X02_Brackman_M03_04906', faction = 'Cybran'},}
Brackman_7  =    {{text = '[{i Brackman}]: Excellent work, my child, excellent work. Now that the Gate is under your control, summon the Seraphim commander. Oh yes.', vid = 'X02_Brackman_M03_04300.sfd', bank = 'X02_VO', cue = 'X02_Brackman_M03_04300', faction = 'Cybran'},}
Brackman_8  =    {{text = '[{i Brackman}]: Please summon the Seraphim ACU, Commander.', vid = 'X02_Brackman_M03_04302.sfd', bank = 'X02_VO', cue = 'X02_Brackman_M03_04302', faction = 'Cybran'},}
Brackman_9  =    {{text = '[{i Brackman}]: We\'re running out of time, out of time. You must summon the Seraphim ACU before our scheme is uncovered.', vid = 'X02_Brackman_M03_04303.sfd', bank = 'X02_VO', cue = 'X02_Brackman_M03_04303', faction = 'Cybran'},}
Brackman_10 =    {{text = '[{i Brackman}]: Excellent! Simply move the Seraphim ACU back to your base. Oh yes. Once it is safe, we will take care of the rest.', vid = 'X02_Brackman_M03_04304.sfd', bank = 'X02_VO', cue = 'X02_Brackman_M03_04304', faction = 'Cybran'},}
Brackman_11 =    {{text = '[{i Brackman}]: QAI is attempting to destroy the Seraphim ACU, my child. You must not allow that to happen.', vid = 'X02_Brackman_M03_04305.sfd', bank = 'X02_VO', cue = 'X02_Brackman_M03_04305', faction = 'Cybran'},}
Brackman_12 =    {{text = '[{i Brackman}]: Protect the Seraphim ACU, Commander. Oh yes.', vid = 'X02_Brackman_M03_04306.sfd', bank = 'X02_VO', cue = 'X02_Brackman_M03_04306', faction = 'Cybran'},}
Brackman_13 =    {{text = '[{i Brackman}]: I cannot complete my work on the translator until you deliver the ACU, Commander.', vid = 'X02_Brackman_M03_04307.sfd', bank = 'X02_VO', cue = 'X02_Brackman_M03_04307', faction = 'Cybran'},}
Brackman_14 =    {{text = '[{i Brackman}]: I am eagerly awaiting the arrival of the Seraphim ACU, my child.', vid = 'X02_Brackman_M03_04308.sfd', bank = 'X02_VO', cue = 'X02_Brackman_M03_04308', faction = 'Cybran'},}
Brackman_15 =    {{text = '[{i Brackman}]: Ah, there we are. Nicely done, my child, nicely done. Thank you.', vid = 'X02_Brackman_M03_04309.sfd', bank = 'X02_VO', cue = 'X02_Brackman_M03_04309', faction = 'Cybran'},}
Brackman_16 =    {{text = '[{i Brackman}]: The Loyalists are enroute, Commander. Just a few more moments, and the Seraphim commander will be ours. Oh yes.', vid = 'X02_Brackman_M03_04857.sfd', bank = 'X02_VO', cue = 'X02_Brackman_M03_04857', faction = 'Cybran'},}
Brackman_17 =    {{text = '[{i Brackman}]: Excellent work, excellent. Soon all of its secrets will be ours.', vid = 'X02_Brackman_M03_04860.sfd', bank = 'X02_VO', cue = 'X02_Brackman_M03_04860', faction = 'Cybran'},}
Brackman_18 =    {{text = '[{i Brackman}]: We are so close, my child, so close to completing work on the translator. Just a few more algorithms to decipher and it will be ready. The intelligence boon will be substantial. Oh yes.', vid = 'X03_Brackman_M01_03290.sfd', bank = 'X03_VO', cue = 'X03_Brackman_M01_03290', faction = 'Cybran'},}
Brackman_19 =    {{text = '[{i Brackman}]: The translator is nearly finished. Oh yes. It will be available to you in the next operation. Well done, my child, well done. The captured Seraphim ACU was the final piece of the puzzle.', vid = 'X03_Brackman_M03_03336.sfd', bank = 'X03_VO', cue = 'X03_Brackman_M03_03336', faction = 'Cybran'},}
Brackman_20 =    {{text = '[{i Brackman}]: My beloved Dostya is gone, cut down by one of our own. Words escape me, my child, escape me. We stand on the verge of triumph, yet those that we know and love are willing to betray us. We must deal with QAI. Oh yes. And Hex5. Some crimes are unforgivable. You know what to do.', vid = 'X04_Brackman_DB01_04024.sfd', bank = 'Briefings', cue = 'X04_Brackman_DB01_04024', faction = 'Cybran'},}
Brackman_21 =    {{text = '[{i Brackman}]: The Seraphim are unstoppable, oh yes. So few of us are left. So few. I shall hide, bide my time. There is no other alternative. No other choice.', vid = 'X04_Brackman_DB01_04406.sfd', bank = 'Briefings', cue = 'X04_Brackman_DB01_04406', faction = 'Cybran'},}
Brackman_22 =    {{text = '[{i Brackman}]: No ... Dostya ... my beloved Dostya ...', vid = 'X04_Brackman_M03_04401.sfd', bank = 'X04_VO', cue = 'X04_Brackman_M03_04401', faction = 'Cybran'},}
Brackman_23 =    {{text = '[{i Brackman}]: My beloved Dostya, she must come home, my child. Must come home. Please locate and recover what remains of her. I cannot leave her there. ', vid = 'X04_Brackman_M03_04015.sfd', bank = 'X04_VO', cue = 'X04_Brackman_M03_04015', faction = 'Cybran'},}
Brackman_24 =    {{text = '[{i Brackman}]: Commander, please recover Ivanna\'s remains.', vid = 'X04_Brackman_M03_04016.sfd', bank = 'X04_VO', cue = 'X04_Brackman_M03_04016', faction = 'Cybran'},}
Brackman_25 =    {{text = '[{i Brackman}]: We cannot leave Ivanna on that planet, Commander. Find her. Bring her home.', vid = 'X04_Brackman_M03_04017.sfd', bank = 'X04_VO', cue = 'X04_Brackman_M03_04017', faction = 'Cybran'},}
Brackman_26 =    {{text = '[{i Brackman}]: Thank you, my child. Thank you.', vid = 'X04_Brackman_M03_03771.sfd', bank = 'X04_VO', cue = 'X04_Brackman_M03_03771', faction = 'Cybran'},}
Brackman_27 =    {{text = '[{i Brackman}]: Hi, this is Jamieson Price, the voice of Dr. Brackman. Your skills are so impressive that you knocked me out of character, and now I have to re-record my VO! Gimme a moment while I dial it back in ... oh yes ... there we go, much better. Much better.', vid = 'X04_Brackman_M03_05106.sfd', bank = 'X04_VO', cue = 'X04_Brackman_M03_05106', faction = 'Cybran'},}
Brackman_28 =    {{text = '[{i Brackman}]: Before QAI was destroyed, I extracted its Gate codes. The path to Earth is now clear. Oh yes. I pray that the invasion will be successful. It is the only way to wipe the slate clean, start anew. You must win the day, my child, win it for all of us.', vid = 'X05_Brackman_DB01_04026.sfd', bank = 'Briefings', cue = 'X05_Brackman__04026', faction = 'Cybran'},}
Brackman_29 =    {{text = '[{i Brackman}]: I have tolerated you in the past, Hex5, forgiven your eccentricities. Oh yes. But no more. Today you will answer for your crimes.', vid = 'X05_Brackman_M01_03790.sfd', bank = 'X05_VO', cue = 'X05_Brackman_M01_03790', faction = 'Cybran'},}
Brackman_30 =    {{text = '[{i Brackman}]: We shall see. Oh yes. We shall see.', vid = 'X05_Brackman_M01_03792.sfd', bank = 'X05_VO', cue = 'X05_Brackman_M01_03792', faction = 'Cybran'},}
Brackman_31 =    {{text = '[{i Brackman}]: So much of this is my fault. Oh yes. The loyalty program, QAI. Foolish choices were made, foolish choices. It is time to make things right. Oh yes. Make them right. Be safe, my child.', vid = 'X05_Brackman_M01_03816.sfd', bank = 'X05_VO', cue = 'X05_Brackman_M01_03816', faction = 'Cybran'},}
Brackman_32 =    {{text = '[{i Brackman}]: Hex5 is south of Fletcher\'s position. He must be eliminated. Oh yes.', vid = 'X05_Brackman_M01_04438.sfd', bank = 'X05_VO', cue = 'X05_Brackman_M01_04438', faction = 'Cybran'},}
Brackman_33 =    {{text = '[{i Brackman}]: I will be landing shortly, my child. Then I shall make things right. Oh yes.', vid = 'X05_Brackman_M02_03843.sfd', bank = 'X05_VO', cue = 'X05_Brackman_M02_03843', faction = 'Cybran'},}
Brackman_34 =    {{text = '[{i Brackman}]: And so a traitor dies. Oh yes. So much loss. So much sorrow.', vid = 'X05_Brackman_M02_03853.sfd', bank = 'X05_VO', cue = 'X05_Brackman_M02_03853', faction = 'Cybran'},}
Brackman_35 =    {{text = '[{i Brackman}]: You had such potential, QAI. Oh yes. All for nothing.', vid = 'X05_Brackman_M02_03856.sfd', bank = 'X05_VO', cue = 'X05_Brackman_M02_03856', faction = 'Cybran'},}
Brackman_36 =    {{text = '[{i Brackman}]: I would surmise that QAI has a physical link established with the structures. Oh yes. Some sort of universal control, perhaps? Very interesting.', vid = 'X05_Brackman_M03_03860.sfd', bank = 'X05_VO', cue = 'X05_Brackman_M03_03860', faction = 'Cybran'},}
Brackman_37 =    {{text = '[{i Brackman}]: I am under attack, my child. Under attack. Please defend me.', vid = 'X05_Brackman_M03_04953.sfd', bank = 'X05_VO', cue = 'X05_Brackman_M03_04953', faction = 'Cybran'},}
Brackman_38 =    {{text = '[{i Brackman}]: My Megalith has absorbed an awful lot of damage. Oh yes. So much damage. Please assist.', vid = 'X05_Brackman_M03_03863.sfd', bank = 'X05_VO', cue = 'X05_Brackman_M03_03863', faction = 'Cybran'},}
Brackman_39 =    {{text = '[{i Brackman}]: Hull integrity is dropping. Please help me, Commander.', vid = 'X05_Brackman_M03_03864.sfd', bank = 'X05_VO', cue = 'X05_Brackman_M03_03864', faction = 'Cybran'},}
Brackman_40 =    {{text = '[{i Brackman}]: At last I shall have peace.', vid = 'X05_Brackman_M03_04444.sfd', bank = 'X05_VO', cue = 'X05_Brackman_M03_04444', faction = 'Cybran'},}
Brackman_41 =    {{text = '[{i Brackman}]: I am in position, my child. I shall take it from here. It\'s just a simple matter of interfacing. Oh yes.', vid = 'X05_Brackman_M03_04447.sfd', bank = 'X05_VO', cue = 'X05_Brackman_M03_04447', faction = 'Cybran'},}
Brackman_42 =    {{text = '[{i Brackman}]: A father always knows the child\'s weakness. Oh yes. There is always a weakness.', vid = 'X05_Brackman_M03_04449.sfd', bank = 'X05_VO', cue = 'X05_Brackman_M03_04449', faction = 'Cybran'},}
Brackman_43 =    {{text = '[{i Brackman}]: Goodbye, QAI.', vid = 'X05_Brackman_M03_04451.sfd', bank = 'X05_VO', cue = 'X05_Brackman_M03_04451', faction = 'Cybran'},}
Brackman_44 =    {{text = '[{i Brackman}]: Goodbye.', vid = 'X05_Brackman_M03_04452.sfd', bank = 'X05_VO', cue = 'X05_Brackman_M03_04452', faction = 'Cybran'},}
Brackman_45 =    {{text = '[{i Brackman}]: It is over. QAI is destroyed. We would be wise to dispatch technicians, though, have them search through QAI\'s systems. Just to be safe.', vid = 'X05_Brackman_M03_03895.sfd', bank = 'X05_VO', cue = 'X05_Brackman_M03_03895', faction = 'Cybran'},}
Brackman_46 =    {{text = '[{i Brackman}]: Our long struggle is nearing its end, my child. Oh yes. Be safe.', vid = 'X06_Brackman_M01_03916.sfd', bank = 'X06_VO', cue = 'X06_Brackman_M01_03916', faction = 'Cybran'},}
Brackman_47 =    {{text = '[{i Brackman}]: My child, you now have access to my Megalith. Use it wisely. Oh yes. Use it wisely.', vid = 'X06_Brackman_M01_04958.sfd', bank = 'X06_VO', cue = 'X06_Brackman_M01_04958', faction = 'Cybran'},}
Brackman_48 =    {{text = '[{i Brackman}]: My child, General Hall has authorized you to stop Brigadier Fletcher. Use any means necessary. He must not interfere with our plans.', vid = 'X06_Brackman_M02_04486.sfd', bank = 'X06_VO', cue = 'X06_Brackman_M02_04486', faction = 'Cybran'},}
Brackman_49 =    {{text = '[{i Brackman}]: You know what must be done, my child. Destroy the Arch.', vid = 'X06_Brackman_M03_03973.sfd', bank = 'X06_VO', cue = 'X06_Brackman_M03_03973', faction = 'Cybran'},}
Brackman_50 =    {{text = '[{i Brackman}]: Focus your efforts on the Arch! It is all that matters!', vid = 'X06_Brackman_M03_03990.sfd', bank = 'X06_VO', cue = 'X06_Brackman_M03_03990', faction = 'Cybran'},}
Brackman_51 =    {{text = '[{i Brackman}]: Destroy the Arch!', vid = 'X06_Brackman_M03_03991.sfd', bank = 'X06_VO', cue = 'X06_Brackman_M03_03991', faction = 'Cybran'},}
-- No vid
Brackman_52 =    {{text = '[{i Brackman}]: I\'m afraid there is no hope for you, oh yes.', bank = 'XGG', cue = 'XGG_Brackman_MP1_04606'},}
Brackman_53 =    {{text = '[{i Brackman}]: Well, at least you provided me with some amusement.', bank = 'XGG', cue = 'XGG_Brackman_MP1_04607'},}
Brackman_54 =    {{text = '[{i Brackman}]: Perhaps some remedial training is in order?', bank = 'XGG', cue = 'XGG_Brackman_MP1_04608'},}
Brackman_55 =    {{text = '[{i Brackman}]: Are you sure you want to do that?', bank = 'XGG', cue = 'XGG_Brackman_MP1_04609'},}
Brackman_56 =    {{text = '[{i Brackman}]: They do not call me a genius for nothing, you know.', bank = 'XGG', cue = 'XGG_Brackman_MP1_04610'},}
Brackman_57 =    {{text = '[{i Brackman}]: Defeating you is hardly worth the effort, oh yes.', bank = 'XGG', cue = 'XGG_Brackman_MP1_04611'},}
Brackman_58 =    {{text = '[{i Brackman}]: There is nothing you can do.', bank = 'XGG', cue = 'XGG_Brackman_MP1_04612'},}
Brackman_59 =    {{text = '[{i Brackman}]: At least you will not suffer long.', bank = 'XGG', cue = 'XGG_Brackman_MP1_04613'},}

Brackman_60 =    {{text = '[{i Brackman}]: Full integration of the Seraphim Technology is complete. My operational systems are now 84.593 percent more efficient.', bank = 'C03_VO', cue = 'C03_B01_001_020'},}
Brackman_61 =    {{text = '[{i Brackman}]: Time is against us. Perhaps Dr. Sweeney will be able to offer us some guidance. No doubt that the UEF misses one of its top scientists, but he\'s our guest now. Oh yes. Ah, Commander. A pity about Mach. He was such a good boy. So full of promise.', bank = 'C03_VO', cue = 'C03_B01_003_010'},}
Brackman_62 =    {{text = '[{i Brackman}]: The ruse worked perfectly, oh yes. While you distracted them with the trucks, I made my escape elsewhere. Well done, my boy.', bank = 'C03_VO', cue = 'C03_M02_050_010'},}
Brackman_63 =    {{text = '[{i Brackman}]: No time, no time. My boy is only one Gate from us. I must escape from Minerva. Tip of the spear.', bank = 'C03_VO', cue = 'C03_B01_004_030'},}

-- Aeon Celene 
Celene_1 =       {{text = '[{i Celene}]: Wait! I\'m not ready to die!', vid = 'X02_Celene_D01_03178.sfd', bank = 'X02_VO', cue = 'X02_Celene_D01_03178', faction = 'Aeon'},}
Celene_2 =       {{text = '[{i Celene}]: You do not comprehend the power that is arrayed against you.', vid = 'X02_Celene_M01_03130.sfd', bank = 'X02_VO', cue = 'X02_Celene_M01_03130', faction = 'Aeon'},}
Celene_3 =       {{text = '[{i Celene}]: Your mere presence here desecrates this planet. You are an abomination.', vid = 'X02_Celene_M01_03131.sfd', bank = 'X02_VO', cue = 'X02_Celene_M01_03131', faction = 'Aeon'},}
Celene_4 =       {{text = '[{i Celene}]: Lay down your weapons and embrace the Seraphim, Champion. It is the only way you\'ll leave this planet alive.', vid = 'X02_Celene_M01_03132.sfd', bank = 'X02_VO', cue = 'X02_Celene_M01_03132', faction = 'Aeon'},}
Celene_5 =       {{text = '[{i Celene}]: The Princess is dead, Rhiza.', vid = 'X02_Celene_M01_04267.sfd', bank = 'X02_VO', cue = 'X02_Celene_M01_04267', faction = 'Aeon'},}
Celene_6 =       {{text = '[{i Celene}]: There is nothing here for you but death.', vid = 'X02_Celene_M02_04277.sfd', bank = 'X02_VO', cue = 'X02_Celene_M02_04277', faction = 'Aeon'},}
Celene_7 =       {{text = '[{i Celene}]: Princess???', vid = 'X02_Celene_M02_04282.sfd', bank = 'X02_VO', cue = 'X02_Celene_M02_04282', faction = 'Aeon'},}
Celene_8 =       {{text = '[{i Celene}]: The Princess is alive ... I don\'t believe it ...', vid = 'X02_Celene_M02_04284.sfd', bank = 'X02_VO', cue = 'X02_Celene_M02_04284', faction = 'Aeon'},}
Celene_9 =       {{text = '[{i Celene}]: I was wrong ... so wrong. Forgive me, Princess.', vid = 'X02_Celene_M02_04286.sfd', bank = 'X02_VO', cue = 'X02_Celene_M02_04286', faction = 'Aeon'},}
Celene_10 =      {{text = '[{i Celene}]: I can still make things right.', vid = 'X02_Celene_M02_04287.sfd', bank = 'X02_VO', cue = 'X02_Celene_M02_04287', faction = 'Aeon'},}
Celene_11 =      {{text = '[{i Celene}]: I have eliminated most of QAI\'s strategic missile launchers; its base is to the northeast. Celene out.', vid = 'X02_Celene_M03_04294.sfd', bank = 'X02_VO', cue = 'X02_Celene_M03_04294', faction = 'Aeon'},}
Celene_12 =      {{text = '[{i Celene}]: I was wrong to have sided with you, monster. I shall atone for my actions by destroying you.', vid = 'X02_Celene_M03_04312.sfd', bank = 'X02_VO', cue = 'X02_Celene_M03_04312', faction = 'Aeon'},}
Celene_13 =      {{text = '[{i Celene}]: Commander, I offer you one of the Order\'s best kept secrets, the Quantum resource generator. It offers a nearly endless supply of Mass and Energy.', vid = 'X02_Celene_M03_04313.sfd', bank = 'X02_VO', cue = 'X02_Celene_M03_04313', faction = 'Aeon'},}
Celene_14 =      {{text = '[{i Celene}]: Sister, I have a CZAR with a full complement of air units at the ready. Click on the Command Signal button and then click on a target. I will direct it to annihilate your target.', vid = 'X02_Celene_M03_04314.sfd', bank = 'X02_VO', cue = 'X02_Celene_M03_04314', faction = 'Aeon'},}
Celene_15 =      {{text = '[{i Celene}]: Keep up the pressure!', vid = 'X02_Celene_M03_03578.sfd', bank = 'X02_VO', cue = 'X02_Celene_M03_03578', faction = 'Aeon'},}
Celene_16 =      {{text = '[{i Celene}]: Where are your boasts now, machine?', vid = 'X02_Celene_M03_03579.sfd', bank = 'X02_VO', cue = 'X02_Celene_M03_03579', faction = 'Aeon'},}
Celene_17 =      {{text = '[{i Celene}]: The glory of the Princess now guides my actions. Your days on this planet are drawing to a close.', vid = 'X02_Celene_M03_03580.sfd', bank = 'X02_VO', cue = 'X02_Celene_M03_03580', faction = 'Aeon'},}
Celene_18 =      {{text = '[{i Celene}]: I\'ve taken too much damage! I\'m sorry, Commander, but I must recall.', vid = 'X02_Celene_M03_04699.sfd', bank = 'X02_VO', cue = 'X02_Celene_M03_04699', faction = 'Aeon'},}
Celene_19 =      {{text = '[{i Celene}]: Please forgive me, Rhiza. I was wrong to reject the Princess.', vid = 'X02_Celene_M03_04315.sfd', bank = 'X02_VO', cue = 'X02_Celene_M03_04315', faction = 'Aeon'},}
Celene_20 =      {{text = '[{i Celene}]: No, you may not have that experimental.', vid = 'X02_Celene_T01_04782.sfd', bank = 'X02_VO', cue = 'X02_Celene_T01_04782', faction = 'Aeon'},}
Celene_21 =      {{text = '[{i Celene}]: I will destroy that experimental before you even finish it!', vid = 'X02_Celene_T01_04783.sfd', bank = 'X02_VO', cue = 'X02_Celene_T01_04783', faction = 'Aeon'},}
Celene_22 =      {{text = '[{i Celene}]: Watch as I destroy your experimental even before it can be activated.', vid = 'X02_Celene_T01_04784.sfd', bank = 'X02_VO', cue = 'X02_Celene_T01_04784', faction = 'Aeon'},}
Celene_23 =      {{text = '[{i Celene}]: QAI will get your friends to talk. It\'s only a matter of time.', vid = 'X02_Celene_T01_04536.sfd', bank = 'X02_VO', cue = 'X02_Celene_T01_04536', faction = 'Aeon'},}
Celene_24 =      {{text = '[{i Celene}]: You\'ll pay dearly for destroying those facilities.', vid = 'X02_Celene_T01_04537.sfd', bank = 'X02_VO', cue = 'X02_Celene_T01_04537', faction = 'Aeon'},}
