Celene_36 =      {{text = '[{i Celene}]: Your treachery knows no limits.', vid = 'X02_Celene_T01_04549.sfd', bank = 'X02_VO', cue = 'X02_Celene_T01_04549', faction = 'Aeon'},}
Celene_37 =      {{text = '[{i Celene}]: How dare you turn your back on your own people!', vid = 'X02_Celene_T01_04550.sfd', bank = 'X02_VO', cue = 'X02_Celene_T01_04550', faction = 'Aeon'},}
Celene_38 =      {{text = '[{i Celene}]: I will not be defeated by the likes of you!', vid = 'X02_Celene_T01_04551.sfd', bank = 'X02_VO', cue = 'X02_Celene_T01_04551', faction = 'Aeon'},}
