Choir_1 =        {{text = '[{i Choir}]: Choir to Knight, respond. Choir to Knight, please respond...we\'ve lost your signal, please respond.', vid = 'A01_Choir_D01_00737.sfd', bank = 'A01_VO', cue = 'A01_Choir_D01_00737', faction = 'Aeon'},}
