Choir_5 =        {{text = '[{i Choir}]: The city of New Catalon is to your north, Commander. Avatar Marxon orders you to commence your attack. Choir out.', vid = 'A02_Choir_M01_00798.sfd', bank = 'A02_VO', cue = 'A02_Choir_M01_00798', faction = 'Aeon'},}
Choir_6 =        {{text = '[{i Choir}]: The Cybran Power Generators have been captured. Choir out.', vid = 'A02_Choir_M01_00799.sfd', bank = 'A02_VO', cue = 'A02_Choir_M01_00799', faction = 'Aeon'},}
Choir_7 =        {{text = '[{i Choir}]: Knight, Cybran units are preparing for a counterattack. Brace for an assault from the north. Choir out.', vid = 'A02_Choir_M01_00800.sfd', bank = 'A02_VO', cue = 'A02_Choir_M01_00800', faction = 'Aeon'},}
Choir_8 =        {{text = '[{i Choir}]: Knight, the Cybran assault has been blunted. Choir out.', vid = 'A02_Choir_M01_00802.sfd', bank = 'A02_VO', cue = 'A02_Choir_M01_00802', faction = 'Aeon'},}
Choir_9 =        {{text = '[{i Choir}]: The Avatar is waiting for word that New Catalon has been cleansed. Choir out. ', vid = 'A02_Choir_M01_00803.sfd', bank = 'A02_VO', cue = 'A02_Choir_M01_00803', faction = 'Aeon'},}
Choir_10 =       {{text = '[{i Choir}]: The Avatar wishes to remind you that there are greater battles awaiting you once you finish with New Catalon. Choir out.', vid = 'A02_Choir_M01_01568.sfd', bank = 'A02_VO', cue = 'A02_Choir_M01_01568', faction = 'Aeon'},}
Choir_11 =       {{text = '[{i Choir}]: New Catalon has been cleansed. The Way can now flourish on Zeta Canis. Choir out.', vid = 'A02_Choir_M01_00805.sfd', bank = 'A02_VO', cue = 'A02_Choir_M01_00805', faction = 'Aeon'},}
Choir_12 =       {{text = '[{i Choir}]: Knight, we\'ve uncovered a Cybran base on an island to your northeast. Destroy it so that The Way can be revealed. Choir out.', vid = 'A02_Choir_M02_00813.sfd', bank = 'A02_VO', cue = 'A02_Choir_M02_00813', faction = 'Aeon'},}
Choir_13 =       {{text = '[{i Choir}]: We have detected a small human settlement on the island with the Cybran base, Commander. Evaluator Toth requests that you use caution and not inflict harm to the settlement.', vid = 'A02_Choir_M02_00807.sfd', bank = 'A02_VO', cue = 'A02_Choir_M02_00807', faction = 'Aeon'},}
Choir_14 =       {{text = '[{i Choir}]: The Cybran\'s position is well-fortified and guarded by Frigates. You should use Torpedo Bombers to destroy the enemy Frigates and then invade the island. Transports will be the fastest way to move your ground units into position. Choir out.', vid = 'A02_Choir_M02_00814.sfd', bank = 'A02_VO', cue = 'A02_Choir_M02_00814', faction = 'Aeon'},}
Choir_15 =       {{text = '[{i Choir}]: We have detected a Cybran base to the east, Knight. It is covered with a stealth field, which is why it wasn\'t located sooner. Watch for attacks from that position. Choir out.', vid = 'A02_Choir_M02_00815.sfd', bank = 'A02_VO', cue = 'A02_Choir_M02_00815', faction = 'Aeon'},}
Choir_16 =       {{text = '[{i Choir}]: There\'s a Cybran force approaching you from the east. We have no explanation for why our scans failed to detect any Cybran power signatures in that area. Be on guard. Choir out.', vid = 'A02_Choir_M02_00816.sfd', bank = 'A02_VO', cue = 'A02_Choir_M02_00816', faction = 'Aeon'},}
Choir_17 =       {{text = '[{i Choir}]: Knight, an attack from the Cybran hidden naval base is imminent! Choir out.', vid = 'A02_Choir_M02_00818.sfd', bank = 'A02_VO', cue = 'A02_Choir_M02_00818', faction = 'Aeon'},}
Choir_18 =       {{text = '[{i Choir}]: New orders from Avatar Marxon: eradicate the hidden Cybran naval base. Choir out', vid = 'A02_Choir_M02_00819.sfd', bank = 'A02_VO', cue = 'A02_Choir_M02_00819', faction = 'Aeon'},}
Choir_19 =       {{text = '[{i Choir}]: Scans show the Cybran Commander is building a fleet at his naval base. Destroy the fleet before it can launch. Choir out.', vid = 'A02_Choir_M02_00820.sfd', bank = 'A02_VO', cue = 'A02_Choir_M02_00820', faction = 'Aeon'},}
Choir_20 =       {{text = '[{i Choir}]: The fleet has launched! Sink it before it can get within range of your base! Choir out.', vid = 'A02_Choir_M02_00821.sfd', bank = 'A02_VO', cue = 'A02_Choir_M02_00821', faction = 'Aeon'},}
Choir_21 =       {{text = '[{i Choir}]: The fleet has been destroyed. Excellent work, Commander. Choir out.', vid = 'A02_Choir_M02_00822.sfd', bank = 'A02_VO', cue = 'A02_Choir_M02_00822', faction = 'Aeon'},}
Choir_22 =       {{text = '[{i Choir}]: Knight, the conquest of the Cybran base seems to be taking a long time. Is there a problem? Choir out. ', vid = 'A02_Choir_M02_00826.sfd', bank = 'A02_VO', cue = 'A02_Choir_M02_00826', faction = 'Aeon'},}
Choir_23 =       {{text = '[{i Choir}]: If you are unable to destroy the Cybran base, the Avatar will have you replaced. Choir out.', vid = 'A02_Choir_M02_01094.sfd', bank = 'A02_VO', cue = 'A02_Choir_M02_01094', faction = 'Aeon'},}
Choir_24 =       {{text = '[{i Choir}]: There have been too many civilian casualties. Abort and return. Choir out.', vid = 'A02_Choir_M02_02670.sfd', bank = 'A02_VO', cue = 'A02_Choir_M02_02670', faction = 'Aeon'},}
Choir_25 =       {{text = '[{i Choir}]: There is a large force of Cybran tanks and bots deploying to the north. We are uploading the schematics for the \'Oblivion\' Tech 2 Point Defense Turret and the Tech 2 Anti-Air Turret into your ACU. Choir out.', vid = 'A02_Choir_M03_00829.sfd', bank = 'A02_VO', cue = 'A02_Choir_M03_00829', faction = 'Aeon'},}
Choir_26 =       {{text = '[{i Choir}]: The assault has been repelled. Attack the enemy\'s stronghold and exterminate the Cybran Commander. Choir out.', vid = 'A02_Choir_M03_00831.sfd', bank = 'A02_VO', cue = 'A02_Choir_M03_00831', faction = 'Aeon'},}
Choir_27 =       {{text = '[{i Choir}]: The enemy stronghold to the east is protected by an artillery position. The artillery is located west of their stronghold; if you can capture it, you can unleash its fury against the Cybran.', vid = 'A02_Choir_M03_00832.sfd', bank = 'A02_VO', cue = 'A02_Choir_M03_00832', faction = 'Aeon'},}
Choir_28 =       {{text = '[{i Choir}]: The western Cybran stronghold has fallen. Choir out.', vid = 'A02_Choir_M03_00833.sfd', bank = 'A02_VO', cue = 'A02_Choir_M03_00833', faction = 'Aeon'},}
Choir_29 =       {{text = '[{i Choir}]: The Cybran base to the east has been defeated. Choir out.', vid = 'A02_Choir_M03_00834.sfd', bank = 'A02_VO', cue = 'A02_Choir_M03_00834', faction = 'Aeon'},}
Choir_30 =       {{text = '[{i Choir}]: We are awaiting word of the destruction of the Cybran base, Knight. Choir out', vid = 'A02_Choir_M03_00836.sfd', bank = 'A02_VO', cue = 'A02_Choir_M03_00836', faction = 'Aeon'},}
Choir_31 =       {{text = '[{i Choir}]: The Avatar is wondering why you are taking so long to defeat the Cybran base, Knight. Choir out.', vid = 'A02_Choir_M03_01095.sfd', bank = 'A02_VO', cue = 'A02_Choir_M03_01095', faction = 'Aeon'},}
Choir_32 =       {{text = '[{i Choir}]: Zeta Canis is almost ours, Knight. Defeat the Cybran Commander. Choir out. ', vid = 'A02_Choir_M03_00838.sfd', bank = 'A02_VO', cue = 'A02_Choir_M03_00838', faction = 'Aeon'},}
Choir_33 =       {{text = '[{i Choir}]: Cybrans can be tough opponents, Knight, but we have every confidence that you will prevail. Choir out. ', vid = 'A02_Choir_M03_00839.sfd', bank = 'A02_VO', cue = 'A02_Choir_M03_00839', faction = 'Aeon'},}
Choir_34 =       {{text = '[{i Choir}]: Zeta Canis is almost ours, Knight. Defeat the Cybran Commander. Choir out. ', vid = 'A02_Choir_M03_01096.sfd', bank = 'A02_VO', cue = 'A02_Choir_M03_01096', faction = 'Aeon'},}
