Choir_36 =       {{text = '[{i Choir}]: Zeta Canis is now under our control. Well done.', vid = 'A02_Choir_M03_01150.sfd', bank = 'A02_VO', cue = 'A02_Choir_M03_01150', faction = 'Aeon'},}
Choir_37 =       {{text = '[{i Choir}]: Choir to Knight, respond. Choir to Knight, please respond.  We\'ve lost your signal, please respond.', vid = 'A03_Choir_D01_00841.sfd', bank = 'A03_VO', cue = 'A03_Choir_D01_00841', faction = 'Aeon'},}
Choir_38 =       {{text = '[{i Choir}]: Crusader Eris, the Avatar-of-War thanks you for your efforts on behalf of the Illuminate. You are relieved and may return to base.', vid = 'A03_Choir_M01_00916.sfd', bank = 'A03_VO', cue = 'A03_Choir_M01_00916', faction = 'Aeon'},}
