Choir_47 =       {{text = '[{i Choir}]: It is now time to strike back. Your main target is the UEF Commander. There are three UEF bases on the islands surrounding your position. The enemy Commander is at one of those bases. Find and eliminate him. Choir out.', vid = 'A03_Choir_M02_00864.sfd', bank = 'A03_VO', cue = 'A03_Choir_M02_00864', faction = 'Aeon'},}
Choir_48 =       {{text = '[{i Choir}]: The UEF Commander is utilizing a personal Quantum Teleportation Upgrade. We are scanning to determine his location. Choir out.', vid = 'A03_Choir_M02_00865.sfd', bank = 'A03_VO', cue = 'A03_Choir_M02_00865', faction = 'Aeon'},}
Choir_49 =       {{text = '[{i Choir}]: The UEF Commander has teleported to the northern island. Choir out.', vid = 'A03_Choir_M02_00866.sfd', bank = 'A03_VO', cue = 'A03_Choir_M02_00866', faction = 'Aeon'},}
Choir_50 =       {{text = '[{i Choir}]: We found him. He has teleported to the southern island. Press the attack. Choir out.', vid = 'A03_Choir_M02_00867.sfd', bank = 'A03_VO', cue = 'A03_Choir_M02_00867', faction = 'Aeon'},}
Choir_51 =       {{text = '[{i Choir}]: We have traced him to the southeastern island. Move into attack position. Choir out.', vid = 'A03_Choir_M02_00868.sfd', bank = 'A03_VO', cue = 'A03_Choir_M02_00868', faction = 'Aeon'},}
Choir_52 =       {{text = '[{i Choir}]: The land base is no longer a threat, Knight. Choir out.', vid = 'A03_Choir_M02_00869.sfd', bank = 'A03_VO', cue = 'A03_Choir_M02_00869', faction = 'Aeon'},}
Choir_53 =       {{text = '[{i Choir}]: The air base has been destroyed. Choir out', vid = 'A03_Choir_M02_00870.sfd', bank = 'A03_VO', cue = 'A03_Choir_M02_00870', faction = 'Aeon'},}
Choir_54 =       {{text = '[{i Choir}]: The UEF naval base is in ruins. Choir out.', vid = 'A03_Choir_M02_00871.sfd', bank = 'A03_VO', cue = 'A03_Choir_M02_00871', faction = 'Aeon'},}
Choir_55 =       {{text = '[{i Choir}]: The UEF Commander has fled the area! Pursue and destroy him! Choir out.', vid = 'A03_Choir_M02_00872.sfd', bank = 'A03_VO', cue = 'A03_Choir_M02_00872', faction = 'Aeon'},}
Choir_56 =       {{text = '[{i Choir}]: Knight, the Avatar expects the UEF Commander to fall by your hand ASAP. Choir out.', vid = 'A03_Choir_M02_00873.sfd', bank = 'A03_VO', cue = 'A03_Choir_M02_00873', faction = 'Aeon'},}
Choir_57 =       {{text = '[{i Choir}]: We are fully confident that you can defeat the UEF Commander. Choir out.', vid = 'A03_Choir_M02_01569.sfd', bank = 'A03_VO', cue = 'A03_Choir_M02_01569', faction = 'Aeon'},}
Choir_58 =       {{text = '[{i Choir}]: Knight, the UEF Commander has fled to his main base. Pursue and destroy him. Choir out.', vid = 'A03_Choir_M03_00875.sfd', bank = 'A03_VO', cue = 'A03_Choir_M03_00875', faction = 'Aeon'},}
Choir_59 =       {{text = '[{i Choir}]: We have detected a large naval force gathering at the UEF base. They are moving into attack position--prepare for a naval assault. Choir out.', vid = 'A03_Choir_M03_00878.sfd', bank = 'A03_VO', cue = 'A03_Choir_M03_00878', faction = 'Aeon'},}
Choir_60 =       {{text = '[{i Choir}]: The naval force has been defeated. Continue your assault against Commander Arnold.', vid = 'A03_Choir_M03_00880.sfd', bank = 'A03_VO', cue = 'A03_Choir_M03_00880', faction = 'Aeon'},}
Choir_61 =       {{text = '[{i Choir}]: The UEF Commander cannot flee from you much longer, Knight. Your victory will be swift. Choir out. ', vid = 'A03_Choir_M03_00883.sfd', bank = 'A03_VO', cue = 'A03_Choir_M03_00883', faction = 'Aeon'},}
Choir_62 =       {{text = '[{i Choir}]: This UEF Commander is skilled, but we have faith you will defeat him, Knight. Choir out.', vid = 'A03_Choir_M03_01570.sfd', bank = 'A03_VO', cue = 'A03_Choir_M03_01570', faction = 'Aeon'},}
Choir_63 =       {{text = '[{i Choir}]: Choir to Champion, respond. Choir to Champion, please respond. We\'ve lost your signal, please respond.', vid = 'A04_Choir_D01_00889.sfd', bank = 'A04_VO', cue = 'A04_Choir_D01_00889', faction = 'Aeon'},}
Choir_64 =       {{text = '[{i Choir}]: Champion, there is a Cybran settlement called Nexus04 to your north. The Princess does not want the population cleansed, but you are to eliminate all military resistance within the settlement. Choir out.', vid = 'A04_Choir_M01_00898.sfd', bank = 'A04_VO', cue = 'A04_Choir_M01_00898', faction = 'Aeon'},}
Choir_65 =       {{text = '[{i Choir}]: Champion, there is a facility called Node73 to your northwest. It appears to house a data-relay station of unknown purpose. Proceed to the facility and capture the \'Node\'. Choir out.', vid = 'A04_Choir_M01_00900.sfd', bank = 'A04_VO', cue = 'A04_Choir_M01_00900', faction = 'Aeon'},}
Choir_66 =       {{text = '[{i Choir}]: Champion, we detect a Cybran naval base on the lake to the west. Naval units are forming a blockade on the river in order to cut off access to it. You are not required to destroy the naval base; attack at your discretion. Choir out.', vid = 'A04_Choir_M01_00901.sfd', bank = 'A04_VO', cue = 'A04_Choir_M01_00901', faction = 'Aeon'},}
Choir_67 =       {{text = '[{i Choir}]: The Cybran naval base has been eliminated. The lake is now secure. Choir out.', vid = 'A04_Choir_M01_00902.sfd', bank = 'A04_VO', cue = 'A04_Choir_M01_00902', faction = 'Aeon'},}
Choir_68 =       {{text = '[{i Choir}]: We are detecting incoming Cybran units. Prepare for battle. Choir out.', vid = 'A04_Choir_M01_00903.sfd', bank = 'A04_VO', cue = 'A04_Choir_M01_00903', faction = 'Aeon'},}
Choir_69 =       {{text = '[{i Choir}]: The Cybran presence here is stronger than we anticipated. We are uploading the \'Shield of Light\' Tech 2 Shield Generator schematic into your ACU. The shield generator was designed to protect you from general ordinance, such as artillery and bombs. Choir out.', vid = 'A04_Choir_M01_00904.sfd', bank = 'A04_VO', cue = 'A04_Choir_M01_00904', faction = 'Aeon'},}
Choir_70 =       {{text = '[{i Choir}]: The Node is heavily defended by artillery, so we are giving you the \'Asylum\' Tech 2 Mobile Shield Generator schematic. Construct a few of them; they should let your units get close to the enemy without taking too much damage. Choir out.', vid = 'A04_Choir_M01_00907.sfd', bank = 'A04_VO', cue = 'A04_Choir_M01_00907', faction = 'Aeon'},}
Choir_71 =       {{text = '[{i Choir}]: Nexus04 is no longer a threat. Advance and capture the Node. Choir out.', vid = 'A04_Choir_M01_00908.sfd', bank = 'A04_VO', cue = 'A04_Choir_M01_00908', faction = 'Aeon'},}
Choir_72 =       {{text = '[{i Choir}]: Remember, Champion, you need only defeat the war machines in the settlement. Choir out. ', vid = 'A04_Choir_M01_00912.sfd', bank = 'A04_VO', cue = 'A04_Choir_M01_00912', faction = 'Aeon'},}
Choir_73 =       {{text = '[{i Choir}]: The Princess is worried that pacifying Nexus04 is taking too long, Champion. Is there a problem? Choir out.', vid = 'A04_Choir_M01_01571.sfd', bank = 'A04_VO', cue = 'A04_Choir_M01_01571', faction = 'Aeon'},}
Choir_74 =       {{text = '[{i Choir}]: The Princess has ordered that the Node be captured ASAP, Champion. Choir out. ', vid = 'A04_Choir_M01_00914.sfd', bank = 'A04_VO', cue = 'A04_Choir_M01_00914', faction = 'Aeon'},}
Choir_75 =       {{text = '[{i Choir}]: The success of the mission hinges on capturing the Node in a timely fashion, Champion. Choir out.', vid = 'A04_Choir_M01_01572.sfd', bank = 'A04_VO', cue = 'A04_Choir_M01_01572', faction = 'Aeon'},}
Choir_76 =       {{text = '[{i Choir}]: The Node has been captured. The techs are preparing to connect to it via a Quantum uplink. Choir out.', vid = 'A04_Choir_M01_00911.sfd', bank = 'A04_VO', cue = 'A04_Choir_M01_00911', faction = 'Aeon'},}
Choir_77 =       {{text = '[{i Choir}]: Our technicians have been unable to penetrate deeper into the Node network. The Nodes simply provide processing power for a larger Mainframe to the north. The Mainframe is heavily defended, but our technicians have been able to isolate and neutralize all nearby defensive structures. Capture the Mainframe so our technicians can continue their work. Once the Mainframe has been captured, the surrounding defenses will come under your control. Choir out.', vid = 'A04_Choir_M02_00918.sfd', bank = 'A04_VO', cue = 'A04_Choir_M02_00918', faction = 'Aeon'},}
Choir_78 =       {{text = '[{i Choir}]: We have hacked into the Mainframe and are investigating. Protect the Mainframe at all costs. Choir out.', vid = 'A04_Choir_M02_00919.sfd', bank = 'A04_VO', cue = 'A04_Choir_M02_00919', faction = 'Aeon'},}
Choir_79 =       {{text = '[{i Choir}]: Node 72 has been captured. Begin constructing defenses around it. Choir out.', vid = 'A04_Choir_M02_00920.sfd', bank = 'A04_VO', cue = 'A04_Choir_M02_00920', faction = 'Aeon'},}
Choir_80 =       {{text = '[{i Choir}]: Node 74 has been captured. Reinforce the position as you see fit. Choir out.', vid = 'A04_Choir_M02_00921.sfd', bank = 'A04_VO', cue = 'A04_Choir_M02_00921', faction = 'Aeon'},}
Choir_81 =       {{text = '[{i Choir}]: Champion, the Cybrans are attempting to cut the Mainframe off from the Network! You must protect the Nodes in our sector until we have extracted the data that we need. Capture the Nodes and then defend them from Cybran attack. Choir out.', vid = 'A04_Choir_M02_00922.sfd', bank = 'A04_VO', cue = 'A04_Choir_M02_00922', faction = 'Aeon'},}
Choir_82 =       {{text = '[{i Choir}]: Champion, our technicians unlocked some of the Node\'s functions and have located an algorithm. Each Node you control increases resource production. Capture all three for the maximum benefit. Choir out.', vid = 'A04_Choir_M02_00924.sfd', bank = 'A04_VO', cue = 'A04_Choir_M02_00924', faction = 'Aeon'},}
Choir_83 =       {{text = '[{i Choir}]: Cybran attacks are incoming! They appear to be focused on the Mainframe, but don\'t forget about defending the Nodes. Choir out.', vid = 'A04_Choir_M02_00925.sfd', bank = 'A04_VO', cue = 'A04_Choir_M02_00925', faction = 'Aeon'},}
Choir_84 =       {{text = '[{i Choir}]: The technicians are still working, Champion. Continue to defend the Nodes! Choir out.', vid = 'A04_Choir_M02_00926.sfd', bank = 'A04_VO', cue = 'A04_Choir_M02_00926', faction = 'Aeon'},}
Choir_85 =       {{text = '[{i Choir}]: The technicians are getting close, but they need more time! Choir out.', vid = 'A04_Choir_M02_00927.sfd', bank = 'A04_VO', cue = 'A04_Choir_M02_00927', faction = 'Aeon'},}
Choir_86 =       {{text = '[{i Choir}]: They\'re almost done. The technicians just need a little more time! Choir out.', vid = 'A04_Choir_M02_00928.sfd', bank = 'A04_VO', cue = 'A04_Choir_M02_00928', faction = 'Aeon'},}
Choir_87 =       {{text = '[{i Choir}]: A Node has been destroyed, but we\'re still using the other two Nodes. Protect them! Choir out.', vid = 'A04_Choir_M02_00929.sfd', bank = 'A04_VO', cue = 'A04_Choir_M02_00929', faction = 'Aeon'},}
Choir_88 =       {{text = '[{i Choir}]: A second Node has been lost. We cannot lose any more! Our mission will end in failure if we lose the last Node! Choir out.', vid = 'A04_Choir_M02_00930.sfd', bank = 'A04_VO', cue = 'A04_Choir_M02_00930', faction = 'Aeon'},}
Choir_89 =       {{text = '[{i Choir}]: The Mainframe is key to the Princess\' plan. Capture it ASAP, Champion. Choir out. ', vid = 'A04_Choir_M02_00931.sfd', bank = 'A04_VO', cue = 'A04_Choir_M02_00931', faction = 'Aeon'},}
Choir_90 =       {{text = '[{i Choir}]: The technicians are ready to begin investigating the Mainframe, but you need to capture it first, Champion. Choir out.', vid = 'A04_Choir_M02_01573.sfd', bank = 'A04_VO', cue = 'A04_Choir_M02_01573', faction = 'Aeon'},}
Choir_91 =       {{text = '[{i Choir}]: The Nodes must be captured so the technicians can continue their work, Champion. Choir out. ', vid = 'A04_Choir_M02_00933.sfd', bank = 'A04_VO', cue = 'A04_Choir_M02_00933', faction = 'Aeon'},}
Choir_92 =       {{text = '[{i Choir}]: We must have full access to the Cybran network if we are to uncover their secrets, Champion. Capture those Nodes ASAP. Choir out.', vid = 'A04_Choir_M02_01574.sfd', bank = 'A04_VO', cue = 'A04_Choir_M02_01574', faction = 'Aeon'},}
Choir_93 =       {{text = '[{i Choir}]: We\'ve lost all the Nodes. The mission cannot be completed. Abort and return home, Champion. Choir out.', vid = 'A04_Choir_M02_00935.sfd', bank = 'A04_VO', cue = 'A04_Choir_M02_00935', faction = 'Aeon'},}
Choir_94 =       {{text = '[{i Choir}]: Choir to Champion, respond. Choir to Champion, please respond. We\'ve lost your signal, please respond.', vid = 'A05_Choir_D01_00937.sfd', bank = 'A05_VO', cue = 'A05_Choir_D01_00937', faction = 'Aeon'},}
Choir_95 =       {{text = '[{i Choir}]: Champion, the Princess has ordered you to protect the civilians. Crusader Rhiza will act as your support-commander. She will help rebuild and repair your base. Choir out.', vid = 'A05_Choir_M01_00959.sfd', bank = 'A05_VO', cue = 'A05_Choir_M01_00959', faction = 'Aeon'},}
Choir_96 =       {{text = '[{i Choir}]: We confirm that Ariel is constructing a squadron of bombers. These markers display the optimal shield placement points. Build a Heavy Shield Generator as indicated. Choir out. ', vid = 'A05_Choir_M01_01154.sfd', bank = 'A05_VO', cue = 'A05_Choir_M01_01154', faction = 'Aeon'},}
Choir_97 =       {{text = '[{i Choir}]: All shields are in place. The colonies are now protected. Choir out. ', vid = 'A05_Choir_M01_00967.sfd', bank = 'A05_VO', cue = 'A05_Choir_M01_00967', faction = 'Aeon'},}
Choir_98 =       {{text = '[{i Choir}]: Champion, Ariel\'s bombers are inbound. Deploy anti-air defenses and protect the civilian colonies. Choir out. ', vid = 'A05_Choir_M01_00969.sfd', bank = 'A05_VO', cue = 'A05_Choir_M01_00969', faction = 'Aeon'},}
Choir_99 =       {{text = '[{i Choir}]: The colonies survived Ariel\'s attack! Choir out.', vid = 'A05_Choir_M01_00970.sfd', bank = 'A05_VO', cue = 'A05_Choir_M01_00970', faction = 'Aeon'},}
Choir_100 =      {{text = '[{i Choir}]: Sensors show that Ariel is building a Strategic Missile Launcher. We are uploading the schematic for the \'Patron\' Tech 3 Strategic Missile Defense to your ACU. Construct a missile defense facility at each of the indicated points. Choir out. ', vid = 'A05_Choir_M01_00972.sfd', bank = 'A05_VO', cue = 'A05_Choir_M01_00972', faction = 'Aeon'},}
Choir_101 =      {{text = '[{i Choir}]: We show that the Strategic Missile Defenses are complete. Ariel\'s missiles should do no more harm. Choir out. ', vid = 'A05_Choir_M01_00973.sfd', bank = 'A05_VO', cue = 'A05_Choir_M01_00973', faction = 'Aeon'},}
Choir_102 =      {{text = '[{i Choir}]: Ariel has launched a nuclear missile! Choir out.', vid = 'A05_Choir_M01_00975.sfd', bank = 'A05_VO', cue = 'A05_Choir_M01_00975', faction = 'Aeon'},}
Choir_103 =      {{text = '[{i Choir}]: Ariel is attacking the eastern colony with air power. Scramble fighters and anti-air units to protect it. Choir out.', vid = 'A05_Choir_M01_00977.sfd', bank = 'A05_VO', cue = 'A05_Choir_M01_00977', faction = 'Aeon'},}
Choir_104 =      {{text = '[{i Choir}]: Crusader Ariel is trying to disembark ground units near the western colony. Prepare your defenses. Choir out. ', vid = 'A05_Choir_M01_00978.sfd', bank = 'A05_VO', cue = 'A05_Choir_M01_00978', faction = 'Aeon'},}
Choir_105 =      {{text = '[{i Choir}]: Ariel is now attacking both colonies with combined air and land assets. Protect the colonies! Choir out.', vid = 'A05_Choir_M01_00979.sfd', bank = 'A05_VO', cue = 'A05_Choir_M01_00979', faction = 'Aeon'},}
Choir_106 =      {{text = '[{i Choir}]: Champion, you still have not deployed all the Strategic Missile Defenses! You must move quickly! Choir out.', vid = 'A05_Choir_M01_00981.sfd', bank = 'A05_VO', cue = 'A05_Choir_M01_00981', faction = 'Aeon'},}
Choir_107 =      {{text = '[{i Choir}]: The colonies are still not protected with Strategic Missile Defenses. You must build them quickly! Choir out.', vid = 'A05_Choir_M01_01575.sfd', bank = 'A05_VO', cue = 'A05_Choir_M01_01575', faction = 'Aeon'},}
Choir_108 =      {{text = '[{i Choir}]: There have been too many civilian casualties--the other Commanders have abandoned us for Marxon. Abort and return. Choir out.', vid = 'A05_Choir_M01_00983.sfd', bank = 'A05_VO', cue = 'A05_Choir_M01_00983', faction = 'Aeon'},}
Choir_109 =      {{text = '[{i Choir}]: The colonies are safe. The Princess will be pleased. Choir out. ', vid = 'A05_Choir_M01_00984.sfd', bank = 'A05_VO', cue = 'A05_Choir_M01_00984', faction = 'Aeon'},}
Choir_110 =      {{text = '[{i Choir}]: Champion, the UEF Commander is constructing artillery positions within range of your base! Only one is online, but we expect him to add two more! Choir out.', vid = 'A05_Choir_M02_00985.sfd', bank = 'A05_VO', cue = 'A05_Choir_M02_00985', faction = 'Aeon'},}
Choir_111 =      {{text = '[{i Choir}]: We are placing markers on the UEF artillery positions. Destroy the artillery as quickly as possible. Choir out.', vid = 'A05_Choir_M02_00987.sfd', bank = 'A05_VO', cue = 'A05_Choir_M02_00987', faction = 'Aeon'},}
Choir_112 =      {{text = '[{i Choir}]: The first UEF artillery piece has been destroyed. Choir out.', vid = 'A05_Choir_M02_00988.sfd', bank = 'A05_VO', cue = 'A05_Choir_M02_00988', faction = 'Aeon'},}
Choir_113 =      {{text = '[{i Choir}]: Champion, we strongly recommend building more shield generators around your base. They are designed to stop artillery and bombs, but work against most kinds of ordinance. They do require a great deal of energy, so support them with Tech 3 Power Generators. Choir out.', vid = 'A05_Choir_M02_00989.sfd', bank = 'A05_VO', cue = 'A05_Choir_M02_00989', faction = 'Aeon'},}
Choir_114 =      {{text = '[{i Choir}]: The second artillery piece has been completed. Choir out. ', vid = 'A05_Choir_M02_00990.sfd', bank = 'A05_VO', cue = 'A05_Choir_M02_00990', faction = 'Aeon'},}
Choir_115 =      {{text = '[{i Choir}]: The second UEF artillery piece has been silenced. Choir out.', vid = 'A05_Choir_M02_00991.sfd', bank = 'A05_VO', cue = 'A05_Choir_M02_00991', faction = 'Aeon'},}
Choir_116 =      {{text = '[{i Choir}]: The third UEF artillery piece is online. Destroy it! Choir out.', vid = 'A05_Choir_M02_00992.sfd', bank = 'A05_VO', cue = 'A05_Choir_M02_00992', faction = 'Aeon'},}
Choir_117 =      {{text = '[{i Choir}]: We show the third UEF artillery piece is no more. Choir out.', vid = 'A05_Choir_M02_00993.sfd', bank = 'A05_VO', cue = 'A05_Choir_M02_00993', faction = 'Aeon'},}
Choir_118 =      {{text = '[{i Choir}]: Ariel has pulled back to her base and is fortifying her position. Sensors show that she doesn\'t appear to be preparing an attack. Deal with the UEF Commander first; we will address Ariel later. Choir out.', vid = 'A05_Choir_M02_00994.sfd', bank = 'A05_VO', cue = 'A05_Choir_M02_00994', faction = 'Aeon'},}
Choir_119 =      {{text = '[{i Choir}]: All the artillery installations have been destroyed. Choir out.', vid = 'A05_Choir_M02_00995.sfd', bank = 'A05_VO', cue = 'A05_Choir_M02_00995', faction = 'Aeon'},}
Choir_120 =      {{text = '[{i Choir}]: The UEF Commander has built a strong artillery position on a plateau just west of his base. From there, he can shell anything approaching him. It is recommended that you destroy his artillery. Choir out.', vid = 'A05_Choir_M02_00996.sfd', bank = 'A05_VO', cue = 'A05_Choir_M02_00996', faction = 'Aeon'},}
Choir_121 =      {{text = '[{i Choir}]: The UEF base is well defended, but a strong assault with Siege Assault Bots, backed up by Mobile Heavy Artillery pieces, should be able to punch through the defensive line. Choir out.', vid = 'A05_Choir_M02_00997.sfd', bank = 'A05_VO', cue = 'A05_Choir_M02_00997', faction = 'Aeon'},}
Choir_122 =      {{text = '[{i Choir}]: Champion, the UEF artillery is still online. Is there a problem? Choir out. ', vid = 'A05_Choir_M02_00999.sfd', bank = 'A05_VO', cue = 'A05_Choir_M02_00999', faction = 'Aeon'},}
Choir_123 =      {{text = '[{i Choir}]: Perhaps utilizing a different unit will help take those UEF artillery positions down. Choir out.', vid = 'A05_Choir_M02_01576.sfd', bank = 'A05_VO', cue = 'A05_Choir_M02_01576', faction = 'Aeon'},}
Choir_124 =      {{text = '[{i Choir}]: You have defeated several UEF Commanders already, Champion. This one should not take long. Choir out. ', vid = 'A05_Choir_M02_01001.sfd', bank = 'A05_VO', cue = 'A05_Choir_M02_01001', faction = 'Aeon'},}
Choir_125 =      {{text = '[{i Choir}]: This UEF Commander will not stand against the Princess\' Champion. Choir out.', vid = 'A05_Choir_M02_01577.sfd', bank = 'A05_VO', cue = 'A05_Choir_M02_01577', faction = 'Aeon'},}
Choir_126 =      {{text = '[{i Choir}]: The UEF Commander has been defeated. You must now defeat Crusader Ariel.', vid = 'A05_Choir_M02_01003.sfd', bank = 'A05_VO', cue = 'A05_Choir_M02_01003', faction = 'Aeon'},}
Choir_127 =      {{text = '[{i Choir}]: Champion, we are reading a unit moving towards your position from Ariel\'s base. It\'s a Galactic Colossus! Ariel has built a Colossus!', vid = 'A05_Choir_M03_01008.sfd', bank = 'A05_VO', cue = 'A05_Choir_M03_01008', faction = 'Aeon'},}
Choir_128 =      {{text = '[{i Choir}]: The Colossus is slow, so it will take some time to reach you. You have been authorized to receive the \'Apocalypse\' Tech 3 Nuclear Missile Launcher schematic. Deploy them as you see fit. Choir out.', vid = 'A05_Choir_M03_01010.sfd', bank = 'A05_VO', cue = 'A05_Choir_M03_01010', faction = 'Aeon'},}
Choir_129 =      {{text = '[{i Choir}]: The Colossus is down! I repeat, the Colossus is down!', vid = 'A05_Choir_M03_01012.sfd', bank = 'A05_VO', cue = 'A05_Choir_M03_01012', faction = 'Aeon'},}
Choir_130 =      {{text = '[{i Choir}]: All that remains is to defeat Ariel. Choir out.', vid = 'A05_Choir_M03_01014.sfd', bank = 'A05_VO', cue = 'A05_Choir_M03_01014', faction = 'Aeon'},}
Choir_131 =      {{text = '[{i Choir}]: Before we can pursue the Avatar, you must first defeat Ariel. Choir out.', vid = 'A05_Choir_M03_01015.sfd', bank = 'A05_VO', cue = 'A05_Choir_M03_01015', faction = 'Aeon'},}
Choir_132 =      {{text = '[{i Choir}]: Choir to Champion, respond. Choir to Champion, please respond. We\'ve lost your signal, please respond.', vid = 'A06_Choir_D01_01017.sfd', bank = 'A06_VO', cue = 'A06_Choir_D01_01017', faction = 'Aeon'},}
Choir_133 =      {{text = '[{i Choir}]: We have prepared a base for you.', vid = 'A06_Choir_M01_01042.sfd', bank = 'A06_VO', cue = 'A06_Choir_M01_01042', faction = 'Aeon'},}
Choir_134 =      {{text = '[{i Choir}]: Champion, Black Sun\'s construction is nearly complete. You must capture the Control Center and prevent the UEF from firing the weapon.\n', vid = 'A06_Choir_M01_01054.sfd', bank = 'A06_VO', cue = 'A06_Choir_M01_01054', faction = 'Aeon'},}
Choir_135 =      {{text = '[{i Choir}]: The Control Center is yours, Champion. Choir out.', vid = 'A06_Choir_M01_01048.sfd', bank = 'A06_VO', cue = 'A06_Choir_M01_01048', faction = 'Aeon'},}
Choir_136 =      {{text = '[{i Choir}]: Champion, a Cybran naval force is heading toward your base. Wait...a UEF force is moving to intercept...now both are converging on your base. Prepare your defenses. Choir out.', vid = 'A06_Choir_M01_01049.sfd', bank = 'A06_VO', cue = 'A06_Choir_M01_01049', faction = 'Aeon'},}
Choir_137 =      {{text = '[{i Choir}]: You must capture the Control Center, Champion! Choir out. ', vid = 'A06_Choir_M01_01052.sfd', bank = 'A06_VO', cue = 'A06_Choir_M01_01052', faction = 'Aeon'},}
Choir_138 =      {{text = '[{i Choir}]: The Princess\' plan depends on you capturing the Control Center, Champion. Choir out.', vid = 'A06_Choir_M01_01053.sfd', bank = 'A06_VO', cue = 'A06_Choir_M01_01053', faction = 'Aeon'},}
Choir_139 =      {{text = '[{i Choir}]: The Control Center has been lost. The Princess will not be able to fulfill her plan!', vid = 'A06_Choir_M01_01056.sfd', bank = 'A06_VO', cue = 'A06_Choir_M01_01056', faction = 'Aeon'},}
Choir_140 =      {{text = '[{i Choir}]: The Control Center is ours, Champion, and both naval forces have been defeated. Choir out.', vid = 'A06_Choir_M01_01057.sfd', bank = 'A06_VO', cue = 'A06_Choir_M01_01057', faction = 'Aeon'},}
Choir_141 =      {{text = '[{i Choir}]: Champion, the Cybran Commander is gathering a large army to the north of the Control Center. Establish a defensive perimeter around Black Sun and repel the assault. Once that has been done, defeat the Commander himself. Choir out.', vid = 'A06_Choir_M02_01058.sfd', bank = 'A06_VO', cue = 'A06_Choir_M02_01058', faction = 'Aeon'},} 
Choir_142 =      {{text = '[{i Choir}]: You now have access to experimental weapons. Use them wisely. Choir out.', vid = 'A06_Choir_M01_01055.sfd', bank = 'A06_VO', cue = 'A06_Choir_M01_01055', faction = 'Aeon'},}
Choir_143 =      {{text = '[{i Choir}]: Princess, Crusader Arnold and Avatar Marxon are advancing on Black Sun. Should we divert the Champion?', vid = 'A06_Choir_M02_01059.sfd', bank = 'A06_VO', cue = 'A06_Choir_M02_01059', faction = 'Aeon'},}
Choir_144 =      {{text = '[{i Choir}]: The Cybran Commander is almost finished preparing his attack. It will begin advancing soon. Choir out.', vid = 'A06_Choir_M02_01062.sfd', bank = 'A06_VO', cue = 'A06_Choir_M02_01062', faction = 'Aeon'},}
Choir_145 =      {{text = '[{i Choir}]: The Cybran will be moving out at any moment. Make sure your forces are in position. Choir out.', vid = 'A06_Choir_M02_01063.sfd', bank = 'A06_VO', cue = 'A06_Choir_M02_01063', faction = 'Aeon'},}
Choir_146 =      {{text = '[{i Choir}]: The attack has begun! Choir out.', vid = 'A06_Choir_M02_01065.sfd', bank = 'A06_VO', cue = 'A06_Choir_M02_01065', faction = 'Aeon'},}
Choir_147 =      {{text = '[{i Choir}]: The Cybran advance has been blunted. Move on his base and defeat the Cybran Commander ASAP. Choir out.', vid = 'A06_Choir_M02_01066.sfd', bank = 'A06_VO', cue = 'A06_Choir_M02_01066', faction = 'Aeon'},}
Choir_148 =      {{text = '[{i Choir}]: The Cybran Commander has been destroyed. He is no longer a threat.', vid = 'A06_Choir_M02_01069.sfd', bank = 'A06_VO', cue = 'A06_Choir_M02_01069', faction = 'Aeon'},}
Choir_149 =      {{text = '[{i Choir}]: You must quickly defeat the Cybran, Champion! Choir out.', vid = 'A06_Choir_M02_01070.sfd', bank = 'A06_VO', cue = 'A06_Choir_M02_01070', faction = 'Aeon'},}
Choir_150 =      {{text = '[{i Choir}]: The UEF Commander has been defeated. Arnold and Marxon have captured Black Sun. Choir out.', vid = 'A06_Choir_M02_01073.sfd', bank = 'A06_VO', cue = 'A06_Choir_M02_01073', faction = 'Aeon'},}
Choir_151 =      {{text = '[{i Choir}]: You are the Princess\' Champion! No mere Cybran can defeat you! Choir out.', vid = 'A06_Choir_M02_01071.sfd', bank = 'A06_VO', cue = 'A06_Choir_M02_01071', faction = 'Aeon'},}
Choir_152 =      {{text = '[{i Choir}]: The Cybran Commander has captured the Control Center and is uploading the Quantum Virus. There\'s no way for us to purge the Virus! Abort mission and return. Choir out.', vid = 'A06_Choir_M02_01072.sfd', bank = 'A06_VO', cue = 'A06_Choir_M02_01072', faction = 'Aeon'},}
Choir_153 =      {{text = '[{i Choir}]: Champion, defeat Marxon. Choir out. ', vid = 'A06_Choir_M03_01081.sfd', bank = 'A06_VO', cue = 'A06_Choir_M03_01081', faction = 'Aeon'},}
Choir_154 =      {{text = '[{i Choir}]: We have UEF units landing to the north. Be prepared for an assault! Choir out. ', vid = 'A06_Choir_M03_01088.sfd', bank = 'A06_VO', cue = 'A06_Choir_M03_01088', faction = 'Aeon'},}
Choir_155 =      {{text = '[{i Choir}]: We have every faith you will defeat Marxon, Champion! Choir out. ', vid = 'A06_Choir_M03_01089.sfd', bank = 'A06_VO', cue = 'A06_Choir_M03_01089', faction = 'Aeon'},}
Choir_156 =      {{text = '[{i Choir}]: Your orders are to destroy Avatar Marxon. Choir out.', vid = 'A06_Choir_M03_01090.sfd', bank = 'A06_VO', cue = 'A06_Choir_M03_01090', faction = 'Aeon'},}

-- Uef Clarke
Clarke_1 =       {{text = '[{i Clarke}]: Get out of there, Zach!', vid = 'E03_Clarke_M01_0115.sfd', bank = 'E03_VO', cue = 'E03_Clarke_M01_0115', faction = 'UEF'},}
Clarke_2 =       {{text = '[{i Clarke}]: Zach! Zach, come in! ', vid = 'E03_Clarke_M01_0116.sfd', bank = 'E03_VO', cue = 'E03_Clarke_M01_0116', faction = 'UEF'},}
Clarke_3 =       {{text = '[{i Clarke}]: Captain, the Aeon are heading toward your position. These are the same units that killed Zach. Teach them a harsh lesson.', vid = 'E03_Clarke_M02_0117.sfd', bank = 'E03_VO', cue = 'E03_Clarke_M02_0117', faction = 'UEF'},}
Clarke_4 =       {{text = '[{i Clarke}]: That crazy religion killed a good man today, Captain. Remember that. Jam her or you\'ll be cleaning comm. arrays for the rest of your career. ', vid = 'E03_Clarke_M04_0118.sfd', bank = 'E03_VO', cue = 'E03_Clarke_M04_0118', faction = 'UEF'},}
Clarke_5 =       {{text = '[{i Clarke}]: Dr. Sweeney was invaluable. His loss means the UEF has no chance of winning the war. ', vid = 'E04_Clarke_M02_0177.sfd', bank = 'E04_VO', cue = 'E04_Clarke_M02_0177', faction = 'UEF'},}
Clarke_6 =       {{text = '[{i Clarke}]: Captain, this is General Clarke. Sweeney has informed us that Dr. Brackman is on Minerva. Kill that crazy old bastard. Do not let him escape.', vid = 'E04_Clarke_M03_0160.sfd', bank = 'E04_VO', cue = 'E04_Clarke_M03_0160', faction = 'UEF'},}
Clarke_7 =       {{text = '[{i Clarke}]: Did we get him? Do we have confirmation that Brackman was there? Did we get him?', vid = 'E04_Clarke_M03_0161.sfd', bank = 'E04_VO', cue = 'E04_Clarke_M03_0161', faction = 'UEF'},}  
Clarke_8 =       {{text = '[{i Clarke}]: Dammit! We were so close.', vid = 'E04_Clarke_M03_0162.sfd', bank = 'E04_VO', cue = 'E04_Clarke_M03_0162', faction = 'UEF'},}
Clarke_9 =       {{text = '[{i Clarke}]: Good job, Commander. The information you collected on the viruses could protect us from further Cybran disruptions. Next time Brackman won\'t escape us.', vid = 'E04_Clarke_M03_01141.sfd', bank = 'E04_VO', cue = 'E04_Clarke_M03_01141', faction = 'UEF'},}
Clarke_10 =      {{text = '[{i Clarke}]: Zach?', vid = 'E05_Clarke_M01_0218.sfd', bank = 'E05_VO', cue = 'E05_Clarke_M01_0218', faction = 'UEF'},}
Clarke_11 =      {{text = '[{i Clarke}]: But...you died on Matar...', vid = 'E05_Clarke_M01_0219.sfd', bank = 'E05_VO', cue = 'E05_Clarke_M01_0219', faction = 'UEF'},} 
Clarke_12 =      {{text = '[{i Clarke}]: ...those monsters brainwashed you!', vid = 'E05_Clarke_M01_0220.sfd', bank = 'E05_VO', cue = 'E05_Clarke_M01_0220', faction = 'UEF'},}
Clarke_13 =      {{text = '[{i Clarke}]: Captain, eradicate the Aeon Commander. ', vid = 'E05_Clarke_M01_0221.sfd', bank = 'E05_VO', cue = 'E05_Clarke_M01_0221', faction = 'UEF'},}  
Clarke_14 =      {{text = '[{i Clarke}]: Too many of the components have been lost. Black Sun cannot be completed. Return to Earth, Captain. We\'re making our final stand here.', vid = 'E05_Clarke_M01_0222.sfd', bank = 'E05_VO', cue = 'E05_Clarke_M01_0222', faction = 'UEF'},}
Clarke_15 =      {{text = '[{i Clarke}]: A facility has been lost. Black Sun cannot be completed. Return to Earth, Captain. We\'ll fight them here.', vid = 'E05_Clarke_M03_0224.sfd', bank = 'E05_VO', cue = 'E05_Clarke_M03_0224', faction = 'UEF'},}
Clarke_16 =      {{text = '[{i Clarke}]: The Control Center has been captured! We cannot fire Black Sun without it. Recapture it! Now!', vid = 'E06_Clarke_M02_0293.sfd', bank = 'E06_VO', cue = 'E06_Clarke_M02_0293', faction = 'UEF'},}
Clarke_17 =      {{text = '[{i Clarke}]: Attention UEF Forces, this is General Clarke. We have lost Black Sun, but we will never surrender! All units return to Earth Command. We will make our final stand there. Clarke out.\n\n', vid = 'E06_Clarke_M02_0294.sfd', bank = 'E06_VO', cue = 'E06_Clarke_M02_0294', faction = 'UEF'},}
Clarke_18 =      {{text = '[{i Clarke}]: Destroy Arnold and any who march with him.', vid = 'E06_Clarke_M03_0295.sfd', bank = 'E06_VO', cue = 'E06_Clarke_M03_0295', faction = 'UEF'},}
Clarke_19 =      {{text = '[{i Clarke}]: Select Black Sun and press the Fire button, Major. It\'s time to end this war.', vid = 'E06_Clarke_M03_0296.sfd', bank = 'E06_VO', cue = 'E06_Clarke_M03_0296', faction = 'UEF'},}
Clarke_20 =      {{text = '[{i Clarke}]: This is General Clarke! Stop them!', vid = 'C06_Clarke_M03_00595.sfd', bank = 'C06_VO', cue = 'C06_Clarke_M03_00595', faction = 'UEF'},}

-- Cybran Dostya
Dostya_1  =     {{text = '[{i Dostya}]: Ignore him, Commander. Concentrate on the battle ahead. Dostya out.', vid = 'X01_Dostya_M01_02878.sfd', bank = 'X01_VO', cue = 'X01_Dostya_M01_02878', faction = 'Cybran'},}
Dostya_2  =     {{text = '[{i Dostya}]: We\'ll fight you to our last breath.', vid = 'X01_Dostya_M02_03667.sfd', bank = 'X01_VO', cue = 'X01_Dostya_M02_03667', faction = 'Cybran'},}
Dostya_3  =     {{text = '[{i Dostya}]: Time is of the essence, Commander. You must destroy the Order commander. Dostya out.', vid = 'X01_Dostya_M02_04887.sfd', bank = 'X01_VO', cue = 'X01_Dostya_M02_04887', faction = 'Cybran'},}
Dostya_4  =     {{text = '[{i Dostya}]: Defeat the Seraphim, Commander. Show this fool how Cybrans fight. Dostya out.', vid = 'X01_Dostya_M03_03698.sfd', bank = 'X01_VO', cue = 'X01_Dostya_M03_03698', faction = 'Cybran'},}
Dostya_5  =     {{text = '[{i Dostya}]: Defeat the Seraphim and its experimentals, Commander. Show this fool how Cybrans fight. Dostya out.', vid = 'X01_Dostya_M03_04696.sfd', bank = 'X01_VO', cue = 'X01_Dostya_M03_04696', faction = 'Cybran'},}
Dostya_6  =     {{text = '[{i Dostya}]: I suspect that there is a traitor in our midst, but I need proof. I have prepared a tracking bug that will let us monitor all Quantum communication to and from QAI. You must find a way to get it into QAI\'s systems. Dostya out.', vid = 'X02_Dostya_M01_03516.sfd', bank = 'X02_VO', cue = 'X02_Dostya_M01_03516', faction = 'Cybran'},}
Dostya_7  =     {{text = '[{i Dostya}]: You have received the schematic for the Jester, a light gunship. It\'s primarily designed for base defense, so put it to good use. Dostya out.', vid = 'X02_Dostya_M01_04892.sfd', bank = 'X02_VO', cue = 'X02_Dostya_M01_04892', faction = 'Cybran'},}    
Dostya_8  =     {{text = '[{i Dostya}]: We\'re sending you the schematic for the Fire Beetle, Commander. It\'s a mobile bomb. Just move it into position and detonate. Dostya out.', vid = 'X02_Dostya_M01_04893.sfd', bank = 'X02_VO', cue = 'X02_Dostya_M01_04893', faction = 'Cybran'},}
Dostya_9  =     {{text = '[{i Dostya}]: The tracking bug is ready to deploy. Move any Engineer to the designated position within QAI\'s base, and the Engineer will upload the tracking bug. Dostya out.', vid = 'X02_Dostya_M03_04317.sfd', bank = 'X02_VO', cue = 'X02_Dostya_M03_04317', faction = 'Cybran'},}
Dostya_10 =     {{text = '[{i Dostya}]: I would urge you to move an Engineer to the designated position, Commander. Dostya out.', vid = 'X02_Dostya_M03_03590.sfd', bank = 'X02_VO', cue = 'X02_Dostya_M03_03590', faction = 'Cybran'},}
Dostya_11 =     {{text = '[{i Dostya}]: A word of warning, Commander. QAI is protecting its shores with a new torpedo system. Because it is constructed on the seafloor, the only way to destroy it is with torpedoes. Dostya out.', vid = 'X02_Dostya_M03_03591.sfd', bank = 'X02_VO', cue = 'X02_Dostya_M03_03591', faction = 'Cybran'},}
Dostya_12 =     {{text = '[{i Dostya}]: The Engineer has reached its destination and the program has been deployed. The program has tagged QAI\'s ACU -- attack and destroy it. Dostya out.', vid = 'X02_Dostya_M03_04700.sfd', bank = 'X02_VO', cue = 'X02_Dostya_M03_04700', faction = 'Cybran'},}
Dostya_13 =     {{text = '[{i Dostya}]: The Engineer has been lost, Commander. You will need to deliver another one to the Loyalists and have the program reinstalled. Dostya out.', vid = 'X02_Dostya_M03_03592.sfd', bank = 'X02_VO', cue = 'X02_Dostya_M03_03592', faction = 'Cybran'},}
Dostya_14 =     {{text = '[{i Dostya}]: The Engineer has reached its destination and the program has been deployed; it is now spreading throughout QAI\'s systems. It\'s tagged QAI\'s ACU -- attack and destroy it. Dostya out.', vid = 'X02_Dostya_M03_03593.sfd', bank = 'X02_VO', cue = 'X02_Dostya_M03_03593', faction = 'Cybran'},}
Dostya_15 =     {{text = '[{i Dostya}]: Our technicians managed to recover one of QAI\'s new torpedo systems. We have christened it the HARMS and are uploading it to your ACU. Dostya out.', vid = 'X03_Dostya_M01_04356.sfd', bank = 'X03_VO', cue = 'X03_Dostya_M01_04356', faction = 'Cybran'},}
Dostya_16 =     {{text = '[{i Dostya}]: I have received a report on the situation, Commander. We are uploading the schematic for our new T2 Barracuda. It\'s a dedicated sub-killer and should help you clear out those subs. Dostya out.', vid = 'X03_Dostya_M02_04362.sfd', bank = 'X03_VO', cue = 'X03_Dostya_M02_04362', faction = 'Cybran'},}
Dostya_17 =     {{text = '[{i Dostya}]: Commander, the tracking bug has detected a signal being sent to QAI, and it is definitely coming from Coalition space. I am investigating the source. Dostya out.', vid = 'X03_Dostya_M02_04369.sfd', bank = 'X03_VO', cue = 'X03_Dostya_M02_04369', faction = 'Cybran'},}
Dostya_18 =     {{text = '[{i Dostya}]: I am detecting an enemy base roughly two clicks north of my position. I will construct a base and attack as soon as possible. Dostya out.', vid = 'X04_Dostya_M01_03595.sfd', bank = 'X04_VO', cue = 'X04_Dostya_M01_03595', faction = 'Cybran'},}
Dostya_19 =     {{text = '[{i Dostya}]: I am beginning my attack. Resistance is minimal, and I am not detecting an enemy ACU. HQ, are you reading anything in the sector?', vid = 'X04_Dostya_M01_03597.sfd', bank = 'X04_VO', cue = 'X04_Dostya_M01_03597', faction = 'Cybran'},}
Dostya_20 =     {{text = '[{i Dostya}]: Keep your eyes open, HQ. I do not wish to be surprised.', vid = 'X04_Dostya_M01_03601.sfd', bank = 'X04_VO', cue = 'X04_Dostya_M01_03601', faction = 'Cybran'},}
Dostya_21 =     {{text = '[{i Dostya}]: Commander, I have a number of Bricks on standby. Use Command Signal to control them. Dostya out.', vid = 'X04_Dostya_M01_04392.sfd', bank = 'X04_VO', cue = 'X04_Dostya_M01_04392', faction = 'Cybran'},}
Dostya_22 =     {{text = '[{i Dostya}]: HQ, I have completed my objective and am reinforcing my position. Dostya out.', vid = 'X04_Dostya_M01_04393.sfd', bank = 'X04_VO', cue = 'X04_Dostya_M01_04393', faction = 'Cybran'},}
Dostya_23 =     {{text = '[{i Dostya}]: Complete your objective as quickly as possible, Commander. Dostya out.', vid = 'X04_Dostya_M01_03731.sfd', bank = 'X04_VO', cue = 'X04_Dostya_M01_03731', faction = 'Cybran'},}
Dostya_24 =     {{text = '[{i Dostya}]: Finish your objective as quickly as possible, Commander. Dostya out.', vid = 'X04_Dostya_M01_03732.sfd', bank = 'X04_VO', cue = 'X04_Dostya_M01_03732', faction = 'Cybran'},}
Dostya_25 =     {{text = '[{i Dostya}]: I have a situation here ... the recall command is auto-failing. HQ, can you confirm? Please run diagnostics on the return Gate. HQ, please respond.', vid = 'X04_Dostya_M01_03734.sfd', bank = 'X04_VO', cue = 'X04_Dostya_M01_03734', faction = 'Cybran'},}
Dostya_26 =     {{text = '[{i Dostya}]: Commander, we have lost all communication with Coalition Command, and we cannot recall. Wait ... I am registering something to your west. It appears to be a jamming array -- it must what is blocking our communications. Locate and destroy the jamming device. Dostya out.', vid = 'X04_Dostya_M01_03735.sfd', bank = 'X04_VO', cue = 'X04_Dostya_M01_03735', faction = 'Cybran'},}
Dostya_27 =     {{text = '[{i Dostya}]: Commander, we have lost all communication with Coalition Command, and we cannot recall.', vid = 'X04_Dostya_M01_03735.sfd', bank = 'X04_VO', cue = 'X04_Dostya_M01_03735', faction = 'Cybran'},}
Dostya_28 =     {{text = '[{i Dostya}]: Wait ... I am registering something to your west.', vid = 'X04_Dostya_M01_03736.sfd', bank = 'X04_VO', cue = 'X04_Dostya_M01_03736', faction = 'Cybran'},}
Dostya_29 =     {{text = '[{i Dostya}]: It appears to be a jamming array -- it must what is blocking our communications.', vid = 'X04_Dostya_M01_03735.sfd', bank = 'X04_VO', cue = 'X04_Dostya_M01_03737', faction = 'Cybran'},}
Dostya_30 =     {{text = '[{i Dostya}]: Destroy the jamming device. Dostya out.', vid = 'X04_Dostya_M01_03738.sfd', bank = 'X04_VO', cue = 'X04_Dostya_M01_03738', faction = 'Cybran'},}
Dostya_31 =     {{text = '[{i Dostya}]: Commander, additional jamming devices are disrupting the Quantum Field and preventing us from recalling or receiving help. Stay on target and destroy that array blocking our communication. Once it\'s down, HQ will be able advise on how to negate the Quantum distortions. Dostya out.', vid = 'X04_Dostya_M02_04014.sfd', bank = 'X04_VO', cue = 'X04_Dostya_M02_04014', faction = 'Cybran'},}
Dostya_32 =     {{text = '[{i Dostya}]: A Seraphim scout just flew over my base, so they know my position. It won\'t be long before they attack.', vid = 'X04_Dostya_M02_02736.sfd', bank = 'X04_VO', cue = 'X04_Dostya_M02_02736', faction = 'Cybran'},}
Dostya_33 =     {{text = '[{i Dostya}]: I have a visual on the device. This can\'t be right ... it looks like a prototype device that Dr. Brackman was creating. Destroy it. We must reach HQ. Dostya out.', vid = 'X04_Dostya_M02_03741.sfd', bank = 'X04_VO', cue = 'X04_Dostya_M02_03741', faction = 'Cybran'},}
Dostya_34 =     {{text = '[{i Dostya}]: Destroy the jammer! We must re-establish contact with Coalition Command!', vid = 'X04_Dostya_M02_03742.sfd', bank = 'X04_VO', cue = 'X04_Dostya_M02_03742', faction = 'Cybran'},}
Dostya_35 =     {{text = '[{i Dostya}]: You must destroy the jammer! Hurry! I fear the Seraphim will attack at any moment!', vid = 'X04_Dostya_M02_03743.sfd', bank = 'X04_VO', cue = 'X04_Dostya_M02_03743', faction = 'Cybran'},}
Dostya_36 =     {{text = '[{i Dostya}]: I have a squadron of air units ready to attack, Commander. If you give me an attack marker, I will unleash them.', vid = 'X04_Dostya_M02_04395.sfd', bank = 'X04_VO', cue = 'X04_Dostya_M02_04395', faction = 'Cybran'},}
Dostya_37 =     {{text = '[{i Dostya}]: I have a Soul Ripper ready to attack, Commander. If you give me an attack marker, I will send it to aid you.', vid = 'X04_Dostya_M02_04396.sfd', bank = 'X04_VO', cue = 'X04_Dostya_M02_04396', faction = 'Cybran'},}
Dostya_38 =     {{text = '[{i Dostya}]: I have several Soul Rippers ready to attack, Commander. If you give me an attack marker, I will send them to aid you.', vid = 'X04_Dostya_M02_04397.sfd', bank = 'X04_VO', cue = 'X04_Dostya_M02_04397', faction = 'Cybran'},}
Dostya_39 =     {{text = '[{i Dostya}]: I am under heavy attack and must recall my air forces. Keep up the attack, Commander. Destroying that jammer is our only hope!', vid = 'X04_Dostya_M02_04398.sfd', bank = 'X04_VO', cue = 'X04_Dostya_M02_04398', faction = 'Cybran'},}
Dostya_40 =     {{text = '[{i Dostya}]: The device is destroyed! Dostya to HQ, respond!', vid = 'X04_Dostya_M02_03744.sfd', bank = 'X04_VO', cue = 'X04_Dostya_M02_03744', faction = 'Cybran'},}
Dostya_41 =     {{text = '[{i Dostya}]: Listen to me! The Seraphim have a jamming device that is preventing recall. It\'s out of our operational area, so you must find a countermeasure.', vid = 'X04_Dostya_M02_03746.sfd', bank = 'X04_VO', cue = 'X04_Dostya_M02_03746', faction = 'Cybran'},}
Dostya_42 =     {{text = '[{i Dostya}]: Inform General Hall that we\'ve been compromised! That jammer was based on a top secret prototype created by Dr. Brackman ... oh no, hundreds of enemy units are descending upon my location. Get us out of here, HQ!', vid = 'X04_Dostya_M02_03748.sfd', bank = 'X04_VO', cue = 'X04_Dostya_M02_03748', faction = 'Cybran'},}
Dostya_43 =     {{text = '[{i Dostya}]: Commander, prepare your defenses! I will hold them off.', vid = 'X04_Dostya_M03_03749.sfd', bank = 'X04_VO', cue = 'X04_Dostya_M03_03749', faction = 'Cybran'},}
Dostya_44 =     {{text = '[{i Dostya}]: They are all over me ... my defenses are failing. I have visual on an ACU, but it\'s not Seraphim. It\'s Cybran ...', vid = 'X04_Dostya_M03_03751.sfd', bank = 'X04_VO', cue = 'X04_Dostya_M03_03751', faction = 'Cybran'},}
Dostya_45 =     {{text = '[{i Dostya}]: Hex5! I should have known that if anyone turned on our beloved father that it would be you.', vid = 'X04_Dostya_M03_03753.sfd', bank = 'X04_VO', cue = 'X04_Dostya_M03_03753', faction = 'Cybran'},}
Dostya_46 =     {{text = '[{i Dostya}]: Getting hit from all sides ... too many of them ... too many ...', vid = 'X04_Dostya_M03_03755.sfd', bank = 'X04_VO', cue = 'X04_Dostya_M03_03755', faction = 'Cybran'},}
-- No vid
Dostya_47 =     {{text = '[{i Dostya}]: I have little to fear from the likes of you.', bank = 'XGG', cue = 'XGG_Dostya_MP1_04598'},}
Dostya_48 =     {{text = '[{i Dostya}]: Observe. You may learn something.', bank = 'XGG', cue = 'XGG_Dostya_MP1_04599'},}
Dostya_49 =     {{text = '[{i Dostya}]: I would flee, if I were you.', bank = 'XGG', cue = 'XGG_Dostya_MP1_04600'},}
Dostya_50 =     {{text = '[{i Dostya}]: You will be just another in my list of victories.', bank = 'XGG', cue = 'XGG_Dostya_MP1_04601'},}
Dostya_51 =     {{text = '[{i Dostya}]: You are not worth my time.', bank = 'XGG', cue = 'XGG_Dostya_MP1_04602'},}
Dostya_52 =     {{text = '[{i Dostya}]: Your defeat is without question.', bank = 'XGG', cue = 'XGG_Dostya_MP1_04603'},}
Dostya_53 =     {{text = '[{i Dostya}]: You seem to have courage. Intelligence seems to be lacking.', bank = 'XGG', cue = 'XGG_Dostya_MP1_04604'},}
Dostya_54 =     {{text = '[{i Dostya}]: I will destroy you.', bank = 'XGG', cue = 'XGG_Dostya_MP1_04605'},}

Dostya_55 =     {{text = '[{i Dostya}]: Dr. Brackman is off-planet and safe. Your orders are to engage the UEF Commander and destroy him. Make him pay for attacking our beloved father.', bank = 'C03_VO', cue = 'C03_M03_010_010'},}
Dostya_56 =     {{text = '[{i Dostya}]: The UEF Commander is no more. Come home.', bank = 'C03_VO', cue = 'C03_M03_050_020'},}
Dostya_56 =     {{text = '[{i Dostya}]: What? The UEF located our base on Minerva? Impossible! I am on my way, Dr. Brackman.', bank = 'C03_VO', cue = 'C03_B01_004_020'},}


-- Uef EarthCom
EarthCom_1  =   {{text = '[{i EarthCom}]: Lieutenant! Come in, Lieutenant!  We\'ve lost your signal...Lieutenant, report...', vid = 'E01_EarthCom_D01_0022.sfd', bank = 'E01_VO', cue = 'E01_EarthCom_D01_0022', faction = 'UEF'},}
EarthCom_2  =   {{text = '[{i EarthCom}]: Sir, Colonel Arnold is clearing out your landing zone. He will provide you with tactical advice during the course of the mission. EarthCom out.', vid = 'E01_EarthCom_M01_0023.sfd', bank = 'E01_VO', cue = 'E01_EarthCom_M01_0023', faction = 'UEF'},}
EarthCom_3  =   {{text = '[{i EarthCom}]: Sir, maybe you should check your objectives.', vid = 'E01_EarthCom_M01_01131.sfd', bank = 'E01_VO', cue = 'E01_EarthCom_M01_01131', faction = 'UEF'},}
EarthCom_4  =   {{text = '[{i EarthCom}]: Sir, General Clarke sends her congratulations for successfully completing your first mission.', vid = 'E01_EarthCom_M07_0024.sfd', bank = 'E01_VO', cue = 'E01_EarthCom_M07_0024', faction = 'UEF'},}
EarthCom_5  =   {{text = '[{i EarthCom}]: Lieutenant! Come in, Lieutenant! We\'ve lost your signal...Lieutenant, report...', vid = 'E02_EarthCom_D01_0056.sfd', bank = 'E02_VO', cue = 'E02_EarthCom_D01_0056', faction = 'UEF'},}
EarthCom_6  =   {{text = '[{i EarthCom}]: Colonel Arnold, General Clarke\'s orders are to get to Station Lima Foxtrot and fix that Power Core ASAP. EarthCom out.', vid = 'E02_EarthCom_M01_0044.sfd', bank = 'E02_VO', cue = 'E02_EarthCom_M01_0044', faction = 'UEF'},}
EarthCom_7  =   {{text = '[{i EarthCom}]: Colonel Arnold has confirmed that you have constructed an Air Factory. We\'re uploading the schematic for the C-6 \'Courier\' Light Air Transport.', vid = 'E02_EarthCom_M01_0057.sfd', bank = 'E02_VO', cue = 'E02_EarthCom_M01_0057', faction = 'UEF'},}
EarthCom_8  =   {{text = '[{i EarthCom}]: Lieutenant, General Clarke has placed you in charge of the OP. Establish a base with some basic defenses and then build an Air Factory. Colonel Arnold will advise as necessary.', vid = 'E02_EarthCom_M01_0058.sfd', bank = 'E02_VO', cue = 'E02_EarthCom_M01_0058', faction = 'UEF'},}
EarthCom_9  =   {{text = '[{i EarthCom}]: Station Lima Foxtrot come in. I repeat, Station Lima Foxtrot come in. It\'s no good. I think we lost them. Abort mission and recall. EarthCom out.', vid = 'E02_EarthCom_M01_0059.sfd', bank = 'E02_VO', cue = 'E02_EarthCom_M01_0059', faction = 'UEF'},}
EarthCom_10 =   {{text = '[{i EarthCom}]: The Core has been repaired and the Station is secure. Good job, Lieutenant. EarthCom out.', vid = 'E02_EarthCom_M01_0060.sfd', bank = 'E02_VO', cue = 'E02_EarthCom_M01_0060', faction = 'UEF'},}
EarthCom_11 =   {{text = '[{i EarthCom}]: Sir, you haven\'t built a transport yet. Is there a problem? EarthCom out. ', vid = 'E02_EarthCom_M01_00691.sfd', bank = 'E02_VO', cue = 'E02_EarthCom_M01_00691', faction = 'UEF'},}
EarthCom_12 =   {{text = '[{i EarthCom}]: Sir, General Clarke is wondering why the Power Core hasn\'t been repaired yet. EarthCom out.', vid = 'E02_EarthCom_M01_00692.sfd', bank = 'E02_VO', cue = 'E02_EarthCom_M01_00692', faction = 'UEF'},}
EarthCom_13 =   {{text = '[{i EarthCom}]: Sir, scans detect a large group of Aeon ground forces. They appear to be readying another attack. General Clarke has authorized you to receive the \'Air Cleaner\' Tech 2 Anti-Air Turret and the \'Triad\' Tech 2 Point Defense Turret. Upgrade any of your factories and construct a Tech 2 Engineer to build these new units. EarthCom out.', vid = 'E02_EarthCom_M02_0061.sfd', bank = 'E02_VO', cue = 'E02_EarthCom_M02_0061', faction = 'UEF'},}
EarthCom_14 =   {{text = '[{i EarthCom}]: Sir, the Aeon will soon launch an attack. EarthCom out.', vid = 'E02_EarthCom_M02_0062.sfd', bank = 'E02_VO', cue = 'E02_EarthCom_M02_0062', faction = 'UEF'},}
EarthCom_15 =   {{text = '[{i EarthCom}]: Sir, more units are approaching. EarthCom out.', vid = 'E02_EarthCom_M02_01139.sfd', bank = 'E02_VO', cue = 'E02_EarthCom_M02_01139', faction = 'UEF'},}
EarthCom_16 =   {{text = '[{i EarthCom}]: Another force is moving in, sir. This is the biggest one yet. EarthCom out.', vid = 'E02_EarthCom_M02_01140.sfd', bank = 'E02_VO', cue = 'E02_EarthCom_M02_01140', faction = 'UEF'},}
EarthCom_17 =   {{text = '[{i EarthCom}]: Sir, we have detected three Aeon Short-Range Radar Installations in the area. Destroy them if you are able. EarthCom out.', vid = 'E02_EarthCom_M03_0063.sfd', bank = 'E02_VO', cue = 'E02_EarthCom_M03_0063', faction = 'UEF'},}
EarthCom_18 =   {{text = '[{i EarthCom}]: All three Aeon Short-Range Radar Installations have been destroyed. EarthCom out.', vid = 'E02_EarthCom_M03_0064.sfd', bank = 'E02_VO', cue = 'E02_EarthCom_M03_0064', faction = 'UEF'},}
EarthCom_19 =   {{text = '[{i EarthCom}]: Sir, General Clarke has decided to evacuate Luthien Colony. You are to escort a convoy of civilian trucks to Station Lima--', vid = 'E02_EarthCom_M03_0065.sfd', bank = 'E02_VO', cue = 'E02_EarthCom_M03_0065', faction = 'UEF'},}
EarthCom_20 =   {{text = '[{i EarthCom}]: A truck has reached Station Lima Foxtrot, sir. EarthCom out.', vid = 'E02_EarthCom_M03_0067.sfd', bank = 'E02_VO', cue = 'E02_EarthCom_M03_0067', faction = 'UEF'},}
EarthCom_21 =   {{text = '[{i EarthCom}]: Sir, another truck has reached the Station safely. EarthCom out.', vid = 'E02_EarthCom_M03_0068.sfd', bank = 'E02_VO', cue = 'E02_EarthCom_M03_0068', faction = 'UEF'},}
EarthCom_22 =   {{text = '[{i EarthCom}]: Lieutenant, another truck has made it to the Station. EarthCom out.', vid = 'E02_EarthCom_M03_0069.sfd', bank = 'E02_VO', cue = 'E02_EarthCom_M03_0069', faction = 'UEF'},}
EarthCom_23 =   {{text = '[{i EarthCom}]: Luthien Colony has been successfully evacuated and medical teams are tending to the survivors. Mission completed. EarthCom out.', vid = 'E02_EarthCom_M03_0070.sfd', bank = 'E02_VO', cue = 'E02_EarthCom_M03_0070', faction = 'UEF'},}
EarthCom_24 =   {{text = '[{i EarthCom}]: Sir, the Luthien Council wishes to thank you for your excellent work during the evacuation effort. EarthCom out.', vid = 'E02_EarthCom_M03_0071.sfd', bank = 'E02_VO', cue = 'E02_EarthCom_M03_0071', faction = 'UEF'},}
EarthCom_25 =   {{text = '[{i EarthCom}]: Sir, there were too many civilian casualties. Abort the mission and return to Earth. Colonel Arnold will oversee the rest of the operation.', vid = 'E02_EarthCom_M03_0073.sfd', bank = 'E02_VO', cue = 'E02_EarthCom_M03_0073', faction = 'UEF'},}
EarthCom_26 =   {{text = '[{i EarthCom}]: Luthien Colony has successfully evacuated and medical teams are seeing to the survivors. Mission completed. EarthCom out.', vid = 'E02_EarthCom_M03_0074.sfd', bank = 'E02_VO', cue = 'E02_EarthCom_M03_0074', faction = 'UEF'},}
EarthCom_27 =   {{text = '[{i EarthCom}]: Sir, Luthien Colony is still waiting for your units to arrive. EarthCom out. ', vid = 'E02_EarthCom_M03_00693.sfd', bank = 'E02_VO', cue = 'E02_EarthCom_M03_00693', faction = 'UEF'},}
EarthCom_28 =   {{text = '[{i EarthCom}]: Sir, Station Lima Foxtrot is still waiting for the convoy from Luthien Colony to arrive. EarthCom out. ', vid = 'E02_EarthCom_M03_00695.sfd', bank = 'E02_VO', cue = 'E02_EarthCom_M03_00695', faction = 'UEF'},}
EarthCom_29 =   {{text = '[{i EarthCom}]: Sir, in addition to defending Station Lima Foxtrot, you are to exterminate the Aeon Commander. Colonel Arnold will continue to advise. EarthCom out.', vid = 'E02_EarthCom_M04_0075.sfd', bank = 'E02_VO', cue = 'E02_EarthCom_M04_0075', faction = 'UEF'},}
EarthCom_30 =   {{text = '[{i EarthCom}]: Sir, we have detected three Aeon Short-Range Radar Installations in the area. Destroy them if you are able. EarthCom out.', vid = 'E02_EarthCom_M04_0076.sfd', bank = 'E02_VO', cue = 'E02_EarthCom_M04_0076', faction = 'UEF'},}
EarthCom_31 =   {{text = '[{i EarthCom}]: All three Aeon Short-Range Radar Installations have been destroyed. EarthCom out.', vid = 'E02_EarthCom_M04_0077.sfd', bank = 'E02_VO', cue = 'E02_EarthCom_M04_0077', faction = 'UEF'},}
EarthCom_32 =   {{text = '[{i EarthCom}]: Captain! Come in, Captain!  We\'ve lost your signal...Captain, report...', vid = 'E03_EarthCom_D01_0119.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_D01_0119', faction = 'UEF'},}
EarthCom_33 =   {{text = '[{i EarthCom}]: Colonel Arnold will gate in 3, 2, 1. The Colonel is on-planet and on-target. The Captain will gate in 3, 2, 1. Wait, the Captain has not gated. Recalibrating. The Captain will gate in 3, 2, 1.', vid = 'E03_EarthCom_M01_0120.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M01_0120', faction = 'UEF'},}
EarthCom_34 =   {{text = '[{i EarthCom}]: Colonel, the Captain missed his LZ. General Clarke is confident you can correct the situation. EarthCom out.', vid = 'E03_EarthCom_M01_0121.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M01_0121', faction = 'UEF'},}
EarthCom_35 =   {{text = '[{i EarthCom}]: Captain, be advised that we have multiple sub signatures near your position. Destroy them.', vid = 'E03_EarthCom_M01_0122.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M01_0122', faction = 'UEF'},}
EarthCom_36 =   {{text = '[{i EarthCom}]: Be advised the Aeon have a naval force protecting the island to the south. Your orders are to build a Sonar Installation and a fleet of warships. When you are ready, destroy the enemy Frigates positioned around that island. EarthCom out.', vid = 'E03_EarthCom_M01_0123.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M01_0123', faction = 'UEF'},}
EarthCom_37 =   {{text = '[{i EarthCom}]: Colonel, can you verify that insignia?', vid = 'E03_EarthCom_M01_0124.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M01_0124', faction = 'UEF'},}
EarthCom_38 =   {{text = '[{i EarthCom}]: That\'s the royal crest, used only by the Princess\' Champion. Stand by for further orders. EarthCom out.', vid = 'E03_EarthCom_M01_0125.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M01_0125', faction = 'UEF'},}
EarthCom_39 =   {{text = '[{i EarthCom}]: Colonel, recall immediately! General Clarke\'s orders!', vid = 'E03_EarthCom_M01_0126.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M01_0126', faction = 'UEF'},}        
EarthCom_40 =   {{text = '[{i EarthCom}]: Captain, proceed to the Colonel\'s position.', vid = 'E03_EarthCom_M01_0127.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M01_0127', faction = 'UEF'},}
EarthCom_41 =   {{text = '[{i EarthCom}]: Captain, we have a confirmed ACU explosion at Colonel Arnold\'s last known position. He\'s gone.', vid = 'E03_EarthCom_M01_01491.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M01_01491', faction = 'UEF'},}
EarthCom_42 =   {{text = '[{i EarthCom}]: Sir, we would advise building a sonar ASAP. EarthCom out. ', vid = 'E03_EarthCom_M01_00699.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M01_00699', faction = 'UEF'},}
EarthCom_43 =   {{text = '[{i EarthCom}]: Captain, a sonar is required to locate submerged submarines. We recommend building one. EarthCom out.', vid = 'E03_EarthCom_M01_01460.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M01_01460', faction = 'UEF'},}
EarthCom_44 =   {{text = '[{i EarthCom}]: Sir, the Aeon Frigate and its escorts are still on the water. They must be destroyed before the operation can continue. EarthCom out. ', vid = 'E03_EarthCom_M01_00701.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M01_00701', faction = 'UEF'},}
EarthCom_45 =   {{text = '[{i EarthCom}]: The Frigate group is still a threat. EarthCom out. ', vid = 'E03_EarthCom_M01_01461.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M01_01461', faction = 'UEF'},}
EarthCom_46 =   {{text = '[{i EarthCom}]: The first attack is incoming! Sensors are picking up air units. EarthCom out.', vid = 'E03_EarthCom_M02_0129.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M02_0129', faction = 'UEF'},}
EarthCom_47 =   {{text = '[{i EarthCom}]: The first wave has been defeated. Repair and rebuild as necessary; the second wave will be hitting you soon. EarthCom out.', vid = 'E03_EarthCom_M02_0130.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M02_0130', faction = 'UEF'},}
EarthCom_48 =   {{text = '[{i EarthCom}]: The second wave is moving into attack position! Detecting naval units this time. EarthCom out.', vid = 'E03_EarthCom_M02_0131.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M02_0131', faction = 'UEF'},}
EarthCom_49 =   {{text = '[{i EarthCom}]: The second attack has been repelled. Fortify as much as you can before the next wave arrives. EarthCom out.', vid = 'E03_EarthCom_M02_0132.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M02_0132', faction = 'UEF'},}
EarthCom_50 =   {{text = '[{i EarthCom}]: The last wave is almost on you. It\'s a combined naval and air force. EarthCom out.', vid = 'E03_EarthCom_M02_0133.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M02_0133', faction = 'UEF'},}
EarthCom_51 =   {{text = '[{i EarthCom}]: You did it, Captain! There is no sign of any Aeon units. EarthCom out.', vid = 'E03_EarthCom_M02_0134.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M02_0134', faction = 'UEF'},}
EarthCom_52 =   {{text = '[{i EarthCom}]: Sir, the Aeon are building at Colonel Arnold\'s former position. You must drive them back so that you can recover the Colonel\'s Black Box. EarthCom out.', vid = 'E03_EarthCom_M03_0135.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M03_0135', faction = 'UEF'},}
EarthCom_53 =   {{text = '[{i EarthCom}]: Captain, we are uploading a new schematic to your ACU. It\'s the SA2-2000 Tech 2 Radar System. It should help identify incoming threats. EarthCom out.', vid = 'E03_EarthCom_M03_0136.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M03_0136', faction = 'UEF'},}
EarthCom_54 =   {{text = '[{i EarthCom}]: Data Link established with Black Box. Download initiated. General Clarke advises that you prepare for an Aeon counterattack. EarthCom out.', vid = 'E03_EarthCom_M03_0137.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M03_0137', faction = 'UEF'},}
EarthCom_55 =   {{text = '[{i EarthCom}]: Sir, we need to get the Colonel\'s Black Box. It has valuable information. EarthCom out. ', vid = 'E03_EarthCom_M03_00703.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M03_00703', faction = 'UEF'},}
EarthCom_56 =   {{text = '[{i EarthCom}]: Sir, the Colonel\'s Black Box is too valuable to leave to the Aeon. Recover it! EarthCom out.', vid = 'E03_EarthCom_M03_01462.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M03_01462', faction = 'UEF'},}
EarthCom_57 =   {{text = '[{i EarthCom}]: Intelligence has confirmed the presence of the Aeon Princess on Matar. You are ordered to assault the main base and eliminate the Princess. EarthCom out.', vid = 'E03_EarthCom_M04_0138.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M04_0138', faction = 'UEF'},}
EarthCom_58 =   {{text = '[{i EarthCom}]: Captain, it appears that the Princess has already fled Matar. Your new orders are to continue advancing and defeat the remaining Aeon Commander. EarthCom out.', vid = 'E03_EarthCom_M04_0139.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M04_0139', faction = 'UEF'},}
EarthCom_59 =   {{text = '[{i EarthCom}]: Sir, the Aeon have a Tactical Missile Launcher positioned on the northern island. Destroy it or capture it if able. EarthCom out.', vid = 'E03_EarthCom_M04_0140.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M04_0140', faction = 'UEF'},}
EarthCom_60 =   {{text = '[{i EarthCom}]: Signal jammed. You are not to communicate with the Princess under any circumstances. EarthCom out.', vid = 'E03_EarthCom_M04_0141.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M04_0141', faction = 'UEF'},}
EarthCom_61 =   {{text = '[{i EarthCom}]: Signal jammed. Sir, begin following Communication Protocol Delta-Delta-Green. EarthCom out.', vid = 'E03_EarthCom_M04_0142.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M04_0142', faction = 'UEF'},}
EarthCom_62 =   {{text = '[{i EarthCom}]: Now that the Tactical Missile Launcher is destroyed, you can assault the northern part of the island. EarthCom out.', vid = 'E03_EarthCom_M04_0143.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M04_0143', faction = 'UEF'},}
EarthCom_63 =   {{text = '[{i EarthCom}]: Sir, if you\'re having trouble with the Aeon base, General Clarke recommends trying different unit combinations. EarthCom out. ', vid = 'E03_EarthCom_M04_00705.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M04_00705', faction = 'UEF'},}
EarthCom_64 =   {{text = '[{i EarthCom}]: Destroy the Aeon! Make them pay for what they did to Colonel Arnold. EarthCom out.', vid = 'E03_EarthCom_M04_01463.sfd', bank = 'E03_VO', cue = 'E03_EarthCom_M04_01463', faction = 'UEF'},}
EarthCom_65 =   {{text = '[{i EarthCom}]: Captain! Come in, Captain!  We\'ve lost your signal...Captain, report...', vid = 'E04_EarthCom_D01_0163.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_D01_0163', faction = 'UEF'},}
EarthCom_66 =   {{text = '[{i EarthCom}]: Cybran Engineers are constructing a base across the river to the southwest of your position. They are building forces for an attack. EarthCom out.\n\n', vid = 'E04_EarthCom_M01_0164.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M01_0164', faction = 'UEF'},}
EarthCom_67 =   {{text = '[{i EarthCom}]: There\'s a Cybran colony to your south. General Clarke wants you to destroy all enemy units in the area and capture some of the Command Centers. Those buildings should provide the intel that you need to find the source of the virus. EarthCom out', vid = 'E04_EarthCom_M01_0392.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M01_0392', faction = 'UEF'},}
EarthCom_68 =   {{text = '[{i EarthCom}]: We detect no enemy units in the area, Captain. The base has been eliminated. EarthCom out.', vid = 'E04_EarthCom_M01_0165.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M01_0165', faction = 'UEF'},}
EarthCom_69 =   {{text = '[{i EarthCom}]: Sufficient Command Centers have been captured. We are downloading the data. EarthCom out.', vid = 'E04_EarthCom_M01_0166.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M01_0166', faction = 'UEF'},}
EarthCom_70 =   {{text = '[{i EarthCom}]: Too many of the Command Centers have been destroyed, sir. Abort the mission and return to Earth. EarthCom out.', vid = 'E04_EarthCom_M01_0167.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M01_0167', faction = 'UEF'},}
EarthCom_71 =   {{text = '[{i EarthCom}]: We\'ve analyzed the data from the Command Centers. Dr. Sweeney is on planet and not too far from your location. Records indicate he\'s being forced to work on the Cybran virus. You are to rescue him and get him off planet. EarthCom out.', vid = 'E04_EarthCom_M01_0168.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M01_0168', faction = 'UEF'},}
EarthCom_72 =   {{text = '[{i EarthCom}]: Sir, the Cybran base to your south still needs to be destroyed. EarthCom out. ', vid = 'E04_EarthCom_M01_00707.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M01_00707', faction = 'UEF'},}
EarthCom_73 =   {{text = '[{i EarthCom}]: We recommend destroying the Cybran base to your south ASAP, Lieutenant. EarthCom out.', vid = 'E04_EarthCom_M01_01464.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M01_01464', faction = 'UEF'},}
EarthCom_74 =   {{text = '[{i EarthCom}]: Don\'t forget to capture the Command Centers, sir. EarthCom out. ', vid = 'E04_EarthCom_M01_00709.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M01_00709', faction = 'UEF'},}
EarthCom_75 =   {{text = '[{i EarthCom}]: We\'re still waiting on the data from those Command Centers. EarthCom out. ', vid = 'E04_EarthCom_M01_01465.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M01_01465', faction = 'UEF'},}
EarthCom_76 =   {{text = '[{i EarthCom}]: Sir, we have a fix on the Facility where Dr. Sweeney is being held. Proceed to the Facility, capture it, and then escort the Doctor to the Quantum Gate located to the south. Sweeney can use it to get off-world. EarthCom out.', vid = 'E04_EarthCom_M02_0169.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M02_0169', faction = 'UEF'},}
EarthCom_77 =   {{text = '[{i EarthCom}]: We\'re uploading the shield schematic to you, sir. Be careful: Shields consume massive amounts of power. EarthCom out.', vid = 'E04_EarthCom_M02_0170.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M02_0170', faction = 'UEF'},}
EarthCom_78 =   {{text = '[{i EarthCom}]: You have been cleared to receive the \'Klink Hammer\' Tech 2 Artillery Installation; schematic is uploading. The artillery has an excellent range, but it does require a substantial amount of power. EarthCom out.', vid = 'E04_EarthCom_M02_0171.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M02_0171', faction = 'UEF'},}
EarthCom_79 =   {{text = '[{i EarthCom}]: Sir, the Cybran Commander is constructing Tactical Missile Launchers. Because of their range, they are an immediate threat. We are uploading the \'Buzzkill\' Tech 2 Tactical Missile Defense schematic so you can counter them. EarthCom out.', vid = 'E04_EarthCom_M02_0172.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M02_0172', faction = 'UEF'},}
EarthCom_80 =   {{text = '[{i EarthCom}]: Sir, the Facility has been captured and we have made contact with Dr. Sweeney. He needs some time to secure a vehicle. We will contact you when he\'s ready. EarthCom out.', vid = 'E04_EarthCom_M02_0173.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M02_0173', faction = 'UEF'},}
EarthCom_81 =   {{text = '[{i EarthCom}]: Dr. Sweeney has a truck, Captain, and has turned it over to you. Get him to the Gate ASAP. Protect him at all costs. EarthCom out.', vid = 'E04_EarthCom_M02_0174.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M02_0174', faction = 'UEF'},}
EarthCom_82 =   {{text = '[{i EarthCom}]: The Cybrans are attempting to intercept Dr. Sweeney\'s truck. Deal with the threat. EarthCom out.', vid = 'E04_EarthCom_M02_0175.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M02_0175', faction = 'UEF'},}
EarthCom_83 =   {{text = '[{i EarthCom}]: Dr. Sweeney is safely back on Earth, Captain. The Cybrans roughed him up a bit. He sends his thanks for the rescue. EarthCom out.', vid = 'E04_EarthCom_M02_0176.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M02_0176', faction = 'UEF'},}
EarthCom_84 =   {{text = '[{i EarthCom}]: Sir, Dr. Sweeney is at the Science Facility. Rescue him! EarthCom out. ', vid = 'E04_EarthCom_M02_00711.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M02_00711', faction = 'UEF'},}
EarthCom_85 =   {{text = '[{i EarthCom}]: Dr. Sweeney is still a prisoner. We need to recover him ASAP. EarthCom out.', vid = 'E04_EarthCom_M02_01540.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M02_01540', faction = 'UEF'},}
EarthCom_86 =   {{text = '[{i EarthCom}]: Dr. Sweeney is in his vehicle and waiting for you to move him to the Gate.  EarthCom out. ', vid = 'E04_EarthCom_M02_00713.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M02_00713', faction = 'UEF'},}
EarthCom_87 =   {{text = '[{i EarthCom}]: You need to move the Doctor to the Gate ASAP. The longer he\'s exposed, the more likely the Cybrans are to attack. EarthCom out. ', vid = 'E04_EarthCom_M02_01541.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M02_01541', faction = 'UEF'},}
EarthCom_88 =   {{text = '[{i EarthCom}]: Sir, if you\'re having trouble with the Cybran, General Clarke suggests trying a different combination of units. EarthCom out. ', vid = 'E04_EarthCom_M02_00715.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M02_00715', faction = 'UEF'},}
EarthCom_89 =   {{text = '[{i EarthCom}]: Take out that Cybran, sir. We know you can do it. EarthCom out. ', vid = 'E04_EarthCom_M02_01542.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M02_01542', faction = 'UEF'},}
EarthCom_90 =   {{text = '[{i EarthCom}]: We can\'t pin down Brackman\'s location--the Cybrans are jamming the area. We need to boost our signal power. Construct three SA2-2000 Radar Systems at the indicated points. Once they\'re online, we should be able to find Brackman. EarthCom out.', vid = 'E04_EarthCom_M03_0178.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M03_0178', faction = 'UEF'},}
EarthCom_91 =   {{text = '[{i EarthCom}]: The first radar system is online. We need two more, sir. EarthCom out.', vid = 'E04_EarthCom_M03_0179.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M03_0179', faction = 'UEF'},}
EarthCom_92 =   {{text = '[{i EarthCom}]: Two radars online, sir. Waiting for the last one. EarthCom out.', vid = 'E04_EarthCom_M03_0180.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M03_0180', faction = 'UEF'},}
EarthCom_93 =   {{text = '[{i EarthCom}]: All three radars are up and functional. We are rescanning the area. Defend the Radar Installations until the scan is complete. EarthCom out.', vid = 'E04_EarthCom_M03_0181.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M03_0181', faction = 'UEF'},}
EarthCom_94 =   {{text = '[{i EarthCom}]: We\'re narrowing down the search, sir. We should have a fix on Brackman\'s location shortly. EarthCom out.', vid = 'E04_EarthCom_M03_0182.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M03_0182', faction = 'UEF'},}
EarthCom_95 =   {{text = '[{i EarthCom}]: We found him! Brackman\'s base is to the southeast. Level it. Leave nothing standing. EarthCom out', vid = 'E04_EarthCom_M03_0183.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M03_0183', faction = 'UEF'},}
EarthCom_96 =   {{text = '[{i EarthCom}]: We\'ve lost a radar, sir. Rebuild it ASAP. EarthCom out.', vid = 'E04_EarthCom_M03_0184.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M03_0184', faction = 'UEF'},}
EarthCom_97 =   {{text = '[{i EarthCom}]: Holy...sir, we don\'t know what that is, but it appears to be moving towards your base. Destroy both it and the Facility. EarthCom out.', vid = 'E04_EarthCom_M03_0185.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M03_0185', faction = 'UEF'},}
EarthCom_98 =   {{text = '[{i EarthCom}]: It\'s down, sir!', vid = 'E04_EarthCom_M03_0186.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M03_0186', faction = 'UEF'},}
EarthCom_99 =   {{text = '[{i EarthCom}]: Scanning now, ma\'am.', vid = 'E04_EarthCom_M03_0187.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M03_0187', faction = 'UEF'},}
EarthCom_100 =  {{text = '[{i EarthCom}]: Sir, a convoy of Engineers has exited Brackman\'s base. They are headed toward the Gate. Destroy them before they can get their cargo off-world. EarthCom out.', vid = 'E04_EarthCom_M03_0188.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M03_0188', faction = 'UEF'},}
EarthCom_101 =  {{text = '[{i EarthCom}]: You\'ve eliminated all the Engineers, sir. Hopefully that\'ll slow the Cybrans down. EarthCom out.', vid = 'E04_EarthCom_M03_0189.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M03_0189', faction = 'UEF'},}
EarthCom_102 =  {{text = '[{i EarthCom}]: Your objectives are complete, sir. General Clarke wants you back on Earth. Come on home. EarthCom out.', vid = 'E04_EarthCom_M03_0190.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M03_0190', faction = 'UEF'},}
EarthCom_103 =  {{text = '[{i EarthCom}]: We\'re still waiting on those radars, sir.  EarthCom out.', vid = 'E04_EarthCom_M03_00717.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M03_00717', faction = 'UEF'},}
EarthCom_104 =  {{text = '[{i EarthCom}]: We\'re ready to start scanning once you get all the radars up, Captain. EarthCom out.', vid = 'E04_EarthCom_M03_01466.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M03_01466', faction = 'UEF'},}
EarthCom_105 =  {{text = '[{i EarthCom}]: Sir, the longer you take to destroy his base, the more likely Brackman is to escape! EarthCom out. ', vid = 'E04_EarthCom_M03_00719.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M03_00719', faction = 'UEF'},}
EarthCom_106 =  {{text = '[{i EarthCom}]: We won\'t get another chance like this, sir. Destroy Brackman\'s base ASAP. EarthCom out.', vid = 'E04_EarthCom_M03_01467.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M03_01467', faction = 'UEF'},}
EarthCom_107 =  {{text = '[{i EarthCom}]: That unit is still heading towards you, sir. EarthCom out. ', vid = 'E04_EarthCom_M03_00721.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M03_00721', faction = 'UEF'},}
EarthCom_108 =  {{text = '[{i EarthCom}]: The Cybran unit is still up and dangerous, Captain. EarthCom out. ', vid = 'E04_EarthCom_M03_01468.sfd', bank = 'E04_VO', cue = 'E04_EarthCom_M03_01468', faction = 'UEF'},}
EarthCom_109 =  {{text = '[{i EarthCom}]: Captain! Come in, Captain! We\'ve lost your signal...Captain, report...', vid = 'E05_EarthCom_D01_0225.sfd', bank = 'E05_VO', cue = 'E05_EarthCom_D01_0225', faction = 'UEF'},}
EarthCom_110 =  {{text = '[{i EarthCom}]: Captain, your primary objective is to protect the Research Facilities and ensure that the final components of Black Sun are gated back to Earth. Use any means necessary to protect those parts. The local defenses have been turned over to you. EarthCom out.', vid = 'E05_EarthCom_M01_0226.sfd', bank = 'E05_VO', cue = 'E05_EarthCom_M01_0226', faction = 'UEF'},}
EarthCom_111 =  {{text = '[{i EarthCom}]: Sir, we have confirmed the presence of Aeon on Pisces IV. I repeat, the Aeon are already on Pisces IV. Be prepared for an enemy assault. EarthCom out.', vid = 'E05_EarthCom_M01_0227.sfd', bank = 'E05_VO', cue = 'E05_EarthCom_M01_0227', faction = 'UEF'},}
EarthCom_112 =  {{text = '[{i EarthCom}]: Sir, we have a nuke inbound! Wait...the SMD system took it down. EarthCom out.', vid = 'E05_EarthCom_M01_0228.sfd', bank = 'E05_VO', cue = 'E05_EarthCom_M01_0228', faction = 'UEF'},}
EarthCom_113 =  {{text = '[{i EarthCom}]: Captain, we cannot lose the Research Facilities. You have been authorized to receive the \'Nuke Eliminator\' Tech 3 Strategic Missile Defense schematic. Once constructed, it will shoot down any incoming nukes. EarthCom out.', vid = 'E05_EarthCom_M01_0229.sfd', bank = 'E05_VO', cue = 'E05_EarthCom_M01_0229', faction = 'UEF'},}
EarthCom_114 =  {{text = '[{i EarthCom}]: At least two of the Research Facilities must survive. We estimate that you have just a few minutes before the Aeon launch their next salvo. Time is against you.', vid = 'E05_EarthCom_M01_0230.sfd', bank = 'E05_VO', cue = 'E05_EarthCom_M01_0230', faction = 'UEF'},}
EarthCom_115 =  {{text = '[{i EarthCom}]: Sir, we\'re reading a large Aeon force moving towards your position. EarthCom out. ', vid = 'E05_EarthCom_M01_0231.sfd', bank = 'E05_VO', cue = 'E05_EarthCom_M01_0231', faction = 'UEF'},}
EarthCom_116 =  {{text = '[{i EarthCom}]: Captain, the Aeon cannot be allowed to have those Strategic Missile Launchers. Destroy them. EarthCom out.', vid = 'E05_EarthCom_M01_01098.sfd', bank = 'E05_VO', cue = 'E05_EarthCom_M01_01098', faction = 'UEF'},}
EarthCom_117 =  {{text = '[{i EarthCom}]: Sir, the components are finished. We\'ll begin extracting them through the Gate momentarily. EarthCom out.', vid = 'E05_EarthCom_M01_0232.sfd', bank = 'E05_VO', cue = 'E05_EarthCom_M01_0232', faction = 'UEF'},}
EarthCom_118 =  {{text = '[{i EarthCom}]: Sir, you have to get those Strategic Missile Defenses up ASAP! EarthCom out. ', vid = 'E05_EarthCom_M01_00723.sfd', bank = 'E05_VO', cue = 'E05_EarthCom_M01_00723', faction = 'UEF'},}
EarthCom_119 =  {{text = '[{i EarthCom}]: Captain, more nukes will be incoming soon! We need those SMDs up ASAP! ', vid = 'E05_EarthCom_M01_00724.sfd', bank = 'E05_VO', cue = 'E05_EarthCom_M01_00724', faction = 'UEF'},}
EarthCom_120 =  {{text = '[{i EarthCom}]: The Aeon Commander still has those strategic missiles, sir. You need to destroy those ASAP. EarthCom out. ', vid = 'E05_EarthCom_M01_00725.sfd', bank = 'E05_VO', cue = 'E05_EarthCom_M01_00725', faction = 'UEF'},}
EarthCom_121 =  {{text = '[{i EarthCom}]: The Aeon still have nuke capability, sir. You need to destroy their nuke sites in order to preserve the safety of the Research Facilities. EarthCom out.', vid = 'E05_EarthCom_M01_00726.sfd', bank = 'E05_VO', cue = 'E05_EarthCom_M01_00726', faction = 'UEF'},}
EarthCom_122 =  {{text = '[{i EarthCom}]: Captain, we have been notified that Black Sun components are ready to be transferred to the Gate. Patching in Dr. Sweeney.', vid = 'E05_EarthCom_M02_0233.sfd', bank = 'E05_VO', cue = 'E05_EarthCom_M02_0233', faction = 'UEF'},}
EarthCom_123 =  {{text = '[{i EarthCom}]: There is a Cybran Commander on-planet! I repeat, we have a Cybran Commander on-planet! He\'s got several artillery pieces trained on the town! We\'ll rescan the area and advise. EarthCom out. ', vid = 'E05_EarthCom_M02_0234.sfd', bank = 'E05_VO', cue = 'E05_EarthCom_M02_0234', faction = 'UEF'},}
EarthCom_124 =  {{text = '[{i EarthCom}]: Destroy those guns! General Clarke has authorized the use of nuclear weapons. We are uploading the schematic for the Tech 3 Nuclear Missile Launcher now. EarthCom out.', vid = 'E05_EarthCom_M02_0235.sfd', bank = 'E05_VO', cue = 'E05_EarthCom_M02_0235', faction = 'UEF'},}
EarthCom_125 =  {{text = '[{i EarthCom}]: The guns are down. Dr. Sweeney will notify you when the convoys are ready to leave. EarthCom out.', vid = 'E05_EarthCom_M02_0236.sfd', bank = 'E05_VO', cue = 'E05_EarthCom_M02_0236', faction = 'UEF'},}
EarthCom_126 =  {{text = '[{i EarthCom}]: Sir, we can\'t risk moving the components with that artillery operational! EarthCom out. ', vid = 'E05_EarthCom_M02_00727.sfd', bank = 'E05_VO', cue = 'E05_EarthCom_M02_00727', faction = 'UEF'},}
EarthCom_127 =  {{text = '[{i EarthCom}]: You must take out those Cybran artillery pieces, sir. The mission depends on it. EarthCom out.', vid = 'E05_EarthCom_M02_00728.sfd', bank = 'E05_VO', cue = 'E05_EarthCom_M02_00728', faction = 'UEF'},}
EarthCom_128 =  {{text = '[{i EarthCom}]: Sir, the trucks will be ready to go shortly. Defend them at all costs. EarthCom out.', vid = 'E05_EarthCom_M03_0237.sfd', bank = 'E05_VO', cue = 'E05_EarthCom_M03_0237', faction = 'UEF'},}
EarthCom_129 =  {{text = '[{i EarthCom}]: Captain, the Site Alpha convoy is safe on Earth. EarthCom out.', vid = 'E05_EarthCom_M03_0238.sfd', bank = 'E05_VO', cue = 'E05_EarthCom_M03_0238', faction = 'UEF'},}
EarthCom_130 =  {{text = '[{i EarthCom}]: The Site Beta convoy has successfully reached Earth. EarthCom out.', vid = 'E05_EarthCom_M03_0239.sfd', bank = 'E05_VO', cue = 'E05_EarthCom_M03_0239', faction = 'UEF'},}
EarthCom_131 =  {{text = '[{i EarthCom}]: Sir, I\'ve just been told that the Site Delta convoy has reached its destination. EarthCom out.', vid = 'E05_EarthCom_M03_0240.sfd', bank = 'E05_VO', cue = 'E05_EarthCom_M03_0240', faction = 'UEF'},}
EarthCom_132 =  {{text = '[{i EarthCom}]: Captain, a convoy is waiting for you! Get it to the Gate!', vid = 'E05_EarthCom_M03_0396.sfd', bank = 'E05_VO', cue = 'E05_EarthCom_M03_0396', faction = 'UEF'},}
EarthCom_133 =  {{text = '[{i EarthCom}]: Don\'t leave the convoy sitting there, sir! Move it!', vid = 'E05_EarthCom_M03_01549.sfd', bank = 'E05_VO', cue = 'E05_EarthCom_M03_01549', faction = 'UEF'},}
EarthCom_134 =  {{text = '[{i EarthCom}]: Sir, I\'ve received word that all the components are safely on Earth. Use the Gate to return when ready. EarthCom out.', vid = 'E05_EarthCom_M03_0241.sfd', bank = 'E05_VO', cue = 'E05_EarthCom_M03_0241', faction = 'UEF'},}
EarthCom_135 =  {{text = '[{i EarthCom}]: Sir, the Gate has been destroyed. You must build a new one! EarthCom out.', vid = 'E05_EarthCom_M03_0242.sfd', bank = 'E05_VO', cue = 'E05_EarthCom_M03_0242', faction = 'UEF'},}
EarthCom_136 =  {{text = '[{i EarthCom}]: Move to the Gate and return to Earth ASAP, sir. EarthCom out. ', vid = 'E05_EarthCom_M03_00729.sfd', bank = 'E05_VO', cue = 'E05_EarthCom_M03_00729', faction = 'UEF'},}
EarthCom_137 =  {{text = '[{i EarthCom}]: Your mission is finished, sir. Return to Earth. EarthCom out.', vid = 'E05_EarthCom_M03_00730.sfd', bank = 'E05_VO', cue = 'E05_EarthCom_M03_00730', faction = 'UEF'},}
EarthCom_138 =  {{text = '[{i EarthCom}]: Major! Come in, Major! We\'ve lost your signal...Major, report...', vid = 'E06_EarthCom_D01_0298.sfd', bank = 'E06_VO', cue = 'E06_EarthCom_D01_0298', faction = 'UEF'},}
EarthCom_139 =  {{text = '[{i EarthCom}]: The last transports are inbound.', vid = 'E06_EarthCom_M01_0398.sfd', bank = 'E06_VO', cue = 'E06_EarthCom_M01_0398', faction = 'UEF'},}
EarthCom_140 =  {{text = '[{i EarthCom}]: There\'s the missing component. Get it to Black Sun! EarthCom out.', vid = 'E06_EarthCom_M01_01118.sfd', bank = 'E06_VO', cue = 'E06_EarthCom_M01_01118', faction = 'UEF'},}
EarthCom_141 =  {{text = '[{i EarthCom}]: We need that component, sir! The UEF will fall without it! EarthCom out. ', vid = 'E06_EarthCom_M01_00731.sfd', bank = 'E06_VO', cue = 'E06_EarthCom_M01_00731', faction = 'UEF'},}
EarthCom_142 =  {{text = '[{i EarthCom}]: Recover that component ASAP, sir! Black Sun needs it to fire! EarthCom out.', vid = 'E06_EarthCom_M01_01469.sfd', bank = 'E06_VO', cue = 'E06_EarthCom_M01_01469', faction = 'UEF'},}
EarthCom_143 =  {{text = '[{i EarthCom}]: We\'re showing that Black Sun is almost ready to fire. Continue to defend it! EarthCom out.', vid = 'E06_EarthCom_M02_01119.sfd', bank = 'E06_VO', cue = 'E06_EarthCom_M02_01119', faction = 'UEF'},}
EarthCom_144 =  {{text = '[{i EarthCom}]: Sir, we\'ve detected enemy units to the south east. We think it could be the Aeon. Send out a Scout and if the Aeon are there, eliminate them. EarthCom out.', vid = 'E06_EarthCom_M02_0299.sfd', bank = 'E06_VO', cue = 'E06_EarthCom_M02_0299', faction = 'UEF'},}
EarthCom_145 =  {{text = '[{i EarthCom}]: There is an Aeon Commander on the island to the southeast. Destroy all enemy forces at that position. EarthCom out.', vid = 'E06_EarthCom_M02_01120.sfd', bank = 'E06_VO', cue = 'E06_EarthCom_M02_01120', faction = 'UEF'},}
EarthCom_146 =  {{text = '[{i EarthCom}]: Major, we\'ve just detected a large force moving down from the north, directly toward your position. It appears to be Cybran. The General wants those units eliminated. EarthCom out.', vid = 'E06_EarthCom_M02_0300.sfd', bank = 'E06_VO', cue = 'E06_EarthCom_M02_0300', faction = 'UEF'},}
EarthCom_147 =  {{text = '[{i EarthCom}]: You got her...wait...sensors are picking up something...', vid = 'E06_EarthCom_M02_01121.sfd', bank = 'E06_VO', cue = 'E06_EarthCom_M02_01121', faction = 'UEF'},}
EarthCom_148 =  {{text = '[{i EarthCom}]: We\'re showing incoming Cybran forces. EarthCom out.', vid = 'E06_EarthCom_M02_01123.sfd', bank = 'E06_VO', cue = 'E06_EarthCom_M02_01123', faction = 'UEF'},}
EarthCom_149 =  {{text = '[{i EarthCom}]: A Cybran transport is attempting to land Engineers near the Black Sun Control Center. You are to stop them. EarthCom out.', vid = 'E06_EarthCom_M02_01593.sfd', bank = 'E06_VO', cue = 'E06_EarthCom_M02_01593', faction = 'UEF'},}
EarthCom_150 =  {{text = '[{i EarthCom}]: The Control Center is back under our control. Our techs are reloading the software to make sure that the Cybrans didn\'t damage the system.', vid = 'E06_EarthCom_M02_01125.sfd', bank = 'E06_VO', cue = 'E06_EarthCom_M02_01125', faction = 'UEF'},}
EarthCom_151 =  {{text = '[{i EarthCom}]: Black Sun is completed! It\'s beginning to charge. Be ready to fire it on our mark. EarthCom out.', vid = 'E06_EarthCom_M02_01126.sfd', bank = 'E06_VO', cue = 'E06_EarthCom_M02_01126', faction = 'UEF'},}
EarthCom_152 =  {{text = '[{i EarthCom}]: Do not let the Aeon attack Black Sun! EarthCom out. ', vid = 'E06_EarthCom_M02_00733.sfd', bank = 'E06_VO', cue = 'E06_EarthCom_M02_00733', faction = 'UEF'},}
EarthCom_153 =  {{text = '[{i EarthCom}]: The technicians are working as fast as they can, but they need more time. Destroy the Aeon! ', vid = 'E06_EarthCom_M02_01470.sfd', bank = 'E06_VO', cue = 'E06_EarthCom_M02_01470', faction = 'UEF'},}
EarthCom_154 =  {{text = '[{i EarthCom}]: Black Sun needs to charge before it can fire. Maintain your defensive position. EarthCom out.', vid = 'E06_EarthCom_M03_01109.sfd', bank = 'E06_VO', cue = 'E06_EarthCom_M03_01109', faction = 'UEF'},}
EarthCom_155 =  {{text = '[{i EarthCom}]: Black Sun is 20 percent charged, sir.', vid = 'E06_EarthCom_M03_01111.sfd', bank = 'E06_VO', cue = 'E06_EarthCom_M03_01111', faction = 'UEF'},}
EarthCom_156 =  {{text = '[{i EarthCom}]: Black Sun is 40 percent charged, sir.', vid = 'E06_EarthCom_M03_01112.sfd', bank = 'E06_VO', cue = 'E06_EarthCom_M03_01112', faction = 'UEF'},}
EarthCom_157 =  {{text = '[{i EarthCom}]: Black Sun is 60 percent charged, sir.', vid = 'E06_EarthCom_M03_01113.sfd', bank = 'E06_VO', cue = 'E06_EarthCom_M03_01113', faction = 'UEF'},}
EarthCom_158 =  {{text = '[{i EarthCom}]: 80 percent! Black Sun is 80 percent charged, sir.', vid = 'E06_EarthCom_M03_01539.sfd', bank = 'E06_VO', cue = 'E06_EarthCom_M03_01539', faction = 'UEF'},}
EarthCom_159 =  {{text = '[{i EarthCom}]: Sensors are picking up something coming in from the northwest. It\'s massive. Hold on...Arnold is landing to the north and is personally leading his forces. Handle the situation. EarthCom out.', vid = 'E06_EarthCom_M03_01115.sfd', bank = 'E06_VO', cue = 'E06_EarthCom_M03_01115', faction = 'UEF'},}
EarthCom_160 =  {{text = '[{i EarthCom}]: It\'s at 100 percent!', vid = 'E06_EarthCom_M03_01117.sfd', bank = 'E06_VO', cue = 'E06_EarthCom_M03_01117', faction = 'UEF'},}
EarthCom_161 =  {{text = '[{i EarthCom}]: The Aeon unit is down! It\'s down! ', vid = 'E06_EarthCom_M03_01110.sfd', bank = 'E06_VO', cue = 'E06_EarthCom_M03_01110', faction = 'UEF'},}
EarthCom_162 =  {{text = '[{i EarthCom}]: Arnold is defeated! Fire Black Sun! EarthCom out.', vid = 'E06_EarthCom_M03_00735.sfd', bank = 'E06_VO', cue = 'E06_EarthCom_M03_00735', faction = 'UEF'},}
EarthCom_163 =  {{text = '[{i EarthCom}]: What are you waiting for! Fire Black Sun! End the war! EarthCom out.', vid = 'E06_EarthCom_M03_01471.sfd', bank = 'E06_VO', cue = 'E06_EarthCom_M03_01471', faction = 'UEF'},}

-- Cybran Elliot
Elliot_1 =      {{text = '[{i Elliott}]: Attention all Cybran Commanders. This is Director Elliott at Node73. An Aeon Commander has just landed southeast of Nexus04. We need assistance! The Aeon will murder us all!', vid = 'A04_Elliott_M01_00899.sfd', bank = 'A04_VO', cue = 'A04_Elliott_M01_00899', faction = 'Cybran'},}
Elliot_2 =      {{text = '[{i Elliott}]: This is Director Elliott. The Aeon are assaulting Node73...someone do something! The Aeon are right on top of us!', vid = 'A04_Elliott_M01_00909.sfd', bank = 'A04_VO', cue = 'A04_Elliott_M01_00909', faction = 'Cybran'},}
Elliot_3 =      {{text = '[{i Elliott}]: Commander, this is Director Elliott of Station 004. We are under Aeon attack! Please help us!', vid = 'C04_Elliott_M02_0475.sfd', bank = 'C04_VO', cue = 'C04_Elliott_M02_0475', faction = 'Cybran'},}
Elliot_4 =      {{text = '[{i Elliott}]: Thank you for your assistance, Commander. That could\'ve gone...very badly for us.', vid = 'C04_Elliott_M02_0479.sfd', bank = 'C04_VO', cue = 'C04_Elliott_M02_0479', faction = 'Cybran'},}

-- Aeon Eris
Eris_1 =        {{text = '[{i Eris}]: Knight, the UEF army is massing for another attack. You need to prepare your defenses. Eris out.', vid = 'A03_Eris_M01_00851.sfd', bank = 'A03_VO', cue = 'A03_Eris_M01_00851', faction = 'Aeon'},}
Eris_2 =        {{text = '[{i Eris}]: It is time to pay for your transgressions against the Aeon.', vid = 'E03_Eris_M01_0144.sfd', bank = 'E03_VO', cue = 'E03_Eris_M01_0144', faction = 'Aeon'},}
Eris_3 =        {{text = '[{i Eris}]: Soon you will join your friend in the abyss.', vid = 'E03_Eris_M02_01537.sfd', bank = 'E03_VO', cue = 'E03_Eris_M02_01537', faction = 'Aeon'},}
Eris_4 =        {{text = '[{i Eris}]: The other one didn\'t put up much of a fight.', vid = 'E03_Eris_M03_0145.sfd', bank = 'E03_VO', cue = 'E03_Eris_M03_0145', faction = 'Aeon'},}
Eris_5 =        {{text = '[{i Eris}]: The Way will soon be embraced throughout the galaxy, and there is nothing you can do to stop it.', vid = 'E03_Eris_M03_0146.sfd', bank = 'E03_VO', cue = 'E03_Eris_M03_0146', faction = 'Aeon'},}
Eris_6 =        {{text = '[{i Eris}]: The Illuminate is coming for you, Imperial.', vid = 'E03_Eris_T01_0147.sfd', bank = 'E03_VO', cue = 'E03_Eris_T01_0147', faction = 'Aeon'},}
Eris_7 =        {{text = '[{i Eris}]: Are you afraid, Imperial? You should be.', vid = 'E03_Eris_T01_0148.sfd', bank = 'E03_VO', cue = 'E03_Eris_T01_0148', faction = 'Aeon'},}
Eris_8 =        {{text = '[{i Eris}]: The tyranny of the UEF will not stand.', vid = 'E03_Eris_T01_0149.sfd', bank = 'E03_VO', cue = 'E03_Eris_T01_0149', faction = 'Aeon'},}
Eris_9 =        {{text = '[{i Eris}]: Your Commander Suit will make a fine funeral pyre.', vid = 'E03_Eris_T01_0150.sfd', bank = 'E03_VO', cue = 'E03_Eris_T01_0150', faction = 'Aeon'},}
Eris_10 =       {{text = '[{i Eris}]: The Aeon will purge the galaxy of those who seek to destroy humanity.', vid = 'E03_Eris_T01_0151.sfd', bank = 'E03_VO', cue = 'E03_Eris_T01_0151', faction = 'Aeon'},}
Eris_11 =       {{text = '[{i Eris}]: It is not too late to accept The Way.', vid = 'E03_Eris_T01_0152.sfd', bank = 'E03_VO', cue = 'E03_Eris_T01_0152', faction = 'Aeon'},}
Eris_12 =       {{text = '[{i Eris}]: The UEF murdered my family. I will have my revenge.', vid = 'E03_Eris_T01_0153.sfd', bank = 'E03_VO', cue = 'E03_Eris_T01_0153', faction = 'Aeon'},}
Eris_13 =       {{text = '[{i Eris}]: You are no match for the power of righteousness.', vid = 'E03_Eris_T01_0154.sfd', bank = 'E03_VO', cue = 'E03_Eris_T01_0154', faction = 'Aeon'},}
Eris_14 =       {{text = '[{i Eris}]: We will purge the galaxy. ', vid = 'C04_Eris_M01_0467.sfd', bank = 'C04_VO', cue = 'C04_Eris_M01_0467', faction = 'Aeon'},}
Eris_15 =       {{text = '[{i Eris}]: As soon as we hack into these Nodes, your schemes will be revealed. ', vid = 'C04_Eris_M01_0468.sfd', bank = 'C04_VO', cue = 'C04_Eris_M01_0468', faction = 'Aeon'},}
Eris_16 =       {{text = '[{i Eris}]: Perhaps the Princess was right about this planet. ', vid = 'C04_Eris_M01_0469.sfd', bank = 'C04_VO', cue = 'C04_Eris_M01_0469', faction = 'Aeon'},}
Eris_17 =       {{text = '[{i Eris}]: You have no hope of defeating me.', vid = 'C04_Eris_M01_0470.sfd', bank = 'C04_VO', cue = 'C04_Eris_M01_0470', faction = 'Aeon'},}
Eris_18 =       {{text = '[{i Eris}]: Not bad, abomination. But this isn\'t finished.', vid = 'C04_Eris_M01_0471.sfd', bank = 'C04_VO', cue = 'C04_Eris_M01_0471', faction = 'Aeon'},}
Eris_19 =       {{text = '[{i Eris}]: Those Nodes will be mine.', vid = 'C04_Eris_M02_0477.sfd', bank = 'C04_VO', cue = 'C04_Eris_M02_0477', faction = 'Aeon'},}
Eris_20 =       {{text = '[{i Eris}]: I will find out what those Nodes do sooner or later.', vid = 'C04_Eris_M02_0478.sfd', bank = 'C04_VO', cue = 'C04_Eris_M02_0478', faction = 'Aeon'},}
Eris_21 =       {{text = '[{i Eris}]: There will be no stopping me next time.', vid = 'C04_Eris_M02_0481.sfd', bank = 'C04_VO', cue = 'C04_Eris_M02_0481', faction = 'Aeon'},}
Eris_22 =       {{text = '[{i Eris}]: Attention, freaks of nature. I have wired your Mainframe with explosives--if you attack me, I will blow it up.', vid = 'C04_Eris_M03_0484.sfd', bank = 'C04_VO', cue = 'C04_Eris_M03_0484', faction = 'Aeon'},}
Eris_23 =       {{text = '[{i Eris}]: Perhaps you didn\'t hear me, freak. Attack my base again and I will destroy the Mainframe.', vid = 'C04_Eris_M03_0486.sfd', bank = 'C04_VO', cue = 'C04_Eris_M03_0486', faction = 'Aeon'},}
Eris_24 =       {{text = '[{i Eris}]: Nothing is responding...no!', vid = 'C04_Eris_M03_0493.sfd', bank = 'C04_VO', cue = 'C04_Eris_M03_0493', faction = 'Aeon'},}
Eris_25 =       {{text = '[{i Eris}]: I\'m going to destroy everything on this planet. It will be nothing but a dead rock. ', vid = 'C04_Eris_M03_01103.sfd', bank = 'C04_VO', cue = 'C04_Eris_M03_01103', faction = 'Aeon'},}
Eris_26 =       {{text = '[{i Eris}]: I REFUSE TO BE BEATEN!!!!', vid = 'C04_Eris_M03_01100.sfd', bank = 'C04_VO', cue = 'C04_Eris_M03_01100', faction = 'Aeon'},}
Eris_27 =       {{text = '[{i Eris}]: It is time to die.', vid = 'C04_Eris_T01_0458.sfd', bank = 'C04_VO', cue = 'C04_Eris_T01_0458', faction = 'Aeon'},}
Eris_28 =       {{text = '[{i Eris}]: I serve the Princess. Your schemes will be exposed.', vid = 'C04_Eris_T01_0459.sfd', bank = 'C04_VO', cue = 'C04_Eris_T01_0459', faction = 'Aeon'},}
Eris_29 =       {{text = '[{i Eris}]: You are an affront to humanity.', vid = 'C04_Eris_T01_0460.sfd', bank = 'C04_VO', cue = 'C04_Eris_T01_0460', faction = 'Aeon'},}
Eris_30 =       {{text = '[{i Eris}]: When we are through, the Cybrans will be extinct.', vid = 'C04_Eris_T01_0461.sfd', bank = 'C04_VO', cue = 'C04_Eris_T01_0461', faction = 'Aeon'},}
Eris_31 =       {{text = '[{i Eris}]: The flames of our righteousness are cleansing the galaxy.', vid = 'C04_Eris_T01_0462.sfd', bank = 'C04_VO', cue = 'C04_Eris_T01_0462', faction = 'Aeon'},}
Eris_32 =       {{text = '[{i Eris}]: You were dead the moment you put that chip in your head.', vid = 'C04_Eris_T01_0463.sfd', bank = 'C04_VO', cue = 'C04_Eris_T01_0463', faction = 'Aeon'},}
Eris_33 =       {{text = '[{i Eris}]: There will be no mercy for you.', vid = 'C04_Eris_T01_0464.sfd', bank = 'C04_VO', cue = 'C04_Eris_T01_0464', faction = 'Aeon'},}
Eris_34 =       {{text = '[{i Eris}]: None will mourn you, Cybran.', vid = 'C04_Eris_T01_0465.sfd', bank = 'C04_VO', cue = 'C04_Eris_T01_0465', faction = 'Aeon'},}

-- Uef Fletcher
Fletcher_1 =     {{text = '[{i Fletcher}]: You just gated into a hell of a mess, Colonel, but I\'m glad you\'re here.', vid = 'X01_Fletcher_M01_03419.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M01_03419', faction = 'UEF'},}
Fletcher_2 =     {{text = '[{i Fletcher}]: A Cybran, huh? I thought you guys would be busy changing the water in Brackman\'s brain tank.', vid = 'X01_Fletcher_M01_02877.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M01_02877', faction = 'UEF'},}
Fletcher_3 =     {{text = '[{i Fletcher}]: I got my eyes on you, Aeon. I haven\'t forgotten what you people did during the War.', vid = 'X01_Fletcher_M01_02879.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M01_02879', faction = 'UEF'},}
Fletcher_4 =     {{text = '[{i Fletcher}]: I could really use your help up here, Colonel. Destroy those bases as soon as you can.', vid = 'X01_Fletcher_M01_03621.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M01_03621', faction = 'UEF'},}
Fletcher_5 =     {{text = '[{i Fletcher}]: We can\'t rendezvous until you eliminate those bases.', vid = 'X01_Fletcher_M01_04232.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M01_04232', faction = 'UEF'},}
Fletcher_6 =     {{text = '[{i Fletcher}]: Quit messin\' around and destroy those bases!', vid = 'X01_Fletcher_M01_04816.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M01_04816', faction = 'UEF'},}
Fletcher_7 =     {{text = '[{i Fletcher}]: Make yourself useful and destroy those bases.', vid = 'X01_Fletcher_M01_04817.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M01_04817', faction = 'UEF'},}
Fletcher_8 =     {{text = '[{i Fletcher}]: I\'m starting to think that you don\'t have the stomach to fight your own kind. Are you going to destroy those bases or what?', vid = 'X01_Fletcher_M01_04813.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M01_04813', faction = 'UEF'},}
Fletcher_9 =     {{text = '[{i Fletcher}]: We\'re going to lose Fort Clarke unless you get on with it. Destroy those bases!', vid = 'X01_Fletcher_M01_04235.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M01_04235', faction = 'UEF'},}
Fletcher_10 =    {{text = '[{i Fletcher}]: Seraphim forces have punched through my defenses, Colonel, and they\'re attacking strategic assets.', vid = 'X01_Fletcher_M03_03684.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M03_03684', faction = 'UEF'},}
Fletcher_11 =    {{text = '[{i Fletcher}]: The Seraphim is pressing the attack! I need help!', vid = 'X01_Fletcher_M03_03685.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M03_03685', faction = 'UEF'},}
Fletcher_12 =    {{text = '[{i Fletcher}]: Get your wired ass up here and help defend the Fort!', vid = 'X01_Fletcher_M03_03686.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M03_03686', faction = 'UEF'},}
Fletcher_13 =    {{text = '[{i Fletcher}]: Help me defend the Fort, Cybran!', vid = 'X01_Fletcher_M03_03687.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M03_03687', faction = 'UEF'},}
Fletcher_14 =    {{text = '[{i Fletcher}]: Quit messin\' around and get some reinforcements up here! The Fort is taking serious damage!', vid = 'X01_Fletcher_M03_03688.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M03_03688', faction = 'UEF'},}
Fletcher_15 =    {{text = '[{i Fletcher}]: Fort Clarke is under heavy attack! I need units up here pronto!', vid = 'X01_Fletcher_M03_03689.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M03_03689', faction = 'UEF'},}
Fletcher_16 =    {{text = '[{i Fletcher}]: I\'m gettin\' shot to hell up here, Colonel. I could really use a hand!', vid = 'X01_Fletcher_M03_03690.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M03_03690', faction = 'UEF'},}
Fletcher_17 =    {{text = '[{i Fletcher}]: This is Brigadier Fletcher requesting assistance! Seraphim forces are pounding me pretty hard!', vid = 'X01_Fletcher_M03_03691.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M03_03691', faction = 'UEF'},}
Fletcher_18 =    {{text = '[{i Fletcher}]: Where are my reinforcements?', vid = 'X01_Fletcher_M03_03695.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M03_03695', faction = 'UEF'},}
Fletcher_19 =    {{text = '[{i Fletcher}]: That alien freak is due east of my location. Assault its position from the south, and I\'ll push in from the west. It won\'t be able to defend two fronts at once.', vid = 'X01_Fletcher_M03_03696.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M03_03696', faction = 'UEF'},}
Fletcher_20 =    {{text = '[{i Fletcher}]: That alien freak is due east of my location. Its got some experimental units stomping around, and we\'ll need to destroy those, too. Assault its position from the south, and I\'ll push in from the west. It won\'t be able to defend two fronts at once.', vid = 'X01_Fletcher_M03_04694.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M03_04694', faction = 'UEF'},}
Fletcher_21 =    {{text = '[{i Fletcher}]: You gonna sit around or are you gonna get your hands dirty? The Seraphim\'s to my east. Attack it.', vid = 'X01_Fletcher_M03_03697.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M03_03697', faction = 'UEF'},}
Fletcher_22 =    {{text = '[{i Fletcher}]: You gonna sit around or are you gonna get your hands dirty? The Seraphim\'s to the east, and it\'s got a bunch of experimental units. Take them out.', vid = 'X01_Fletcher_M03_04695.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M03_04695', faction = 'UEF'},}
Fletcher_23 =    {{text = '[{i Fletcher}]: It\'s about time! The Seraphim is east of Fort Clarke. Make yourself useful and try to distract it.', vid = 'X01_Fletcher_M03_03699.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M03_03699', faction = 'UEF'},}
Fletcher_24 =    {{text = '[{i Fletcher}]: It\'s about time! The Seraphim is east of Fort Clarke, and it\'s got a bunch of experimentals. Make yourself useful and try to distract them.', vid = 'X01_Fletcher_M03_04697.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M03_04697', faction = 'UEF'},}
Fletcher_25 =    {{text = '[{i Fletcher}]: That\'s what I love to see. Burn, baby, burn!', vid = 'X01_Fletcher_M03_03701.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M03_03701', faction = 'UEF'},}
Fletcher_26 =    {{text = '[{i Fletcher}]: You looking for a medal or something? This fight ain\'t over yet.', vid = 'X01_Fletcher_M03_03702.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M03_03702', faction = 'UEF'},}
Fletcher_27 =    {{text = '[{i Fletcher}]: Maybe you freaks are willing to fight.', vid = 'X01_Fletcher_M03_03704.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M03_03704', faction = 'UEF'},}
Fletcher_28 =    {{text = '[{i Fletcher}]: The Seraphim is still kicking, Colonel. Let\'s remedy that.', vid = 'X01_Fletcher_M03_03706.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M03_03706', faction = 'UEF'},}
Fletcher_29 =    {{text = '[{i Fletcher}]: We need to eliminate that Seraphim, Colonel.', vid = 'X01_Fletcher_M03_03707.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M03_03707', faction = 'UEF'},}
Fletcher_30 =    {{text = '[{i Fletcher}]: Pick up the pace, Cybran! We gotta get rid of that Seraphim!', vid = 'X01_Fletcher_M03_03708.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M03_03708', faction = 'UEF'},}
Fletcher_31 =    {{text = '[{i Fletcher}]: Are you waiting for that alien to die of old age?', vid = 'X01_Fletcher_M03_03709.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M03_03709', faction = 'UEF'},}
Fletcher_32 =    {{text = '[{i Fletcher}]: Get up here and help me deal with the Seraphim.', vid = 'X01_Fletcher_M03_03710.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M03_03710', faction = 'UEF'},}
Fletcher_33 =    {{text = '[{i Fletcher}]: Are you waiting for the Seraphim to die of old age?', vid = 'X01_Fletcher_M03_03711.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M03_03711', faction = 'UEF'},}
Fletcher_34 =    {{text = '[{i Fletcher}]: That alien freak is starting to soften. Hit it again!', vid = 'X01_Fletcher_M03_03712.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M03_03712', faction = 'UEF'},}
Fletcher_35 =    {{text = '[{i Fletcher}]: We got it on the run.', vid = 'X01_Fletcher_M03_03713.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M03_03713', faction = 'UEF'},}
Fletcher_36 =    {{text = '[{i Fletcher}]: It won\'t last much longer now. Make it pay for what they did to Earth.', vid = 'X01_Fletcher_M03_03714.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M03_03714', faction = 'UEF'},}
Fletcher_37 =    {{text = '[{i Fletcher}]: I see it! Commander, hit that thing with whatever you\'ve got!', vid = 'X01_Fletcher_M03_04256.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_M03_04256', faction = 'UEF'},}
Fletcher_38 =    {{text = '[{i Fletcher}]: It\'s time for this to get serious.', vid = 'X01_Fletcher_T01_04531.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_T01_04531', faction = 'UEF'},}
Fletcher_39 =    {{text = '[{i Fletcher}]: You freaks are going to pay for what you did to Earth.', vid = 'X01_Fletcher_T01_04532.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_T01_04532', faction = 'UEF'},}
Fletcher_40 =    {{text = '[{i Fletcher}]: Commander, I could use a hand over here. I\'m getting hit pretty hard.', vid = 'X01_Fletcher_T01_04533.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_T01_04533', faction = 'UEF'},}
Fletcher_41 =    {{text = '[{i Fletcher}]: I\'m in a lot of trouble!', vid = 'X01_Fletcher_T01_04534.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_T01_04534', faction = 'UEF'},}
Fletcher_42 =    {{text = '[{i Fletcher}]: You\'ve got to be kidding!', vid = 'X01_Fletcher_T01_04535.sfd', bank = 'X01_VO', cue = 'X01_Fletcher_T01_04535', faction = 'UEF'},}
Fletcher_43 =    {{text = '[{i Fletcher}]: Using a secure channel here, Colonel. Never know who else might be listening. You gotta watch your back. You really think once this is over the Aeon and Cybran are going to join hands and let us live in peace? We have to be ready for anything, even if it means kicking off another war. Fletcher out.', vid = 'X03_Fletcher_M01_03289.sfd', bank = 'X03_VO', cue = 'X03_Fletcher_M01_03289', faction = 'UEF'},}
Fletcher_44 =    {{text = '[{i Fletcher}]: I\'m hearing all kinds of crazy stuff on this end, Colonel, and none of it is good. An Aeon civil war? Who do they think they\'re kidding? Those freaks will kill all of us if given the chance. This is all going to get really ugly. You just watch. Fletcher out.', vid = 'X03_Fletcher_M02_03323.sfd', bank = 'X03_VO', cue = 'X03_Fletcher_M02_03323', faction = 'UEF'},}
Fletcher_45 =    {{text = '[{i Fletcher}]: The Seraphim to your south has a nuke launcher. It\'s lightly defended, so you shouldn\'t have any problem capturing it. You never know when you\'re going to have to nuke an alien ... or one of our so-called allies.', vid = 'X03_Fletcher_M03_03354.sfd', bank = 'X03_VO', cue = 'X03_Fletcher_M03_03354', faction = 'UEF'},}
Fletcher_46 =    {{text = '[{i Fletcher}]: Grab that launcher if you can, Colonel. Fletcher out.', vid = 'X03_Fletcher_M03_03356.sfd', bank = 'X03_VO', cue = 'X03_Fletcher_M03_03356', faction = 'UEF'},}
Fletcher_47 =    {{text = '[{i Fletcher}]: The nuke launcher is yours. Put it to use. Fletcher out.', vid = 'X03_Fletcher_M03_03358.sfd', bank = 'X03_VO', cue = 'X03_Fletcher_M03_03358', faction = 'UEF'},}
Fletcher_48 =    {{text = '[{i Fletcher}]: That was a hell of a missed opportunity, Colonel. Fletcher out.', vid = 'X03_Fletcher_M03_03359.sfd', bank = 'X03_VO', cue = 'X03_Fletcher_M03_03359', faction = 'UEF'},}
Fletcher_49 =    {{text = '[{i Fletcher}]: I\'m on-ground and on-target, Colonel. Let\'s unplug that damn computer and head home. Fletcher out.', vid = 'X05_Fletcher_M01_03796.sfd', bank = 'X05_VO', cue = 'X05_Fletcher_M01_03796', faction = 'UEF'},}
Fletcher_50 =    {{text = '[{i Fletcher}]: It\'s about time, Commander. I was starting to think that you and QAI were building a little love nest.', vid = 'X05_Fletcher_M01_03805.sfd', bank = 'X05_VO', cue = 'X05_Fletcher_M01_03805', faction = 'UEF'},}
Fletcher_51 =    {{text = '[{i Fletcher}]: Took your sweet old time, didn\'t you? QAI isn\'t going to kill itself, you know.', vid = 'X05_Fletcher_M01_03810.sfd', bank = 'X05_VO', cue = 'X05_Fletcher_M01_03810', faction = 'UEF'},}
Fletcher_52 =    {{text = '[{i Fletcher}]: This whole damn thing is a mess. Cybrans turning on each other? Aeon switching sides just cause some Princess shows up? I tell you, we can\'t trust any of them. The blue of the UEF, that\'s what matters. That\'s the only thing that matters. Fletcher out.', vid = 'X05_Fletcher_M01_03815.sfd', bank = 'X05_VO', cue = 'X05_Fletcher_M01_03815', faction = 'UEF'},}
Fletcher_53 =    {{text = '[{i Fletcher}]: Commander, if I\'m going to get anywhere, you\'re going to have to provide me with air cover. Defend my position against air attacks. Fletcher out.', vid = 'X05_Fletcher_M02_03827.sfd', bank = 'X05_VO', cue = 'X05_Fletcher_M02_03827', faction = 'UEF'},}
Fletcher_54 =    {{text = '[{i Fletcher}]: Looks like we got the last Spiderbot. Fletcher out.', vid = 'X05_Fletcher_M02_03828.sfd', bank = 'X05_VO', cue = 'X05_Fletcher_M02_03828', faction = 'UEF'},}
Fletcher_55 =    {{text = '[{i Fletcher}]: Get some units on that thing!', vid = 'X05_Fletcher_M02_03830.sfd', bank = 'X05_VO', cue = 'X05_Fletcher_M02_03830', faction = 'UEF'},}
Fletcher_56 =    {{text = '[{i Fletcher}]: Scratch one Scathis. Fletcher out.', vid = 'X05_Fletcher_M02_03831.sfd', bank = 'X05_VO', cue = 'X05_Fletcher_M02_03831', faction = 'UEF'},}
Fletcher_57 =    {{text = '[{i Fletcher}]: I\'ve hit Tech 2. Ramping up to Tech 3.', vid = 'X05_Fletcher_M02_04710.sfd', bank = 'X05_VO', cue = 'X05_Fletcher_M02_04710', faction = 'UEF'},}
Fletcher_58 =    {{text = '[{i Fletcher}]: I\'m at Tech 3. Starting to produce units.', vid = 'X05_Fletcher_M02_04711.sfd', bank = 'X05_VO', cue = 'X05_Fletcher_M02_04711', faction = 'UEF'},}
Fletcher_59 =    {{text = '[{i Fletcher}]: Starting work on a Fatboy. Maintain air cover for me. Fletcher out.', vid = 'X05_Fletcher_M02_04712.sfd', bank = 'X05_VO', cue = 'X05_Fletcher_M02_04712', faction = 'UEF'},}
Fletcher_60 =    {{text = '[{i Fletcher}]: Producing air units.', vid = 'X05_Fletcher_M02_04713.sfd', bank = 'X05_VO', cue = 'X05_Fletcher_M02_04713', faction = 'UEF'},}
Fletcher_61 =    {{text = '[{i Fletcher}]: Producing land units.', vid = 'X05_Fletcher_M02_04714.sfd', bank = 'X05_VO', cue = 'X05_Fletcher_M02_04714', faction = 'UEF'},}
Fletcher_62 =    {{text = '[{i Fletcher}]: We got him on the ropes!', vid = 'X05_Fletcher_M02_03845.sfd', bank = 'X05_VO', cue = 'X05_Fletcher_M02_03845', faction = 'UEF'},}
Fletcher_63 =    {{text = '[{i Fletcher}]: Keep pushing! His base is buckling!', vid = 'X05_Fletcher_M02_03846.sfd', bank = 'X05_VO', cue = 'X05_Fletcher_M02_03846', faction = 'UEF'},}
Fletcher_64 =    {{text = '[{i Fletcher}]: He\'s got almost nothing left! Take him out!', vid = 'X05_Fletcher_M02_03847.sfd', bank = 'X05_VO', cue = 'X05_Fletcher_M02_03847', faction = 'UEF'},}
Fletcher_65 =    {{text = '[{i Fletcher}]: Concentrate on QAI. I\'ll mop up Hex5\'s base and join you as soon as I can. Fletcher out.', vid = 'X05_Fletcher_M02_04944.sfd', bank = 'X05_VO', cue = 'X05_Fletcher_M02_04944', faction = 'UEF'},}
Fletcher_66 =    {{text = '[{i Fletcher}]: Soul Rippers are tearing up my Fatboy! I need air cover, now!', vid = 'X05_Fletcher_M02_04945.sfd', bank = 'X05_VO', cue = 'X05_Fletcher_M02_04945', faction = 'UEF'},}
Fletcher_67 =    {{text = '[{i Fletcher}]: It\'s about time. Keep those bastards off me while I build up my base.', vid = 'X05_Fletcher_M02_04948.sfd', bank = 'X05_VO', cue = 'X05_Fletcher_M02_04948', faction = 'UEF'},}
Fletcher_68 =    {{text = '[{i Fletcher}]: Yeah, yeah. Give it a rest already.', vid = 'X05_Fletcher_M02_04950.sfd', bank = 'X05_VO', cue = 'X05_Fletcher_M02_04950', faction = 'UEF'},}
Fletcher_69 =    {{text = '[{i Fletcher}]: Starting up my economy, Commander. Fletcher out.', vid = 'X05_Fletcher_M02_04952.sfd', bank = 'X05_VO', cue = 'X05_Fletcher_M02_04952', faction = 'UEF'},}
Fletcher_70 =    {{text = '[{i Fletcher}]: I\'m getting hit pretty hard! Get over here and help me! Fletcher out.', vid = 'X05_Fletcher_M02_05108.sfd', bank = 'X05_VO', cue = 'X05_Fletcher_M02_05108', faction = 'UEF'},}
Fletcher_71 =    {{text = '[{i Fletcher}]: Colonel, I\'d really appreciate it if you could help me out. The enemy is pounding me pretty hard. Fletcher out.', vid = 'X05_Fletcher_M02_05109.sfd', bank = 'X05_VO', cue = 'X05_Fletcher_M02_05109', faction = 'UEF'},}
Fletcher_72 =    {{text = '[{i Fletcher}]: Get it in gear, Cybran! The enemy is kicking the tar out of me and I need your help. Fletcher out.', vid = 'X05_Fletcher_M02_05110.sfd', bank = 'X05_VO', cue = 'X05_Fletcher_M02_05110', faction = 'UEF'},}
Fletcher_73 =    {{text = '[{i Fletcher}]: My base is being destroyed. I need help! I can\'t hold them off!', vid = 'X05_Fletcher_M02_05111.sfd', bank = 'X05_VO', cue = 'X05_Fletcher_M02_05111', faction = 'UEF'},}
Fletcher_74 =    {{text = '[{i Fletcher}]: Enemy units are hitting my base pretty hard. I need you to reinforce my position. Fletcher out.', vid = 'X05_Fletcher_M02_05112.sfd', bank = 'X05_VO', cue = 'X05_Fletcher_M02_05112', faction = 'UEF'},}
Fletcher_75 =    {{text = '[{i Fletcher}]: Commander, get a spy plane in the air and locate Hex5\'s position. He\'s cloaked and I can\'t get a lock on him. Fletcher out.', vid = 'X05_Fletcher_M02_05113.sfd', bank = 'X05_VO', cue = 'X05_Fletcher_M02_05113', faction = 'UEF'},}
Fletcher_76 =    {{text = '[{i Fletcher}]: Commander, send up a spy plane or two and track down Hex5. He\'s cloaked and stealthed, so the only way we\'re going to find him is through line of sight. Fletcher out.', vid = 'X05_Fletcher_M02_05114.sfd', bank = 'X05_VO', cue = 'X05_Fletcher_M02_05114', faction = 'UEF'},}
Fletcher_77 =    {{text = '[{i Fletcher}]: Hall\'s snooping around. Gotta watch him, Colonel. Our list of friends keeps getting shorter and shorter. Fletcher out.', vid = 'X05_Fletcher_M03_03889.sfd', bank = 'X05_VO', cue = 'X05_Fletcher_M03_03889', faction = 'UEF'},}
Fletcher_78 =    {{text = '[{i Fletcher}]: I\'m getting pounded from all sides! I have to get out of here! Recalling!', vid = 'X05_Fletcher_M03_04715.sfd', bank = 'X05_VO', cue = 'X05_Fletcher_M03_04715', faction = 'UEF'},}
Fletcher_79 =    {{text = '[{i Fletcher}]: Here are some reinforcements, Colonel. And watch your back -- I\'ve got a bad feeling about this OP.', vid = 'X06_Fletcher_M01_03904.sfd', bank = 'X06_VO', cue = 'X06_Fletcher_M01_03904', faction = 'UEF'},}
Fletcher_80 =    {{text = '[{i Fletcher}]: I\'ve been ordered to provide you with reinforcements, Commander. Just don\'t go thinking this makes us friends -- I know how you chip-heads operate.', vid = 'X06_Fletcher_M01_03905.sfd', bank = 'X06_VO', cue = 'X06_Fletcher_M01_03905', faction = 'UEF'},}
Fletcher_81 =    {{text = '[{i Fletcher}]: I\'ve been ordered to provide you with reinforcements, Commander. Just don\'t go thinking this makes us friends -- I know how you freaks operate.', vid = 'X06_Fletcher_M01_03907.sfd', bank = 'X06_VO', cue = 'X06_Fletcher_M01_03907', faction = 'UEF'},}
Fletcher_82 =    {{text = '[{i Fletcher}]: Why should I help you? You\'re no different from the rest of them. Once this over, you\'re going to turn on us.', vid = 'X06_Fletcher_M02_04475.sfd', bank = 'X06_VO', cue = 'X06_Fletcher_M02_04475', faction = 'UEF'},}
Fletcher_83 =    {{text = '[{i Fletcher}]: You sold us out, Hall. Allied us with monsters and freaks. Once I get my hands on Black Sun, I\'ll be able rebuild it and end this once and for all.', vid = 'X06_Fletcher_M02_04477.sfd', bank = 'X06_VO', cue = 'X06_Fletcher_M02_04477', faction = 'UEF'},}
Fletcher_84 =    {{text = '[{i Fletcher}]: You fool. They\'re scheming and plotting right under your nose, and you\'re too blind to notice.', vid = 'X06_Fletcher_M02_04479.sfd', bank = 'X06_VO', cue = 'X06_Fletcher_M02_04479', faction = 'UEF'},}
Fletcher_85 =    {{text = '[{i Fletcher}]: I spent a lot of time thinking about this. There\'s only one possible outcome. One way for this to end.', vid = 'X06_Fletcher_M02_04482.sfd', bank = 'X06_VO', cue = 'X06_Fletcher_M02_04482', faction = 'UEF'},}
Fletcher_86 =    {{text = '[{i Fletcher}]: I won\'t let you use that experimental!', vid = 'X06_Fletcher_T01_04803.sfd', bank = 'X06_VO', cue = 'X06_Fletcher_T01_04803', faction = 'UEF'},}
Fletcher_87 =    {{text = '[{i Fletcher}]: You\'re going to use that experimental on Black Sun! I won\'t let you do it!', vid = 'X06_Fletcher_T01_04804.sfd', bank = 'X06_VO', cue = 'X06_Fletcher_T01_04804', faction = 'UEF'},}
Fletcher_88 =    {{text = '[{i Fletcher}]: You can\'t stop me with that experimental! I\'ll destroy it first!', vid = 'X06_Fletcher_T01_04805.sfd', bank = 'X06_VO', cue = 'X06_Fletcher_T01_04805', faction = 'UEF'},}
Fletcher_89 =    {{text = '[{i Fletcher}]: You are no better than the rest of them!', vid = 'X06_Fletcher_T01_03027.sfd', bank = 'X06_VO', cue = 'X06_Fletcher_T01_03027', faction = 'UEF'},}
Fletcher_90 =    {{text = '[{i Fletcher}]: This war will never end. Never!', vid = 'X06_Fletcher_T01_03028.sfd', bank = 'X06_VO', cue = 'X06_Fletcher_T01_03028', faction = 'UEF'},}
Fletcher_91 =    {{text = '[{i Fletcher}]: I thought I could trust you! You\'re a traitor.', vid = 'X06_Fletcher_T01_03029.sfd', bank = 'X06_VO', cue = 'X06_Fletcher_T01_03029', faction = 'UEF'},}
Fletcher_92 =    {{text = '[{i Fletcher}]: You\'re a coward.', vid = 'X06_Fletcher_T01_03030.sfd', bank = 'X06_VO', cue = 'X06_Fletcher_T01_03030', faction = 'UEF'},}
Fletcher_93 =    {{text = '[{i Fletcher}]: You\'re a traitor!', vid = 'X06_Fletcher_T01_03033.sfd', bank = 'X06_VO', cue = 'X06_Fletcher_T01_03033', faction = 'UEF'},}
Fletcher_94 =    {{text = '[{i Fletcher}]: Once I have Black Sun, I will be able destroy anyone that stands in my way.', vid = 'X06_Fletcher_T01_03034.sfd', bank = 'X06_VO', cue = 'X06_Fletcher_T01_03034', faction = 'UEF'},}
Fletcher_95 =    {{text = '[{i Fletcher}]: Don\'t you see? Black Sun is the answer!', vid = 'X06_Fletcher_T01_03035.sfd', bank = 'X06_VO', cue = 'X06_Fletcher_T01_03035', faction = 'UEF'},}
Fletcher_96 =    {{text = '[{i Fletcher}]: I will rebuild Black Sun!', vid = 'X06_Fletcher_T01_03036.sfd', bank = 'X06_VO', cue = 'X06_Fletcher_T01_03036', faction = 'UEF'},}
Fletcher_97 =    {{text = '[{i Fletcher}]: They are our enemies! Why can\'t you see that?', vid = 'X06_Fletcher_T01_03041.sfd', bank = 'X06_VO', cue = 'X06_Fletcher_T01_03041', faction = 'UEF'},}
Fletcher_98 =    {{text = '[{i Fletcher}]: Don\'t you understand? Seraphim, Aeon, Cybran ... they\'re all the same. They\'ll kill us!', vid = 'X06_Fletcher_T01_03042.sfd', bank = 'X06_VO', cue = 'X06_Fletcher_T01_03042', faction = 'UEF'},}
Fletcher_99 =    {{text = '[{i Fletcher}]: You and your kind are responsible for this war.', vid = 'X06_Fletcher_T01_03043.sfd', bank = 'X06_VO', cue = 'X06_Fletcher_T01_03043', faction = 'UEF'},}
Fletcher_100 =   {{text = '[{i Fletcher}]: This war is your fault! And now you will pay.', vid = 'X06_Fletcher_T01_03044.sfd', bank = 'X06_VO', cue = 'X06_Fletcher_T01_03044', faction = 'UEF'},}
Fletcher_101 =   {{text = '[{i Fletcher}]: You and your kind are responsible for this war.', vid = 'X06_Fletcher_T01_03045.sfd', bank = 'X06_VO', cue = 'X06_Fletcher_T01_03045', faction = 'UEF'},}
Fletcher_102 =   {{text = '[{i Fletcher}]: This war is your fault! And now you will pay.', vid = 'X06_Fletcher_T01_03046.sfd', bank = 'X06_VO', cue = 'X06_Fletcher_T01_03046', faction = 'UEF'},}
Fletcher_103 =   {{text = '[{i Fletcher}]: There is no stopping me!', vid = 'X06_Fletcher_T01_03047.sfd', bank = 'X06_VO', cue = 'X06_Fletcher_T01_03047', faction = 'UEF'},}
Fletcher_104 =   {{text = '[{i Fletcher}]: Black Sun is the answer!', vid = 'X06_Fletcher_T01_03048.sfd', bank = 'X06_VO', cue = 'X06_Fletcher_T01_03048', faction = 'UEF'},}
Fletcher_105 =   {{text = '[{i Fletcher}]: Why won\'t you listen!', vid = 'X06_Fletcher_T01_03049.sfd', bank = 'X06_VO', cue = 'X06_Fletcher_T01_03049', faction = 'UEF'},}
Fletcher_106 =   {{text = '[{i Fletcher}]: Listen up, Colonel. These exercises will fully train you in all operational aspects of your Armored Command Unit (ACU), including the construction and deployment of structures and units. You need to get through this stuff as quickly as possible because the Order and their alien buddies are kicking the crap out of us from one end of the galaxy to the other. So pay attention.', vid = 'X1T_Fletcher_TU01_04974.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU01_04974', faction = 'UEF'},}
Fletcher_107 =   {{text = '[{i Fletcher}]: The first thing you need to learn is how to navigate the battlefield.', vid = 'X1T_Fletcher_TU01_04975.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU01_04975', faction = 'UEF'},}
Fletcher_108 =   {{text = '[{i Fletcher}]: This exercise is brain dead simple, but the fat cats make me run everyone through it. There are two ways to pan the battlefield -- with the mouse and with the arrow keys. To use the mouse, simply move the pointer to the edge of the map, and it\'ll automatically scroll. Pressing and holding the arrow keys does the same thing.', vid = 'X1T_Fletcher_TU02_04976.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU02_04976', faction = 'UEF'},}
Fletcher_109 =   {{text = '[{i Fletcher}]: Excellent work, Commander.', vid = 'X1T_Fletcher_TU02_04984.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU02_04984', faction = 'UEF'},}
Fletcher_110 =   {{text = '[{i Fletcher}]: Your ACU offers a full view of the battlefield, including the ability to zoom in and out. You use the mouse wheel -- or Q and W keys -- to zoom in and out. ', vid = 'X1T_Fletcher_TU02_04981.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU02_04981', faction = 'UEF'},}
Fletcher_111 =   {{text = '[{i Fletcher}]: Good. Let\'s move on.', vid = 'X1T_Fletcher_TU02_04985.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU02_04985', faction = 'UEF'},}
Fletcher_112 =   {{text = '[{i Fletcher}]: Center the screen over the objective markers, Commander.', vid = 'X1T_Fletcher_TU02_05096.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU02_05096', faction = 'UEF'},}
Fletcher_113 =   {{text = '[{i Fletcher}]: Let\'s go, Commander! Complete the objective.', vid = 'X1T_Fletcher_TU02_05097.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU02_05097', faction = 'UEF'},}
Fletcher_114 =   {{text = '[{i Fletcher}]: Zoom out until you see the objective marker.', vid = 'X1T_Fletcher_TU02_05098.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU02_05098', faction = 'UEF'},}
Fletcher_115 =   {{text = '[{i Fletcher}]: We don\'t have all day, Commander!', vid = 'X1T_Fletcher_TU02_05099.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU02_05099', faction = 'UEF'},}
Fletcher_116 =   {{text = '[{i Fletcher}]: Now it\'s time to take your ACU for a stroll. Left-click the ACU to select it and right-click on a map location to give it a move order.', vid = 'X1T_Fletcher_TU03_04986.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU03_04986', faction = 'UEF'},}
Fletcher_117 =   {{text = '[{i Fletcher}]: When you\'re ready, move your ACU to the designated location on the map.', vid = 'X1T_Fletcher_TU03_05126.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU03_05126', faction = 'UEF'},}
Fletcher_118 =   {{text = '[{i Fletcher}]: Well done.', vid = 'X1T_Fletcher_TU03_04991.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU03_04991', faction = 'UEF'},}
Fletcher_119 =   {{text = '[{i Fletcher}]: Commander, left-click to select your ACU and right-click to move. ', vid = 'X1T_Fletcher_TU03_04992.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU03_04992', faction = 'UEF'},}
Fletcher_120 =   {{text = '[{i Fletcher}]: Let\'s see if we can speed this up, okay?', vid = 'X1T_Fletcher_TU03_04993.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU03_04993', faction = 'UEF'},}
Fletcher_121 =   {{text = '[{i Fletcher}]: Your ACU is a powerful weapons system, and can easily fend off lower-level enemy units. To attack an enemy, select your ACU and right-click on the unit you want to destroy. Remember, your ACU will automatically attack as soon as it\'s within range.', vid = 'X1T_Fletcher_TU03_04996.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU03_04996', faction = 'UEF'},}
Fletcher_122 =   {{text = '[{i Fletcher}]: Attack and destroy the enemy unit.', vid = 'X1T_Fletcher_TU03_05127.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU03_05127', faction = 'UEF'},}
Fletcher_123 =   {{text = '[{i Fletcher}]: Good job soldier, but don\'t get too cocky.', vid = 'X1T_Fletcher_TU03_04997.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU03_04997', faction = 'UEF'},}
Fletcher_124 =   {{text = '[{i Fletcher}]: Destroy that unit, Commander.', vid = 'X1T_Fletcher_TU03_04998.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU03_04998', faction = 'UEF'},}
Fletcher_125 =   {{text = '[{i Fletcher}]: If you take this long on a real battlefield, you won\'t last five seconds. Get it done.', vid = 'X1T_Fletcher_TU03_04999.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU03_04999', faction = 'UEF'},}
Fletcher_126 =   {{text = '[{i Fletcher}]: Two resources create and maintain everything you build: Mass and Energy. In order to have a fully functioning economy, you must balance how you gather and spend each resource.', vid = 'X1T_Fletcher_TU04_05002.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU04_05002', faction = 'UEF'},}
Fletcher_127 =   {{text = '[{i Fletcher}]: This HUD element indicates how much Energy you\'re gathering and spending, while this HUD element shows your intake of Mass. If you spend more than you\'re bringing in, you will have negative income and run the risk of stalling your economy.', vid = 'X1T_Fletcher_TU04_05003.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU04_05003', faction = 'UEF'},}
Fletcher_128 =   {{text = '[{i Fletcher}]: You obtain Mass by building Mass extractors on Mass deposits; Mass deposits are displayed on your HUD as a green box with a green X through its middle. You can also get Mass with Mass fabricators and by reclamation.', vid = 'X1T_Fletcher_TU04_05004.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU04_05004', faction = 'UEF'},}
Fletcher_129 =   {{text = '[{i Fletcher}]: I want you to build three Mass extractors. Select your ACU, click the Mass extractor icon in the Build UI, and then click on one of the highlighted Mass deposits. Do this for each extractor.', vid = 'X1T_Fletcher_TU04_05005.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU04_05005', faction = 'UEF'},}
Fletcher_130 =   {{text = '[{i Fletcher}]: Well done. You\'re now collecting Mass.', vid = 'X1T_Fletcher_TU04_05006.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU04_05006', faction = 'UEF'},}
Fletcher_131 =   {{text = '[{i Fletcher}]: Let\'s get those three Mass extractors built, Commander.', vid = 'X1T_Fletcher_TU04_05007.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU04_05007', faction = 'UEF'},}
Fletcher_132 =   {{text = '[{i Fletcher}]: Can\'t build anything without Mass. Complete your objective.', vid = 'X1T_Fletcher_TU04_05008.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU04_05008', faction = 'UEF'},}
Fletcher_133 =   {{text = '[{i Fletcher}]: Energy is collected through the use of power generators. There are three levels of power generators, and unlike Mass extractors, they can be built anywhere on the map. ', vid = 'X1T_Fletcher_TU04_05011.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU04_05011', faction = 'UEF'},}
Fletcher_134 =   {{text = '[{i Fletcher}]: Your orders are to construct two power generators. Select your ACU, click on the power generator icon in the Build UI, and then click on any open location on the ground. ', vid = 'X1T_Fletcher_TU04_05017.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU04_05017', faction = 'UEF'},}
Fletcher_135 =   {{text = '[{i Fletcher}]: Now that those generators are online, you\'ll receive a steady stream of Energy.', vid = 'X1T_Fletcher_TU04_05014.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU04_05014', faction = 'UEF'},}
Fletcher_136 =   {{text = '[{i Fletcher}]: Build two power generators, Commander.', vid = 'X1T_Fletcher_TU04_05015.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU04_05015', faction = 'UEF'},}
Fletcher_137 =   {{text = '[{i Fletcher}]: Got Order forces beating on the door, Commander. Build the power generators.', vid = 'X1T_Fletcher_TU04_05016.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU04_05016', faction = 'UEF'},}
Fletcher_138 =   {{text = '[{i Fletcher}]: Okay, let\'s start constructing an army. Factories build Engineers and tactical units like tanks, bombers and submarines. Go ahead and build a Land Factory.', vid = 'X1T_Fletcher_TU04_05019.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU04_05019', faction = 'UEF'},}
Fletcher_139 =   {{text = '[{i Fletcher}]: Well done, Commander. Now you can start constructing an army.', vid = 'X1T_Fletcher_TU04_05020.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU04_05020', faction = 'UEF'},}
Fletcher_140 =   {{text = '[{i Fletcher}]: Build the Land Factory, Commander.', vid = 'X1T_Fletcher_TU04_05021.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU04_05021', faction = 'UEF'},}
Fletcher_141 =   {{text = '[{i Fletcher}]: You waiting for an invitation? Build the Land Factory.', vid = 'X1T_Fletcher_TU04_05022.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU04_05022', faction = 'UEF'},}
Fletcher_142 =   {{text = '[{i Fletcher}]: Now that you have a Land Factory, you can construct mobile land units -- left-click on the factory to select it and then click on the units you want to build. For this exercise, construct two light assault bots and two light artillery. Each time you click on a unit icon, it will be added to the factory\'s Build Queue.', vid = 'X1T_Fletcher_TU05_05018.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU05_05018', faction = 'UEF'},}
Fletcher_143 =   {{text = '[{i Fletcher}]: Good job. Now you have the beginnings of an actual army.', vid = 'X1T_Fletcher_TU05_05025.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU05_05025', faction = 'UEF'},}
Fletcher_144 =   {{text = '[{i Fletcher}]: Let\'s get those units built, Commander.', vid = 'X1T_Fletcher_TU05_05026.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU05_05026', faction = 'UEF'},}
Fletcher_145 =   {{text = '[{i Fletcher}]: Can\'t fight the UEF\'s enemies with harsh language, Commander. Build those units.', vid = 'X1T_Fletcher_TU05_05027.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU05_05027', faction = 'UEF'},}
Fletcher_146 =   {{text = '[{i Fletcher}]: Control groups let you quickly select a group of units without having to use the mouse. To create a control group, drag-select or Shift-click the units you created and press Ctrl+1. Those units are now in control group 1; if you press the number 1 key, you will automatically select all units within the control group.', vid = 'X1T_Fletcher_TU05_05030.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU05_05030', faction = 'UEF'},}
Fletcher_147 =   {{text = '[{i Fletcher}]: Make a control group.', vid = 'X1T_Fletcher_TU05_05031.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU05_05031', faction = 'UEF'},}
Fletcher_148 =   {{text = '[{i Fletcher}]: Now you\'re cooking.', vid = 'X1T_Fletcher_TU05_05032.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU05_05032', faction = 'UEF'},}
Fletcher_149 =   {{text = '[{i Fletcher}]: Go ahead and make a control group, Commander.', vid = 'X1T_Fletcher_TU05_05033.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU05_05033', faction = 'UEF'},}
Fletcher_150 =   {{text = '[{i Fletcher}]: Let\'s get it done, Commander. Create a control group.', vid = 'X1T_Fletcher_TU05_05034.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU05_05034', faction = 'UEF'},}
Fletcher_151 =   {{text = '[{i Fletcher}]: Factories also produce Engineers, which are able to construct structures that are unavailable to your ACU, like Mass and Energy storage and radar and sonar installations. Engineers can also reclaim structures and units, capture enemy units, repair damaged units and structures and assist with other construction.', vid = 'X1T_Fletcher_TU06_05037.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU06_05037', faction = 'UEF'},}
Fletcher_152 =   {{text = '[{i Fletcher}]: Construct two Engineers from the Land Factory.', vid = 'X1T_Fletcher_TU06_05038.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU06_05038', faction = 'UEF'},}
Fletcher_153 =   {{text = '[{i Fletcher}]: That a fella. Well done.', vid = 'X1T_Fletcher_TU06_05039.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU06_05039', faction = 'UEF'},}
Fletcher_154 =   {{text = '[{i Fletcher}]: Build two Engineers, Commander.', vid = 'X1T_Fletcher_TU06_05040.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU06_05040', faction = 'UEF'},}
Fletcher_155 =   {{text = '[{i Fletcher}]: C\'mon, Commander, let\'s get those Engineers built.', vid = 'X1T_Fletcher_TU06_05041.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU06_05041', faction = 'UEF'},}
Fletcher_156 =   {{text = '[{i Fletcher}]: Both your ACU and Engineers can capture enemy units or structures, and nothing drives those Order scumbags crazier than watching you use their own gear against them. A power generator has been placed on the map -- capture it with your Engineers by selecting the Engineers and right-clicking on the power generator.', vid = 'X1T_Fletcher_TU06_05042.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU06_05042', faction = 'UEF'},}
Fletcher_157 =   {{text = '[{i Fletcher}]: That power generator is now under your control. Good job.', vid = 'X1T_Fletcher_TU06_05043.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU06_05043', faction = 'UEF'},}
Fletcher_158 =   {{text = '[{i Fletcher}]: Capture the power generator with your Engineers, Commander.', vid = 'X1T_Fletcher_TU06_05044.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU06_05044', faction = 'UEF'},}
Fletcher_159 =   {{text = '[{i Fletcher}]: Let\'s get with it, Commander. Capture the power generator.', vid = 'X1T_Fletcher_TU06_05045.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU06_05045', faction = 'UEF'},}
Fletcher_160 =   {{text = '[{i Fletcher}]: Never know when one of those damn aliens is going to show up, so always fortify your base with defensive structures. Point defenses offer decent protection and can fend off most land-based attacks. Build a point defense at the marked location.', vid = 'X1T_Fletcher_TU06_05046.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU06_05046', faction = 'UEF'},}
Fletcher_161 =   {{text = '[{i Fletcher}]: Good. Now if you\'re attacked, you\'ll at least have a fighting chance.', vid = 'X1T_Fletcher_TU06_05047.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU06_05047', faction = 'UEF'},}
Fletcher_162 =   {{text = '[{i Fletcher}]: Commander, build a point defense at the marked location. ', vid = 'X1T_Fletcher_TU06_05048.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU06_05048', faction = 'UEF'},}
Fletcher_163 =   {{text = '[{i Fletcher}]: Build that point defense, Commander.', vid = 'X1T_Fletcher_TU06_05049.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU06_05049', faction = 'UEF'},}
Fletcher_164 =   {{text = '[{i Fletcher}]: Well, what do you know -- here come some enemy units. Good thing you built a point defense.', vid = 'X1T_Fletcher_TU06_05050.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU06_05050', faction = 'UEF'},}
Fletcher_165 =   {{text = '[{i Fletcher}]: Defending against land-based attacks is only half the battle; you also need to be able to ward off air attacks. Build an anti-air tower at the designated spot.', vid = 'X1T_Fletcher_TU06_05051.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU06_05051', faction = 'UEF'},}
Fletcher_166 =   {{text = '[{i Fletcher}]: Maybe we\'ll make a commander out of you, yet. Good job.', vid = 'X1T_Fletcher_TU06_05052.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU06_05052', faction = 'UEF'},}
Fletcher_167 =   {{text = '[{i Fletcher}]: Get that anti-air built, Commander.', vid = 'X1T_Fletcher_TU06_05053.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU06_05053', faction = 'UEF'},}
Fletcher_168 =   {{text = '[{i Fletcher}]: If the enemy sends in bombers, you\'re a sitting duck. Build that AA.', vid = 'X1T_Fletcher_TU06_05054.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU06_05054', faction = 'UEF'},}
Fletcher_169 =   {{text = '[{i Fletcher}]: The enemy is attacking with bombers. Now aren\'t you glad that you built that AA?', vid = 'X1T_Fletcher_TU06_05055.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU06_05055', faction = 'UEF'},}
Fletcher_170 =   {{text = '[{i Fletcher}]: You can have all of the tanks and bombers you want, but if you don\'t know what the enemy is up to, you\'ll be fighting with one hand tied behind your back. That\'s why one of the most critical parts of waging war is intelligence. Radar is a critical component in gathering intel on your enemies. Build one now at the designated location.', vid = 'X1T_Fletcher_TU07_05064.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU07_05064', faction = 'UEF'},}
Fletcher_171 =   {{text = '[{i Fletcher}]: Now you can keep tabs on the enemy. Good work.', vid = 'X1T_Fletcher_TU07_05065.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU07_05065', faction = 'UEF'},}
Fletcher_172 =   {{text = '[{i Fletcher}]: Build a radar, Commander.', vid = 'X1T_Fletcher_TU07_05066.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU07_05066', faction = 'UEF'},}
Fletcher_173 =   {{text = '[{i Fletcher}]: Can\'t fight if you\'re blind, Commander. Build that radar.', vid = 'X1T_Fletcher_TU07_05067.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU07_05067', faction = 'UEF'},}
Fletcher_174 =   {{text = '[{i Fletcher}]: Your radar has uncovered the existence of a small Order base. Build up an army and destroy that base!', vid = 'X1T_Fletcher_TU08_05070.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU08_05070', faction = 'UEF'},}
Fletcher_175 =   {{text = '[{i Fletcher}]: I love seeing Order units burn. Great job, Commander.', vid = 'X1T_Fletcher_TU08_05071.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU08_05071', faction = 'UEF'},}
Fletcher_176 =   {{text = '[{i Fletcher}]: Wipe out that enemy base, Commander.', vid = 'X1T_Fletcher_TU08_05072.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU08_05072', faction = 'UEF'},}
Fletcher_177 =   {{text = '[{i Fletcher}]: I want that enemy base destroyed. Get it done.', vid = 'X1T_Fletcher_TU08_05073.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU08_05073', faction = 'UEF'},}
Fletcher_178 =   {{text = '[{i Fletcher}]: Picking up a larger Order base to the north. We\'re sending you reinforcements; add them to your attack force and erase that enemy base. I want nothing left standing.', vid = 'X1T_Fletcher_TU09_05076.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU09_05076', faction = 'UEF'},}
Fletcher_179 =   {{text = '[{i Fletcher}]: Scratch one scumbag. I love seeing Order commanders going up like that.', vid = 'X1T_Fletcher_TU09_05077.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU09_05077', faction = 'UEF'},}
Fletcher_180 =   {{text = '[{i Fletcher}]: Destroy the enemy commander.', vid = 'X1T_Fletcher_TU09_05078.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU09_05078', faction = 'UEF'},}
Fletcher_181 =   {{text = '[{i Fletcher}]: You waiting for that scumbag to come over and introduce himself? Attack and destroy him.', vid = 'X1T_Fletcher_TU09_05079.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU09_05079', faction = 'UEF'},}
Fletcher_182 =   {{text = '[{i Fletcher}]: To access more powerful weapons, you need to upgrade your factories. There are a total of three tech levels: Tech 1, Tech 2 and Tech 3. To upgrade to Tech 2, select your Land Factory and then click the Upgrade Icon.', vid = 'X1T_Fletcher_TU10_05082.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher__05082', faction = 'UEF'},}
Fletcher_183 =   {{text = '[{i Fletcher}]: Upgrade your Land Factory to Tech 2.', vid = 'X1T_Fletcher_TU10_05083.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher__05083', faction = 'UEF'},}
Fletcher_184 =   {{text = '[{i Fletcher}]: Excellent. You can now build Tech 2 units and Engineers.', vid = 'X1T_Fletcher_TU10_05084.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher__05084', faction = 'UEF'},}
Fletcher_185 =   {{text = '[{i Fletcher}]: Let\'s get that Land Factory upgraded, Commander.', vid = 'X1T_Fletcher_TU10_05085.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher__05085', faction = 'UEF'},}
Fletcher_186 =   {{text = '[{i Fletcher}]: Going to take more than pea-shooters to win this war, Commander. Upgrade your factory.', vid = 'X1T_Fletcher_TU10_05086.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher__05086', faction = 'UEF'},}
Fletcher_187 =   {{text = '[{i Fletcher}]: If you\'ve done your homework, you know that most OPs will take place on massive battlefields. Transports are the most effective way of moving units across vast distances. There are multiple ways to use transports, so check your field manual. For the purposes of this exercise, we\'re only going to cover basic use, and we\'ve given you a transport.', vid = 'X1T_Fletcher_TU11_05089.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU11_05089', faction = 'UEF'},}
Fletcher_188 =   {{text = '[{i Fletcher}]: To load a transport, select the units you wish to transport and then right-click on the transport. Once the transport is full, select it, click the Transport icon and then the spot on the map where the transport should unload. ', vid = 'X1T_Fletcher_TU11_05090.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU11_05090', faction = 'UEF'},}
Fletcher_189 =   {{text = '[{i Fletcher}]: Load a transport and move it to the designated location.', vid = 'X1T_Fletcher_TU11_05128.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU11_05128', faction = 'UEF'},}
Fletcher_190 =   {{text = '[{i Fletcher}]: Now you know the basics of transports. Good job.', vid = 'X1T_Fletcher_TU11_05091.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU11_05091', faction = 'UEF'},}
Fletcher_191 =   {{text = '[{i Fletcher}]: Transport those units, Commander.', vid = 'X1T_Fletcher_TU11_05092.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU11_05092', faction = 'UEF'},}
Fletcher_192 =   {{text = '[{i Fletcher}]: Get that transport loaded, Commander.', vid = 'X1T_Fletcher_TU11_05093.sfd', bank = 'X1T_VO', cue = 'X1T_Fletcher_TU11_05093', faction = 'UEF'},}
-- No vid
Fletcher_193 =   {{text = '[{i Fletcher}]: This ain\'t gonna be much of a fight.', bank = 'XGG', cue = 'XGG_Fletcher_MP1_04574'},}
Fletcher_194 =   {{text = '[{i Fletcher}]: You\'re not puttin\' up much of a fight.', bank = 'XGG', cue = 'XGG_Fletcher_MP1_04575'},}
Fletcher_195 =   {{text = '[{i Fletcher}]: Do you have any idea of what you\'re doing?', bank = 'XGG', cue = 'XGG_Fletcher_MP1_04576'},}
Fletcher_196 =   {{text = '[{i Fletcher}]: Not much on tactics, are ya?', bank = 'XGG', cue = 'XGG_Fletcher_MP1_04577'},}
Fletcher_197 =   {{text = '[{i Fletcher}]: If you run now, I\'ll let ya go.', bank = 'XGG', cue = 'XGG_Fletcher_MP1_04578'},}
Fletcher_198 =   {{text = '[{i Fletcher}]: You ain\'t too good at this, are you?', bank = 'XGG', cue = 'XGG_Fletcher_MP1_04579'},}
Fletcher_199 =   {{text = '[{i Fletcher}]: Guess I got time to smack you around.', bank = 'XGG', cue = 'XGG_Fletcher_MP1_04580'},}
Fletcher_200 =   {{text = '[{i Fletcher}]: I feel a bit bad, beatin\' up on you like this.', bank = 'XGG', cue = 'XGG_Fletcher_MP1_04581'},}
Fletcher_201 =   {{text = '[{i Fletcher}]: A Cybran, huh? I thought you guys would be busy changing the water in Brackman\'s brain tank.', bank = 'X01_VO', cue = 'X01_Fletcher_M01_02877'},}
Fletcher_202 =   {{text = '[{i Fletcher}]: That\'s what I love to see. Burn, baby, burn!', bank = 'X01_VO', cue = 'X01_Fletcher_M03_03701'},}
Fletcher_203 =   {{text = '[{i Fletcher}]: Yeah, yeah. Give it a rest already.', bank = 'X05_VO', cue = 'X05_Fletcher_M02_04950'},}
Fletcher_204 =   {{text = '[{i Fletcher}]: You can\'t stop me with that experimental! I\'ll destroy it first!', bank = 'X06_VO', cue = 'X06_Fletcher_T01_04805'},}
Fletcher_205 =   {{text = '[{i Fletcher}]: You\'re a coward.', bank = 'X06_VO', cue = 'X06_Fletcher_T01_03030'},}
Fletcher_206 =   {{text = '[{i Fletcher}]: There is no stopping me!', bank = 'X06_VO', cue = 'X06_Fletcher_T01_03047'},}

-- Aeon Gari
Gari_1 =         {{text = '[{i Gari}]: On this day, the Coalition will fall. No one will escape our wrath.', vid = 'X01_Gari_M01_04005.sfd', bank = 'X01_VO', cue = 'X01_Gari_M01_04005', faction = 'Aeon'},}
Gari_2 =         {{text = '[{i Gari}]: I shall cleanse everyone on this planet! You are fools to stand against our might!', vid = 'X01_Gari_M02_02896.sfd', bank = 'X01_VO', cue = 'X01_Gari_M02_02896', faction = 'Aeon'},}
Gari_3 =         {{text = '[{i Gari}]: Those people will make a fine sacrifice to our gods.', vid = 'X01_Gari_M02_04241.sfd', bank = 'X01_VO', cue = 'X01_Gari_M02_04241', faction = 'Aeon'},}
Gari_4 =         {{text = '[{i Gari}]: My scans show a group of trucks coming online. Full of civilians, no doubt. I shall take great pleasure in hunting them.', vid = 'X01_Gari_M02_03639.sfd', bank = 'X01_VO', cue = 'X01_Gari_M02_03639', faction = 'Aeon'},}
Gari_5 =         {{text = '[{i Gari}]: Ha-ha-ha!', vid = 'X01_Gari_M02_04245.sfd', bank = 'X01_VO', cue = 'X01_Gari_M02_04245', faction = 'Aeon'},}
Gari_6 =         {{text = '[{i Gari}]: At long last, the end of the UEF is within my sights. This day has been a long time coming.', vid = 'X01_Gari_M02_03664.sfd', bank = 'X01_VO', cue = 'X01_Gari_M02_03664', faction = 'Aeon'},}
Gari_7 =         {{text = '[{i Gari}]: When I am done here, I will seek out and destroy your beloved Dr. Brackman. And there will be no one left to stop me.', vid = 'X01_Gari_M02_03666.sfd', bank = 'X01_VO', cue = 'X01_Gari_M02_03666', faction = 'Aeon'},}
Gari_8 =         {{text = '[{i Gari}]: You have abandoned your people, your heritage and your gods. For that, you will be destroyed.', vid = 'X01_Gari_M02_03668.sfd', bank = 'X01_VO', cue = 'X01_Gari_M02_03668', faction = 'Aeon'},}
Gari_9 =         {{text = '[{i Gari}]: I will not allow you to build that experimental.', vid = 'X01_Gari_T01_04779.sfd', bank = 'X01_VO', cue = 'X01_Gari_T01_04779', faction = 'Aeon'},}
Gari_10 =        {{text = '[{i Gari}]: No, you will not complete that experimental.', vid = 'X01_Gari_T01_04780.sfd', bank = 'X01_VO', cue = 'X01_Gari_T01_04780', faction = 'Aeon'},}
Gari_11 =        {{text = '[{i Gari}]: An experimental? I am not so foolish as to let you finish that.', vid = 'X01_Gari_T01_04781.sfd', bank = 'X01_VO', cue = 'X01_Gari_T01_04781', faction = 'Aeon'},}
Gari_12 =        {{text = '[{i Gari}]: You will never reach Fort Clarke.', vid = 'X01_Gari_T01_04511.sfd', bank = 'X01_VO', cue = 'X01_Gari_T01_04511', faction = 'Aeon'},}
Gari_13 =        {{text = '[{i Gari}]: I continue to press closer to your beloved Fort Clarke. Soon it will burn.', vid = 'X01_Gari_T01_04512.sfd', bank = 'X01_VO', cue = 'X01_Gari_T01_04512', faction = 'Aeon'},}
Gari_14 =        {{text = '[{i Gari}]: You are simply prolonging the inevitable. I will cleanse the civilian population.', vid = 'X01_Gari_T01_04513.sfd', bank = 'X01_VO', cue = 'X01_Gari_T01_04513', faction = 'Aeon'},}
Gari_15 =        {{text = '[{i Gari}]: Your tenacity is admirable, but the outcome of this battle was determined long ago.', vid = 'X01_Gari_T01_04514.sfd', bank = 'X01_VO', cue = 'X01_Gari_T01_04514', faction = 'Aeon'},}
Gari_16 =        {{text = '[{i Gari}]: You may have stopped me, but you have no hope of defeating the Seraphim.', vid = 'X01_Gari_T01_04515.sfd', bank = 'X01_VO', cue = 'X01_Gari_T01_04515', faction = 'Aeon'},}
Gari_17 =        {{text = '[{i Gari}]: Now you will taste the fury of the Order of the Illuminate.', vid = 'X01_Gari_T01_04516.sfd', bank = 'X01_VO', cue = 'X01_Gari_T01_04516', faction = 'Aeon'},}
Gari_18 =        {{text = '[{i Gari}]: You have nowhere to hide, nowhere to run.', vid = 'X01_Gari_T01_04517.sfd', bank = 'X01_VO', cue = 'X01_Gari_T01_04517', faction = 'Aeon'},}
Gari_19 =        {{text = '[{i Gari}]: This planet shall share the same fate as Earth.', vid = 'X01_Gari_T01_04518.sfd', bank = 'X01_VO', cue = 'X01_Gari_T01_04518', faction = 'Aeon'},}
Gari_20 =        {{text = '[{i Gari}]: Your Princess is dead. Only the Order of the Illuminate remains.', vid = 'X01_Gari_T01_04519.sfd', bank = 'X01_VO', cue = 'X01_Gari_T01_04519', faction = 'Aeon'},}
Gari_21 =        {{text = '[{i Gari}]: Not even your most powerful weapon can stand before me.', vid = 'X01_Gari_T01_04520.sfd', bank = 'X01_VO', cue = 'X01_Gari_T01_04520', faction = 'Aeon'},}
Gari_22 =        {{text = '[{i Gari}]: Beg for mercy and perhaps I shall grant you an honorable death.', vid = 'X01_Gari_T01_04521.sfd', bank = 'X01_VO', cue = 'X01_Gari_T01_04521', faction = 'Aeon'},}
Gari_23 =        {{text = '[{i Gari}]: The UEF is finished. There will be no escaping us this time.', vid = 'X01_Gari_T01_04522.sfd', bank = 'X01_VO', cue = 'X01_Gari_T01_04522', faction = 'Aeon'},}
Gari_24 =        {{text = '[{i Gari}]: Once Fort Clarke falls, I will cleanse every person on this planet.', vid = 'X01_Gari_T01_04523.sfd', bank = 'X01_VO', cue = 'X01_Gari_T01_04523', faction = 'Aeon'},}
Gari_25 =        {{text = '[{i Gari}]: Brackman is a doddering old fool.', vid = 'X01_Gari_T01_04524.sfd', bank = 'X01_VO', cue = 'X01_Gari_T01_04524', faction = 'Aeon'},}
Gari_26 =        {{text = '[{i Gari}]: You are an abomination. I will take great pleasure in exterminating you.', vid = 'X01_Gari_T01_04525.sfd', bank = 'X01_VO', cue = 'X01_Gari_T01_04525', faction = 'Aeon'},}
Gari_27 =        {{text = '[{i Gari}]: You are a fool for rejecting the Seraphim.', vid = 'X01_Gari_T01_04526.sfd', bank = 'X01_VO', cue = 'X01_Gari_T01_04526', faction = 'Aeon'},}
Gari_28 =        {{text = '[{i Gari}]: The Order leads us now. Your Princess is nothing more than a faded memory.', vid = 'X01_Gari_T01_04527.sfd', bank = 'X01_VO', cue = 'X01_Gari_T01_04527', faction = 'Aeon'},}
Gari_29 =        {{text = '[{i Gari}]: The Princess is dead.', vid = 'X01_Gari_T01_04528.sfd', bank = 'X01_VO', cue = 'X01_Gari_T01_04528', faction = 'Aeon'},}
Gari_30 =        {{text = '[{i Gari}]: The Order is eternal. There is no stopping us.', vid = 'X01_Gari_T01_04529.sfd', bank = 'X01_VO', cue = 'X01_Gari_T01_04529', faction = 'Aeon'},}
Gari_31 =        {{text = '[{i Gari}]: The Seraphim will never be defeated!', vid = 'X01_Gari_T01_04530.sfd', bank = 'X01_VO', cue = 'X01_Gari_T01_04530', faction = 'Aeon'},}
