Gari_33 =        {{text = '[{i Gari}]: Now you will taste the fury of the Order of the Illuminate.', bank = 'X01_VO', cue = 'X01_Gari_T01_04516'},}
Gari_34 =        {{text = '[{i Gari}]: You have nowhere to hide, nowhere to run.', bank = 'X01_VO', cue = 'X01_Gari_T01_04517'},}
Gari_35 =        {{text = '[{i Gari}]: Not even your most powerful weapon can stand before me.', bank = 'X01_VO', cue = 'X01_Gari_T01_04520'},}
