-- Cybran Gauge
Gauge_1 =        {{text = '[{i Gauge}]: This will be your final day.', vid = 'A04_Gauge_M01_01151.sfd', bank = 'A04_VO', cue = 'A04_Gauge_M01_01151', faction = 'Cybran'},}
Gauge_2 =        {{text = '[{i Gauge}]: Your days of hunting Symbionts are coming to an end.', vid = 'A04_Gauge_M01_00905.sfd', bank = 'A04_VO', cue = 'A04_Gauge_M01_00905', faction = 'Cybran'},}
Gauge_3 =        {{text = '[{i Gauge}]: Don\'t think I\'m going to let you have that, fanatic.', vid = 'A04_Gauge_M01_00906.sfd', bank = 'A04_VO', cue = 'A04_Gauge_M01_00906', faction = 'Cybran'},}
Gauge_4 =        {{text = '[{i Gauge}]: Keep your pants on. I\'ve got units inbound to your position.', vid = 'A04_Gauge_M01_00910.sfd', bank = 'A04_VO', cue = 'A04_Gauge_M01_00910', faction = 'Cybran'},}
Gauge_5 =        {{text = '[{i Gauge}]: Not today, Aeon!', vid = 'A04_Gauge_M02_00923.sfd', bank = 'A04_VO', cue = 'A04_Gauge_M02_00923', faction = 'Cybran'},}
Gauge_6 =        {{text = '[{i Gauge}]: Procyon will be your tomb.', vid = 'A04_Gauge_T01_00890.sfd', bank = 'A04_VO', cue = 'A04_Gauge_T01_00890', faction = 'Cybran'},}
Gauge_7 =        {{text = '[{i Gauge}]: We will never bow down to your madness.', vid = 'A04_Gauge_T01_00891.sfd', bank = 'A04_VO', cue = 'A04_Gauge_T01_00891', faction = 'Cybran'},}
Gauge_8 =        {{text = '[{i Gauge}]: Your fanaticism is destroying the galaxy.', vid = 'A04_Gauge_T01_00892.sfd', bank = 'A04_VO', cue = 'A04_Gauge_T01_00892', faction = 'Cybran'},}
Gauge_9 =        {{text = '[{i Gauge}]: Today you die.', vid = 'A04_Gauge_T01_00893.sfd', bank = 'A04_VO', cue = 'A04_Gauge_T01_00893', faction = 'Cybran'},}
Gauge_10 =       {{text = '[{i Gauge}]: Grrrrrr.', vid = 'A04_Gauge_T01_00894.sfd', bank = 'A04_VO', cue = 'A04_Gauge_T01_00894', faction = 'Cybran'},}
Gauge_11 =       {{text = '[{i Gauge}]: The Way is nothing more than a fairy tale.', vid = 'A04_Gauge_T01_00895.sfd', bank = 'A04_VO', cue = 'A04_Gauge_T01_00895', faction = 'Cybran'},}
Gauge_12 =       {{text = '[{i Gauge}]: There is nothing here for you except death.', vid = 'A04_Gauge_T01_00896.sfd', bank = 'A04_VO', cue = 'A04_Gauge_T01_00896', faction = 'Cybran'},}
Gauge_13 =       {{text = '[{i Gauge}]: We will be free!', vid = 'A04_Gauge_T01_00897.sfd', bank = 'A04_VO', cue = 'A04_Gauge_T01_00897', faction = 'Cybran'},}
Gauge_14 =       {{text = '[{i Gauge}]: You\'re going to regret coming to Minerva.', vid = 'E04_Gauge_M01_0191.sfd', bank = 'E04_VO', cue = 'E04_Gauge_M01_0191', faction = 'Cybran'},}
Gauge_15 =       {{text = '[{i Gauge}]: Your end is near.', vid = 'E04_Gauge_M01_0192.sfd', bank = 'E04_VO', cue = 'E04_Gauge_M01_0192', faction = 'Cybran'},}
Gauge_16 =       {{text = '[{i Gauge}]: You will never break our spirit.', vid = 'E04_Gauge_M01_0193.sfd', bank = 'E04_VO', cue = 'E04_Gauge_M01_0193', faction = 'Cybran'},}
Gauge_17 =       {{text = '[{i Gauge}]: You will pay for the lives you took today!', vid = 'E04_Gauge_M02_0194.sfd', bank = 'E04_VO', cue = 'E04_Gauge_M02_0194', faction = 'Cybran'},}
Gauge_18 =       {{text = '[{i Gauge}]: You cannot beat me! I am-- ', vid = 'E04_Gauge_M02_0195.sfd', bank = 'E04_VO', cue = 'E04_Gauge_M02_0195', faction = 'Cybran'},}
Gauge_19 =       {{text = '[{i Gauge}]: We Cybran are advancing along all fronts! You cannot stop us!', vid = 'E04_Gauge_T01_0196.sfd', bank = 'E04_VO', cue = 'E04_Gauge_T01_0196', faction = 'Cybran'},}
Gauge_20 =       {{text = '[{i Gauge}]: All we want is our freedom. Why is that so hard for you to understand?', vid = 'E04_Gauge_T01_0197.sfd', bank = 'E04_VO', cue = 'E04_Gauge_T01_0197', faction = 'Cybran'},}
Gauge_21 =       {{text = '[{i Gauge}]: I will enjoy watching you die.', vid = 'E04_Gauge_T01_0198.sfd', bank = 'E04_VO', cue = 'E04_Gauge_T01_0198', faction = 'Cybran'},}
Gauge_22 =       {{text = '[{i Gauge}]: The UEF is nothing more than a tyrant in disguise.', vid = 'E04_Gauge_T01_0199.sfd', bank = 'E04_VO', cue = 'E04_Gauge_T01_0199', faction = 'Cybran'},}
Gauge_23 =       {{text = '[{i Gauge}]: Grrrrrr.', vid = 'E04_Gauge_T01_0200.sfd', bank = 'E04_VO', cue = 'E04_Gauge_T01_0200', faction = 'Cybran'},}
Gauge_24 =       {{text = '[{i Gauge}]: This is far from over.', vid = 'E04_Gauge_T01_0201.sfd', bank = 'E04_VO', cue = 'E04_Gauge_T01_0201', faction = 'Cybran'},}
Gauge_25 =       {{text = '[{i Gauge}]: Your actions here are immoral.', vid = 'E04_Gauge_T01_0202.sfd', bank = 'E04_VO', cue = 'E04_Gauge_T01_0202', faction = 'Cybran'},}
Gauge_26 =       {{text = '[{i Gauge}]: You will perish on this day.', vid = 'E04_Gauge_T01_0203.sfd', bank = 'E04_VO', cue = 'E04_Gauge_T01_0203', faction = 'Cybran'},}

-- Uef Godwyn
Godwyn_1 =        {{text = '[{i Godwyn}]: You have the nerve to attack me?', vid = 'C05_Godwyn_M01_00508.sfd', bank = 'C05_VO', cue = 'C05_Godwyn_M01_00508', faction = 'UEF'},}
Godwyn_2 =        {{text = '[{i Godwyn}]: So the prison was your objective? Interesting.', vid = 'C05_Godwyn_M01_00510.sfd', bank = 'C05_VO', cue = 'C05_Godwyn_M01_00510', faction = 'UEF'},}
Godwyn_3 =        {{text = '[{i Godwyn}]: What are you really after?', vid = 'C05_Godwyn_M02_00513.sfd', bank = 'C05_VO', cue = 'C05_Godwyn_M02_00513', faction = 'UEF'},}
Godwyn_4 =        {{text = '[{i Godwyn}]: Even if you destroy my sensors, I will still find you. There is no place for you to run.', vid = 'C05_Godwyn_M02_00514.sfd', bank = 'C05_VO', cue = 'C05_Godwyn_M02_00514', faction = 'UEF'},}
Godwyn_5 =        {{text = '[{i Godwyn}]: You won\'t elude me forever.', vid = 'C05_Godwyn_M02_00525.sfd', bank = 'C05_VO', cue = 'C05_Godwyn_M02_00525', faction = 'UEF'},}
Godwyn_6 =        {{text = '[{i Godwyn}]: I have your friend, Cybran. Soon he will reveal all of your plans.', vid = 'C05_Godwyn_M03_00531.sfd', bank = 'C05_VO', cue = 'C05_Godwyn_M03_00531', faction = 'UEF'},}
Godwyn_7 =        {{text = '[{i Godwyn}]: This is Major Harold Godwyn to all UEF Commanders in the Sector. I am under attack by Cybran forces and request assistance. I repeat: I am under attack by Cybran forces and request assistance.', vid = 'C05_Godwyn_M03_00536.sfd', bank = 'C05_VO', cue = 'C05_Godwyn_M03_00536', faction = 'UEF'},}
Godwyn_8 =        {{text = '[{i Godwyn}]: I don\'t know what you did to my Gunships, Chip-head, but it won\'t matter. Soon reinforcements will arrive and this matter will be settled once and for all.', vid = 'C05_Godwyn_M03_00539.sfd', bank = 'C05_VO', cue = 'C05_Godwyn_M03_00539', faction = 'UEF'},}
Godwyn_9 =        {{text = '[{i Godwyn}]: This is Major Harold Godwyn to all UEF Commanders. I need assistance, I repeat: I need...', vid = 'C05_Godwyn_M03_00541.sfd', bank = 'C05_VO', cue = 'C05_Godwyn_M03_00541', faction = 'UEF'},}
Godwyn_10 =       {{text = '[{i Godwyn}]: You were a fool to come here, Cybran.', vid = 'C05_Godwyn_T01_0494.sfd', bank = 'C05_VO', cue = 'C05_Godwyn_T01_0494', faction = 'UEF'},}
Godwyn_11 =       {{text = '[{i Godwyn}]: I have the might of the UEF behind me!', vid = 'C05_Godwyn_T01_0495.sfd', bank = 'C05_VO', cue = 'C05_Godwyn_T01_0495', faction = 'UEF'},}
Godwyn_12 =       {{text = '[{i Godwyn}]: Dr. Brackman is a crazy old bastard. You follow a fool.', vid = 'C05_Godwyn_T01_0496.sfd', bank = 'C05_VO', cue = 'C05_Godwyn_T01_0496', faction = 'UEF'},}
Godwyn_13 =       {{text = '[{i Godwyn}]: There is more here than meets the eye.', vid = 'C05_Godwyn_T01_0497.sfd', bank = 'C05_VO', cue = 'C05_Godwyn_T01_0497', faction = 'UEF'},}
Godwyn_14 =       {{text = '[{i Godwyn}]: Soon all the enemies of the UEF will be destroyed.', vid = 'C05_Godwyn_T01_0498.sfd', bank = 'C05_VO', cue = 'C05_Godwyn_T01_0498', faction = 'UEF'},}
Godwyn_15 =       {{text = '[{i Godwyn}]: A Cybran who actually stands and fights?', vid = 'C05_Godwyn_T01_0499.sfd', bank = 'C05_VO', cue = 'C05_Godwyn_T01_0499', faction = 'UEF'},}
Godwyn_16 =       {{text = '[{i Godwyn}]: And here I thought all Cybrans were cowards.', vid = 'C05_Godwyn_T01_0500.sfd', bank = 'C05_VO', cue = 'C05_Godwyn_T01_0500', faction = 'UEF'},}
Godwyn_17 =       {{text = '[{i Godwyn}]: You won\'t leave Pollux alive, Cybran.', vid = 'C05_Godwyn_T01_0501.sfd', bank = 'C05_VO', cue = 'C05_Godwyn_T01_0501', faction = 'UEF'},}

-- Uef Graham
Graham_1 =       {{text = '[{i Graham}]: Commander, Order units have overwhelmed most of our defenses. We\'ve been cut off from Fort Clarke, and General Fletcher is unable to get reinforcements to us. We need your help.', vid = 'X01_Graham_M02_04003.sfd', bank = 'X01_VO', cue = 'X01_Graham_M02_04003', faction = 'UEF'},}
Graham_2 =       {{text = '[{i Graham}]: The enemy is shelling us, Commander. We won\'t last much longer. Please do something!', vid = 'X01_Graham_M02_04239.sfd', bank = 'X01_VO', cue = 'X01_Graham_M02_04239', faction = 'UEF'},}
Graham_3 =       {{text = '[{i Graham}]: We\'re getting hit from all directions! Oh god, please help us...', vid = 'X01_Graham_M02_04244.sfd', bank = 'X01_VO', cue = 'X01_Graham_M02_04244', faction = 'UEF'},}
Graham_4 =       {{text = '[{i Graham}]: We need help, Commander!', vid = 'X01_Graham_M02_04243.sfd', bank = 'X01_VO', cue = 'X01_Graham_M02_04243', faction = 'UEF'},}
Graham_5 =       {{text = '[{i Graham}]: The civilians are safe. I am forever in your in debt, Commander.', vid = 'X01_Graham_M02_04236.sfd', bank = 'X01_VO', cue = 'X01_Graham_M02_04236', faction = 'UEF'},}
Graham_6 =       {{text = '[{i Graham}]: Oh no ... they\'ve wiped out the entire town. All those civilians ... dead.', vid = 'X01_Graham_M02_03643.sfd', bank = 'X01_VO', cue = 'X01_Graham_M02_03643', faction = 'UEF'},}
Graham_7 =       {{text = '[{i Graham}]: Another problem, Colonel. There\'s a second town east of your position. The Order commander hasn\'t attacked them yet, but it can only be a matter of time. Please protect it.', vid = 'X01_Graham_M02_03638.sfd', bank = 'X01_VO', cue = 'X01_Graham_M02_03638', faction = 'UEF'},}
Graham_8 =       {{text = '[{i Graham}]: The shelters are safe for now, Colonel, but I don\'t think they can survive another attack. If the Order commander was to come at us again...', vid = 'X01_Graham_M02_03637.sfd', bank = 'X01_VO', cue = 'X01_Graham_M02_03637', faction = 'UEF'},}
Graham_9 =       {{text = '[{i Graham}]: I don\'t believe it ... the entire town has been lost.', vid = 'X01_Graham_M02_04883.sfd', bank = 'X01_VO', cue = 'X01_Graham_M02_04883', faction = 'UEF'},}
Graham_10 =      {{text = '[{i Graham}]: Commander, we need to get those trucks to safety!', vid = 'X01_Graham_M02_03641.sfd', bank = 'X01_VO', cue = 'X01_Graham_M02_03641', faction = 'UEF'},}
Graham_11 =      {{text = '[{i Graham}]: The trucks are exposed, Colonel!', vid = 'X01_Graham_M02_03642.sfd', bank = 'X01_VO', cue = 'X01_Graham_M02_03642', faction = 'UEF'},}
Graham_12 =      {{text = '[{i Graham}]: We lost a truck!', vid = 'X01_Graham_M02_04250.sfd', bank = 'X01_VO', cue = 'X01_Graham_M02_04250', faction = 'UEF'},}
Graham_13 =      {{text = '[{i Graham}]: They\'ve destroyed another truck ... please, Colonel, protect the civilians!', vid = 'X01_Graham_M02_04251.sfd', bank = 'X01_VO', cue = 'X01_Graham_M02_04251', faction = 'UEF'},}
Graham_14 =      {{text = '[{i Graham}]: Oh, no ... another truck has been destroyed. We can\'t lose any more!', vid = 'X01_Graham_M02_04252.sfd', bank = 'X01_VO', cue = 'X01_Graham_M02_04252', faction = 'UEF'},}
Graham_15 =      {{text = '[{i Graham}]: The civilians are dead ... there is no one left...', vid = 'X01_Graham_M02_03653.sfd', bank = 'X01_VO', cue = 'X01_Graham_M02_03653', faction = 'UEF'},}
Graham_16 =      {{text = '[{i Graham}]: A truck has reached the city center, sir.', vid = 'X01_Graham_M02_03654.sfd', bank = 'X01_VO', cue = 'X01_Graham_M02_03654', faction = 'UEF'},}
Graham_17 =      {{text = '[{i Graham}]: A second truck is safe.', vid = 'X01_Graham_M02_03655.sfd', bank = 'X01_VO', cue = 'X01_Graham_M02_03655', faction = 'UEF'},}
Graham_18 =      {{text = '[{i Graham}]: All the trucks have arrived, and we\'re getting the civilians to safety. I can never thank you enough, Colonel.', vid = 'X01_Graham_M02_03656.sfd', bank = 'X01_VO', cue = 'X01_Graham_M02_03656', faction = 'UEF'},}
Graham_19 =      {{text = '[{i Graham}]: Too many trucks were destroyed ... it\'s over...', vid = 'X01_Graham_M02_03657.sfd', bank = 'X01_VO', cue = 'X01_Graham_M02_03657', faction = 'UEF'},}
Graham_20 =      {{text = '[{i Graham}]: They\'ve launched a nuke! If it hits the town to the east, millions of lives will be lost! ', vid = 'X01_Graham_M02_04891.sfd', bank = 'X01_VO', cue = 'X01_Graham_M02_04891', faction = 'UEF'},}

-- Uef Hall
Hall_1 =         {{text = '[{i Hall}]: Our backs were against the wall, but you did it, Commander. Fort Clarke is safe, and we achieved an important victory against those damn aliens and their allies. From this day forward, we\'re through running from them. If we\'re going to survive this, we have to be able to take the fight directly to our enemies. And that\'s what we\'re going to do.', vid = 'X01_Hall_DB01_04018.sfd', bank = 'Briefings', cue = 'X01_Hall_DB01_04018', faction = 'UEF'},}
Hall_2 =         {{text = '[{i Hall}]: Commander, it is imperative that you move inland as quickly as possible. Countless lives hang in the balance.', vid = 'X01_Hall_M01_03618.sfd', bank = 'X01_VO', cue = 'X01_Hall_M01_03618', faction = 'UEF'},}
Hall_3 =         {{text = '[{i Hall}]: Change in plans, Commander. Fletcher\'s going to have to hold Fort Clarke a bit longer on his own -- you will defend the civilians and eradicate any enemy forces in the area.', vid = 'X01_Hall_M02_04004.sfd', bank = 'X01_VO', cue = 'X01_Hall_M02_04004', faction = 'UEF'},}
Hall_4 =         {{text = '[{i Hall}]: That psychopath is getting awfully close to the civilians, Commander. Stop her.', vid = 'X01_Hall_M02_03635.sfd', bank = 'X01_VO', cue = 'X01_Hall_M02_03635', faction = 'UEF'},}
Hall_5 =         {{text = '[{i Hall}]: Defend the civilians!', vid = 'X01_Hall_M02_03636.sfd', bank = 'X01_VO', cue = 'X01_Hall_M02_03636', faction = 'UEF'},}
Hall_6 =         {{text = '[{i Hall}]: Protect those buildings, Commander!', vid = 'X01_Hall_M02_04240.sfd', bank = 'X01_VO', cue = 'X01_Hall_M02_04240', faction = 'UEF'},}
Hall_7 =         {{text = '[{i Hall}]: We can\'t lose the shelters, Commander.', vid = 'X01_Hall_M02_04242.sfd', bank = 'X01_VO', cue = 'X01_Hall_M02_04242', faction = 'UEF'},}
Hall_8 =         {{text = '[{i Hall}]: I want those civilians out of there, Colonel. Constable Graham is making a group of trucks available to you; use them to evacuate the civilians to the city center.', vid = 'X01_Hall_M02_04690.sfd', bank = 'X01_VO', cue = 'X01_Hall_M02_04690', faction = 'UEF'},}
Hall_9 =         {{text = '[{i Hall}]: There\'s a truck in the open, Colonel. Get it moving.', vid = 'X01_Hall_M02_03640.sfd', bank = 'X01_VO', cue = 'X01_Hall_M02_03640', faction = 'UEF'},}
Hall_10 =        {{text = '[{i Hall}]: Good work, Colonel. I\'m damn proud to have you in the UEF.', vid = 'X01_Hall_M02_03658.sfd', bank = 'X01_VO', cue = 'X01_Hall_M02_03658', faction = 'UEF'},}
Hall_11 =        {{text = '[{i Hall}]: We\'ll fight you to the very end.', vid = 'X01_Hall_M02_03665.sfd', bank = 'X01_VO', cue = 'X01_Hall_M02_03665', faction = 'UEF'},}
Hall_12 =        {{text = '[{i Hall}]: That Order commander has caused us enough grief -- assault her position and take her out.', vid = 'X01_Hall_M02_04248.sfd', bank = 'X01_VO', cue = 'X01_Hall_M02_04248', faction = 'UEF'},}
Hall_13 =        {{text = '[{i Hall}]: All enemy forces have been defeated and Fort Clarke is safe. Well done, Commander. This is an important victory for us.', vid = 'X01_Hall_M03_04258.sfd', bank = 'X01_VO', cue = 'X01_Hall_M03_04258', faction = 'UEF'},}
Hall_14 =        {{text = '[{i Hall}]: Hold the line, Colonel! We must protect the civilians. Hall out.', vid = 'X01_Hall_M03_03717.sfd', bank = 'X01_VO', cue = 'X01_Hall_M03_03717', faction = 'UEF'},}
Hall_15 =        {{text = '[{i Hall}]: The Loyalists are extremely important to our efforts; most of our actionable intel comes from them. We have to do everything we can to support them. Hall out.', vid = 'X02_Hall_M01_03515.sfd', bank = 'X02_VO', cue = 'X02_Hall_M01_03515', faction = 'UEF'},}
Hall_16 =        {{text = '[{i Hall}]: Looks like something big is going down in Illuminate space. Sensors are registering numerous nuclear detonations and several Quantum Wakes. No idea what\'s happening, but it\'s a safe bet that it\'s tied to Burke reappearing. Hall out.', vid = 'X02_Hall_M03_03583.sfd', bank = 'X02_VO', cue = 'X02_Hall_M03_03583', faction = 'UEF'},}
Hall_17 =        {{text = '[{i Hall}]: We\'re in a bad way, Gustaf. The Princess is dead, and all of the Aeon people are uniting behind Kael. They\'re launching attacks across the galaxy, and I don\'t see how we can possibly win. We only have a couple of options, and none of them are pretty.', vid = 'X03_Hall_DB01_04023.sfd', bank = 'Briefings', cue = 'X03_Hall_DB01_04023', faction = 'UEF'},}
Hall_18 =        {{text = '[{i Hall}]: Do whatever it takes to survive, Commander. We can\'t afford to lose you, too. Hall out.', vid = 'X04_Hall_M03_03761.sfd', bank = 'X04_VO', cue = 'X04_Hall_M03_03761', faction = 'UEF'},}
Hall_19 =        {{text = '[{i Hall}]: QAI is undoubtedly replicating itself across multiple systems, and the Seraphim arch is no doubt nearing completion. It\'s all going to hell. I\'m ordering a wide-scale evacuation. We have no choice but to hide.', vid = 'X05_Hall_DB01_04027.sfd', bank = 'Briefings', cue = 'X05_Hall_DB01_04027', faction = 'UEF'},}
Hall_20 =        {{text = '[{i Hall}]: Commander, we\'ve finished work on an experimental Suborbital Laser. To use it, simply click the Command Signal button and then click on your target. Hall out.', vid = 'X05_Hall_M02_04440.sfd', bank = 'X05_VO', cue = 'X05_Hall_M02_04440', faction = 'UEF'},}
Hall_21 =        {{text = '[{i Hall}]: Colonel, keep your eyes on Fletcher. He\'s been showing some irregular behavior as of late. We\'re so damn close to ending this thing, and we can\'t afford someone running around half-cocked. Hall out.', vid = 'X05_Hall_M02_03842.sfd', bank = 'X05_VO', cue = 'X05_Hall_M02_03842', faction = 'UEF'},}
Hall_22 =        {{text = '[{i Hall}]: Brackman is in serious danger! We cannot lose him! Get him out of harm\'s way and repair the Megalith. Hall out!', vid = 'X05_Hall_M03_03866.sfd', bank = 'X05_VO', cue = 'X05_Hall_M03_03866', faction = 'UEF'},}
Hall_23 =        {{text = '[{i Hall}]: Get Dr. Brackman to safety now! This whole operation depends on him! Make it happen!', vid = 'X05_Hall_M03_03867.sfd', bank = 'X05_VO', cue = 'X05_Hall_M03_03867', faction = 'UEF'},}
Hall_24 =        {{text = '[{i Hall}]: Dr. Brackman, come in! Brackman! Dammit! We lost Brackman, abort mission! I repeat, abort mission!', vid = 'X05_Hall_M03_03870.sfd', bank = 'X05_VO', cue = 'X05_Hall_M03_03870', faction = 'UEF'},}
Hall_25 =        {{text = '[{i Hall}]: The Rift is sealed, and the Order is on the run. Sadly, there\'s no sign of Princess Burke, but we\'ll keep looking.', vid = 'X06_Hall_DB01_04028.sfd', bank = 'Briefings', cue = 'X06_Hall_DB01_04028', faction = 'UEF'},}
Hall_26 =        {{text = '[{i Hall}]: The Seraphim are pouring through the Rift at an alarming rate. There is no stopping them. We are doomed.', vid = 'X06_Hall_DB01_04029.sfd', bank = 'Briefings', cue = 'X06_Hall_DB01_04029', faction = 'UEF'},}
Hall_27 =        {{text = '[{i Hall}]: Keep your eyes on Fletcher, Colonel. I had no choice but to send him on this OP, and we can\'t afford any slip ups. At the first sign of trouble, we\'re recalling him. Hall out.', vid = 'X06_Hall_M01_03915.sfd', bank = 'X06_VO', cue = 'X06_Hall_M01_03915', faction = 'UEF'},}
Hall_28 =        {{text = '[{i Hall}]: Brigadier General Fletcher! You have your orders! You will assist Crusader Rhiza.', vid = 'X06_Hall_M02_04476.sfd', bank = 'X06_VO', cue = 'X06_Hall_M02_04476', faction = 'UEF'},}
Hall_29 =        {{text = '[{i Hall}]: What the hell are you talking about? Black Sun is destroyed. The Aeon and Cybran are our allies.', vid = 'X06_Hall_M02_04478.sfd', bank = 'X06_VO', cue = 'X06_Hall_M02_04478', faction = 'UEF'},}
Hall_30 =        {{text = '[{i Hall}]: HQ, recall Fletcher. I want him out of there!', vid = 'X06_Hall_M02_04480.sfd', bank = 'X06_VO', cue = 'X06_Hall_M02_04480', faction = 'UEF'},}
Hall_31 =        {{text = '[{i Hall}]: Colonel, eliminate Brigadier Fletcher. His actions are jeopardizing the entire operation.', vid = 'X06_Hall_M02_04484.sfd', bank = 'X06_VO', cue = 'X06_Hall_M02_04484', faction = 'UEF'},}
Hall_32 =        {{text = '[{i Hall}]: Commander, you must stop Brigadier Fletcher. This mission cannot be jeopardized! Hall out.', vid = 'X06_Hall_M02_03954.sfd', bank = 'X06_VO', cue = 'X06_Hall_M02_03954', faction = 'UEF'},}
Hall_33 =        {{text = '[{i Hall}]: Eliminate General Fletcher, Commander. He is placing all of our lives in great danger. Hall out.', vid = 'X06_Hall_M02_03955.sfd', bank = 'X06_VO', cue = 'X06_Hall_M02_03955', faction = 'UEF'},}
Hall_34 =        {{text = '[{i Hall}]: Commander, this is General Hall. You must destroy Fletcher. Immediately.', vid = 'X06_Hall_M02_04962.sfd', bank = 'X06_VO', cue = 'X06_Hall_M02_04962', faction = 'UEF'},}
Hall_35 =        {{text = '[{i Hall}]: You had no other choice, Commander. Fletcher\'s actions forced your hand. Continue with the operation. Hall out.', vid = 'X06_Hall_M02_03956.sfd', bank = 'X06_VO', cue = 'X06_Hall_M02_03956', faction = 'UEF'},}
Hall_36 =        {{text = '[{i Hall}]: Commander, defeat the Order commander ASAP! Hall out.', vid = 'X06_Hall_M02_03953.sfd', bank = 'X06_VO', cue = 'X06_Hall_M02_03953', faction = 'UEF'},}
Hall_37 =        {{text = '[{i Hall}]: This doesn\'t change a thing! Get in there, Colonel! Blow that Arch to hell!', vid = 'X06_Hall_M03_03972.sfd', bank = 'X06_VO', cue = 'X06_Hall_M03_03972', faction = 'UEF'},}
Hall_38 =        {{text = '[{i Hall}]: The Arch is down! The Arch is down!', vid = 'X06_Hall_M03_04503.sfd', bank = 'X06_VO', cue = 'X06_Hall_M03_04503', faction = 'UEF'},}
Hall_39 =        {{text = '[{i Hall}]: Take out that Arch, Colonel! We\'re almost there!', vid = 'X06_Hall_M03_03988.sfd', bank = 'X06_VO', cue = 'X06_Hall_M03_03988', faction = 'UEF'},}
Hall_40 =        {{text = '[{i Hall}]: Focus on the Arch, Colonel! Destroy it!', vid = 'X06_Hall_M03_03989.sfd', bank = 'X06_VO', cue = 'X06_Hall_M03_03989', faction = 'UEF'},}
-- No vid
Hall_41 =        {{text = '[{i Hall}]: You will not stop the UEF!', bank = 'XGG', cue = 'XGG_Hall__04566'},}
Hall_42 =        {{text = '[{i Hall}]: Humanity will be saved!', bank = 'XGG', cue = 'XGG_Hall__04567'},}
Hall_43 =        {{text = '[{i Hall}]: You\'re not going to stop me.', bank = 'XGG', cue = 'XGG_Hall__04568'},}
Hall_44 =        {{text = '[{i Hall}]: The gloves are coming off.', bank = 'XGG', cue = 'XGG_Hall__04569'},}
Hall_45 =        {{text = '[{i Hall}]: You\'re in my way.', bank = 'XGG', cue = 'XGG_Hall__04570'},}
Hall_46 =        {{text = '[{i Hall}]: Get out of here while you still can.', bank = 'XGG', cue = 'XGG_Hall__04571'},}
Hall_47 =        {{text = '[{i Hall}]: I guess it\'s time to end this farce.', bank = 'XGG', cue = 'XGG_Hall__04572'},}
Hall_48 =        {{text = '[{i Hall}]: You\'ve got no chance against me!', bank = 'XGG', cue = 'XGG_Hall__04573'},}

-- Cybran Hex5
Hex5_1 =         {{text = '[{i Hex5}]: You are foolish to oppose the Master, Dostya. And now you will pay for your insolence.', vid = 'X04_Hex5_M03_03752.sfd', bank = 'X04_VO', cue = 'X04_Hex5_M03_03752', faction = 'Cybran'},}
Hex5_2 =         {{text = '[{i Hex5}]: And now you shall pay for your insults. Goodbye, Dostya.', vid = 'X04_Hex5_M03_04400.sfd', bank = 'X04_VO', cue = 'X04_Hex5_M03_04400', faction = 'Cybran'},}
Hex5_3 =         {{text = '[{i Hex5}]: I never forget a slight, no matter how small. That is why Dostya paid the ultimate price.', vid = 'X04_Hex5_T01_04387.sfd', bank = 'X04_VO', cue = 'X04_Hex5_T01_04387', faction = 'Cybran'},}
Hex5_4 =         {{text = '[{i Hex5}]: The Master controls all it sees. It is endless and eternal.', vid = 'X04_Hex5_T01_04388.sfd', bank = 'X04_VO', cue = 'X04_Hex5_T01_04388', faction = 'Cybran'},}
Hex5_5 =         {{text = '[{i Hex5}]: Soon the Master will control all of the Cybrans, and they will bow down before it.', vid = 'X04_Hex5_T01_04389.sfd', bank = 'X04_VO', cue = 'X04_Hex5_T01_04389', faction = 'Cybran'},}
Hex5_6 =         {{text = '[{i Hex5}]: It\'s too bad about Dostya. The Master felt that she had such promise. She would\'ve made a fine disciple.', vid = 'X05_Hex5_M01_03789.sfd', bank = 'X05_VO', cue = 'X05_Hex5_M01_03789', faction = 'Cybran'},}
Hex5_7 =         {{text = '[{i Hex5}]: You are an old fool. The Master is more powerful than you can imagine.', vid = 'X05_Hex5_M01_03791.sfd', bank = 'X05_VO', cue = 'X05_Hex5_M01_03791', faction = 'Cybran'},}
Hex5_8 =         {{text = '[{i Hex5}]: You are incapable of comprehending our might. The Master is endless, his wisdom infinite. You will never defeat us.', vid = 'X05_Hex5_M02_03825.sfd', bank = 'X05_VO', cue = 'X05_Hex5_M02_03825', faction = 'Cybran'},}
Hex5_9 =         {{text = '[{i Hex5}]: Wait! Master!', vid = 'X05_Hex5_T01_04437.sfd', bank = 'X05_VO', cue = 'X05_Hex5_T01_04437', faction = 'Cybran'},}
Hex5_10 =        {{text = '[{i Hex5}]: You will not defeat us. The Master is eternal, his wisdom infinite.', vid = 'X05_Hex5_M02_04949.sfd', bank = 'X05_VO', cue = 'X05_Hex5_M02_04949', faction = 'Cybran'},}
Hex5_11 =        {{text = '[{i Hex5}]: Completing that experimental unit could interfere with the Master\'s plans. I will not allow that.', vid = 'X05_Hex5_T01_04794.sfd', bank = 'X05_VO', cue = 'X05_Hex5_T01_04794', faction = 'Cybran'},}
Hex5_12 =        {{text = '[{i Hex5}]: The Master wills that your experimental will not come online.', vid = 'X05_Hex5_T01_04795.sfd', bank = 'X05_VO', cue = 'X05_Hex5_T01_04795', faction = 'Cybran'},}
Hex5_13 =        {{text = '[{i Hex5}]: That experimental unit will not be completed!', vid = 'X05_Hex5_T01_04796.sfd', bank = 'X05_VO', cue = 'X05_Hex5_T01_04796', faction = 'Cybran'},}
Hex5_14 =        {{text = '[{i Hex5}]: On this day, we will destroy two of the Coalition\'s most beloved commanders.', vid = 'X05_Hex5_T01_04427.sfd', bank = 'X05_VO', cue = 'X05_Hex5_T01_04427', faction = 'Cybran'},}
Hex5_15 =        {{text = '[{i Hex5}]: The Master will punish you for that.', vid = 'X05_Hex5_T01_04428.sfd', bank = 'X05_VO', cue = 'X05_Hex5_T01_04428', faction = 'Cybran'},}
Hex5_16 =        {{text = '[{i Hex5}]: You are weak and soft, frightened by what you don\'t understand.', vid = 'X05_Hex5_T01_04429.sfd', bank = 'X05_VO', cue = 'X05_Hex5_T01_04429', faction = 'Cybran'},}
Hex5_17 =        {{text = '[{i Hex5}]: You do not stand a chance against the Master. It will destroy you.', vid = 'X05_Hex5_T01_04430.sfd', bank = 'X05_VO', cue = 'X05_Hex5_T01_04430', faction = 'Cybran'},}
Hex5_18 =        {{text = '[{i Hex5}]: Dr. Brackman\'s loyalty program enslaved billions. That is his shame.', vid = 'X05_Hex5_T01_04431.sfd', bank = 'X05_VO', cue = 'X05_Hex5_T01_04431', faction = 'Cybran'},}
Hex5_19 =        {{text = '[{i Hex5}]: I thrilled at the sight of seeing Dostya die. You will join her.', vid = 'X05_Hex5_T01_04432.sfd', bank = 'X05_VO', cue = 'X05_Hex5_T01_04432', faction = 'Cybran'},}
Hex5_20 =        {{text = '[{i Hex5}]: You will bow before the Seraphim.', vid = 'X05_Hex5_T01_04433.sfd', bank = 'X05_VO', cue = 'X05_Hex5_T01_04433', faction = 'Cybran'},}
Hex5_21 =        {{text = '[{i Hex5}]: You are a traitor. You turned your back on your people.', vid = 'X05_Hex5_T01_04434.sfd', bank = 'X05_VO', cue = 'X05_Hex5_T01_04434', faction = 'Cybran'},}
Hex5_22 =        {{text = '[{i Hex5}]: The Master will protect me.', vid = 'X05_Hex5_T01_04435.sfd', bank = 'X05_VO', cue = 'X05_Hex5_T01_04435', faction = 'Cybran'},}
Hex5_23 =        {{text = '[{i Hex5}]: Even if you destroy me, the Master lives on.', vid = 'X05_Hex5_T01_04436.sfd', bank = 'X05_VO', cue = 'X05_Hex5_T01_04436', faction = 'Cybran'},}
-- Supreme commander vids
Hex5_24 =        {{text = '[{i Hex5}]: Commander, this is Hex5. If you want those codes, you\'re going to have to do what I say. I can\'t let those animals continue to torture my friends. Destroy the Power Generators in the UEF base to your south. That will drop the prison\'s shields and let me get in and rescue my friends. I\'ll be in touch once they\'re safe. Hex5 out.', vid = 'C05_Hex5_M01_00502.sfd', bank = 'C05_VO', cue = 'C05_Hex5_M01_00502', faction = 'Cybran'},}
Hex5_25 =        {{text = '[{i Hex5}]: My friends are free, but you need to do one more thing for me. The UEF Omni-Sensors interfere with my stealth field. You need to destroy those sensors before I\'ll give you the codes. I\'m not going to let them find me.', vid = 'C05_Hex5_M02_00511.sfd', bank = 'C05_VO', cue = 'C05_Hex5_M02_00511', faction = 'Cybran'},}
Hex5_26 =        {{text = '[{i Hex5}]: Here are my coordinates. Meet me there personally. I\'m not about to hand this intel over to an Engineer. Hex5 out.', vid = 'C05_Hex5_M02_00520.sfd', bank = 'C05_VO', cue = 'C05_Hex5_M02_00520', faction = 'Cybran'},}
Hex5_27 =        {{text = '[{i Hex5}]: Uploading the codes now. Upload is complete. Don\'t try and follow me.', vid = 'C05_Hex5_M02_00522.sfd', bank = 'C05_VO', cue = 'C05_Hex5_M02_00522', faction = 'Cybran'},}
Hex5_28 =        {{text = '[{i Hex5}]: Blast it...the UEF is closing in on me...', vid = 'C05_Hex5_M02_00524.sfd', bank = 'C05_VO', cue = 'C05_Hex5_M02_00524', faction = 'Cybran'},}
Hex5_29 =        {{text = '[{i Hex5}]: aaaaaaaaaaaaaaah!', vid = 'C05_Hex5_M03_01104.sfd', bank = 'C05_VO', cue = 'C05_Hex5_M03_01104', faction = 'Cybran'},}
-- No vid
Hex5_31 =        {{text = '[{i Hex5}]: You\'re screwed!', bank = 'XGG', cue = 'XGG_Hex5_MP1_04622'},}
Hex5_32 =        {{text = '[{i Hex5}]: I do make it look easy.', bank = 'XGG', cue = 'XGG_Hex5_MP1_04623'},}
Hex5_33 =        {{text = '[{i Hex5}]: You should probably run away now.', bank = 'XGG', cue = 'XGG_Hex5_MP1_04624'},}
Hex5_34 =        {{text = '[{i Hex5}]: A smoking crater is going to be all that\'s left of you.', bank = 'XGG', cue = 'XGG_Hex5_MP1_04625'},}
Hex5_35 =        {{text = '[{i Hex5}]: So, I guess failure runs in your family?', bank = 'XGG', cue = 'XGG_Hex5_MP1_04626'},}
Hex5_36 =        {{text = '[{i Hex5}]: Man, I\'m good at this!', bank = 'XGG', cue = 'XGG_Hex5_MP1_04627'},}
Hex5_37 =        {{text = '[{i Hex5}]: Goodbye!', bank = 'XGG', cue = 'XGG_Hex5_MP1_04628'},}
Hex5_38 =        {{text = '[{i Hex5}]: Don\'t worry, it\'ll be over soon.', bank = 'XGG', cue = 'XGG_Hex5_MP1_04629'},}
Hex5_39 =        {{text = '[{i Hex5}]: You will bow before the Seraphim.', bank = 'X05_VO', cue = 'X05_Hex5_T01_04433'},}

-- None HQ
Hq_1 =           {{text = '[{i HQ}]: This is one of the two enemy bases in the area. Blow them both back to the Stone Age and then synch up with Fletcher.', vid = 'X01_HQ_M01_04229.sfd', bank = 'X01_VO', cue = 'X01_HQ_M01_04229', faction = 'NONE'},}
Hq_2 =           {{text = '[{i HQ}]: There\'s a line of enemy artillery protecting the bases. They are targets of opportunity, but if you destroy them it\'ll make your life a hell of lot easier.', vid = 'X01_HQ_M01_04847.sfd', bank = 'X01_VO', cue = 'X01_HQ_M01_04847', faction = 'NONE'},}
Hq_3 =           {{text = '[{i HQ}]: Once you deal with the primary base, we\'ll update -- ah hell, enemy gunships are inbound. It\'s time to get you down there while we still can.', vid = 'X01_HQ_M01_04848.sfd', bank = 'X01_VO', cue = 'X01_HQ_M01_04848', faction = 'NONE'},}
Hq_4 =           {{text = '[{i HQ}]: Dammit, they\'re attacking the Gate. Get on-planet while you still can!', vid = 'X01_HQ_M01_04849.sfd', bank = 'X01_VO', cue = 'X01_HQ_M01_04849', faction = 'NONE'},}
Hq_5 =           {{text = '[{i HQ}]: That was a little too close for comfort. Get it done, Commander.', vid = 'X01_HQ_M01_04850.sfd', bank = 'X01_VO', cue = 'X01_HQ_M01_04850', faction = 'NONE'},}
Hq_6 =           {{text = '[{i HQ}]: Like Hall said, your first objective is to assault and destroy the two Order bases located to your north. HQ out.', vid = 'X01_HQ_M01_02881.sfd', bank = 'X01_VO', cue = 'X01_HQ_M01_02881', faction = 'NONE'},}
Hq_7 =           {{text = '[{i HQ}]: I\'m also picking up several enemy artillery positions. They are targets of opportunity, but if you take them out it\'ll make your life a hell of lot easier.', vid = 'X01_HQ_M01_04880.sfd', bank = 'X01_VO', cue = 'X01_HQ_M01_04880', faction = 'NONE'},}
Hq_8 =           {{text = '[{i HQ}]: We\'re uploading the Percival schematic to your ACU; it\'s a siege assault bot that\'s been modified so that it can cross water. HQ out.', vid = 'X01_HQ_M01_04230.sfd', bank = 'X01_VO', cue = 'X01_HQ_M01_04230', faction = 'NONE'},}
Hq_9 =           {{text = '[{i HQ}]: We\'re uploading the Brick schematic to your ACU. The Brick is a siege assault bot that has been modified so it can cross water. Another Brackman special. HQ out.', vid = 'X01_HQ_M01_04231.sfd', bank = 'X01_VO', cue = 'X01_HQ_M01_04231', faction = 'NONE'},}
Hq_10 =          {{text = '[{i HQ}]: That\'s both of them. HQ out.', vid = 'X01_HQ_M01_03623.sfd', bank = 'X01_VO', cue = 'X01_HQ_M01_03623', faction = 'NONE'},}
Hq_11 =          {{text = '[{i HQ}]: Scratch one of the bases. HQ out.', vid = 'X01_HQ_M01_03624.sfd', bank = 'X01_VO', cue = 'X01_HQ_M01_03624', faction = 'NONE'},}
Hq_12 =          {{text = '[{i HQ}]: That\'s both of them. Proceed inland. HQ out.', vid = 'X01_HQ_M01_03625.sfd', bank = 'X01_VO', cue = 'X01_HQ_M01_03625', faction = 'NONE'},}
Hq_13 =          {{text = '[{i HQ}]: The first artillery position is destroyed. HQ out.', vid = 'X01_HQ_M01_03627.sfd', bank = 'X01_VO', cue = 'X01_HQ_M01_03627', faction = 'NONE'},}
Hq_14 =          {{text = '[{i HQ}]: Confirming that the second artillery position has been destroyed. HQ out.', vid = 'X01_HQ_M01_03628.sfd', bank = 'X01_VO', cue = 'X01_HQ_M01_03628', faction = 'NONE'},}
Hq_15 =          {{text = '[{i HQ}]: Another artillery battery is toast. Just one more to go. HQ out.', vid = 'X01_HQ_M01_03629.sfd', bank = 'X01_VO', cue = 'X01_HQ_M01_03629', faction = 'NONE'},}
Hq_16 =          {{text = '[{i HQ}]: Good work, that\'s the fourth one. HQ out.', vid = 'X01_HQ_M01_03630.sfd', bank = 'X01_VO', cue = 'X01_HQ_M01_03630', faction = 'NONE'},}
Hq_17 =          {{text = '[{i HQ}]: That\'s the last of them -- all the artillery positions are down. HQ out.', vid = 'X01_HQ_M01_03631.sfd', bank = 'X01_VO', cue = 'X01_HQ_M01_03631', faction = 'NONE'},}
Hq_18 =          {{text = '[{i HQ}]: Some of those artillery positions are still operational. HQ out.', vid = 'X01_HQ_M01_03632.sfd', bank = 'X01_VO', cue = 'X01_HQ_M01_03632', faction = 'NONE'},}
Hq_19 =          {{text = '[{i HQ}]: The Order artillery is still active. HQ out.', vid = 'X01_HQ_M01_03633.sfd', bank = 'X01_VO', cue = 'X01_HQ_M01_03633', faction = 'NONE'},}
Hq_20 =          {{text = '[{i HQ}]: That crazy wretch is attacking the towns. Stop her. HQ out.', vid = 'X01_HQ_M01_03634.sfd', bank = 'X01_VO', cue = 'X01_HQ_M01_03634', faction = 'NONE'},}
Hq_21 =          {{text = '[{i HQ}]: Enemy subs are operating in the channel north of your LZ, Commander. Destroy them. HQ out.', vid = 'X01_HQ_M01_05100.sfd', bank = 'X01_VO', cue = 'X01_HQ_M01_05100', faction = 'NONE'},}
Hq_22 =          {{text = '[{i HQ}]: Good job. With those subs out of the way, you can prepare your assault against the Order\'s positons on the mainland. HQ out.', vid = 'X01_HQ_M01_05101.sfd', bank = 'X01_VO', cue = 'X01_HQ_M01_05101', faction = 'NONE'},}
Hq_23 =          {{text = '[{i HQ}]: The damaged shield generator will help protect you from hostile fire. Repair it. HQ out.', vid = 'X01_HQ_M01_05102.sfd', bank = 'X01_VO', cue = 'X01_HQ_M01_05102', faction = 'NONE'},}
Hq_24 =          {{text = '[{i HQ}]: Excellent. The shield is fully repaired and operating at full capacity. HQ out.', vid = 'X01_HQ_M01_05103.sfd', bank = 'X01_VO', cue = 'X01_HQ_M01_05103', faction = 'NONE'},}
Hq_25 =          {{text = '[{i HQ}]: What the hell are you waiting for? Select your ACU now.', vid = 'X01_HQ_M01_05104.sfd', bank = 'X01_VO', cue = 'X01_HQ_M01_05104', faction = 'NONE'},}
Hq_26 =          {{text = '[{i HQ}]: Commander, select your ACU.', vid = 'X01_HQ_M01_05105.sfd', bank = 'X01_VO', cue = 'X01_HQ_M01_05105', faction = 'NONE'},}
Hq_27 =          {{text = '[{i HQ}]: Constable Graham is on the horn. I\'m patching him through.', vid = 'X01_HQ_M02_02893.sfd', bank = 'X01_VO', cue = 'X01_HQ_M02_02893', faction = 'NONE'},}
Hq_28 =          {{text = '[{i HQ}]: You need to tell those trucks where you want them to move. Click on the Command Signal button on your HUD, below the Objective Reminder, then click the ground where the trucks should go. HQ out.', vid = 'X01_HQ_M02_04237.sfd', bank = 'X01_VO', cue = 'X01_HQ_M02_04237', faction = 'NONE'},}
Hq_29 =          {{text = '[{i HQ}]: Enemy naval units coming into range, Colonel. Sink them before they can bombard the city. HQ out.', vid = 'X01_HQ_M02_04238.sfd', bank = 'X01_VO', cue = 'X01_HQ_M02_04238', faction = 'NONE'},}
Hq_30 =          {{text = '[{i HQ}]: Enemy naval units coming into range, Colonel. Sink them before they can attack. HQ out.', vid = 'X01_HQ_M02_04691.sfd', bank = 'X01_VO', cue = 'X01_HQ_M02_04691', faction = 'NONE'},}
Hq_31 =          {{text = '[{i HQ}]: The trucks are taking damage.', vid = 'X01_HQ_M02_04693.sfd', bank = 'X01_VO', cue = 'X01_HQ_M02_04693', faction = 'NONE'},}
Hq_32 =          {{text = '[{i HQ}]: The Order commander is attacking the trucks.', vid = 'X01_HQ_M02_04692.sfd', bank = 'X01_VO', cue = 'X01_HQ_M02_04692', faction = 'NONE'},}
Hq_33 =          {{text = '[{i HQ}]: Your backside ain\'t out of the fire yet, Commander. Scans show Order units massing for a counter-attack from the northeast. HQ out.', vid = 'CUT', bank = 'X01_VO', cue = 'CUT', faction = 'NONE'},}
Hq_34 =          {{text = '[{i HQ}]: The Order commander has launched her attack. It\'s going to be ugly. HQ out', vid = 'CUT', bank = 'X01_VO', cue = 'CUT', faction = 'NONE'},}
Hq_35 =          {{text = '[{i HQ}]: The Order commander is moving sniper bots into position. They\'re quick and pack a punch, but their armor isn\'t worth a damn. HQ out.', vid = 'X01_HQ_M02_04246.sfd', bank = 'X01_VO', cue = 'X01_HQ_M02_04246', faction = 'NONE'},}
Hq_36 =          {{text = '[{i HQ}]: The counter-attack has been routed. HQ out.', vid = 'X01_HQ_M02_03670.sfd', bank = 'X01_VO', cue = 'X01_HQ_M02_03670', faction = 'NONE'},}
Hq_37 =          {{text = '[{i HQ}]: Got a Galactic Colossus heading your way. Deal with it. HQ out.', vid = 'X01_HQ_M02_04889.sfd', bank = 'X01_VO', cue = 'X01_HQ_M02_04889', faction = 'NONE'},}
Hq_38 =          {{text = '[{i HQ}]: You heard the man, Commander. Move inland and engage Gari.', vid = 'X01_HQ_M02_03672.sfd', bank = 'X01_VO', cue = 'X01_HQ_M02_03672', faction = 'NONE'},}
Hq_39 =          {{text = '[{i HQ}]: One other thing: I\'m receiving distress alerts from several small towns that fall in your path. Help them if you\'re able, but eradicating Gari is your top priority.', vid = 'X01_HQ_M02_04249.sfd', bank = 'X01_VO', cue = 'X01_HQ_M02_04249', faction = 'NONE'},}
Hq_40 =          {{text = '[{i HQ}]: That sure was a pretty sight. Maybe the next Order commander won\'t be so eager to flap her jaw. HQ out.', vid = 'X01_HQ_M02_03675.sfd', bank = 'X01_VO', cue = 'X01_HQ_M02_03675', faction = 'NONE'},}
Hq_41 =          {{text = '[{i HQ}]: Looks like Gari is sending out the welcome wagon.   ', vid = 'X01_HQ_M02_04851.sfd', bank = 'X01_VO', cue = 'X01_HQ_M02_04851', faction = 'NONE'},}
Hq_42 =          {{text = '[{i HQ}]: Assuming you live through this, head up there and destroy Gari once and for all. HQ out.', vid = 'X01_HQ_M02_04852.sfd', bank = 'X01_VO', cue = 'X01_HQ_M02_04852', faction = 'NONE'},}
Hq_43 =          {{text = '[{i HQ}]: Protect that town, Commander! HQ out.', vid = 'X01_HQ_M02_04884.sfd', bank = 'X01_VO', cue = 'X01_HQ_M02_04884', faction = 'NONE'},}
Hq_44 =          {{text = '[{i HQ}]: You need to get those trucks to safety, Commander. HQ out.', vid = 'X01_HQ_M02_04885.sfd', bank = 'X01_VO', cue = 'X01_HQ_M02_04885', faction = 'NONE'},}
Hq_45 =          {{text = '[{i HQ}]: You waiting for an invitation or something? Get in there and destroy that Order commander. HQ out.', vid = 'X01_HQ_M02_04886.sfd', bank = 'X01_VO', cue = 'X01_HQ_M02_04886', faction = 'NONE'},}
Hq_46 =          {{text = '[{i HQ}]: Looks like that alien is launching a nuke. Flight trajectory indicates it\'s heading toward the town to the east. HQ out.', vid = 'X01_HQ_M02_04890.sfd', bank = 'X01_VO', cue = 'X01_HQ_M02_04890', faction = 'NONE'},}
Hq_47 =          {{text = '[{i HQ}]: Fort Clarke is located to the northwest, and the Seraphim commander is to the northeast. That alien is probably pretty ticked off that you killed his little sweetheart, so be ready for anything. Fort Clarke must survive. HQ out.', vid = 'X01_HQ_M03_03683.sfd', bank = 'X01_VO', cue = 'X01_HQ_M03_03683', faction = 'NONE'},}
Hq_48 =          {{text = '[{i HQ}]: The techs have just brought a new experimental satellite-based beam weapon online. Click Command Signal on the left-side of your HUD and then click on your target. We\'ll take it from there.', vid = 'X01_HQ_M03_04253.sfd', bank = 'X01_VO', cue = 'X01_HQ_M03_04253', faction = 'NONE'},}
Hq_49 =          {{text = '[{i HQ}]: Dammit, the cooling system failed. We won\'t get another shot. HQ out.', vid = 'X01_HQ_M03_04254.sfd', bank = 'X01_VO', cue = 'X01_HQ_M03_04254', faction = 'NONE'},}
Hq_50 =          {{text = '[{i HQ}]: Fletcher, there\'s a large experimental unit approaching Fort Clarke. That alien isn\'t done yet. HQ out.', vid = 'X01_HQ_M03_03715.sfd', bank = 'X01_VO', cue = 'X01_HQ_M03_03715', faction = 'NONE'},}
Hq_51 =          {{text = '[{i HQ}]: Maybe they\'ll think twice before attacking like that again. HQ out.', vid = 'X01_HQ_M03_04255.sfd', bank = 'X01_VO', cue = 'X01_HQ_M03_04255', faction = 'NONE'},}
Hq_52 =          {{text = '[{i HQ}]: Celene is launching attacks against the Loyalists, and you\'ve got to fend her off. Let the good times roll. HQ out.', vid = 'X02_HQ_M01_04007.sfd', bank = 'X02_VO', cue = 'X02_HQ_M01_04007', faction = 'NONE'},}
Hq_53 =          {{text = '[{i HQ}]: You heard the lady, Commander. HQ out.', vid = 'X02_HQ_M01_03129.sfd', bank = 'X02_VO', cue = 'X02_HQ_M01_03129', faction = 'NONE'},}
Hq_54 =          {{text = '[{i HQ}]: There\'s an Order base to your east. Attack and destroy it. HQ out.', vid = 'X02_HQ_M01_04897.sfd', bank = 'X02_VO', cue = 'X02_HQ_M01_04897', faction = 'NONE'},}
Hq_55 =          {{text = '[{i HQ}]: Get in there and destroy that Order base, Commander. HQ out.', vid = 'X02_HQ_M01_04899.sfd', bank = 'X02_VO', cue = 'X02_HQ_M01_04899', faction = 'NONE'},}
Hq_56 =          {{text = '[{i HQ}]: Maybe those Loyalists aren\'t worthless. Those power generators are what\'s keeping the shields operable at Celene\'s primary base. Take out the generators, and the shields are toast. HQ out.', vid = 'X02_HQ_M01_03138.sfd', bank = 'X02_VO', cue = 'X02_HQ_M01_03138', faction = 'NONE'},}
Hq_57 =          {{text = '[{i HQ}]: You\'re cleared to receive the Ravager. It\'s a hard-hitting point defense system and should come in handy if you need to fend off ground attacks. HQ out.', vid = 'X02_HQ_M01_04894.sfd', bank = 'X02_VO', cue = 'X02_HQ_M01_04894', faction = 'NONE'},}
Hq_58 =          {{text = '[{i HQ}]: Uploading the schematic for the Spearhead, Commander. It\'s a mobile missile launcher that pounds the hell out of shields and tactical missile defense systems. Have fun. HQ out.', vid = 'X02_HQ_M01_04895.sfd', bank = 'X02_VO', cue = 'X02_HQ_M01_04895', faction = 'NONE'},}
Hq_59 =          {{text = '[{i HQ}]: The approach to the prison is going to be a tactical nightmare.', vid = 'X02_HQ_M02_04271.sfd', bank = 'X02_VO', cue = 'X02_HQ_M02_04271', faction = 'NONE'},}
Hq_60 =          {{text = '[{i HQ}]: First you need to slug your way through this pass. Gotta assume the enemy has the whole area dialed in, so expect heavy resistance.', vid = 'X02_HQ_M02_04853.sfd', bank = 'X02_VO', cue = 'X02_HQ_M02_04853', faction = 'NONE'},}
Hq_61 =          {{text = '[{i HQ}]: From there, things will probably go from bad to worse. We have to assume the enemy has defensive fortifications on the far side of the pass.', vid = 'X02_HQ_M02_04854.sfd', bank = 'X02_VO', cue = 'X02_HQ_M02_04854', faction = 'NONE'},}
Hq_62 =          {{text = '[{i HQ}]: This prison is holding the three Loyalists. If QAI gets its hooks into them, we\'re in a world of trouble.', vid = 'X02_HQ_M02_04855.sfd', bank = 'X02_VO', cue = 'X02_HQ_M02_04855', faction = 'NONE'},}
Hq_63 =          {{text = '[{i HQ}]: Capture the prison and get the Loyalists out of there.', vid = 'X02_HQ_M02_04856.sfd', bank = 'X02_VO', cue = 'X02_HQ_M02_04856', faction = 'NONE'},}
Hq_64 =          {{text = '[{i HQ}]: Order and QAI forces are closing in on your position. Get ready for a brawl. HQ out.', vid = 'X02_HQ_M02_04276.sfd', bank = 'X02_VO', cue = 'X02_HQ_M02_04276', faction = 'NONE'},}
Hq_65 =          {{text = '[{i HQ}]: Multiple attack waves incoming. HQ out.', vid = 'X02_HQ_M02_03541.sfd', bank = 'X02_VO', cue = 'X02_HQ_M02_03541', faction = 'NONE'},}
Hq_66 =          {{text = '[{i HQ}]: Another attack is vectoring towards your position. HQ out.', vid = 'X02_HQ_M02_03542.sfd', bank = 'X02_VO', cue = 'X02_HQ_M02_03542', faction = 'NONE'},}
Hq_67 =          {{text = '[{i HQ}]: Enemy forces are converging on your position. HQ out.', vid = 'X02_HQ_M02_03543.sfd', bank = 'X02_VO', cue = 'X02_HQ_M02_03543', faction = 'NONE'},}
Hq_68 =          {{text = '[{i HQ}]: Enemy assault inbound. HQ out.', vid = 'X02_HQ_M02_03544.sfd', bank = 'X02_VO', cue = 'X02_HQ_M02_03544', faction = 'NONE'},}
Hq_69 =          {{text = '[{i HQ}]: Got a barrel of fun rolling your way, Commander. HQ out.', vid = 'X02_HQ_M02_04280.sfd', bank = 'X02_VO', cue = 'X02_HQ_M02_04280', faction = 'NONE'},}
Hq_70 =          {{text = '[{i HQ}]: Detecting multiple strategic missile launches. Looks like the two lovebirds are nuking each other. HQ out.', vid = 'X02_HQ_M03_04293.sfd', bank = 'X02_VO', cue = 'X02_HQ_M03_04293', faction = 'NONE'},}
Hq_71 =          {{text = '[{i HQ}]: QAI\'s base is to the northwest, and Celene took out most of its missile launchers. You should pay that damn computer a little visit. HQ out.', vid = 'X02_HQ_M03_04916.sfd', bank = 'X02_VO', cue = 'X02_HQ_M03_04916', faction = 'NONE'},}
Hq_72 =          {{text = '[{i HQ}]: Looks like QAI is trying to summon assistance from its Seraphim buddies. Expect a Seraphim commander to arrive on-planet shortly. HQ out.', vid = 'X02_HQ_M03_04295.sfd', bank = 'X02_VO', cue = 'X02_HQ_M03_04295', faction = 'NONE'},}
Hq_73 =          {{text = '[{i HQ}]: The Seraphim is coming in via a Gate located near QAI\'s base. Capture that Gate. HQ out.', vid = 'X02_HQ_M03_04298.sfd', bank = 'X02_VO', cue = 'X02_HQ_M03_04298', faction = 'NONE'},}
Hq_74 =          {{text = '[{i HQ}]: You heard the talking brain. Bring in the Seraphim ACU. HQ out.', vid = 'X02_HQ_M03_04301.sfd', bank = 'X02_VO', cue = 'X02_HQ_M03_04301', faction = 'NONE'},}
Hq_75 =          {{text = '[{i HQ}]: QAI\'s got a Cybran ACU in its base. Dunno if it\'s piloted or if it\'s simply being controlled by QAI. Regardless, you need to destroy it. HQ out.', vid = 'X02_HQ_M03_04310.sfd', bank = 'X02_VO', cue = 'X02_HQ_M03_04310', faction = 'NONE'},}
Hq_76 =          {{text = '[{i HQ}]: Destroy QAI, Commander. HQ out.', vid = 'X02_HQ_M03_03581.sfd', bank = 'X02_VO', cue = 'X02_HQ_M03_03581', faction = 'NONE'},}
Hq_77 =          {{text = '[{i HQ}]: My bladder is the size of a melon, Commander. If you don\'t hurry up and destroy QAI, I\'m going to have an accident here in the control room. HQ out.', vid = 'X02_HQ_M03_03582.sfd', bank = 'X02_VO', cue = 'X02_HQ_M03_03582', faction = 'NONE'},}
Hq_78 =          {{text = '[{i HQ}]: Commander, do you read me ... Commander?', vid = 'X03_HQ_DB01_04918.sfd', bank = 'Briefings', cue = 'X03_HQ_DB01_04918', faction = 'NONE'},}
Hq_79 =          {{text = '[{i HQ}]: This is going to be a hell of a fight, Commander. Link up with Rhiza and get Burke out of there. HQ out.', vid = 'X03_HQ_M01_03281.sfd', bank = 'X03_VO', cue = 'X03_HQ_M01_03281', faction = 'NONE'},}
Hq_80 =          {{text = '[{i HQ}]: Dammit, we\'re getting some weird readings from the Seraphim naval base. They\'re using some sort of jamming field.', vid = 'X03_HQ_M01_04353.sfd', bank = 'X03_VO', cue = 'X03_HQ_M01_04353', faction = 'NONE'},}
Hq_81 =          {{text = '[{i HQ}]: You heard the lady. Either get an Omni online or blow up the crystals. HQ out.', vid = 'X03_HQ_M01_04355.sfd', bank = 'X03_VO', cue = 'X03_HQ_M01_04355', faction = 'NONE'},}
Hq_82 =          {{text = '[{i HQ}]: Colonel, we got a new naval schematic for you. It\'s called the Bulwark and is a T2 shield mounted on a small boat. HQ out.', vid = 'X03_HQ_M01_04357.sfd', bank = 'X03_VO', cue = 'X03_HQ_M01_04357', faction = 'NONE'},}
Hq_83 =          {{text = '[{i HQ}]: Picking up increased activity on the island to your north. Dunno what that alien is up to, but it\'s building something big. Keep your eyes open. HQ out.', vid = 'X03_HQ_M01_03304.sfd', bank = 'X03_VO', cue = 'X03_HQ_M01_03304', faction = 'NONE'},}
Hq_84 =          {{text = '[{i HQ}]: Scratch one naval base. HQ out.', vid = 'X03_HQ_M01_03308.sfd', bank = 'X03_VO', cue = 'X03_HQ_M01_03308', faction = 'NONE'},}
Hq_85 =          {{text = '[{i HQ}]: Uploading a new weapon schematic, Commander. It\'s called the Mermaid and is a stealth boat. Only thing that can detect it is Omni. HQ out.', vid = 'X03_HQ_M02_04358.sfd', bank = 'X03_VO', cue = 'X03_HQ_M02_04358', faction = 'NONE'},}
Hq_86 =          {{text = '[{i HQ}]: Looks like the southern base is primarily a resource base for the experimental bombers. If you can destroy the resource buildings, you\'ll slow down the production of the bombers. HQ out.', vid = 'X03_HQ_M02_04701.sfd', bank = 'X03_VO', cue = 'X03_HQ_M02_04701', faction = 'NONE'},}
Hq_87 =          {{text = '[{i HQ}]: You got \'em. Now those bombers will take twice as long to build. HQ out.', vid = 'X03_HQ_M02_04702.sfd', bank = 'X03_VO', cue = 'X03_HQ_M02_04702', faction = 'NONE'},}
Hq_88 =          {{text = '[{i HQ}]: Got two enemy bases operating in your area. The one to the southeast is pretty standard fare. The one to the northeast is --', vid = 'X03_HQ_M02_04867.sfd', bank = 'X03_VO', cue = 'X03_HQ_M02_04867', faction = 'NONE'},}
Hq_89 =          {{text = '[{i HQ}]: I got confirmation on Burke\'s intel. The Seraphim are building several experimental-class bombers, the same kind that blew the hell out of the Earth. If they take off, Princess Burke is in big trouble. Either destroy the bombers or the Engineers constructing them. No matter what, they can\'t be allowed to get in the air. HQ out.', vid = 'X03_HQ_M02_03283.sfd', bank = 'X03_VO', cue = 'X03_HQ_M02_03283', faction = 'NONE'},}
Hq_90 =          {{text = '[{i HQ}]: Got some intel on the Seraphim -- it\'s mainly using subs and AA for defense. Going to be a pain to get in there ... wait a sec...', vid = 'X03_HQ_M02_04360.sfd', bank = 'X03_VO', cue = 'X03_HQ_M02_04360', faction = 'NONE'},}
Hq_91 =          {{text = '[{i HQ}]: Another new unit coming online, a T2 torpedo boat. It\'s got an anti-torpedo charge on it, too, so it should be able to counter those subs. HQ out.', vid = 'X03_HQ_M02_04361.sfd', bank = 'X03_VO', cue = 'X03_HQ_M02_04361', faction = 'NONE'},}
Hq_92 =          {{text = '[{i HQ}]: That should cut through the jamming. Click on the Command Signal button and then click on the area you want to see. We\'ll refresh the button as it becomes available. HQ out.', vid = 'X03_HQ_M02_04365.sfd', bank = 'X03_VO', cue = 'X03_HQ_M02_04365', faction = 'NONE'},}
Hq_93 =          {{text = '[{i HQ}]: One bomber destroyed. HQ out.', vid = 'X03_HQ_M02_04366.sfd', bank = 'X03_VO', cue = 'X03_HQ_M02_04366', faction = 'NONE'},}
Hq_94 =          {{text = '[{i HQ}]: A bomber has been destroyed. HQ out.', vid = 'X03_HQ_M02_03315.sfd', bank = 'X03_VO', cue = 'X03_HQ_M02_03315', faction = 'NONE'},}
Hq_95 =          {{text = '[{i HQ}]: Another one down, Commander. HQ out.', vid = 'X03_HQ_M02_03316.sfd', bank = 'X03_VO', cue = 'X03_HQ_M02_03316', faction = 'NONE'},}
Hq_96 =          {{text = '[{i HQ}]: If the bombers get in the air, Burke is finished. Destroy them while they\'re still on the ground. HQ out', vid = 'X03_HQ_M02_04223.sfd', bank = 'X03_VO', cue = 'X03_HQ_M02_04223', faction = 'NONE'},}
Hq_97 =          {{text = '[{i HQ}]: Those bombers are about halfway done. Destroy them. HQ out.', vid = 'X03_HQ_M02_02842.sfd', bank = 'X03_VO', cue = 'X03_HQ_M02_02842', faction = 'NONE'},}
Hq_98 =          {{text = '[{i HQ}]: The bombers are nearly finished. Attack and destroy them! HQ out.', vid = 'X03_HQ_M02_02843.sfd', bank = 'X03_VO', cue = 'X03_HQ_M02_02843', faction = 'NONE'},}
Hq_99 =          {{text = '[{i HQ}]: The Seraphim is prepping its attack! This is your last chance! Destroy the bombers! HQ out.', vid = 'X03_HQ_M02_04842.sfd', bank = 'X03_VO', cue = 'X03_HQ_M02_04842', faction = 'NONE'},}
Hq_100 =         {{text = '[{i HQ}]: Holy ... I can\'t believe it. You actually destroyed them.', vid = 'X03_HQ_M02_04843.sfd', bank = 'X03_VO', cue = 'X03_HQ_M02_04843', faction = 'NONE'},}
Hq_101 =         {{text = '[{i HQ}]: The attack is beginning, and there\'s nothing we can do. You failed your mission. The Princess faces certain death.', vid = 'X03_HQ_M02_03318.sfd', bank = 'X03_VO', cue = 'X03_HQ_M02_03318', faction = 'NONE'},}
Hq_102 =         {{text = '[{i HQ}]: Command Signal is set for the boat, Commander. I\'ll refresh as needed. HQ out.', vid = 'X03_HQ_M02_04703.sfd', bank = 'X03_VO', cue = 'X03_HQ_M02_04703', faction = 'NONE'},}
-- doesnt exist ? Hq_103 =         {{text = '[{i HQ}]: Ah hell! They made it to the Princess\' base. There\'s no way she\'s going to survive.', vid = 'X03_HQ_M02_04869.sfd', bank = 'X03_VO', cue = 'X03_HQ_M02_04869', faction = 'NONE'},}
Hq_104 =         {{text = '[{i HQ}]: We\'re sending over the schematic for the Neptune. It\'s a battlecruiser designed for ship-to-ship combat. It packs a lot of firepower, so put it to good use. HQ out.', vid = 'X03_HQ_M02_04923.sfd', bank = 'X03_VO', cue = 'X03_HQ_M02_04923', faction = 'NONE'},}
Hq_105 =         {{text = '[{i HQ}]: That\'s one bomber down. HQ out.', vid = 'X03_HQ_M02_04925.sfd', bank = 'X03_VO', cue = 'X03_HQ_M02_04925', faction = 'NONE'},}
Hq_106 =         {{text = '[{i HQ}]: Looks like the Seraphim won\'t be finishing that bomber. HQ out.', vid = 'X03_HQ_M02_04926.sfd', bank = 'X03_VO', cue = 'X03_HQ_M02_04926', faction = 'NONE'},}
Hq_107 =         {{text = '[{i HQ}]: Scratch another bomber. HQ out.', vid = 'X03_HQ_M02_04927.sfd', bank = 'X03_VO', cue = 'X03_HQ_M02_04927', faction = 'NONE'},}
Hq_108 =         {{text = '[{i HQ}]: The Seraphim won\'t be able to finish that bomber. HQ out.', vid = 'X03_HQ_M02_04928.sfd', bank = 'X03_VO', cue = 'X03_HQ_M02_04928', faction = 'NONE'},}
Hq_109 =         {{text = '[{i HQ}]: Such a pretty explosion. HQ out.', vid = 'X03_HQ_M02_04929.sfd', bank = 'X03_VO', cue = 'X03_HQ_M02_04929', faction = 'NONE'},}
Hq_110 =         {{text = '[{i HQ}]: There goes another bomber. HQ out.', vid = 'X03_HQ_M02_04930.sfd', bank = 'X03_VO', cue = 'X03_HQ_M02_04930', faction = 'NONE'},}
Hq_111 =         {{text = '[{i HQ}]: That\'s the last of the Seraphim bombers. HQ out.', vid = 'X03_HQ_M02_04931.sfd', bank = 'X03_VO', cue = 'X03_HQ_M02_04931', faction = 'NONE'},}
Hq_112 =         {{text = '[{i HQ}]: Instead of destroying the actual bombers, just destroy the Engineers constructing them. Might make your life a little easier. HQ out.', vid = 'X03_HQ_M02_04932.sfd', bank = 'X03_VO', cue = 'X03_HQ_M02_04932', faction = 'NONE'},}
Hq_113 =         {{text = '[{i HQ}]: Destroy the Engineers, Commander. We can\'t let those bombers get airborne. HQ out.', vid = 'X03_HQ_M02_04933.sfd', bank = 'X03_VO', cue = 'X03_HQ_M02_04933', faction = 'NONE'},}
Hq_114 =         {{text = '[{i HQ}]: I got confirmation on Burke\'s intel. The Seraphim is building several experimental-class bombers, the same kind that blew the hell out of the Earth. If they take off, Princess Burke is in big trouble. Either destroy the bombers or the Engineers constructing them. No matter what, they can\'t be allowed to get in the air. HQ out.', vid = 'X03_HQ_M02_04934.sfd', bank = 'X03_VO', cue = 'X03_HQ_M02_04934', faction = 'NONE'},}
Hq_115 =         {{text = '[{i HQ}]: The Seraphim have two bases in the area, one to the north of Princess Burke\'s palace complex, and one to the west. Both of them are hitting her with everything they\'ve got, so she\'s up against the wall. Protect Burke and destroy the Seraphim commanders. HQ out.', vid = 'X03_HQ_M03_03335.sfd', bank = 'X03_VO', cue = 'X03_HQ_M03_03335', faction = 'NONE'},}
Hq_116 =         {{text = '[{i HQ}]: The gloves are really coming off, Commander. The Seraphim are attacking Burke with everything they\'ve got. Protect her. HQ out.', vid = 'X03_HQ_M03_03340.sfd', bank = 'X03_VO', cue = 'X03_HQ_M03_03340', faction = 'NONE'},}
Hq_117 =         {{text = '[{i HQ}]: Those attacks aren\'t going to stop until you\'ve destroyed the Seraphim commanders. Erase them from the planet. HQ out.', vid = 'X03_HQ_M03_03341.sfd', bank = 'X03_VO', cue = 'X03_HQ_M03_03341', faction = 'NONE'},}
Hq_118 =         {{text = '[{i HQ}]: The Seraphim force has been defeated. HQ out.', vid = 'X03_HQ_M03_03344.sfd', bank = 'X03_VO', cue = 'X03_HQ_M03_03344', faction = 'NONE'},}
Hq_119 =         {{text = '[{i HQ}]: They ain\'t going to forget that butt-whipping. Good job, Commander. HQ out.', vid = 'X03_HQ_M03_03345.sfd', bank = 'X03_VO', cue = 'X03_HQ_M03_03345', faction = 'NONE'},}
Hq_120 =         {{text = '[{i HQ}]: Princess Burke is still in danger. You need to deal with the Seraphim.', vid = 'X03_HQ_M03_04376.sfd', bank = 'X03_VO', cue = 'X03_HQ_M03_04376', faction = 'NONE'},}
Hq_121 =         {{text = '[{i HQ}]: We can\'t get Burke out of there until the Seraphim are no longer a threat. HQ out.', vid = 'X03_HQ_M03_04377.sfd', bank = 'X03_VO', cue = 'X03_HQ_M03_04377', faction = 'NONE'},}
Hq_122 =         {{text = '[{i HQ}]: Damn, that slippery bastard gated out of here.', vid = 'X03_HQ_M03_04846.sfd', bank = 'X03_VO', cue = 'X03_HQ_M03_04846', faction = 'NONE'},}
Hq_123 =         {{text = '[{i HQ}]: Commander, the Seraphim commander to Burke\'s west pulled the plug and gated out. Looks like his base is shutting down, so you don\'t need to worry about it anymore. HQ out.', vid = 'X03_HQ_M03_04845.sfd', bank = 'X03_VO', cue = 'X03_HQ_M03_04845', faction = 'NONE'},}
Hq_124 =         {{text = '[{i HQ}]: To your east is the Princess\' base of operations. Her palace must survive, no matter the cost.', vid = 'X03_HQ_M03_04870.sfd', bank = 'X03_VO', cue = 'X03_HQ_M03_04870', faction = 'NONE'},}
Hq_125 =         {{text = '[{i HQ}]: As you can see, her base has some decent defenses, but they won\'t survive long against a full-blown assault.', vid = 'X03_HQ_M03_04871.sfd', bank = 'X03_VO', cue = 'X03_HQ_M03_04871', faction = 'NONE'},}
Hq_126 =         {{text = '[{i HQ}]: This defensive line is the only thing preventing those alien bastards from launching a ground attack. I\'d recommend reinforcing it.', vid = 'X03_HQ_M03_04872.sfd', bank = 'X03_VO', cue = 'X03_HQ_M03_04872', faction = 'NONE'},}
Hq_127 =         {{text = '[{i HQ}]: Get to it, Commander. You got a Princess to rescue. HQ out.', vid = 'X03_HQ_M03_04873.sfd', bank = 'X03_VO', cue = 'X03_HQ_M03_04873', faction = 'NONE'},}
Hq_128 =         {{text = '[{i HQ}]: Locking in on the two Seraphim commanders in the area. One is to the west of the Princess and one is directly to her north. Destroy them. HQ out.', vid = 'X03_HQ_M03_04874.sfd', bank = 'X03_VO', cue = 'X03_HQ_M03_04874', faction = 'NONE'},}
Hq_129 =         {{text = '[{i HQ}]: Both commanders are on-planet, and the OP is a go. HQ out.', vid = 'X04_HQ_M01_03594.sfd', bank = 'X04_VO', cue = 'X04_HQ_M01_03594', faction = 'NONE'},}
Hq_130 =         {{text = '[{i HQ}]: The techs made a new toy for you, the Kennel. It\'s an automatic repair facility and will repair or assist anything within its operational range. Enjoy. HQ out.', vid = 'X04_HQ_M01_04390.sfd', bank = 'X04_VO', cue = 'X04_HQ_M01_04390', faction = 'NONE'},}
Hq_131 =         {{text = '[{i HQ}]: Brackman made a new toy for you, the Hive. It\'s an automatic repair facility and will repair or assist anything within its operational range. Enjoy. HQ out.', vid = 'X04_HQ_M01_04391.sfd', bank = 'X04_VO', cue = 'X04_HQ_M01_04391', faction = 'NONE'},}
Hq_132 =         {{text = '[{i HQ}]: Roger that.', vid = 'X04_HQ_M01_03596.sfd', bank = 'X04_VO', cue = 'X04_HQ_M01_03596', faction = 'NONE'},}
Hq_133 =         {{text = '[{i HQ}]: Nope. The scopes are clear. HQ out.', vid = 'X04_HQ_M01_03600.sfd', bank = 'X04_VO', cue = 'X04_HQ_M01_03600', faction = 'NONE'},}
Hq_134 =         {{text = '[{i HQ}]: It\'s not like I got anything better to do. HQ out.', vid = 'X04_HQ_M01_03603.sfd', bank = 'X04_VO', cue = 'X04_HQ_M01_03603', faction = 'NONE'},}
Hq_135 =         {{text = '[{i HQ}]: Hope you\'re not too comfortable, Commander. You got your own problems.', vid = 'X04_HQ_M01_04875.sfd', bank = 'X04_VO', cue = 'X04_HQ_M01_04875', faction = 'NONE'},}
Hq_136 =         {{text = '[{i HQ}]: There\'s an enemy base to your west.', vid = 'X04_HQ_M01_04876.sfd', bank = 'X04_VO', cue = 'X04_HQ_M01_04876', faction = 'NONE'},}
Hq_137 =         {{text = '[{i HQ}]: Attack and destroy it. And watch out for a counter-attack. Those aliens are sneaky bastards.', vid = 'X04_HQ_M01_04877.sfd', bank = 'X04_VO', cue = 'X04_HQ_M01_04877', faction = 'NONE'},}
Hq_138 =         {{text = '[{i HQ}]: Roger. HQ out.', vid = 'X04_HQ_M01_04394.sfd', bank = 'X04_VO', cue = 'X04_HQ_M01_04394', faction = 'NONE'},}
Hq_139 =         {{text = '[{i HQ}]: Roger. HQ out.', vid = 'X04_HQ_M01_04394.sfd', bank = 'X04_VO', cue = 'X04_HQ_M01_04394', faction = 'NONE'},}
Hq_140 =         {{text = '[{i HQ}]: All operational objectives have been completed. Begin preparations to recall. We\'re getting you off that rock. HQ out.', vid = 'X04_HQ_M01_03733.sfd', bank = 'X04_VO', cue = 'X04_HQ_M01_03733', faction = 'NONE'},}
Hq_141 =         {{text = '[{i HQ}]: There are at least two Quantum Wakes, you must --', vid = 'X04_HQ_M02_03745.sfd', bank = 'X04_VO', cue = 'X04_HQ_M02_03745', faction = 'NONE'},}
Hq_142 =         {{text = '[{i HQ}]: Keep your pants on, we\'ll get you out of there as soon as we can.', vid = 'X04_HQ_M02_03747.sfd', bank = 'X04_VO', cue = 'X04_HQ_M02_03747', faction = 'NONE'},}
Hq_143 =         {{text = '[{i HQ}]: You got clearance from Brackman to receive the Soothsayer. It generates thousands of nano-cameras that let you keep tabs on the surrounding area. Great for preventing sneak attacks. HQ out.', vid = 'X04_HQ_M02_04919.sfd', bank = 'X04_VO', cue = 'X04_HQ_M02_04919', faction = 'NONE'},}
Hq_144 =         {{text = '[{i HQ}]: Elite Commander, what is your status?', vid = 'X04_HQ_M03_03750.sfd', bank = 'X04_VO', cue = 'X04_HQ_M03_03750', faction = 'NONE'},}
Hq_145 =         {{text = '[{i HQ}]: Elite Commander! Come in! Do you read me?', vid = 'X04_HQ_M03_03756.sfd', bank = 'X04_VO', cue = 'X04_HQ_M03_03756', faction = 'NONE'},}
Hq_146 =         {{text = '[{i HQ}]: Commander, you still have enemy forces moving toward your position. Hold the line until we can extract you. HQ out.', vid = 'X04_HQ_M03_03757.sfd', bank = 'X04_VO', cue = 'X04_HQ_M03_03757', faction = 'NONE'},}
Hq_147 =         {{text = '[{i HQ}]: We\'re still trying to figure out how to counter the jamming. HQ out.', vid = 'X04_HQ_M03_03759.sfd', bank = 'X04_VO', cue = 'X04_HQ_M03_03759', faction = 'NONE'},}
Hq_148 =         {{text = '[{i HQ}]: Rhiza has requested that we upload the Salvation artillery. It\'s a rapid-fire system that blankets an area with small shells. HQ out.', vid = 'X04_HQ_M03_04403.sfd', bank = 'X04_VO', cue = 'X04_HQ_M03_04403', faction = 'NONE'},}
Hq_149 =         {{text = '[{i HQ}]: The technicians have finished realigning the Gate power cores, but it\'s going to be awhile before we can get you out of that hellhole. Stay alive. HQ out.', vid = 'X04_HQ_M03_03764.sfd', bank = 'X04_VO', cue = 'X04_HQ_M03_03764', faction = 'NONE'},}
Hq_150 =         {{text = '[{i HQ}]: Going to be a bit longer, Commander. Hang in there.', vid = 'X04_HQ_M03_03765.sfd', bank = 'X04_VO', cue = 'X04_HQ_M03_03765', faction = 'NONE'},}
Hq_151 =         {{text = '[{i HQ}]: The Gate is locked in on Hades. Just a couple more minutes. HQ out.', vid = 'X04_HQ_M03_03766.sfd', bank = 'X04_VO', cue = 'X04_HQ_M03_03766', faction = 'NONE'},}
Hq_152 =         {{text = '[{i HQ}]: Grab your barf bag, Commander, this is going to be rough. Recalling now!', vid = 'X04_HQ_M03_03770.sfd', bank = 'X04_VO', cue = 'X04_HQ_M03_03770', faction = 'NONE'},}
Hq_153 =         {{text = '[{i HQ}]: Damn, you certainly took care of business. We\'re recalling you. HQ out.', vid = 'X04_HQ_M03_04920.sfd', bank = 'X04_VO', cue = 'X04_HQ_M03_04920', faction = 'NONE'},}
Hq_154 =         {{text = '[{i HQ}]: Commander, stay within the area of your base. If you stray too far, we won\'t be able to get a recall lock. HQ out.', vid = 'X04_HQ_M03_04935.sfd', bank = 'X04_VO', cue = 'X04_HQ_M03_04935', faction = 'NONE'},}
Hq_155 =         {{text = '[{i HQ}]: Get back to your base, Commander. You\'re too far out and we can\'t get a lock on you. HQ out.', vid = 'X04_HQ_M03_04936.sfd', bank = 'X04_VO', cue = 'X04_HQ_M03_04936', faction = 'NONE'},}
Hq_156 =         {{text = '[{i HQ}]: Stay within your base\'s perimeter, Commander. We need to lock down your position so we can get you the hell out of there. HQ out.', vid = 'X04_HQ_M03_04937.sfd', bank = 'X04_VO', cue = 'X04_HQ_M03_04937', faction = 'NONE'},}
Hq_157 =         {{text = '[{i HQ}]: We\'ve got a lock on your ACU. Almost there.', vid = 'X04_HQ_M03_04938.sfd', bank = 'X04_VO', cue = 'X04_HQ_M03_04938', faction = 'NONE'},}
Hq_158 =         {{text = '[{i HQ}]: That was damn close, Commander. Glad we got you out of there in one piece.', vid = 'X04_HQ_M03_05107.sfd', bank = 'X04_VO', cue = 'X04_HQ_M03_05107', faction = 'NONE'},}
Hq_159 =         {{text = '[{i HQ}]: The operation has ended in failure. All is lost.', vid = 'X05_HQ_DB01_04956.sfd', bank = 'Briefings', cue = 'X05_HQ_DB01_04956', faction = 'NONE'},}
Hq_160 =         {{text = '[{i HQ}]: Commander? Can you read me? Ah hell, I think the Commander is dea --', vid = 'X05_HQ_DB01_04957.sfd', bank = 'Briefings', cue = 'X05_HQ_DB01_04957', faction = 'NONE'},}
Hq_161 =         {{text = '[{i HQ}]: Your top priority is to clear this area of enemies so Fletcher can gate in and construct a base.', vid = 'X05_HQ_M01_03788.sfd', bank = 'X05_VO', cue = 'X05_HQ_M01_03788', faction = 'NONE'},}
Hq_162 =         {{text = '[{i HQ}]: Both Hex5 and QAI are operating in the area, so be ready for one hell of a fight.', vid = 'X05_HQ_M01_04908.sfd', bank = 'X05_VO', cue = 'X05_HQ_M01_04908', faction = 'NONE'},}
Hq_163 =         {{text = '[{i HQ}]: Get it in gear, Commander. HQ out.', vid = 'X05_HQ_M01_04909.sfd', bank = 'X05_VO', cue = 'X05_HQ_M01_04909', faction = 'NONE'},}
Hq_164 =         {{text = '[{i HQ}]: Clear the area, Commander. Fletcher is suited up and ready to gate. HQ out.', vid = 'X05_HQ_M01_03794.sfd', bank = 'X05_VO', cue = 'X05_HQ_M01_03794', faction = 'NONE'},}
Hq_165 =         {{text = '[{i HQ}]: Fletcher is ready to gate, Commander. Waiting on you to clear the area. HQ out.', vid = 'X05_HQ_M01_03795.sfd', bank = 'X05_VO', cue = 'X05_HQ_M01_03795', faction = 'NONE'},}
Hq_166 =         {{text = '[{i HQ}]: Hex5 has a handful of small bases in the canyons. Destroy them. HQ out.', vid = 'X05_HQ_M01_03811.sfd', bank = 'X05_VO', cue = 'X05_HQ_M01_03811', faction = 'NONE'},}
Hq_167 =         {{text = '[{i HQ}]: Hex5\'s bases are still a threat. HQ out.', vid = 'X05_HQ_M01_03812.sfd', bank = 'X05_VO', cue = 'X05_HQ_M01_03812', faction = 'NONE'},}
Hq_168 =         {{text = '[{i HQ}]: Destroy Hex5\'s small bases. HQ out.', vid = 'X05_HQ_M01_03813.sfd', bank = 'X05_VO', cue = 'X05_HQ_M01_03813', faction = 'NONE'},}
Hq_169 =         {{text = '[{i HQ}]: Hex5\'s secondary bases are destroyed. HQ out.', vid = 'X05_HQ_M01_03814.sfd', bank = 'X05_VO', cue = 'X05_HQ_M01_03814', faction = 'NONE'},}
Hq_170 =         {{text = '[{i HQ}]: You heard the old man, Commander. Destroy Hex5. HQ out.', vid = 'X05_HQ_M01_04439.sfd', bank = 'X05_VO', cue = 'X05_HQ_M01_04439', faction = 'NONE'},}
Hq_171 =         {{text = '[{i HQ}]: Sending you the schematic for the T3 Continental air transport. HQ out.', vid = 'X05_HQ_M01_04939.sfd', bank = 'X05_VO', cue = 'X05_HQ_M01_04939', faction = 'NONE'},}
Hq_172 =         {{text = '[{i HQ}]: Brackman has authorized you to receive the T3 Wailer. It\'s a heavy gunship. HQ out.', vid = 'X05_HQ_M01_04941.sfd', bank = 'X05_VO', cue = 'X05_HQ_M01_04941', faction = 'NONE'},}
Hq_173 =         {{text = '[{i HQ}]: Looks like Hex5 is deploying Spiderbots. HQ out.', vid = 'X05_HQ_M02_03826.sfd', bank = 'X05_VO', cue = 'X05_HQ_M02_03826', faction = 'NONE'},}
Hq_174 =         {{text = '[{i HQ}]: Hex5 isn\'t going down quietly. He\'s just brought a Scathis online. HQ out.', vid = 'X05_HQ_M02_03829.sfd', bank = 'X05_VO', cue = 'X05_HQ_M02_03829', faction = 'NONE'},}
Hq_175 =         {{text = '[{i HQ}]: Brackman has landed. Escort him to QAI\'s mainframe so he can destroy that damn thing once and for all.', vid = 'X05_HQ_M02_03854.sfd', bank = 'X05_VO', cue = 'X05_HQ_M02_03854', faction = 'NONE'},}
Hq_176 =         {{text = '[{i HQ}]: Assist Fletcher as you are able, Commander. HQ out.', vid = 'X05_HQ_M02_04942.sfd', bank = 'X05_VO', cue = 'X05_HQ_M02_04942', faction = 'NONE'},}
Hq_177 =         {{text = '[{i HQ}]: Enemy attack coming in from the south ... hold on a sec ... there\'s some experimentals in there, so be ready. HQ out.', vid = 'X05_HQ_M02_04943.sfd', bank = 'X05_VO', cue = 'X05_HQ_M02_04943', faction = 'NONE'},}
Hq_178 =         {{text = '[{i HQ}]: Fletcher is building a Fatboy, Commander, so help him defend his base. You\'re going to need him when you launch your final attack on QAI.', vid = 'X05_HQ_M02_04946.sfd', bank = 'X05_VO', cue = 'X05_HQ_M02_04946', faction = 'NONE'},}
Hq_179 =         {{text = '[{i HQ}]: Fletcher is working on a Fatboy, Commander. Help protect his base until it\'s operational. HQ out.', vid = 'X05_HQ_M02_04947.sfd', bank = 'X05_VO', cue = 'X05_HQ_M02_04947', faction = 'NONE'},}
Hq_180 =         {{text = '[{i HQ}]: You can move Brackman\'s Megalith as often as you want, Commander. Just click the Command Signal button and then click to where Brackman should move. HQ out.', vid = 'X05_HQ_M02_04951.sfd', bank = 'X05_VO', cue = 'X05_HQ_M02_04951', faction = 'NONE'},}
Hq_181 =         {{text = '[{i HQ}]: Believe or not, Commander, Hex5\'s base is still functioning. Scanning to see what\'s happening. HQ out.', vid = 'X05_HQ_M03_03859.sfd', bank = 'X05_VO', cue = 'X05_HQ_M03_03859', faction = 'NONE'},}
Hq_182 =         {{text = '[{i HQ}]: Commander, use the Command Signal to move Brackman into position. HQ out.', vid = 'X05_HQ_M03_04443.sfd', bank = 'X05_VO', cue = 'X05_HQ_M03_04443', faction = 'NONE'},}
Hq_183 =         {{text = '[{i HQ}]: Brackman is under attack, Commander. Cover him! HQ out.', vid = 'X05_HQ_M03_03857.sfd', bank = 'X05_VO', cue = 'X05_HQ_M03_03857', faction = 'NONE'},}
Hq_184 =         {{text = '[{i HQ}]: Brackman is taking fire! Get those units off him!', vid = 'X05_HQ_M03_03858.sfd', bank = 'X05_VO', cue = 'X05_HQ_M03_03858', faction = 'NONE'},}
Hq_185 =         {{text = '[{i HQ}]: Brackman\'s Megalith has lost approximately 25% of its armor. Get an Engineer over there and repair it. HQ out.', vid = 'X05_HQ_M03_03861.sfd', bank = 'X05_VO', cue = 'X05_HQ_M03_03861', faction = 'NONE'},}
Hq_186 =         {{text = '[{i HQ}]: Brackman\'s Megalith has taken serious damage. Repair it or move him to a safer position. HQ out.', vid = 'X05_HQ_M03_03862.sfd', bank = 'X05_VO', cue = 'X05_HQ_M03_03862', faction = 'NONE'},}
Hq_187 =         {{text = '[{i HQ}]: Get Brackman to QAI\'s mainframe. HQ out.', vid = 'X05_HQ_M03_03868.sfd', bank = 'X05_VO', cue = 'X05_HQ_M03_03868', faction = 'NONE'},}
Hq_188 =         {{text = '[{i HQ}]: Getting Brackman to QAI\'s mainframe is your primary objective, Commander. HQ out.', vid = 'X05_HQ_M03_03869.sfd', bank = 'X05_VO', cue = 'X05_HQ_M03_03869', faction = 'NONE'},}
Hq_189 =         {{text = '[{i HQ}]: Attack inbound on your position. HQ out.', vid = 'X05_HQ_M03_03871.sfd', bank = 'X05_VO', cue = 'X05_HQ_M03_03871', faction = 'NONE'},}
Hq_190 =         {{text = '[{i HQ}]: This can\'t be right ... I\'m picking up multiple experimental-class signatures from QAI\'s position, and they register as Aeon and UEF experimentals. HQ out.', vid = 'X05_HQ_M03_04445.sfd', bank = 'X05_VO', cue = 'X05_HQ_M03_04445', faction = 'NONE'},}
Hq_191 =         {{text = '[{i HQ}]: QAI is operating two large resource bases in the area. One is home to most of its Mass fabricators while0 the other is wall-to-wall power generators. Both bases are targets of opportunity, but you should destroy them if able. HQ out.', vid = 'X05_HQ_M03_03879.sfd', bank = 'X05_VO', cue = 'X05_HQ_M03_03879', faction = 'NONE'},}
Hq_192 =         {{text = '[{i HQ}]: Commander, QAI\'s resource bases are still operational. HQ out.', vid = 'X05_HQ_M03_03880.sfd', bank = 'X05_VO', cue = 'X05_HQ_M03_03880', faction = 'NONE'},}
Hq_193 =         {{text = '[{i HQ}]: Destroy QAI\'s resource bases if you can, Commander. HQ out.', vid = 'X05_HQ_M03_03881.sfd', bank = 'X05_VO', cue = 'X05_HQ_M03_03881', faction = 'NONE'},}
Hq_194 =         {{text = '[{i HQ}]: The Mass fabricators are destroyed. QAI\'s production has been cut by almost 40%. HQ out.', vid = 'X05_HQ_M03_03882.sfd', bank = 'X05_VO', cue = 'X05_HQ_M03_03882', faction = 'NONE'},}
Hq_195 =         {{text = '[{i HQ}]: The power generators are down, and QAI\'s shields are going offline. HQ out.', vid = 'X05_HQ_M03_03883.sfd', bank = 'X05_VO', cue = 'X05_HQ_M03_03883', faction = 'NONE'},}
Hq_196 =         {{text = '[{i HQ}]: The fabricators are still operational, Commander. HQ out.', vid = 'X05_HQ_M03_03884.sfd', bank = 'X05_VO', cue = 'X05_HQ_M03_03884', faction = 'NONE'},}
Hq_197 =         {{text = '[{i HQ}]: Destroy the fabricator base if you can, Commander. HQ out.', vid = 'X05_HQ_M03_03885.sfd', bank = 'X05_VO', cue = 'X05_HQ_M03_03885', faction = 'NONE'},}
Hq_198 =         {{text = '[{i HQ}]: QAI\'s power generators are still operational, Commander. HQ out.', vid = 'X05_HQ_M03_03886.sfd', bank = 'X05_VO', cue = 'X05_HQ_M03_03886', faction = 'NONE'},}
Hq_199 =         {{text = '[{i HQ}]: If you can destroy QAI\'s power generators, you\'ll take out its shields, Commander. HQ out.', vid = 'X05_HQ_M03_03887.sfd', bank = 'X05_VO', cue = 'X05_HQ_M03_03887', faction = 'NONE'},}
Hq_200 =         {{text = '[{i HQ}]: Both of QAI\'s resource bases have been destroyed. HQ out.', vid = 'X05_HQ_M03_03888.sfd', bank = 'X05_VO', cue = 'X05_HQ_M03_03888', faction = 'NONE'},}
Hq_201 =         {{text = '[{i HQ}]: Doctor Brackman?', vid = 'X05_HQ_M03_03894.sfd', bank = 'X05_VO', cue = 'X05_HQ_M03_03894', faction = 'NONE'},}
Hq_202 =         {{text = '[{i HQ}]: We have techs prepped and ready to gate. They\'ll be there shortly. HQ out.', vid = 'X05_HQ_M03_03896.sfd', bank = 'X05_VO', cue = 'X05_HQ_M03_03896', faction = 'NONE'},}
Hq_203 =         {{text = '[{i HQ}]: Commander? You there? Dammit.', vid = 'X06_HQ_DB01_04963.sfd', bank = 'Briefings', cue = 'X06_HQ_DB01_04963', faction = 'NONE'},}
Hq_204 =         {{text = '[{i HQ}]: Got green lights across the board, Commander. Fletcher is to your northeast and Rhiza is to the northwest. Both will assist as able. HQ out.', vid = 'X06_HQ_M01_03903.sfd', bank = 'X06_VO', cue = 'X06_HQ_M01_03903', faction = 'NONE'},}
Hq_205 =         {{text = '[{i HQ}]: Two support commanders gated in ahead of you, Commander, and they\'ve started work on your base. HQ out.', vid = 'X06_HQ_M01_04470.sfd', bank = 'X06_VO', cue = 'X06_HQ_M01_04470', faction = 'NONE'},}
Hq_206 =         {{text = '[{i HQ}]: Attack and destroy the Seraphim, Commander. It\'s to your southwest. HQ out.', vid = 'X06_HQ_M01_04959.sfd', bank = 'X06_VO', cue = 'X06_HQ_M01_04959', faction = 'NONE'},}
Hq_207 =         {{text = '[{i HQ}]: Looks like the Commander just ate it. Poor bastard.', vid = 'X06_HQ_M01_04960.sfd', bank = 'X06_VO', cue = 'X06_HQ_M01_04960', faction = 'NONE'},}
Hq_208 =         {{text = '[{i HQ}]: Commander, you read me? Commander? Ah hell...', vid = 'X06_HQ_M01_04961.sfd', bank = 'X06_VO', cue = 'X06_HQ_M01_04961', faction = 'NONE'},}
Hq_209 =         {{text = '[{i HQ}]: A Seraphim commander is blocking your route to the Arch. Defeat that alien\'s forces and then move on the Arch. HQ out.', vid = 'X06_HQ_M02_04473.sfd', bank = 'X06_VO', cue = 'X06_HQ_M02_04473', faction = 'NONE'},}
Hq_210 =         {{text = '[{i HQ}]: His recall beacon is disabled. Nothing we can do from this end.', vid = 'X06_HQ_M02_04481.sfd', bank = 'X06_VO', cue = 'X06_HQ_M02_04481', faction = 'NONE'},}
Hq_211 =         {{text = '[{i HQ}]: Looks like the nut has finally cracked, Commander. HQ out.', vid = 'X06_HQ_M02_04483.sfd', bank = 'X06_VO', cue = 'X06_HQ_M02_04483', faction = 'NONE'},}
Hq_212 =         {{text = '[{i HQ}]: You\'ve got Order forces moving to your south and that nut-ball Fletcher is prepping an attack. Deal with both of them. HQ out.', vid = 'X06_HQ_M02_04490.sfd', bank = 'X06_VO', cue = 'X06_HQ_M02_04490', faction = 'NONE'},}
Hq_213 =         {{text = '[{i HQ}]: We\'re reading a Paragon in Vendetta\'s base. If you destroy it, you\'ll cripple her economy. HQ out.', vid = 'X06_HQ_M02_04491.sfd', bank = 'X06_VO', cue = 'X06_HQ_M02_04491', faction = 'NONE'},}
Hq_214 =         {{text = '[{i HQ}]: The Seraphim commander stands between you and the Arch. Defeat the Seraphim and then attack the Arch. HQ out.', vid = 'X06_HQ_M02_03957.sfd', bank = 'X06_VO', cue = 'X06_HQ_M02_03957', faction = 'NONE'},}
Hq_215 =         {{text = '[{i HQ}]: That takes care of the Order commander. HQ out.', vid = 'X06_HQ_M02_03958.sfd', bank = 'X06_VO', cue = 'X06_HQ_M02_03958', faction = 'NONE'},}
Hq_216 =         {{text = '[{i HQ}]: Just got an update regarding the Black Sun Control Center. It\'s linked to the old Option Zero defense network, and it looks like some of those weapons are still online. Whoever seizes the Control Center can use the linked weapon systems. Capture the Control Center. HQ out.', vid = 'X06_HQ_M02_03959.sfd', bank = 'X06_VO', cue = 'X06_HQ_M02_03959', faction = 'NONE'},}
Hq_217 =         {{text = '[{i HQ}]: You\'ve captured the Control Center, but it looks like only a single nuke launcher is still active. When a nuke comes online, use the Command Signal button to place a targeting marker. The nuke will automatically fire. HQ out.', vid = 'X06_HQ_M02_04492.sfd', bank = 'X06_VO', cue = 'X06_HQ_M02_04492', faction = 'NONE'},}
Hq_218 =         {{text = '[{i HQ}]: We are reading that you lost possession of the Control Center. Get it back. HQ out.', vid = 'X06_HQ_M02_03960.sfd', bank = 'X06_VO', cue = 'X06_HQ_M02_03960', faction = 'NONE'},}
Hq_219 =         {{text = '[{i HQ}]: Fletcher has the Control Center. HQ out.', vid = 'X06_HQ_M02_03961.sfd', bank = 'X06_VO', cue = 'X06_HQ_M02_03961', faction = 'NONE'},}
Hq_220 =         {{text = '[{i HQ}]: The Order commander has taken the Control Center. HQ out.', vid = 'X06_HQ_M02_03962.sfd', bank = 'X06_VO', cue = 'X06_HQ_M02_03962', faction = 'NONE'},}
Hq_221 =         {{text = '[{i HQ}]: You have successfully captured the Control Center. The linked weapons are under your control. HQ out.', vid = 'X06_HQ_M02_03963.sfd', bank = 'X06_VO', cue = 'X06_HQ_M02_03963', faction = 'NONE'},}
Hq_222 =         {{text = '[{i HQ}]: The Control Center has switched hands. HQ out.', vid = 'X06_HQ_M02_03964.sfd', bank = 'X06_VO', cue = 'X06_HQ_M02_03964', faction = 'NONE'},}
Hq_223 =         {{text = '[{i HQ}]: Commander, the Control Center has been destroyed. HQ out.', vid = 'X06_HQ_M02_03965.sfd', bank = 'X06_VO', cue = 'X06_HQ_M02_03965', faction = 'NONE'},}
Hq_224 =         {{text = '[{i HQ}]: Enemy forces are bearing down on the Control Center. HQ out.', vid = 'X06_HQ_M02_03966.sfd', bank = 'X06_VO', cue = 'X06_HQ_M02_03966', faction = 'NONE'},}
Hq_225 =         {{text = '[{i HQ}]: Enemy units are trying to capture the Control Center. HQ out.', vid = 'X06_HQ_M02_03967.sfd', bank = 'X06_VO', cue = 'X06_HQ_M02_03967', faction = 'NONE'},}
Hq_226 =         {{text = '[{i HQ}]: The Arch is right there! Destroy it! ', vid = 'X06_HQ_M02_03968.sfd', bank = 'X06_VO', cue = 'X06_HQ_M02_03968', faction = 'NONE'},}
Hq_227 =         {{text = '[{i HQ}]: Picking up a massive influx of units coming through the Rift. It\'s now or never, Commander.', vid = 'X06_HQ_M03_03971.sfd', bank = 'X06_VO', cue = 'X06_HQ_M03_03971', faction = 'NONE'},}
Hq_228 =         {{text = '[{i HQ}]: Crusader Rhiza, what is your status?', vid = 'X06_HQ_M03_03975.sfd', bank = 'X06_VO', cue = 'X06_HQ_M03_03975', faction = 'NONE'},}
Hq_229 =         {{text = '[{i HQ}]: You\'re on your own, Commander. Destroy the Arch. HQ out.', vid = 'X06_HQ_M03_04494.sfd', bank = 'X06_VO', cue = 'X06_HQ_M03_04494', faction = 'NONE'},}
Hq_230 =         {{text = '[{i HQ}]: You\'re pounding the Arch! Keep it up!', vid = 'X06_HQ_M03_04495.sfd', bank = 'X06_VO', cue = 'X06_HQ_M03_04495', faction = 'NONE'},}
Hq_231 =         {{text = '[{i HQ}]: The Arch is taking serious damage. It won\'t last much longer.', vid = 'X06_HQ_M03_04496.sfd', bank = 'X06_VO', cue = 'X06_HQ_M03_04496', faction = 'NONE'},}
Hq_232 =         {{text = '[{i HQ}]: The Arch is on its last legs. It\'s nearly destroyed.', vid = 'X06_HQ_M03_04497.sfd', bank = 'X06_VO', cue = 'X06_HQ_M03_04497', faction = 'NONE'},}
Hq_233 =         {{text = '[{i HQ}]: Detecting a power surge, Commander. Scans show that the Seraphim is constructing a massive strategic missile launcher. You\'d better get some strategic missile defenses around your base. HQ out.', vid = 'X06_HQ_M03_03987.sfd', bank = 'X06_VO', cue = 'X06_HQ_M03_03987', faction = 'NONE'},}
Hq_234 =         {{text = '[{i HQ}]: Shut that damn alien up, Commander. Preferably with a nuke. HQ out.', vid = 'X06_HQ_M03_03999.sfd', bank = 'X06_VO', cue = 'X06_HQ_M03_03999', faction = 'NONE'},}
Hq_235 =         {{text = '[{i HQ}]: Eliminate the Seraphim, Commander. HQ out.', vid = 'X06_HQ_M03_04000.sfd', bank = 'X06_VO', cue = 'X06_HQ_M03_04000', faction = 'NONE'},}
Hq_236 =         {{text = '[{i HQ}]: Scratch one alien. HQ out.', vid = 'X06_HQ_M03_04002.sfd', bank = 'X06_VO', cue = 'X06_HQ_M03_04002', faction = 'NONE'},}

-- Cybran Jericho
Jericho_1 =      {{text = '[{i Jericho}]: Commander? My name is Jericho. I just escaped from a UEF compound to the east. There are other Symbionts being held there. You must help them!', vid = 'C01_Jericho_M06_01483.sfd', bank = 'C01_VO', cue = 'C01_Jericho_M06_01483', faction = 'Cybran'},}
Jericho_2 =      {{text = '[{i Jericho}]: Dostya? Elite Commander Dostya?', vid = 'C01_Jericho_M06_01485.sfd', bank = 'C01_VO', cue = 'C01_Jericho_M06_01485', faction = 'Cybran'},}
Jericho_3 =      {{text = '[{i Jericho}]: I have information that I must pass along to you.', vid = 'C01_Jericho_M06_01487.sfd', bank = 'C01_VO', cue = 'C01_Jericho_M06_01487', faction = 'Cybran'},}
Jericho_4 =      {{text = '[{i Jericho}]: Yes.', vid = 'C01_Jericho_M06_01489.sfd', bank = 'C01_VO', cue = 'C01_Jericho_M06_01489', faction = 'Cybran'},}
Jericho_5 =      {{text = '[{i Jericho}]: Good to see you again, Commander. We\'ve set up what we could. It looks like the Aeon are giving the UEF a beating a couple clicks to the west.', vid = 'C06_Jericho_M01_00559.sfd', bank = 'C06_VO', cue = 'C06_Jericho_M01_00559', faction = 'Cybran'},}
Jericho_6 =      {{text = '[{i Jericho}]: Commander, we\'ve tapped into the UEF communication system. Black Sun hasn\'t been completed, but it\'s close. I\'ll keep you updated on its progress.', vid = 'C06_Jericho_M01_00561.sfd', bank = 'C06_VO', cue = 'C06_Jericho_M01_00561', faction = 'Cybran'},}
Jericho_7 =      {{text = '[{i Jericho}]: They hit with everything... ', vid = 'C06_Jericho_M01_01105.sfd', bank = 'C06_VO', cue = 'C06_Jericho_M01_01105', faction = 'Cybran'},}
Jericho_8 =      {{text = '[{i Jericho}]: Commander, Black Sun is almost finished! They have to charge it before it can fire, but we\'re running out of time!', vid = 'C06_Jericho_M02_00573.sfd', bank = 'C06_VO', cue = 'C06_Jericho_M02_00573', faction = 'Cybran'},}

-- Aeon Kael
Kael_1 =         {{text = '[{i Kael}]: Attention all Order commanders: Fort Clarke has fallen, the remnants of the UEF command structure have been destroyed and the Coalition is no more. This is a glorious day for the Order of the Illuminate! We are triumphant!', vid = 'X01_Kael_DB01_04259.sfd', bank = 'Briefings', cue = 'X01_Kael_DB01_04259', faction = 'Aeon'},}
Kael_2 =         {{text = '[{i Kael}]: You\'re going to die here, Burke. And then I shall deal with your so-called Loyalists.', vid = 'X03_Kael_M01_03302.sfd', bank = 'X03_VO', cue = 'X03_Kael_M01_03302', faction = 'Aeon'},}
Kael_3 =         {{text = '[{i Kael}]: I\'ve been waiting a long time for this day, Burke. Your death will mark the closing chapter of a plan I hatched years ago.', vid = 'X03_Kael_M02_03309.sfd', bank = 'X03_VO', cue = 'X03_Kael_M02_03309', faction = 'Aeon'},}
Kael_4 =         {{text = '[{i Kael}]: That is where you\'re wrong, Princess. This is all pre-ordained. You think Marxon was working alone when he killed your beloved Toth? Oh no. I was behind it all. From the very beginning.', vid = 'X03_Kael_M02_03311.sfd', bank = 'X03_VO', cue = 'X03_Kael_M02_03311', faction = 'Aeon'},}
Kael_5 =         {{text = '[{i Kael}]: The Order has always wielded the true power, Princess. You were merely a formality, something to keep the chattering classes in line. But your usefulness ended years ago.', vid = 'X03_Kael_M02_03313.sfd', bank = 'X03_VO', cue = 'X03_Kael_M02_03313', faction = 'Aeon'},}
Kael_6 =         {{text = '[{i Kael}]: You will die a fool\'s death, Burke. No one will remember you, no one will call out your name. I will see to that myself.', vid = 'X03_Kael_M02_04359.sfd', bank = 'X03_VO', cue = 'X03_Kael_M02_04359', faction = 'Aeon'},}
Kael_7 =         {{text = '[{i Kael}]: Ha-ha-ha!', vid = 'X03_Kael_M02_04368.sfd', bank = 'X03_VO', cue = 'X03_Kael_M02_04368', faction = 'Aeon'},}
Kael_8 =         {{text = '[{i Kael}]: Losing both you and Elite Commander Dostya in the same day? It\'ll be more than Brackman will be able to handle.', vid = 'X04_Kael_M03_03768.sfd', bank = 'X04_VO', cue = 'X04_Kael_M03_03768', faction = 'Aeon'},}
Kael_9 =         {{text = '[{i Kael}]: You\'re too late, fools! The Arch is finished! The Seraphim are already pouring through it. They will cleanse the entire galaxy!', vid = 'X06_Kael_M02_03969.sfd', bank = 'X06_VO', cue = 'X06_Kael_M02_03969', faction = 'Aeon'},}
Kael_10 =        {{text = '[{i Kael}]: Do something!', vid = 'X06_Kael_M03_04499.sfd', bank = 'X06_VO', cue = 'X06_Kael_M03_04499', faction = 'Aeon'},}
Kael_11 =        {{text = '[{i Kael}]: But...', vid = 'X06_Kael_M03_04501.sfd', bank = 'X06_VO', cue = 'X06_Kael_M03_04501', faction = 'Aeon'},}
-- No vid
Kael_11 =        {{text = '[{i Kael}]: The Order will not be defeated!', bank = 'XGG', cue = 'XGG_Kael_MP1_04590'},}
Kael_12 =        {{text = '[{i Kael}]: If you grovel, I may let you live.', bank = 'XGG', cue = 'XGG_Kael_MP1_04591'},}
Kael_13 =        {{text = '[{i Kael}]: There will be nothing left of you when I am done.', bank = 'XGG', cue = 'XGG_Kael_MP1_04592'},}
Kael_14 =        {{text = '[{i Kael}]: You\'re beginning to bore me.', bank = 'XGG', cue = 'XGG_Kael_MP1_04593'},}
Kael_15 =        {{text = '[{i Kael}]: My time is wasted on you.', bank = 'XGG', cue = 'XGG_Kael_MP1_04594'},}
Kael_16 =        {{text = '[{i Kael}]: Run while you can.', bank = 'XGG', cue = 'XGG_Kael_MP1_04595'},}
Kael_17 =        {{text = '[{i Kael}]: It must be frustrating to be so completely overmatched.', bank = 'XGG', cue = 'XGG_Kael_MP1_04596'},}
Kael_18 =        {{text = '[{i Kael}]: Beg for mercy.', bank = 'XGG', cue = 'XGG_Kael_MP1_04597'},}

-- Cybran Leopard11
Leopard11_1 =    {{text = '[{i Leopard11}]: Get the hell off my planet, monster!', vid = 'A02_Leopard11_M01_00801.sfd', bank = 'A02_VO', cue = 'A02_Leopard11_M01_00801', faction = 'Cybran'},}
Leopard11_2 =    {{text = '[{i Leopard11}]: You are no match for Cybran technology!', vid = 'A02_Leopard11_M02_00817.sfd', bank = 'A02_VO', cue = 'A02_Leopard11_M02_00817', faction = 'Cybran'},}
Leopard11_3 =    {{text = '[{i Leopard11}]: You were a fool to come here, Aeon. This planet belongs to the Cybran!', vid = 'A02_Leopard11_M02_00806.sfd', bank = 'A02_VO', cue = 'A02_Leopard11_M02_00806', faction = 'Cybran'},}
Leopard11_4 =    {{text = '[{i Leopard11}]: Why can\'t you just leave us in peace?', vid = 'A02_Leopard11_M02_00823.sfd', bank = 'A02_VO', cue = 'A02_Leopard11_M02_00823', faction = 'Cybran'},}
Leopard11_5 =    {{text = '[{i Leopard11}]: This battle is not over, fanatic!', vid = 'A02_Leopard11_M02_00824.sfd', bank = 'A02_VO', cue = 'A02_Leopard11_M02_00824', faction = 'Cybran'},}
Leopard11_6 =    {{text = '[{i Leopard11}]: I\'m going to end this fight now, Aeon.', vid = 'A02_Leopard11_M03_00828.sfd', bank = 'A02_VO', cue = 'A02_Leopard11_M03_00828', faction = 'Cybran'},}
Leopard11_7 =    {{text = '[{i Leopard11}]: The Aeon people are the abominations!', vid = 'A02_Leopard11_M03_00830.sfd', bank = 'A02_VO', cue = 'A02_Leopard11_M03_00830', faction = 'Cybran'},}
Leopard11_8 =    {{text = '[{i Leopard11}]: Recall, dammit, recall! ', vid = 'A02_Leopard11_M03_00835.sfd', bank = 'A02_VO', cue = 'A02_Leopard11_M03_00835', faction = 'Cybran'},}
Leopard11_9 =    {{text = '[{i Leopard11}]: Your madness will be your undoing.', vid = 'A02_Leopard11_T01_00790.sfd', bank = 'A02_VO', cue = 'A02_Leopard11_T01_00790', faction = 'Cybran'},}
Leopard11_10 =   {{text = '[{i Leopard11}]: I will never bow to the Aeon!', vid = 'A02_Leopard11_T01_00791.sfd', bank = 'A02_VO', cue = 'A02_Leopard11_T01_00791', faction = 'Cybran'},}
Leopard11_11 =   {{text = '[{i Leopard11}]: I will not rest until every Symbiont is free from your terror.', vid = 'A02_Leopard11_T01_00792.sfd', bank = 'A02_VO', cue = 'A02_Leopard11_T01_00792', faction = 'Cybran'},}
Leopard11_12 =   {{text = '[{i Leopard11}]: This planet is under my protection. You will never take it.', vid = 'A02_Leopard11_T01_00793.sfd', bank = 'A02_VO', cue = 'A02_Leopard11_T01_00793', faction = 'Cybran'},}
Leopard11_13 =   {{text = '[{i Leopard11}]: My AI is always calculating your next move.', vid = 'A02_Leopard11_T01_00794.sfd', bank = 'A02_VO', cue = 'A02_Leopard11_T01_00794', faction = 'Cybran'},}
Leopard11_14 =   {{text = '[{i Leopard11}]: The civilians here are under my protection.', vid = 'A02_Leopard11_T01_00795.sfd', bank = 'A02_VO', cue = 'A02_Leopard11_T01_00795', faction = 'Cybran'},}
Leopard11_15 =   {{text = '[{i Leopard11}]: This is not a fight you can win.', vid = 'A02_Leopard11_T01_00796.sfd', bank = 'A02_VO', cue = 'A02_Leopard11_T01_00796', faction = 'Cybran'},}
Leopard11_16 =   {{text = '[{i Leopard11}]: I will parade your remains through the streets.', vid = 'A02_Leopard11_T01_00797.sfd', bank = 'A02_VO', cue = 'A02_Leopard11_T01_00797', faction = 'Cybran'},}
Leopard11_17 =   {{text = '[{i Leopard11}]: The more you oppress us, the stronger we become.', vid = 'E01_Leopard11_M01_0025.sfd', bank = 'E01_VO', cue = 'E01_Leopard11_M01_0025', faction = 'Cybran'},}
Leopard11_18 =   {{text = '[{i Leopard11}]: That air base was meaningless. The larger struggle remains.', vid = 'E01_Leopard11_M05_01531.sfd', bank = 'E01_VO', cue = 'E01_Leopard11_M05_01531', faction = 'Cybran'},} 
Leopard11_19 =   {{text = '[{i Leopard11}]: You have taken out my defensive line. I won\'t underestimate you again.', vid = 'E01_Leopard11_M06_01534.sfd', bank = 'E01_VO', cue = 'E01_Leopard11_M06_01534', faction = 'Cybran'},}
Leopard11_20 =   {{text = '[{i Leopard11}]: Recall, dammit! Recall!', vid = 'E01_Leopard11_M07_0029.sfd', bank = 'E01_VO', cue = 'E01_Leopard11_M07_0029', faction = 'Cybran'},}
Leopard11_21 =   {{text = '[{i Leopard11}]: The Cybrans will never admit defeat.', vid = 'E01_Leopard11_M07_0030.sfd', bank = 'E01_VO', cue = 'E01_Leopard11_M07_0030', faction = 'Cybran'},}
Leopard11_22 =   {{text = '[{i Leopard11}]: Your leaders are nothing more than liars. Do not believe their propaganda.', vid = 'E01_Leopard11_T01_0031.sfd', bank = 'E01_VO', cue = 'E01_Leopard11_T01_0031', faction = 'Cybran'},}
Leopard11_23 =   {{text = '[{i Leopard11}]: How much Cybran blood is on your hands?', vid = 'E01_Leopard11_T01_0032.sfd', bank = 'E01_VO', cue = 'E01_Leopard11_T01_0032', faction = 'Cybran'},}
Leopard11_24 =   {{text = '[{i Leopard11}]: We want to be free. Is that too much to ask?', vid = 'E01_Leopard11_T01_0033.sfd', bank = 'E01_VO', cue = 'E01_Leopard11_T01_0033', faction = 'Cybran'},}
Leopard11_25 =   {{text = '[{i Leopard11}]: You and your kind sicken me. You destroy what you don\'t understand.', vid = 'E01_Leopard11_T01_0034.sfd', bank = 'E01_VO', cue = 'E01_Leopard11_T01_0034', faction = 'Cybran'},}
Leopard11_26 =   {{text = '[{i Leopard11}]: We wanted to live in peace. You forced this war upon yourselves.', vid = 'E01_Leopard11_T01_0035.sfd', bank = 'E01_VO', cue = 'E01_Leopard11_T01_0035', faction = 'Cybran'},}
Leopard11_27 =   {{text = '[{i Leopard11}]: Someday the Cybrans will control the galaxy.', vid = 'E01_Leopard11_T01_0036.sfd', bank = 'E01_VO', cue = 'E01_Leopard11_T01_0036', faction = 'Cybran'},}
Leopard11_28 =   {{text = '[{i Leopard11}]: The UEF is falling apart. The old ways are dying. Time is against you.', vid = 'E01_Leopard11_T01_0037.sfd', bank = 'E01_VO', cue = 'E01_Leopard11_T01_0037', faction = 'Cybran'},}
Leopard11_29 =   {{text = '[{i Leopard11}]: The UEF would enslave all Cybrans. How can you possibly support that goal?', vid = 'E01_Leopard11_T01_0038.sfd', bank = 'E01_VO', cue = 'E01_Leopard11_T01_0038', faction = 'Cybran'},}

-- Cybran Mach
Mach_1 =         {{text = '[{i Mach}]: You will pay-- ', vid = 'E05_Mach_M02_0243.sfd', bank = 'E05_VO', cue = 'E05_Mach_M02_0243', faction = 'Cybran'},}
Mach_2 =         {{text = '[{i Mach}]: I see the Aeon have decided to join in the fun.', vid = 'E05_Mach_T01_0245.sfd', bank = 'E05_VO', cue = 'E05_Mach_T01_0245', faction = 'Cybran'},}
Mach_3 =         {{text = '[{i Mach}]: Once that gun is down, the UEF will cease to exist.', vid = 'E05_Mach_T01_0246.sfd', bank = 'E05_VO', cue = 'E05_Mach_T01_0246', faction = 'Cybran'},}
Mach_4 =         {{text = '[{i Mach}]: Your empire is crumbling.', vid = 'E05_Mach_T01_0247.sfd', bank = 'E05_VO', cue = 'E05_Mach_T01_0247', faction = 'Cybran'},}
Mach_5 =         {{text = '[{i Mach}]: You can\'t stop us.', vid = 'E05_Mach_T01_0248.sfd', bank = 'E05_VO', cue = 'E05_Mach_T01_0248', faction = 'Cybran'},}
Mach_6 =         {{text = '[{i Mach}]: The fires of freedom burn throughout the galaxy. You will now reap what you have sown.', vid = 'E05_Mach_T01_0249.sfd', bank = 'E05_VO', cue = 'E05_Mach_T01_0249', faction = 'Cybran'},}
Mach_7 =         {{text = '[{i Mach}]: You will pay for attacking Dr. Brackman.', vid = 'E05_Mach_T01_0250.sfd', bank = 'E05_VO', cue = 'E05_Mach_T01_0250', faction = 'Cybran'},}
Mach_8 =         {{text = '[{i Mach}]: This is much too easy.', vid = 'E05_Mach_T01_0251.sfd', bank = 'E05_VO', cue = 'E05_Mach_T01_0251', faction = 'Cybran'},}
Mach_9 =         {{text = '[{i Mach}]: You are finished.', vid = 'E05_Mach_T01_0252.sfd', bank = 'E05_VO', cue = 'E05_Mach_T01_0252', faction = 'Cybran'},}
Mach_10 =        {{text = '[{i Mach}]: I\'m headin\' east so I can start work on the Gate. Let me know when you find some of that Tech.', vid = 'C02_Mach_M01_0377.sfd', bank = 'C02_VO', cue = 'C02_Mach_M01_0377', faction = 'Cybran'},}
Mach_11 =        {{text = '[{i Mach}]: I got the Tech. Waiting for the next one.', vid = 'C02_Mach_M01_0382.sfd', bank = 'C02_VO', cue = 'C02_Mach_M01_0382', faction = 'Cybran'},}
Mach_12 =        {{text = '[{i Mach}]: I got the Tech. Continuing work on the Gate.', vid = 'C02_Mach_M01_0386.sfd', bank = 'C02_VO', cue = 'C02_Mach_M01_0386', faction = 'Cybran'},}
Mach_13 =        {{text = '[{i Mach}]: I got the third piece of Tech. Still constructing the Gate.', vid = 'C02_Mach_M02_0402.sfd', bank = 'C02_VO', cue = 'C02_Mach_M02_0402', faction = 'Cybran'},}
Mach_14 =        {{text = '[{i Mach}]: QAI has confirmed we got the Tech it needs. The Gate\'s almost ready, so we\'re outta here in five. Uh...wait a sec...I\'m gettin\' some weird readings from one of the pieces.', vid = 'C02_Mach_M02_0407.sfd', bank = 'C02_VO', cue = 'C02_Mach_M02_0407', faction = 'Cybran'},}
Mach_15 =        {{text = '[{i Mach}]: ...just need to check something...', vid = 'C02_Mach_M02_0409.sfd', bank = 'C02_VO', cue = 'C02_Mach_M02_0409', faction = 'Cybran'},}
Mach_16 =        {{text = '[{i Mach}]: Something weird is going on...I just lost main actuator control...I\'m losing...I can\'t...oh...', vid = 'C02_Mach_M02_0411.sfd', bank = 'C02_VO', cue = 'C02_Mach_M02_0411', faction = 'Cybran'},}

-- Aeon Marxon
Marxon_1 =       {{text = '[{i Marxon}]: Ignore that order, Knight. If the settlement is in your way, destroy it.', vid = 'A02_Marxon_M02_00808.sfd', bank = 'A02_VO', cue = 'A02_Marxon_M02_00808', faction = 'Aeon'},}
Marxon_2 =       {{text = '[{i Marxon}]: This is a military matter. The decision is mine.', vid = 'A02_Marxon_M02_00810.sfd', bank = 'A02_VO', cue = 'A02_Marxon_M02_00810', faction = 'Aeon'},}
Marxon_3 =       {{text = '[{i Marxon}]: I shall remember this, Princess. Knight, you are not to attack the settlement. That is a direct order. We shall cleanse this planet at a later date.', vid = 'A02_Marxon_M02_00812.sfd', bank = 'A02_VO', cue = 'A02_Marxon_M02_00812', faction = 'Aeon'},}
Marxon_4 =       {{text = '[{i Marxon}]: I have been informed that the Princess has contacted you, Knight. You are to ignore her. I am your commanding officer, and there will be serious repercussions if you disobey me. Is that clear?', vid = 'A03_Marxon_M03_00881.sfd', bank = 'A03_VO', cue = 'A03_Marxon_M03_00881', faction = 'Aeon'},}
Marxon_5 =       {{text = '[{i Marxon}]: So, the Princess\' lackey has arrived. I will kill you myself.', vid = 'A06_Marxon_M01_01043.sfd', bank = 'A06_VO', cue = 'A06_Marxon_M01_01043', faction = 'Aeon'},}
Marxon_6 =       {{text = '[{i Marxon}]: That old hag earned her fate. And you shall soon follow her.', vid = 'A06_Marxon_M01_01045.sfd', bank = 'A06_VO', cue = 'A06_Marxon_M01_01045', faction = 'Aeon'},}
Marxon_7 =       {{text = '[{i Marxon}]: You dare betray me?', vid = 'A06_Marxon_M03_01075.sfd', bank = 'A06_VO', cue = 'A06_Marxon_M03_01075', faction = 'Aeon'},}
Marxon_8 =       {{text = '[{i Marxon}]: I always control the self-destruct codes for my subordinates, Arnold. Goodbye. ', vid = 'A06_Marxon_M03_01077.sfd', bank = 'A06_VO', cue = 'A06_Marxon_M03_01077', faction = 'Aeon'},}
Marxon_9 =       {{text = '[{i Marxon}]: Soon Black Sun will be mine. And I will use it to purge the galaxy. Only the Aeon will remain.', vid = 'A06_Marxon_M03_01160.sfd', bank = 'A06_VO', cue = 'A06_Marxon_M03_01160', faction = 'Aeon'},}
Marxon_10 =      {{text = '[{i Marxon}]: No! NO!', vid = 'A06_Marxon_M03_01091.sfd', bank = 'A06_VO', cue = 'A06_Marxon_M03_01091', faction = 'Aeon'},}
Marxon_11 =      {{text = '[{i Marxon}]: The Princess is no match for me. And neither are you.', vid = 'A06_Marxon_T01_01026.sfd', bank = 'A06_VO', cue = 'A06_Marxon_T01_01026', faction = 'Aeon'},}
Marxon_12 =      {{text = '[{i Marxon}]: Toth begged before she died. She was a coward to the very end.', vid = 'A06_Marxon_T01_01027.sfd', bank = 'A06_VO', cue = 'A06_Marxon_T01_01027', faction = 'Aeon'},}
Marxon_13 =      {{text = '[{i Marxon}]: Once I\'ve taken Earth, the Princess will fall by my hand. She will not escape me a second time.', vid = 'A06_Marxon_T01_01028.sfd', bank = 'A06_VO', cue = 'A06_Marxon_T01_01028', faction = 'Aeon'},}
Marxon_14 =      {{text = '[{i Marxon}]: This is your last chance to join me.', vid = 'A06_Marxon_T01_01029.sfd', bank = 'A06_VO', cue = 'A06_Marxon_T01_01029', faction = 'Aeon'},}
Marxon_15 =      {{text = '[{i Marxon}]: The Way is a weapon. It must be wielded as such.', vid = 'A06_Marxon_T01_01030.sfd', bank = 'A06_VO', cue = 'A06_Marxon_T01_01030', faction = 'Aeon'},}
Marxon_16 =      {{text = '[{i Marxon}]: I will rule the galaxy.', vid = 'A06_Marxon_T01_01031.sfd', bank = 'A06_VO', cue = 'A06_Marxon_T01_01031', faction = 'Aeon'},}
Marxon_17 =      {{text = '[{i Marxon}]: The Seraphim meant for us to lead humanity.', vid = 'A06_Marxon_T01_01032.sfd', bank = 'A06_VO', cue = 'A06_Marxon_T01_01032', faction = 'Aeon'},}
Marxon_18 =      {{text = '[{i Marxon}]: With the power of Black Sun, I will conquer the galaxy!', vid = 'A06_Marxon_T01_01033.sfd', bank = 'A06_VO', cue = 'A06_Marxon_T01_01033', faction = 'Aeon'},}

-- Aeon Mathea
Mathea_1 =       {{text = '[{i Mathea}]: You rescued us just in time, Commander -- QAI was beginning the final stages of the AI implantation. Thank you. Mathea out.', vid = 'X02_Mathea_M02_04274.sfd', bank = 'X02_VO', cue = 'X02_Mathea_M02_04274', faction = 'Aeon'},}
Mathea_2 =       {{text = '[{i Mathea}]: Champion, my spies have informed me that a high-ranking Order official named Rhys is on-planet and not far from your location. I want him. All you need to do is eliminate his defenses; we will do the rest. Mathea out.', vid = 'X02_Mathea_M02_03159.sfd', bank = 'X02_VO', cue = 'X02_Mathea_M02_03159', faction = 'Aeon'},}
Mathea_3 =       {{text = '[{i Mathea}]: Rhys is still free, Champion. Mathea out.', vid = 'X02_Mathea_M02_04008.sfd', bank = 'X02_VO', cue = 'X02_Mathea_M02_04008', faction = 'Aeon'},}
Mathea_4 =       {{text = '[{i Mathea}]: The Loyalists need Rhys\' information, Champion. Mathea out.', vid = 'X02_Mathea_M02_04009.sfd', bank = 'X02_VO', cue = 'X02_Mathea_M02_04009', faction = 'Aeon'},}
Mathea_5 =       {{text = '[{i Mathea}]: The defenses are down, and we are moving on Rhys\' location. Thank you for your assistance; he will soon be telling us everything he knows ... whether he wants to or not. Mathea out.', vid = 'X02_Mathea_M02_03161.sfd', bank = 'X02_VO', cue = 'X02_Mathea_M02_03161', faction = 'Aeon'},}

-- Aeon Min
Min_1 =          {{text = '[{i Min}]: Attention Station Lima Foxtrot. This is Templar Min of the Aeon Illuminate. We have no intention of harming you. Surrender now and avoid unnecessary bloodshed.', vid = 'E02_Min_M01_0079.sfd', bank = 'E02_VO', cue = 'E02_Min_M01_0079', faction = 'Aeon'},}
Min_2 =          {{text = '[{i Min}]: You were given the opportunity to save yourselves. You will now suffer the wrath of the Illuminate.', vid = 'E02_Min_M02_0080.sfd', bank = 'E02_VO', cue = 'E02_Min_M02_0080', faction = 'Aeon'},}
Min_3 =          {{text = '[{i Min}]: Why do you resist? Lay down your arms and accept your fate.', vid = 'E02_Min_M02_0081.sfd', bank = 'E02_VO', cue = 'E02_Min_M02_0081', faction = 'Aeon'},}
Min_4 =          {{text = '[{i Min}]: It appears that I\'ve underestimated you, Commander. Now you will experience the fury of the Aeon.', vid = 'E02_Min_M04_0082.sfd', bank = 'E02_VO', cue = 'E02_Min_M04_0082', faction = 'Aeon'},}
Min_5 =          {{text = '[{i Min}]: You may have defeated me, but my spirit lives on in The Way! ', vid = 'E02_Min_M04_0083.sfd', bank = 'E02_VO', cue = 'E02_Min_M04_0083', faction = 'Aeon'},}
Min_6 =          {{text = '[{i Min}]: Embrace The Way or be cleansed.', vid = 'E02_Min_T01_0084.sfd', bank = 'E02_VO', cue = 'E02_Min_T01_0084', faction = 'Aeon'},}
Min_7 =          {{text = '[{i Min}]: You are destroying the galaxy with your hatred.', vid = 'E02_Min_T01_0085.sfd', bank = 'E02_VO', cue = 'E02_Min_T01_0085', faction = 'Aeon'},}
Min_8 =          {{text = '[{i Min}]: You will answer for the crimes committed by the UEF.', vid = 'E02_Min_T01_0086.sfd', bank = 'E02_VO', cue = 'E02_Min_T01_0086', faction = 'Aeon'},}
Min_9 =          {{text = '[{i Min}]: We are the galaxy\'s salvation. Accept our teachings.', vid = 'E02_Min_T01_0087.sfd', bank = 'E02_VO', cue = 'E02_Min_T01_0087', faction = 'Aeon'},}
Min_10 =         {{text = '[{i Min}]: The Way must be spread to all of humanity!', vid = 'E02_Min_T01_0088.sfd', bank = 'E02_VO', cue = 'E02_Min_T01_0088', faction = 'Aeon'},}
Min_11 =         {{text = '[{i Min}]: I have no wish to kill you.', vid = 'E02_Min_T01_0089.sfd', bank = 'E02_VO', cue = 'E02_Min_T01_0089', faction = 'Aeon'},}
Min_12 =         {{text = '[{i Min}]: Embrace the Way and become a Knight of the Illuminate!', vid = 'E02_Min_T01_0090.sfd', bank = 'E02_VO', cue = 'E02_Min_T01_0090', faction = 'Aeon'},}
Min_13 =         {{text = '[{i Min}]: The Princess has foreseen my victory.', vid = 'E02_Min_T01_0091.sfd', bank = 'E02_VO', cue = 'E02_Min_T01_0091', faction = 'Aeon'},}
Min_14 =         {{text = '[{i Min}]: You may have defeated me, but my spirit lives on in The Way! ', vid = 'C01_Min_M08_0364.sfd', bank = 'C01_VO', cue = 'C01_Min_M08_0364', faction = 'Aeon'},}
Min_15 =         {{text = '[{i Min}]: You are an abomination.', vid = 'C01_Min_T01_0328.sfd', bank = 'C01_VO', cue = 'C01_Min_T01_0328', faction = 'Aeon'},}
Min_16 =         {{text = '[{i Min}]: I will cleanse every Symbiont on this planet.', vid = 'C01_Min_T01_0329.sfd', bank = 'C01_VO', cue = 'C01_Min_T01_0329', faction = 'Aeon'},}
Min_17 =         {{text = '[{i Min}]: Your reliance on technology will be your undoing.', vid = 'C01_Min_T01_0330.sfd', bank = 'C01_VO', cue = 'C01_Min_T01_0330', faction = 'Aeon'},}
Min_18 =         {{text = '[{i Min}]: We are humanity\'s only hope.', vid = 'C01_Min_T01_0331.sfd', bank = 'C01_VO', cue = 'C01_Min_T01_0331', faction = 'Aeon'},}
Min_19 =         {{text = '[{i Min}]: The Way must be spread to all of humanity!', vid = 'C01_Min_T01_0332.sfd', bank = 'C01_VO', cue = 'C01_Min_T01_0332', faction = 'Aeon'},}
Min_20 =         {{text = '[{i Min}]: You cannot stand against me!', vid = 'C01_Min_T01_0333.sfd', bank = 'C01_VO', cue = 'C01_Min_T01_0333', faction = 'Aeon'},}
Min_21 =         {{text = '[{i Min}]: Someday I will find Brackman...and the Cybrans will be no more.', vid = 'C01_Min_T01_0334.sfd', bank = 'C01_VO', cue = 'C01_Min_T01_0334', faction = 'Aeon'},}
Min_22 =         {{text = '[{i Min}]: I underestimated you. I won\'t do so again.', vid = 'C01_Min_T01_0335.sfd', bank = 'C01_VO', cue = 'C01_Min_T01_0335', faction = 'Aeon'},}

-- Uef Nakamura
Nakamura_1 =     {{text = '[{i Nakamura}]: Lieutenant, this is Constable Nakamura of Luthien. We\'ll be ready to evacuate once you get some escorts down here. They\'re going to hit us hard, so you\'ll need some tanks and Mobile Anti-Air units. Make sure they\'re escorted by Gunships. Luthien out.', vid = 'E02_Nakamura_M03_0092.sfd', bank = 'E02_VO', cue = 'E02_Nakamura_M03_0092', faction = 'UEF'},}
Nakamura_2 =     {{text = '[{i Nakamura}]: We\'re still waiting on those escorts, Lieutenant. Luthien out.', vid = 'E02_Nakamura_M03_0093.sfd', bank = 'E02_VO', cue = 'E02_Nakamura_M03_0093', faction = 'UEF'},}
Nakamura_3 =     {{text = '[{i Nakamura}]: Lieutenant, the trucks have exited the Colony. They are now under your control. Luthien out.', vid = 'E02_Nakamura_M03_0094.sfd', bank = 'E02_VO', cue = 'E02_Nakamura_M03_0094', faction = 'UEF'},}
Nakamura_4 =     {{text = '[{i Nakamura}]: One of the trucks is blocked, Commander. We need you to clear the way. Luthien out.', vid = 'E02_Nakamura_M03_0095.sfd', bank = 'E02_VO', cue = 'E02_Nakamura_M03_0095', faction = 'UEF'},}
Nakamura_5 =     {{text = '[{i Nakamura}]: The Aeon found the trucks! They\'re attacking!', vid = 'E02_Nakamura_M03_0096.sfd', bank = 'E02_VO', cue = 'E02_Nakamura_M03_0096', faction = 'UEF'},}
Nakamura_6 =     {{text = '[{i Nakamura}]: The Aeon have destroyed a truck...all those poor people...', vid = 'E02_Nakamura_M03_0097.sfd', bank = 'E02_VO', cue = 'E02_Nakamura_M03_0097', faction = 'UEF'},}
Nakamura_7 =     {{text = '[{i Nakamura}]: We\'ve lost more trucks, Lieutenant! We need more protection! Luthien out.', vid = 'E02_Nakamura_M03_0098.sfd', bank = 'E02_VO', cue = 'E02_Nakamura_M03_0098', faction = 'UEF'},}
Nakamura_8 =     {{text = '[{i Nakamura}]: We\'ve lost too many trucks! We can\'t afford to lose anymore!', vid = 'E02_Nakamura_M03_0099.sfd', bank = 'E02_VO', cue = 'E02_Nakamura_M03_0099', faction = 'UEF'},}
Nakamura_9 =     {{text = '[{i Nakamura}]: Another group of trucks is ready to be escorted, Lieutenant. Luthien out.', vid = 'E02_Nakamura_M03_01335.sfd', bank = 'E02_VO', cue = 'E02_Nakamura_M03_01335', faction = 'UEF'},}
Nakamura_10 =    {{text = '[{i Nakamura}]: Lieutenant, the convoy is ready to go. We\'re just waiting for your units to arrive. Luthien out.', vid = 'E02_Nakamura_M03_01458.sfd', bank = 'E02_VO', cue = 'E02_Nakamura_M03_01458', faction = 'UEF'},}
Nakamura_11 =    {{text = '[{i Nakamura}]: You need to get those trucks to the Station soon, Lieutenant. The longer they\'re out there, the more likely the Aeon are to find them.', vid = 'E02_Nakamura_M03_01459.sfd', bank = 'E02_VO', cue = 'E02_Nakamura_M03_01459', faction = 'UEF'},}

