Ops_1 =          {{text = '[{i Ops}]: Commander? What\'s your status? Commander?', vid = 'C01_Ops_D01_0003.sfd', bank = 'C01_VO', cue = 'C01_Ops_D01_0003', faction = 'Cybran'},}
