Ops_93 =         {{text = '[{i Ops}]: The EMP burst was successful! Go! Go! Go!', vid = 'C04_Ops_M03_0492.sfd', bank = 'C04_VO', cue = 'C04_Ops_M03_0492', faction = 'Cybran'},}
Ops_94 =         {{text = '[{i Ops}]: You still need to capture the Nodes, sir. Ops out. ', vid = 'C04_Ops_M03_00618.sfd', bank = 'C04_VO', cue = 'C04_Ops_M03_00618', faction = 'Cybran'},}
Ops_95 =         {{text = '[{i Ops}]: We can\'t get rid of the Aeon until you capture those Nodes, sir. Ops out.', vid = 'C04_Ops_M03_01518.sfd', bank = 'C04_VO', cue = 'C04_Ops_M03_01518', faction = 'Cybran'},}
Ops_96 =         {{text = '[{i Ops}]: The Mainframe still needs to be liberated from the Aeon, sir. Ops out. ', vid = 'C04_Ops_M03_00620.sfd', bank = 'C04_VO', cue = 'C04_Ops_M03_00620', faction = 'Cybran'},}
Ops_97 =         {{text = '[{i Ops}]: Defeat the Aeon forces around the Mainframe ASAP, sir. Ops out. ', vid = 'C04_Ops_M03_01519.sfd', bank = 'C04_VO', cue = 'C04_Ops_M03_01519', faction = 'Cybran'},}
Ops_98 =         {{text = '[{i Ops}]: Commander? What\'s your status? Commander?', vid = 'C05_Ops_D01_02594.sfd', bank = 'C05_VO', cue = 'C05_Ops_D01_02594', faction = 'Cybran'},}
Ops_99 =         {{text = '[{i Ops}]: You know your objectives, Commander. Establish a defensive perimeter and start building units as rapidly as possible. Do not attack the UEF until you have a large army. Ops out.', vid = 'C05_Ops_M01_00503.sfd', bank = 'C05_VO', cue = 'C05_Ops_M01_00503', faction = 'Cybran'},}
Ops_100 =        {{text = '[{i Ops}]: Move on the Power Generators whenever you\'re ready. Ops out.', vid = 'C05_Ops_M01_00504.sfd', bank = 'C05_VO', cue = 'C05_Ops_M01_00504', faction = 'Cybran'},}
Ops_101 =        {{text = '[{i Ops}]: Sir, the UEF has naval units patrolling the lake to your west. Destroy them if the opportunity presents itself. Ops out.', vid = 'C05_Ops_M01_00505.sfd', bank = 'C05_VO', cue = 'C05_Ops_M01_00505', faction = 'Cybran'},}
Ops_102 =        {{text = '[{i Ops}]: All right, it looks like you\'ve cleared the lake of enemy units. The water is yours for now. Ops out.', vid = 'C05_Ops_M01_00506.sfd', bank = 'C05_VO', cue = 'C05_Ops_M01_00506', faction = 'Cybran'},}
Ops_103 =        {{text = '[{i Ops}]: You\'ve been spotted! Get ready for an attack! Ops out.', vid = 'C05_Ops_M01_00507.sfd', bank = 'C05_VO', cue = 'C05_Ops_M01_00507', faction = 'Cybran'},}
Ops_104 =        {{text = '[{i Ops}]: The shields are down, Hex5. You\'re clear to enter the prison. Ops out.', vid = 'C05_Ops_M01_00509.sfd', bank = 'C05_VO', cue = 'C05_Ops_M01_00509', faction = 'Cybran'},}
Ops_105 =        {{text = '[{i Ops}]: Hex5 is still waiting for those Generators to go offline, Commander. Ops out. ', vid = 'C05_Ops_M01_00604.sfd', bank = 'C05_VO', cue = 'C05_Ops_M01_00604', faction = 'Cybran'},}
Ops_106 =        {{text = '[{i Ops}]: The Generators are still online, sir. We need to get those down ASAP. Ops out.', vid = 'C05_Ops_M01_01520.sfd', bank = 'C05_VO', cue = 'C05_Ops_M01_01520', faction = 'Cybran'},}
Ops_107 =        {{text = '[{i Ops}]: This keeps getting better and better. There are three Omni-Sensors in the area: to the south; the southwest; and northwest. Destroy them. We need those codes! Ops out.', vid = 'C05_Ops_M02_00512.sfd', bank = 'C05_VO', cue = 'C05_Ops_M02_00512', faction = 'Cybran'},}
Ops_108 =        {{text = '[{i Ops}]: Because of the UEF\'s reliance on Gunships, you should counter with Air Superiority Fighters. The schematic for the \'Gemini\' Tech 3 Air Superiority Fighter has uploaded. Ops out.', vid = 'C05_Ops_M02_00527.sfd', bank = 'C05_VO', cue = 'C05_Ops_M02_00527', faction = 'Cybran'},}
Ops_109 =        {{text = '[{i Ops}]: The UEF\'s naval base has been destroyed. Nice job. Ops out.', vid = 'C05_Ops_M02_00516.sfd', bank = 'C05_VO', cue = 'C05_Ops_M02_00516', faction = 'Cybran'},}
Ops_110 =        {{text = '[{i Ops}]: That\'s one Omni-Sensor down. Ops out.', vid = 'C05_Ops_M02_00517.sfd', bank = 'C05_VO', cue = 'C05_Ops_M02_00517', faction = 'Cybran'},}
Ops_111 =        {{text = '[{i Ops}]: Two Omnis are down, Commander. Ops out.', vid = 'C05_Ops_M02_00518.sfd', bank = 'C05_VO', cue = 'C05_Ops_M02_00518', faction = 'Cybran'},}
Ops_112 =        {{text = '[{i Ops}]: The Omni-Sensors are down. Hex5 should be contacting you soon. Ops out.', vid = 'C05_Ops_M02_00519.sfd', bank = 'C05_VO', cue = 'C05_Ops_M02_00519', faction = 'Cybran'},}
Ops_113 =        {{text = '[{i Ops}]: Move your ACU to Hex5\'s position and get the codes, Commander. We only get one shot at this! Ops out.', vid = 'C05_Ops_M02_00521.sfd', bank = 'C05_VO', cue = 'C05_Ops_M02_00521', faction = 'Cybran'},}
Ops_114 =        {{text = '[{i Ops}]: Detecting UEF Gunships, Commander! They\'re vectoring in from the southeast and heading directly toward your base.', vid = 'C05_Ops_M02_00523.sfd', bank = 'C05_VO', cue = 'C05_Ops_M02_00523', faction = 'Cybran'},}
Ops_115 =        {{text = '[{i Ops}]: Hex5 is waiting for the Omnis to go offline, sir. Ops out. ', vid = 'C05_Ops_M02_00606.sfd', bank = 'C05_VO', cue = 'C05_Ops_M02_00606', faction = 'Cybran'},}
Ops_116 =        {{text = '[{i Ops}]: The sooner those Omnis are destroyed, the sooner you can get off that planet. Ops out.', vid = 'C05_Ops_M02_01521.sfd', bank = 'C05_VO', cue = 'C05_Ops_M02_01521', faction = 'Cybran'},}
Ops_117 =        {{text = '[{i Ops}]: Hex5 is waiting to deliver the codes, sir. Ops out. ', vid = 'C05_Ops_M02_00608.sfd', bank = 'C05_VO', cue = 'C05_Ops_M02_00608', faction = 'Cybran'},}
Ops_118 =        {{text = '[{i Ops}]: Dr. Brackman is eager to get those codes, sir. Go meet Hex5 ASAP. Ops out. ', vid = 'C05_Ops_M02_01522.sfd', bank = 'C05_VO', cue = 'C05_Ops_M02_01522', faction = 'Cybran'},}
Ops_119 =        {{text = '[{i Ops}]: The Gunships have been routed and the codes are secure. Let\'s get you off that rock. Ops out.', vid = 'C05_Ops_M02_00526.sfd', bank = 'C05_VO', cue = 'C05_Ops_M02_00526', faction = 'Cybran'},}
Ops_120 =        {{text = '[{i Ops}]: We have a situation. Hex5 was captured by UEF forces and is being held in their primary base.', vid = 'C05_Ops_M03_00528.sfd', bank = 'C05_VO', cue = 'C05_Ops_M03_00528', faction = 'Cybran'},}
Ops_121 =        {{text = '[{i Ops}]: You did it, sir! The Gunships are self-destructing!', vid = 'C05_Ops_M03_00535.sfd', bank = 'C05_VO', cue = 'C05_Ops_M03_00535', faction = 'Cybran'},}
Ops_122 =        {{text = '[{i Ops}]: That\'s the prison complex, sir. Don\'t worry about it, Hex5 is being held in the main UEF base. Ops out.', vid = 'C05_Ops_M03_00538.sfd', bank = 'C05_VO', cue = 'C05_Ops_M03_00538', faction = 'Cybran'},}
Ops_123 =        {{text = '[{i Ops}]: Hex5 has been killed, sir. At least we still got the codes. Come on home, Ops out.', vid = 'C05_Ops_M03_00540.sfd', bank = 'C05_VO', cue = 'C05_Ops_M03_00540', faction = 'Cybran'},}
Ops_124 =        {{text = '[{i Ops}]: You\'re done here, Commander. Return to base with those codes. We\'ll extract Hex5. Ops out.', vid = 'C05_Ops_M03_00542.sfd', bank = 'C05_VO', cue = 'C05_Ops_M03_00542', faction = 'Cybran'},}
Ops_125 =        {{text = '[{i Ops}]: We can\'t let the UEF know of our plans! Destroy that Commander ASAP! Ops out. ', vid = 'C05_Ops_M03_00610.sfd', bank = 'C05_VO', cue = 'C05_Ops_M03_00610', faction = 'Cybran'},}
Ops_126 =        {{text = '[{i Ops}]: The longer that UEF Commander is around, the more likely he is to figure out why Hex5 was here. Kill the Commander. Ops out.', vid = 'C05_Ops_M03_01523.sfd', bank = 'C05_VO', cue = 'C05_Ops_M03_01523', faction = 'Cybran'},}
Ops_127 =        {{text = '[{i Ops}]: Commander? What\'s your status? Commander?', vid = 'C06_Ops_D01_02595.sfd', bank = 'C06_VO', cue = 'C06_Ops_D01_02595', faction = 'Cybran'},}
Ops_128 =        {{text = '[{i Ops}]: Sir, it looks like the Aeon are building some sort of...flying saucer. We don\'t know what it\'s capable of, but it\'s a safe bet it\'ll attack Black Sun. Destroy it before it can take off. It cannot be allowed to reach Black Sun! Ops out.', vid = 'C06_Ops_M01_00560.sfd', bank = 'C06_VO', cue = 'C06_Ops_M01_00560', faction = 'Cybran'},}
Ops_129 =        {{text = '[{i Ops}]: We have managed to tap into the UEF\'s communication system. Black Sun isn\'t finished, but it\'s close. We will update as the situation warrants.', vid = 'C06_Ops_M01_01108.sfd', bank = 'C06_VO', cue = 'C06_Ops_M01_01108', faction = 'Cybran'},}
Ops_130 =        {{text = '[{i Ops}]: The Aeon saucer is taking off and vectoring toward the Black Sun Control Center. Destroy it!', vid = 'C06_Ops_M01_00564.sfd', bank = 'C06_VO', cue = 'C06_Ops_M01_00564', faction = 'Cybran'},}
Ops_131 =        {{text = '[{i Ops}]: The saucer is almost to the Control Center! TAKE IT OUT!', vid = 'C06_Ops_M01_00565.sfd', bank = 'C06_VO', cue = 'C06_Ops_M01_00565', faction = 'Cybran'},}
Ops_132 =        {{text = '[{i Ops}]: Sir, you have to take out the saucer! Ops out. ', vid = 'C06_Ops_M01_00566.sfd', bank = 'C06_VO', cue = 'C06_Ops_M01_00566', faction = 'Cybran'},}
Ops_133 =        {{text = '[{i Ops}]: Everything is riding on Black Sun, sir! We can\'t let the Aeon saucer destroy it! Ops out.', vid = 'C06_Ops_M01_01525.sfd', bank = 'C06_VO', cue = 'C06_Ops_M01_01525', faction = 'Cybran'},}
Ops_134 =        {{text = '[{i Ops}]: I would suggest you check your objectives, Commander. Ops out.', vid = 'C06_Ops_M01_01535.sfd', bank = 'C06_VO', cue = 'C06_Ops_M01_01535', faction = 'Cybran'},}
Ops_135 =        {{text = '[{i Ops}]: YES! The Aeon saucer is down!', vid = 'C06_Ops_M01_00570.sfd', bank = 'C06_VO', cue = 'C06_Ops_M01_00570', faction = 'Cybran'},}
Ops_136 =        {{text = '[{i Ops}]: Commander, because of the bandwidth demands, you need to build a Quantum Gate before we can upload the QAI into your ACU. The upload will take some time, so make sure the area around the Gate is secure. Ops out.', vid = 'C06_Ops_M02_01127.sfd', bank = 'C06_VO', cue = 'C06_Ops_M02_01127', faction = 'Cybran'},}
Ops_137 =        {{text = '[{i Ops}]: Black Sun is nearly complete! They have to charge it before it can fire, but time is against us!', vid = 'C06_Ops_M02_01128.sfd', bank = 'C06_VO', cue = 'C06_Ops_M02_01128', faction = 'Cybran'},}
Ops_138 =        {{text = '[{i Ops}]: Sensors are indicating that UEF forces are moving north and establishing a defensive line. You must break through their position in order to reach the Control Center. Ops out.', vid = 'C06_Ops_M02_00574.sfd', bank = 'C06_VO', cue = 'C06_Ops_M02_00574', faction = 'Cybran'},}
Ops_139 =        {{text = '[{i Ops}]: The Quantum Gate was destroyed! You have to build another one before we can upload QAI! Ops out.', vid = 'C06_Ops_M02_00575.sfd', bank = 'C06_VO', cue = 'C06_Ops_M02_00575', faction = 'Cybran'},}
Ops_140 =        {{text = '[{i Ops}]: Okay, Commander, move your ACU next to the Gate. The upload will begin automatically. Do not move away from the Gate until you are notified that the upload is complete. Ops out.', vid = 'C06_Ops_M02_00576.sfd', bank = 'C06_VO', cue = 'C06_Ops_M02_00576', faction = 'Cybran'},}
Ops_141 =        {{text = '[{i Ops}]: The upload has started, Commander. The Gate must survive, otherwise the upload will have to start again. Defend your position! Ops out.', vid = 'C06_Ops_M02_00577.sfd', bank = 'C06_VO', cue = 'C06_Ops_M02_00577', faction = 'Cybran'},}
Ops_142 =        {{text = '[{i Ops}]: Sir, the upload was interrupted! Move your ACU back to the Gate to restart it. Ops out.', vid = 'C06_Ops_M02_00578.sfd', bank = 'C06_VO', cue = 'C06_Ops_M02_00578', faction = 'Cybran'},}
Ops_143 =        {{text = '[{i Ops}]: Incoming UEF forces! Ops out.', vid = 'C06_Ops_M02_00581.sfd', bank = 'C06_VO', cue = 'C06_Ops_M02_00581', faction = 'Cybran'},}
Ops_144 =        {{text = '[{i Ops}]: Aeon forces are vectoring towards your base! Get ready! Ops out.', vid = 'C06_Ops_M02_00582.sfd', bank = 'C06_VO', cue = 'C06_Ops_M02_00582', faction = 'Cybran'},}
Ops_145 =        {{text = '[{i Ops}]: You did it! Once QAI finishes its integration, you can fire Black Sun. Ops out.', vid = 'C06_Ops_M02_00584.sfd', bank = 'C06_VO', cue = 'C06_Ops_M02_00584', faction = 'Cybran'},}
Ops_146 =        {{text = '[{i Ops}]: We need that Gate built so we can upload the QAI, sir. Ops out. ', vid = 'C06_Ops_M02_00585.sfd', bank = 'C06_VO', cue = 'C06_Ops_M02_00585', faction = 'Cybran'},}
Ops_147 =        {{text = '[{i Ops}]: Where\'s the Gate? We\'re running out of time. Ops out.', vid = 'C06_Ops_M02_01526.sfd', bank = 'C06_VO', cue = 'C06_Ops_M02_01526', faction = 'Cybran'},}
Ops_148 =        {{text = '[{i Ops}]: Move your ACU next to the Gate to start the download, Commander. Ops out. ', vid = 'C06_Ops_M02_00587.sfd', bank = 'C06_VO', cue = 'C06_Ops_M02_00587', faction = 'Cybran'},}
Ops_149 =        {{text = '[{i Ops}]: Sir, we need to start the upload! Move your ACU next to the Gate and we\'ll start immediately. Ops out. ', vid = 'C06_Ops_M02_01527.sfd', bank = 'C06_VO', cue = 'C06_Ops_M02_01527', faction = 'Cybran'},}
Ops_150 =        {{text = '[{i Ops}]: You must capture the Control Center and get QAI downloaded ASAP! Ops out. ', vid = 'C06_Ops_M02_00589.sfd', bank = 'C06_VO', cue = 'C06_Ops_M02_00589', faction = 'Cybran'},}
Ops_151 =        {{text = '[{i Ops}]: Sir, we need to start the upload! Move your ACU next to the Gate and we\'ll start immediately. Ops out. ', vid = 'C06_Ops_M02_01528.sfd', bank = 'C06_VO', cue = 'C06_Ops_M02_01528', faction = 'Cybran'},}
Ops_152 =        {{text = '[{i Ops}]: All that stands between us and victory is you capturing Black Sun, sir. Ops out. ', vid = 'C06_Ops_M03_00599.sfd', bank = 'C06_VO', cue = 'C06_Ops_M03_00599', faction = 'Cybran'},}
Ops_153 =        {{text = '[{i Ops}]: You need to capture Black Sun, sir! QAI still needs to reconfigure it! Ops out.', vid = 'C06_Ops_M03_01529.sfd', bank = 'C06_VO', cue = 'C06_Ops_M03_01529', faction = 'Cybran'},}
Ops_154 =        {{text = '[{i Ops}]: Fire Black Sun! Fire it now! ', vid = 'C06_Ops_M03_00601.sfd', bank = 'C06_VO', cue = 'C06_Ops_M03_00601', faction = 'Cybran'},}
Ops_155 =        {{text = '[{i Ops}]: We\'re so close! Just fire Black Sun and we\'ll win!', vid = 'C06_Ops_M03_01530.sfd', bank = 'C06_VO', cue = 'C06_Ops_M03_01530', faction = 'Cybran'},}


-- Sera OumEoshi
OumEoshi_1 =     {{text = '[{i OumEoshi}]: The great Commander Dostya has fallen. You are next.', vid = 'X04_Oum-Eoshi_M03_03758.sfd', bank = 'X04_VO', cue = 'X04_Oum-Eoshi_M03_03758', faction = 'Seraphim'},}
OumEoshi_2 =     {{text = '[{i OumEoshi}]: Only now do you realize the futility of your situation. We know what you know, we see what you see. There is no stopping us.', vid = 'X04_Oum-Eoshi_M03_04402.sfd', bank = 'X04_VO', cue = 'X04_Oum-Eoshi_M03_04402', faction = 'Seraphim'},}
OumEoshi_3 =     {{text = '[{i OumEoshi}]: Humanity\'s time is at an end. You will be rendered extinct.', vid = 'X04_Oum-Eoshi_M03_04404.sfd', bank = 'X04_VO', cue = 'X04_Oum-Eoshi_M03_04404', faction = 'Seraphim'},}
OumEoshi_4 =     {{text = '[{i OumEoshi}]: You will share the fate of Riley and Clarke. Goodbye, Colonel.', vid = 'X04_Oum-Eoshi_M03_03767.sfd', bank = 'X04_VO', cue = 'X04_Oum-Eoshi_M03_03767', faction = 'Seraphim'},}
OumEoshi_5 =     {{text = '[{i OumEoshi}]: Watching the Princess\' Champion die will fill me with immeasurable joy.', vid = 'X04_Oum-Eoshi_M03_03769.sfd', bank = 'X04_VO', cue = 'X04_Oum-Eoshi_M03_03769', faction = 'Seraphim'},}
OumEoshi_6 =     {{text = '[{i OumEoshi}]: Your experimental units are actually troublesome. It will be destroyed before it is completed.', vid = 'X04_Oum-Eoshi_T01_04788.sfd', bank = 'X04_VO', cue = 'X04_Oum-Eoshi_T01_04788', faction = 'Seraphim'},}
OumEoshi_7 =     {{text = '[{i OumEoshi}]: I admire your tenacity, but I will not allow you to complete that experimental unit.', vid = 'X04_Oum-Eoshi_T01_04789.sfd', bank = 'X04_VO', cue = 'X04_Oum-Eoshi_T01_04789', faction = 'Seraphim'},}
OumEoshi_8 =     {{text = '[{i OumEoshi}]: Your doom is without question, yet I cannot allow you to finish that experimental.', vid = 'X04_Oum-Eoshi_T01_04790.sfd', bank = 'X04_VO', cue = 'X04_Oum-Eoshi_T01_04790', faction = 'Seraphim'},}
OumEoshi_9 =     {{text = '[{i OumEoshi}]: Your galaxy will soon be ours.', vid = 'X04_Oum-Eoshi_T01_04383.sfd', bank = 'X04_VO', cue = 'X04_Oum-Eoshi_T01_04383', faction = 'Seraphim'},}
OumEoshi_10 =    {{text = '[{i OumEoshi}]: Only one species can attain perfection.', vid = 'X04_Oum-Eoshi_T01_04384.sfd', bank = 'X04_VO', cue = 'X04_Oum-Eoshi_T01_04384', faction = 'Seraphim'},}
OumEoshi_11 =    {{text = '[{i OumEoshi}]: Do not fret. Dying by my hand is the supreme honor.', vid = 'X04_Oum-Eoshi_T01_04385.sfd', bank = 'X04_VO', cue = 'X04_Oum-Eoshi_T01_04385', faction = 'Seraphim'},}
OumEoshi_12 =    {{text = '[{i OumEoshi}]: Soon there will be more of us than you can possibly ever hope to defeat.', vid = 'X04_Oum-Eoshi_T01_04386.sfd', bank = 'X04_VO', cue = 'X04_Oum-Eoshi_T01_04386', faction = 'Seraphim'},}
