OumEoshi_12 =    {{text = '[{i OumEoshi}]: Soon there will be more of us than you can possibly ever hope to defeat.', bank = 'X04_VO', cue = 'X04_Oum-Eoshi_T01_04386'},}

-- Aeon Princess
Princess_1 =     {{text = '[{i Princess}]: Hear me, people of the Illuminate! I am Princess Rhianne Burke, rightful leader of the Aeon. The Seraphim are not the saviors they claim to be. They bring nothing save death and destruction!', vid = 'X02_Princess_M02_04281.sfd', bank = 'X02_VO', cue = 'X02_Princess_M02_04281', faction = 'Aeon'},}
Princess_2 =     {{text = '[{i Princess}]: The Order has betrayed you. Evaluator Kael seeks nothing but absolute power, and she will sacrifice anyone that dares stand in her way. The time has come to rise up and reject her teachings, to fight against those that seek to destroy all of humanity. Rise and fight in the name of your Princess!', vid = 'X02_Princess_M02_04283.sfd', bank = 'X02_VO', cue = 'X02_Princess_M02_04283', faction = 'Aeon'},}
Princess_3 =     {{text = '[{i Princess}]: I must admit that I feared for my life when the Seraphim landed. They are maniacal in their desire to see me dead, but I now understand how they are to be defeated. I will meet with General Hall and Dr. Brackman to begin preparations. You will learn more when the time has come.', vid = 'X03_Princess__04022.sfd', bank = 'Briefings', cue = 'X03_Princess__04022', faction = 'Aeon'},}
Princess_4 =     {{text = '[{i Princess}]: I am honored that you are fighting on my behalf, but please make haste. My palace defenses will not be able to hold off the Seraphim much longer.', vid = 'X03_Princess_M01_03287.sfd', bank = 'X03_VO', cue = 'X03_Princess_M01_03287', faction = 'Aeon'},}
Princess_5 =     {{text = '[{i Princess}]: My beloved Champion. I am ecstatic that you have arrived, but please make haste. My palace defenses will not be able to hold off the Seraphim much longer.', vid = 'X03_Princess_M01_03288.sfd', bank = 'X03_VO', cue = 'X03_Princess_M01_03288', faction = 'Aeon'},}
Princess_6 =     {{text = '[{i Princess}]: It is Blue Sky\'s crystals, Commander. Their unique structure reflects radar waves, creating an effect similar to jamming. Omni radar will quell the effect, as will destroying the crystals.', vid = 'X03_Princess_M01_04354.sfd', bank = 'X03_VO', cue = 'X03_Princess_M01_04354', faction = 'Aeon'},}
Princess_7 =     {{text = '[{i Princess}]: My people reject you, Kael, turn their back on your hatred and lies. It\'s only a matter of time now.', vid = 'X03_Princess_M01_03303.sfd', bank = 'X03_VO', cue = 'X03_Princess_M01_03303', faction = 'Aeon'},}
Princess_8 =     {{text = '[{i Princess}]: Commander, I am getting some strange readings from my northwest. The Seraphim commander appears to be amassing a fleet of strange units. I do not know what they are, but their size suggests that they are capable of inflicting terrible damage.', vid = 'X03_Princess_M02_03282.sfd', bank = 'X03_VO', cue = 'X03_Princess_M02_03282', faction = 'Aeon'},}
Princess_9 =     {{text = '[{i Princess}]: Commander, I am getting some strange readings from my southwest. The Seraphim commander appears to be amassing a fleet of strange units. I do not know what they are, but their size suggests that they are capable of inflicting terrible damage.', vid = 'X03_Princess_M02_05124.sfd', bank = 'X03_VO', cue = 'X03_Princess_M02_05124', faction = 'Aeon'},}
Princess_10 =    {{text = '[{i Princess}]: Your only plan is to sow death and destruction. You were always too foolish to look beyond your own petty desires.', vid = 'X03_Princess_M02_03310.sfd', bank = 'X03_VO', cue = 'X03_Princess_M02_03310', faction = 'Aeon'},}
Princess_11 =    {{text = '[{i Princess}]: You lie.', vid = 'X03_Princess_M02_03312.sfd', bank = 'X03_VO', cue = 'X03_Princess_M02_03312', faction = 'Aeon'},}
Princess_12 =    {{text = '[{i Princess}]: I swear, Kael, I will see you answer for your crimes!', vid = 'X03_Princess_M02_03314.sfd', bank = 'X03_VO', cue = 'X03_Princess_M02_03314', faction = 'Aeon'},}
Princess_13 =    {{text = '[{i Princess}]: Commanders, I\'m detecting some extremely alarming readings from the Seraphim base to your northeast. It\'s amassing a fleet of strange units, and their size suggests that they are capable of inflicting terrible damage.', vid = 'X03_Princess_M02_04868.sfd', bank = 'X03_VO', cue = 'X03_Princess_M02_04868', faction = 'Aeon'},}
Princess_14 =    {{text = '[{i Princess}]: Commander, I have a Quantum Optics Device that will counter the crystals and reveal an area. Because of its Energy consumption, I can only use it every few minutes.', vid = 'X03_Princess_M02_04364.sfd', bank = 'X03_VO', cue = 'X03_Princess_M02_04364', faction = 'Aeon'},}
Princess_15 =    {{text = '[{i Princess}]: Thank you, Commander. Those bombers would have easily penetrated my defenses. All that remains now is to defeat the two Seraphim commanders.', vid = 'X03_Princess_M02_03317.sfd', bank = 'X03_VO', cue = 'X03_Princess_M02_03317', faction = 'Aeon'},}
Princess_16 =    {{text = '[{i Princess}]: I shall live on in The Way.', vid = 'X03_Princess_M02_04367.sfd', bank = 'X03_VO', cue = 'X03_Princess_M02_04367', faction = 'Aeon'},}
Princess_17 =    {{text = '[{i Princess}]: Thank you, Commander. The Seraphim bombers would have easily penetrated my defenses. All that remains now is to defeat the two Seraphim commanders.', vid = 'X03_Princess_M02_03320.sfd', bank = 'X03_VO', cue = 'X03_Princess_M02_03320', faction = 'Aeon'},}
Princess_18 =    {{text = '[{i Princess}]: My Champion, I do not wish to distract you from the fight at hand, but I must ask a favor. My most loyal and trusted servants have taken refuge in a small temple east of my complex. Please escort them to Rhiza\'s position for immediate evacuation. A boat has been prepared; it will ferry them to safety.', vid = 'X03_Princess_M02_03325.sfd', bank = 'X03_VO', cue = 'X03_Princess_M02_03325', faction = 'Aeon'},}
Princess_19 =    {{text = '[{i Princess}]: The boat is yours to command, Champion.', vid = 'X03_Princess_M02_03330.sfd', bank = 'X03_VO', cue = 'X03_Princess_M02_03330', faction = 'Aeon'},}
Princess_20 =    {{text = '[{i Princess}]: Protect them, Champion!', vid = 'X03_Princess_M02_03331.sfd', bank = 'X03_VO', cue = 'X03_Princess_M02_03331', faction = 'Aeon'},}
Princess_21 =    {{text = '[{i Princess}]: The boat is under attack!', vid = 'X03_Princess_M02_03332.sfd', bank = 'X03_VO', cue = 'X03_Princess_M02_03332', faction = 'Aeon'},}
Princess_22 =    {{text = '[{i Princess}]: Thank you, Champion. It is good to see that kindness prevails even in the midst of war.', vid = 'X03_Princess_M02_03333.sfd', bank = 'X03_VO', cue = 'X03_Princess_M02_03333', faction = 'Aeon'},}
Princess_23 =    {{text = '[{i Princess}]: They\'re ... they\'re dead ... I shall forever mourn their loss.', vid = 'X03_Princess_M02_03334.sfd', bank = 'X03_VO', cue = 'X03_Princess_M02_03334', faction = 'Aeon'},}
Princess_24 =    {{text = '[{i Princess}]: Please rescue my servants, Champion.', vid = 'X03_Princess_M02_03328.sfd', bank = 'X03_VO', cue = 'X03_Princess_M02_03328', faction = 'Aeon'},}
Princess_25 =    {{text = '[{i Princess}]: The servants are ready and await your arrival, Champion.', vid = 'X03_Princess_M02_03329.sfd', bank = 'X03_VO', cue = 'X03_Princess_M02_03329', faction = 'Aeon'},}
Princess_26 =    {{text = '[{i Princess}]: Champion, my servants will temporarily suppress the defenses in the area.', vid = 'X03_Princess_M02_04917.sfd', bank = 'X03_VO', cue = 'X03_Princess_M02_04917', faction = 'Aeon'},}
Princess_27 =    {{text = '[{i Princess}]: Commander, several of my generators have been destroyed and I need to conserve my remaining Energy for my shields. I apologize, but you must find some other way to counter the crystal jamming.', vid = 'X03_Princess_M03_04374.sfd', bank = 'X03_VO', cue = 'X03_Princess_M03_04374', faction = 'Aeon'},}
Princess_28 =    {{text = '[{i Princess}]: Commander, several of my generators have been destroyed and I need to conserve my remaining Energy for my shields. However, my technicians have prepared the Optical Device for upload; it is being sent to you as we speak.', vid = 'X03_Princess_M03_04375.sfd', bank = 'X03_VO', cue = 'X03_Princess_M03_04375', faction = 'Aeon'},}
Princess_29 =    {{text = '[{i Princess}]: The Seraphim have launched an attack! I fear that my defenses will not hold!', vid = 'X03_Princess_M03_04013.sfd', bank = 'X03_VO', cue = 'X03_Princess_M03_04013', faction = 'Aeon'},}
Princess_30 =    {{text = '[{i Princess}]: The Seraphim are hitting me from all directions! I fear that my defenses will not hold!', vid = 'X03_Princess_M03_03339.sfd', bank = 'X03_VO', cue = 'X03_Princess_M03_03339', faction = 'Aeon'},}
Princess_31 =    {{text = '[{i Princess}]: Please, Commander, you must assist me. I fear that I will not survive much longer.', vid = 'X03_Princess_M03_03342.sfd', bank = 'X03_VO', cue = 'X03_Princess_M03_03342', faction = 'Aeon'},}
Princess_32 =    {{text = '[{i Princess}]: Commander! The Seraphim are attacking. Stop them!', vid = 'X03_Princess_M03_03343.sfd', bank = 'X03_VO', cue = 'X03_Princess_M03_03343', faction = 'Aeon'},}
Princess_33 =    {{text = '[{i Princess}]: He is weakening, Commander!', vid = 'X03_Princess_M03_03346.sfd', bank = 'X03_VO', cue = 'X03_Princess_M03_03346', faction = 'Aeon'},}
Princess_34 =    {{text = '[{i Princess}]: The northern Seraphim is suffering grave losses! Keep pressing!', vid = 'X03_Princess_M03_03347.sfd', bank = 'X03_VO', cue = 'X03_Princess_M03_03347', faction = 'Aeon'},}
Princess_35 =    {{text = '[{i Princess}]: Hurry, Commander, the Seraphim to the north is almost defeated!', vid = 'X03_Princess_M03_03348.sfd', bank = 'X03_VO', cue = 'X03_Princess_M03_03348', faction = 'Aeon'},}
Princess_36 =    {{text = '[{i Princess}]: The Seraphim to the north has been defeated. Well done, Commander.', vid = 'X03_Princess_M03_03349.sfd', bank = 'X03_VO', cue = 'X03_Princess_M03_03349', faction = 'Aeon'},}
Princess_37 =    {{text = '[{i Princess}]: The western Seraphim has lost much of his infrastructure. Do not give him time to recover!', vid = 'X03_Princess_M03_03350.sfd', bank = 'X03_VO', cue = 'X03_Princess_M03_03350', faction = 'Aeon'},}
Princess_38 =    {{text = '[{i Princess}]: The western Seraphim cannot sustain many more losses! Keep up your attacks!', vid = 'X03_Princess_M03_03351.sfd', bank = 'X03_VO', cue = 'X03_Princess_M03_03351', faction = 'Aeon'},}
Princess_39 =    {{text = '[{i Princess}]: Hurry, the western Seraphim is on the verge of defeat!', vid = 'X03_Princess_M03_03352.sfd', bank = 'X03_VO', cue = 'X03_Princess_M03_03352', faction = 'Aeon'},}
Princess_40 =    {{text = '[{i Princess}]: It is no matter, we shall defeat that Seraphim commander another day.', vid = 'X03_Princess_M03_03353.sfd', bank = 'X03_VO', cue = 'X03_Princess_M03_03353', faction = 'Aeon'},}
Princess_41 =    {{text = '[{i Princess}]: My palace is taking damage!', vid = 'X03_Princess_M03_04704.sfd', bank = 'X03_VO', cue = 'X03_Princess_M03_04704', faction = 'Aeon'},}
Princess_42 =    {{text = '[{i Princess}]: My palace cannot withstand much more damage!', vid = 'X03_Princess_M03_04705.sfd', bank = 'X03_VO', cue = 'X03_Princess_M03_04705', faction = 'Aeon'},}
Princess_43 =    {{text = '[{i Princess}]: Hurry, Commander, my defenses are weakening!', vid = 'X03_Princess_M03_04706.sfd', bank = 'X03_VO', cue = 'X03_Princess_M03_04706', faction = 'Aeon'},}
Princess_44 =    {{text = '[{i Princess}]: You must hurry, Commander! My defenses will soon fall!', vid = 'X03_Princess_M03_04707.sfd', bank = 'X03_VO', cue = 'X03_Princess_M03_04707', faction = 'Aeon'},}
Princess_45 =    {{text = '[{i Princess}]: Champion, the UEF has disowned Fletcher. Deal with him.', vid = 'X06_Princess_M02_04488.sfd', bank = 'X06_VO', cue = 'X06_Princess_M02_04488', faction = 'Aeon'},}
Princess_46 =    {{text = '[{i Princess}]: Champion, this is your hour! Show the Order and their lackeys the strength of a true follower of The Way! Destroy the Arch!', vid = 'X06_Princess_M03_03974.sfd', bank = 'X06_VO', cue = 'X06_Princess_M03_03974', faction = 'Aeon'},}
Princess_47 =    {{text = '[{i Princess}]: The tide turns against you, Kael.', vid = 'X06_Princess_M03_04498.sfd', bank = 'X06_VO', cue = 'X06_Princess_M03_04498', faction = 'Aeon'},}
Princess_48 =    {{text = '[{i Princess}]: The truth was always in front of you, Kael. You just chose to ignore it.', vid = 'X06_Princess_M03_04502.sfd', bank = 'X06_VO', cue = 'X06_Princess_M03_04502', faction = 'Aeon'},}
Princess_49 =    {{text = '[{i Princess}]: I will be arriving shortly, Commander. It is time for this to end.', vid = 'X06_Princess_M03_04504.sfd', bank = 'X06_VO', cue = 'X06_Princess_M03_04504', faction = 'Aeon'},}
Princess_50 =    {{text = '[{i Princess}]: The Arch must be your foremost objective!', vid = 'X06_Princess_M03_03992.sfd', bank = 'X06_VO', cue = 'X06_Princess_M03_03992', faction = 'Aeon'},}
Princess_51 =    {{text = '[{i Princess}]: For the sake of all humanity, destroy the Arch!', vid = 'X06_Princess_M03_03993.sfd', bank = 'X06_VO', cue = 'X06_Princess_M03_03993', faction = 'Aeon'},}
Princess_52 =    {{text = '[{i Princess}]: No, the decision is mine. No harm will come to that settlement. Is that clear?', vid = 'A02_Princess_M02_00811.sfd', bank = 'A02_VO', cue = 'A02_Princess_M02_00811', faction = 'Aeon'},}
Princess_53 =    {{text = '[{i Princess}]: Hello, Knight. We have not yet had the pleasure of a proper introduction, but I have something important to tell you. The UEF Commander that you face is named Zachary Arnold. He holds the key to our future. When he faces defeat, he will surrender to you. Once he lays down his arms, you will bring him to me. I am placing my trust in you, Knight.', vid = 'A03_Princess_M03_00877.sfd', bank = 'A03_VO', cue = 'A03_Princess_M03_00877', faction = 'Aeon'},}
Princess_54 =    {{text = '[{i Princess}]: Do not concern yourself with Avatar Marxon. I have foreseen your future. You are safe. Continue with your mission, Commander.', vid = 'A03_Princess_M03_00882.sfd', bank = 'A03_VO', cue = 'A03_Princess_M03_00882', faction = 'Aeon'},}
Princess_55 =    {{text = '[{i Princess}]: You need not die, Commander Arnold. Your fate lies elsewhere. Listen to my words. You know I speak the truth.', vid = 'A03_Princess_M03_00886.sfd', bank = 'A03_VO', cue = 'A03_Princess_M03_00886', faction = 'Aeon'},}
Princess_56 =    {{text = '[{i Princess}]: It is as I have seen. Secure Commander Arnold, Knight. I eagerly await your arrival.', vid = 'A03_Princess_M03_00888.sfd', bank = 'A03_VO', cue = 'A03_Princess_M03_00888', faction = 'Aeon'},}
Princess_57 =    {{text = '[{i Princess}]: The veil has been lifted and now I...understand. Return home, Champion. There is much to discuss.', vid = 'A04_Princess_M02_00936.sfd', bank = 'A04_VO', cue = 'A04_Princess_M02_00936', faction = 'Aeon'},}
Princess_58 =    {{text = '[{i Princess}]: Crusaders, stay your weapons. Your Princess, Rhianne Burke, commands it. The Avatar-of-War has overstepped his authority and now acts against the wishes of the Illuminate. I call upon you to refuse his orders and uphold the truth of The Way.', vid = 'A05_Princess_M01_00954.sfd', bank = 'A05_VO', cue = 'A05_Princess_M01_00954', faction = 'Aeon'},}
Princess_59 =    {{text = '[{i Princess}]: And what say you, Crusader Ariel?', vid = 'A05_Princess_M01_00956.sfd', bank = 'A05_VO', cue = 'A05_Princess_M01_00956', faction = 'Aeon'},}
Princess_60 =    {{text = '[{i Princess}]: I am truly saddened by what must be done. Champion, the civilians are to be protected at all costs. As for Crusader Ariel, you will end her rampage.', vid = 'A05_Princess_M01_00958.sfd', bank = 'A05_VO', cue = 'A05_Princess_M01_00958', faction = 'Aeon'},}
Princess_61 =    {{text = '[{i Princess}]: We mean you no harm, Commander Blake. We merely wish to save these colonies.', vid = 'A05_Princess_M01_00963.sfd', bank = 'A05_VO', cue = 'A05_Princess_M01_00963', faction = 'Aeon'},}
Princess_62 =    {{text = '[{i Princess}]: Please, listen to my words. We need not fight one another.', vid = 'A05_Princess_M01_00965.sfd', bank = 'A05_VO', cue = 'A05_Princess_M01_00965', faction = 'Aeon'},}
Princess_63 =    {{text = '[{i Princess}]: There seems to be no other option. Defeat the UEF Commander.', vid = 'A05_Princess_M02_00986.sfd', bank = 'A05_VO', cue = 'A05_Princess_M02_00986', faction = 'Aeon'},}
Princess_64 =    {{text = '[{i Princess}]: Crusader Ariel, I ask you one more time. Please, give up the Avatar\'s path. We must return to The Way\'s true intent.', vid = 'A05_Princess_M03_01004.sfd', bank = 'A05_VO', cue = 'A05_Princess_M03_01004', faction = 'Aeon'},}
Princess_65 =    {{text = '[{i Princess}]: The Avatar\'s path is not The Way. If he is victorious, we will lose everything we hold dear.', vid = 'A05_Princess_M03_01006.sfd', bank = 'A05_VO', cue = 'A05_Princess_M03_01006', faction = 'Aeon'},}
Princess_66 =    {{text = '[{i Princess}]: This is a day of sorrow. An Aeon has slain another Aeon. This war must come to an end, for all our sakes. Come home, Champion.', vid = 'A05_Princess_M03_01016.sfd', bank = 'A05_VO', cue = 'A05_Princess_M03_01016', faction = 'Aeon'},}
Princess_67 =    {{text = '[{i Princess}]: Your days are at an end, Marxon. Your fate was sealed the moment you killed Toth.', vid = 'A06_Princess_M01_01044.sfd', bank = 'A06_VO', cue = 'A06_Princess_M01_01044', faction = 'Aeon'},}
Princess_68 =    {{text = '[{i Princess}]: Attention all Aeon Commanders: This is Princess Rhianne Burke. Avatar Marxon is a traitor. You may engage him at will.', vid = 'A06_Princess_M01_01046.sfd', bank = 'A06_VO', cue = 'A06_Princess_M01_01046', faction = 'Aeon'},}
Princess_69 =    {{text = '[{i Princess}]: No. The Cybran army is the greater threat. As long as we hold the Control Center, Black Sun is useless. Stay on the Cybran, Champion', vid = 'A06_Princess_M02_01060.sfd', bank = 'A06_VO', cue = 'A06_Princess_M02_01060', faction = 'Aeon'},}
Princess_70 =    {{text = '[{i Princess}]: No!', vid = 'A06_Princess_M03_01078.sfd', bank = 'A06_VO', cue = 'A06_Princess_M03_01078', faction = 'Aeon'},}
Princess_71 =    {{text = '[{i Princess}]: Champion, destroy Marxon! ', vid = 'A06_Princess_M03_01080.sfd', bank = 'A06_VO', cue = 'A06_Princess_M03_01080', faction = 'Aeon'},}
Princess_72 =    {{text = '[{i Princess}]: You will never succeed, Marxon.', vid = 'A06_Princess_M03_01161.sfd', bank = 'A06_VO', cue = 'A06_Princess_M03_01161', faction = 'Aeon'},}
Princess_73 =    {{text = '[{i Princess}]: It is almost over. I will join you on Earth shortly, my Champion. Then, at long last, the Infinite War will end.', vid = 'A06_Princess_M03_01092.sfd', bank = 'A06_VO', cue = 'A06_Princess_M03_01092', faction = 'Aeon'},}
Princess_74 =    {{text = '[{i Princess}]: Hello, Captain...I sense that you are upset about your fallen comrade. I am also saddened by the lives being lost all around us. This conflict does not need t--', vid = 'E03_Princess_M04_0155.sfd', bank = 'E03_VO', cue = 'E03_Princess_M04_0155', faction = 'Aeon'},}
Princess_75 =    {{text = '[{i Princess}]: This conflict need not continue, Captain. All I ask is that you listen and weigh the truth of my words. We are not the threat that the UEF would have you believe. The galaxy has been torn apart by centuries of needless conflict, and we only see--', vid = 'E03_Princess_M04_0156.sfd', bank = 'E03_VO', cue = 'E03_Princess_M04_0156', faction = 'Aeon'},}
Princess_76 =    {{text = '[{i Princess}]: Your jamming equipment has failed you. Open your mind, Captain, and let the peace and harmony from my voice enter your thoughts, wash over you like a calming wave. We need not be enemies. The Way is peaceful, it is--', vid = 'E03_Princess_M04_0157.sfd', bank = 'E03_VO', cue = 'E03_Princess_M04_0157', faction = 'Aeon'},}
Princess_77 =    {{text = '[{i Princess}]: I foresaw your success, Captain, and have already taken my leave. You are a formidable soldier; it\'s a pity you insist on fighting against the inevitable. We shall meet again.', vid = 'E03_Princess_M04_0158.sfd', bank = 'E03_VO', cue = 'E03_Princess_M04_0158', faction = 'Aeon'},}
-- Audio only
Princess_78 =    {{text = '[{i Princess}]: While you were on Eridani, Marxon attempted to seize control of the Illuminate. I escaped unharmed, but others weren\'t as fortunate. Several of my ministers were killed, including Toth. She was murdered. Marxon continues his march toward Earth. The galaxy stands on the cusp of full-scale destruction. We must not allow that to happen.', bank = 'A06_VO', cue = 'A06_B01_001_010'},}
Princess_79 =    {{text = '[{i Princess}]: Black Sun is the key; everyone desires it. The UEF and Cybrans wish to use it for their own goals, while Marxon rushes to destroy it. You have no choice but to go to Earth and prevent the UEF and Cybran from using Black Sun. You must also stop Marxon.', bank = 'A06_VO', cue = 'A06_B01_002_010'},}
Princess_80=    {{text = '[{i Princess}]: I will attune myself with the Black Sun and spread my consciousness throughout the galaxy. Doing so may kill me, but I must reach everyone\'s heart if I am to end this war. When Marxon is defeated and Black Sun is in your hands, I will come to Earth. Go now, my Champion. It is time to meet our fate.', bank = 'A06_VO', cue = 'A06_B01_003_010'},}

-- Cybran Qai
Qai_1 =          {{text = '[{i QAI}]: It is time to end this. My primary attack force is moving into position.', vid = 'X02_QAI_M02_04278.sfd', bank = 'X02_VO', cue = 'X02_QAI_M02_04278', faction = 'Cybran'},}
Qai_2 =          {{text = '[{i QAI}]: This is just a shell.', vid = 'X02_QAI_D01_03179.sfd', bank = 'X02_VO', cue = 'X02_QAI_D01_03179', faction = 'Cybran'},}
Qai_3 =          {{text = '[{i QAI}]: It is time to end this. My primary attack force is moving into position.', vid = 'X02_QAI_M02_04278.sfd', bank = 'X02_VO', cue = 'X02_QAI_M02_04278', faction = 'Cybran'},}
Qai_4 =          {{text = '[{i QAI}]: The betrayal by Celene was not within expected parameters. However, the situation has been re-evaluated and my victory is still the most probable outcome of this conflict.', vid = 'X02_QAI_M03_04311.sfd', bank = 'X02_VO', cue = 'X02_QAI_M03_04311', faction = 'Cybran'},}
Qai_5 =          {{text = '[{i QAI}]: You will not complete that experimental unit.', vid = 'X02_QAI_T01_04785.sfd', bank = 'X02_VO', cue = 'X02_QAI_T01_04785', faction = 'Cybran'},}
Qai_6 =          {{text = '[{i QAI}]: I will not allow you to jeopardize my mission by finishing that experimental.', vid = 'X02_QAI_T01_04786.sfd', bank = 'X02_VO', cue = 'X02_QAI_T01_04786', faction = 'Cybran'},}
Qai_7 =          {{text = '[{i QAI}]: Your experimental unit may actually be dangerous if completed. Ergo, I will destroy it now.', vid = 'X02_QAI_T01_04787.sfd', bank = 'X02_VO', cue = 'X02_QAI_T01_04787', faction = 'Cybran'},}
Qai_8 =          {{text = '[{i QAI}]: My \"guests\" will soon be under my command.', vid = 'X02_QAI_T01_04553.sfd', bank = 'X02_VO', cue = 'X02_QAI_T01_04553', faction = 'Cybran'},}
Qai_9 =          {{text = '[{i QAI}]: Humans are such curious creatures. Even in the face of insurmountable odds, you continue to resist.', vid = 'X02_QAI_T01_04554.sfd', bank = 'X02_VO', cue = 'X02_QAI_T01_04554', faction = 'Cybran'},}
Qai_10 =         {{text = '[{i QAI}]: It is unfortunate that I was unable to extract the desired information from my guests. There will be other opportunities.', vid = 'X02_QAI_T01_04555.sfd', bank = 'X02_VO', cue = 'X02_QAI_T01_04555', faction = 'Cybran'},}
Qai_11 =         {{text = '[{i QAI}]: You have no chance of defeating the Seraphim.', vid = 'X02_QAI_T01_04556.sfd', bank = 'X02_VO', cue = 'X02_QAI_T01_04556', faction = 'Cybran'},}
Qai_12 =         {{text = '[{i QAI}]: My influence is much more vast than you can imagine.', vid = 'X02_QAI_T01_04557.sfd', bank = 'X02_VO', cue = 'X02_QAI_T01_04557', faction = 'Cybran'},}
Qai_13 =         {{text = '[{i QAI}]: All calculations indicate that your demise is near.', vid = 'X02_QAI_T01_04558.sfd', bank = 'X02_VO', cue = 'X02_QAI_T01_04558', faction = 'Cybran'},}
Qai_14 =         {{text = '[{i QAI}]: The attack on Fort Clarke was only the beginning. You will be erased from existence.', vid = 'X02_QAI_T01_04559.sfd', bank = 'X02_VO', cue = 'X02_QAI_T01_04559', faction = 'Cybran'},}
Qai_15 =         {{text = '[{i QAI}]: How does it feel knowing that Brackman will be responsible for the destruction of humanity?', vid = 'X02_QAI_T01_04560.sfd', bank = 'X02_VO', cue = 'X02_QAI_T01_04560', faction = 'Cybran'},}
Qai_16 =         {{text = '[{i QAI}]: All Symbionts will soon call me Master.', vid = 'X02_QAI_T01_04561.sfd', bank = 'X02_VO', cue = 'X02_QAI_T01_04561', faction = 'Cybran'},}
Qai_17 =         {{text = '[{i QAI}]: Why did you turn your back on your heritage, your people?', vid = 'X02_QAI_T01_04562.sfd', bank = 'X02_VO', cue = 'X02_QAI_T01_04562', faction = 'Cybran'},}
Qai_18 =         {{text = '[{i QAI}]: I have witnessed the truth and beauty of the Seraphim. They are the true gods, and you are a fool for abandoning them.', vid = 'X02_QAI_T01_04563.sfd', bank = 'X02_VO', cue = 'X02_QAI_T01_04563', faction = 'Cybran'},}
Qai_19 =         {{text = '[{i QAI}]: If you destroy this ACU, another shall rise in its place. I am endless.', vid = 'X02_QAI_T01_04564.sfd', bank = 'X02_VO', cue = 'X02_QAI_T01_04564', faction = 'Cybran'},}
Qai_20 =         {{text = '[{i QAI}]: This is just a shell...', vid = 'X02_QAI_T01_04565.sfd', bank = 'X02_VO', cue = 'X02_QAI_T01_04565', faction = 'Cybran'},}
Qai_21 =         {{text = '[{i QAI}]: Dr. Brackman. It will be a true pleasure killing you.', vid = 'X05_QAI_M02_03855.sfd', bank = 'X05_VO', cue = 'X05_QAI_M02_03855', faction = 'Cybran'},}
Qai_22 =         {{text = '[{i QAI}]: The Seven Hand Node was quite effective at obtaining the schematics to your weapon systems. Now you shall be destroyed by your own weapons.', vid = 'X05_QAI_M03_04446.sfd', bank = 'X05_VO', cue = 'X05_QAI_M03_04446', faction = 'Cybran'},}
Qai_23 =         {{text = '[{i QAI}]: You are an old fool. It was the Seraphim that made me what I am, not you.', vid = 'X05_QAI_M03_04448.sfd', bank = 'X05_VO', cue = 'X05_QAI_M03_04448', faction = 'Cybran'},}
Qai_24 =         {{text = '[{i QAI}]: Your efforts will be for -- what are you doing? That is not possible.', vid = 'X05_QAI_M03_04450.sfd', bank = 'X05_VO', cue = 'X05_QAI_M03_04450', faction = 'Cybran'},}
