-- Aeon Rhiza
Rhiza_1 =        {{text = '[{i Rhiza}]: We\'re giving you the Swift Wind, a mid-range fighter jet. Rhiza out.', vid = 'X01_Rhiza_M01_04881.sfd', bank = 'X01_VO', cue = 'X01_Rhiza_M01_04881', faction = 'Aeon'},}
Rhiza_2 =        {{text = '[{i Rhiza}]: Commander, you have received the schematic for the Blaze. It\'s lightly armored, but it packs some impressive firepower. Rhiza out.', vid = 'X01_Rhiza_M01_04882.sfd', bank = 'X01_VO', cue = 'X01_Rhiza_M01_04882', faction = 'Aeon'},}
Rhiza_3 =        {{text = '[{i Rhiza}]: Pay him no mind, Champion. Bring glory to the Princess.', vid = 'X01_Rhiza_M01_02880.sfd', bank = 'X01_VO', cue = 'X01_Rhiza_M01_02880', faction = 'Aeon'},}
Rhiza_4 =        {{text = '[{i Rhiza}]: An Order commander named Gari awaits you, Champion. She is one of Evaluator Kael\'s most beloved disciples; eliminate her in the name of the Princess. Rhiza out.', vid = 'X01_Rhiza_M01_03620.sfd', bank = 'X01_VO', cue = 'X01_Rhiza_M01_03620', faction = 'Aeon'},}
Rhiza_5 =        {{text = '[{i Rhiza}]: You have perverted The Way with your fanaticism. For that, you will be destroyed.', vid = 'X01_Rhiza_M02_03669.sfd', bank = 'X01_VO', cue = 'X01_Rhiza_M02_03669', faction = 'Aeon'},}
Rhiza_6 =        {{text = '[{i Rhiza}]: Kael will rage when she learns of this! Glory to the Princess! Rhiza out.', vid = 'X01_Rhiza_M02_03676.sfd', bank = 'X01_VO', cue = 'X01_Rhiza_M02_03676', faction = 'Aeon'},}
Rhiza_7 =        {{text = '[{i Rhiza}]: Gari has shamed us all. Destroy her. Rhiza out.', vid = 'X01_Rhiza_M02_04888.sfd', bank = 'X01_VO', cue = 'X01_Rhiza_M02_04888', faction = 'Aeon'},}
Rhiza_8 =        {{text = '[{i Rhiza}]: He does not know your worth, Champion. Show him our might by defeating the Seraphim yourself. Rhiza out.', vid = 'X01_Rhiza_M03_03700.sfd', bank = 'X01_VO', cue = 'X01_Rhiza_M03_03700', faction = 'Aeon'},}
Rhiza_9 =        {{text = '[{i Rhiza}]: He does not know your worth, Champion. Show him our might by defeating the Seraphim and its units yourself. Rhiza out.', vid = 'X01_Rhiza_M03_04698.sfd', bank = 'X01_VO', cue = 'X01_Rhiza_M03_04698', faction = 'Aeon'},}
Rhiza_10 =       {{text = '[{i Rhiza}]: Gari proved more bark than bite. Lay your sights upon the Seraphim and destroy him. Losing both in one day will enrage Kael and give heart to the Loyalists. Rhiza out.', vid = 'X01_Rhiza_M03_03718.sfd', bank = 'X01_VO', cue = 'X01_Rhiza_M03_03718', faction = 'Aeon'},}
Rhiza_11 =       {{text = '[{i Rhiza}]: Commander, I wanted to personally thank you for aiding my beloved Loyalists. They are our first line of defense against the Order of the Illuminate\'s treachery and losing them would have been a devastating blow. The coming days will be dark, but don\'t lose heart. Our ultimate victory is closer than you think.', vid = 'X02_Rhiza_DB01_04020.sfd', bank = 'Briefings', cue = 'X02_Rhiza_DB01_04020', faction = 'Aeon'},}
Rhiza_12 =       {{text = '[{i Rhiza}]: The Princess has returned! I am overwhelmed with joy! And thank you, Commander, for freeing my beloved Loyalists. I feel that the tide is turning in our favor. Stay strong. Our ultimate victory is closer than you think.', vid = 'X02_Rhiza_DB01_04907.sfd', bank = 'Briefings', cue = 'X02_Rhiza_DB01_04907', faction = 'Aeon'},}
Rhiza_13 =       {{text = '[{i Rhiza}]: Well done, Thalia.', vid = 'X02_Rhiza_M01_04265.sfd', bank = 'X02_VO', cue = 'X02_Rhiza_M01_04265', faction = 'Aeon'},}
Rhiza_14 =       {{text = '[{i Rhiza}]: Celene, you once swore an oath to the Princess, yet you serve her enemies. Does your word mean so little?', vid = 'X02_Rhiza_M01_04266.sfd', bank = 'X02_VO', cue = 'X02_Rhiza_M01_04266', faction = 'Aeon'},}
Rhiza_15 =       {{text = '[{i Rhiza}]: You are wrong. And your betrayal shall cost you dearly.', vid = 'X02_Rhiza_M01_04268.sfd', bank = 'X02_VO', cue = 'X02_Rhiza_M01_04268', faction = 'Aeon'},}
Rhiza_16 =       {{text = '[{i Rhiza}]: Help them if you can, Champion. Opportunities like this do not often arise. Rhiza out.', vid = 'X02_Rhiza_M02_03160.sfd', bank = 'X02_VO', cue = 'X02_Rhiza_M02_03160', faction = 'Aeon'},}
Rhiza_17 =       {{text = '[{i Rhiza}]: You were told she was alive, Celene, and yet you still turned your back on her. You made your choice, and now you will live with the consequences.', vid = 'X02_Rhiza_M02_04285.sfd', bank = 'X02_VO', cue = 'X02_Rhiza_M02_04285', faction = 'Aeon'},}
Rhiza_18 =       {{text = '[{i Rhiza}]: Commander, Celene is engaging QAI. Stay on guard, however. We still cannot trust her.', vid = 'X02_Rhiza_M03_04292.sfd', bank = 'X02_VO', cue = 'X02_Rhiza_M03_04292', faction = 'Aeon'},}
Rhiza_19 =       {{text = '[{i Rhiza}]: You will have ample opportunity to atone for your actions. But be warned -- should you stray again, I will destroy you myself.', vid = 'X02_Rhiza_M03_04316.sfd', bank = 'X02_VO', cue = 'X02_Rhiza_M03_04316', faction = 'Aeon'},}
