Rhiza_40 =       {{text = '[{i Rhiza}]: I\'m taking too much damage -- I must recall. Commander, continue the fight without me!', vid = 'X03_Rhiza_M03_04708.sfd', bank = 'X03_VO', cue = 'X03_Rhiza_M03_04708', faction = 'Aeon'},}
Rhiza_41 =       {{text = '[{i Rhiza}]: Day by day, my fury grows. Survive and come home, Champion, so that we might avenge Dostya\'s death and destroy the Seraphim once and for all. Rhiza out.', vid = 'X04_Rhiza_M03_03760.sfd', bank = 'X04_VO', cue = 'X04_Rhiza_M03_03760', faction = 'Aeon'},}
Rhiza_42 =       {{text = '[{i Rhiza}]: Champion, the Loyalists have secured a handful of Seraphim security codes. If you capture a Seraphim radar, we can use the codes to capture several Seraphim defensive structures. Attack the radar at your discretion. Rhiza out.', vid = 'X04_Rhiza_M03_03772.sfd', bank = 'X04_VO', cue = 'X04_Rhiza_M03_03772', faction = 'Aeon'},}
Rhiza_43 =       {{text = '[{i Rhiza}]: Champion, the Loyalists have secured a handful of Seraphim security codes. If you capture a Seraphim radar, the codes can be used to cause some of the Seraphim defenses to self destruct. Capture the radar at your discretion. Rhiza out.', vid = 'X04_Rhiza_M03_04709.sfd', bank = 'X04_VO', cue = 'X04_Rhiza_M03_04709', faction = 'Aeon'},}
Rhiza_44 =       {{text = '[{i Rhiza}]: Capture a radar if you can, Champion. Rhiza out.', vid = 'X04_Rhiza_M03_03773.sfd', bank = 'X04_VO', cue = 'X04_Rhiza_M03_03773', faction = 'Aeon'},}
Rhiza_45 =       {{text = '[{i Rhiza}]: Capturing a radar will help relieve some of the attacks, Champion. Rhiza out.', vid = 'X04_Rhiza_M03_03774.sfd', bank = 'X04_VO', cue = 'X04_Rhiza_M03_03774', faction = 'Aeon'},}
Rhiza_46 =       {{text = '[{i Rhiza}]: Glorious! Attack while their defenses are in chaos. Rhiza out.', vid = 'X04_Rhiza_M03_03775.sfd', bank = 'X04_VO', cue = 'X04_Rhiza_M03_03775', faction = 'Aeon'},}
Rhiza_47 =       {{text = '[{i Rhiza}]: Kael is unleashing extreme measures. She has leveled an entire city, killing millions. Civil war is spreading across Aeon-controlled space. There is no way the Order will be able to survive. Rhiza out.', vid = 'X05_Rhiza_M01_03817.sfd', bank = 'X05_VO', cue = 'X05_Rhiza_M01_03817', faction = 'Aeon'},}
Rhiza_48 =       {{text = '[{i Rhiza}]: A Loyalist named Amalia is being held in a prison to your east, which has been marked for capture. Capture the prison and free Amalia. Rhiza out.', vid = 'X05_Rhiza_M01_03818.sfd', bank = 'X05_VO', cue = 'X05_Rhiza_M01_03818', faction = 'Aeon'},}
Rhiza_49 =       {{text = '[{i Rhiza}]: Amalia is still being held. Rescue her if you can. Rhiza out.', vid = 'X05_Rhiza_M01_03819.sfd', bank = 'X05_VO', cue = 'X05_Rhiza_M01_03819', faction = 'Aeon'},}
Rhiza_50 =       {{text = '[{i Rhiza}]: Please rescue Amalia, Commander. Rhiza out.', vid = 'X05_Rhiza_M01_03820.sfd', bank = 'X05_VO', cue = 'X05_Rhiza_M01_03820', faction = 'Aeon'},}
Rhiza_51 =       {{text = '[{i Rhiza}]: Champion, you have received the schematic for the Restorer gunship. It can attack both land and air units. Rhiza out.', vid = 'X05_Rhiza_M01_04940.sfd', bank = 'X05_VO', cue = 'X05_Rhiza_M01_04940', faction = 'Aeon'},}
Rhiza_52 =       {{text = '[{i Rhiza}]: Champion, you did not capture the Science Facility in time. The enemy Colossus is operational and heading your way. Rhiza out.', vid = 'X05_Rhiza_M03_04954.sfd', bank = 'X05_VO', cue = 'X05_Rhiza_M03_04954', faction = 'Aeon'},}
Rhiza_53 =       {{text = '[{i Rhiza}]: Because you destroyed the Colossus, there is no reason to capture the Science Facility. Rhiza out.', vid = 'X05_Rhiza_M03_04955.sfd', bank = 'X05_VO', cue = 'X05_Rhiza_M03_04955', faction = 'Aeon'},}
Rhiza_54 =       {{text = '[{i Rhiza}]: Here are reinforcements. Rhiza out.', vid = 'X06_Rhiza_M01_03909.sfd', bank = 'X06_VO', cue = 'X06_Rhiza_M01_03909', faction = 'Aeon'},}
Rhiza_55 =       {{text = '[{i Rhiza}]: Here are reinforcements, Champion. Use them to bring glory to the Princess. Rhiza out.', vid = 'X06_Rhiza_M01_04468.sfd', bank = 'X06_VO', cue = 'X06_Rhiza_M01_04468', faction = 'Aeon'},}
Rhiza_56 =       {{text = '[{i Rhiza}]: Commander, Brigadier Fletcher and I are planning a joint assault on the main Seraphim installation. Defeat the Seraphim base near your position. Rhiza out.', vid = 'X06_Rhiza_M01_04471.sfd', bank = 'X06_VO', cue = 'X06_Rhiza_M01_04471', faction = 'Aeon'},}
Rhiza_57 =       {{text = '[{i Rhiza}]: Champion, Fletcher and I will soon move against the Seraphim. Conquer the enemy base in your area and join us in our attack. Rhiza out.', vid = 'X06_Rhiza_M01_04472.sfd', bank = 'X06_VO', cue = 'X06_Rhiza_M01_04472', faction = 'Aeon'},}
Rhiza_58 =       {{text = '[{i Rhiza}]: Kael is becoming increasingly desperate, and the Order has descended into madness. Rhiza out.', vid = 'X06_Rhiza_M01_03917.sfd', bank = 'X06_VO', cue = 'X06_Rhiza_M01_03917', faction = 'Aeon'},}
Rhiza_59 =       {{text = '[{i Rhiza}]: Our attack begins. Glory to the Princess!', vid = 'X06_Rhiza_M01_03923.sfd', bank = 'X06_VO', cue = 'X06_Rhiza_M01_03923', faction = 'Aeon'},}
Rhiza_60 =       {{text = '[{i Rhiza}]: Brigadier Fletcher, the Seraphim is hitting me from all directions. I need help! Where are you?', vid = 'X06_Rhiza_M02_04474.sfd', bank = 'X06_VO', cue = 'X06_Rhiza_M02_04474', faction = 'Aeon'},}
Rhiza_61 =       {{text = '[{i Rhiza}]: Damn him! Can\'t the UEF control their own soldiers?', vid = 'X06_Rhiza_M02_04879.sfd', bank = 'X06_VO', cue = 'X06_Rhiza_M02_04879', faction = 'Aeon'},}
Rhiza_62 =       {{text = '[{i Rhiza}]: I am fighting the Seraphim, but I will assist if I am able. Rhiza out.', vid = 'X06_Rhiza_M02_04485.sfd', bank = 'X06_VO', cue = 'X06_Rhiza_M02_04485', faction = 'Aeon'},}
Rhiza_63 =       {{text = '[{i Rhiza}]: Doctor, I will help as I am able, but I am currently fighting the Seraphim. Rhiza out.', vid = 'X06_Rhiza_M02_04487.sfd', bank = 'X06_VO', cue = 'X06_Rhiza_M02_04487', faction = 'Aeon'},}
Rhiza_64 =       {{text = '[{i Rhiza}]: Champion, I am doing battle with the Seraphim, but I will assist if I am able. Rhiza out.', vid = 'X06_Rhiza_M02_04488.sfd', bank = 'X06_VO', cue = 'X06_Rhiza_M02_04488', faction = 'Aeon'},}
Rhiza_65 =       {{text = '[{i Rhiza}]: Nooooooo!', vid = 'X06_Rhiza_M02_05599.sfd', bank = 'X06_VO', cue = 'X06_Rhiza_M02_05599', faction = 'Aeon'},}
Rhiza_66 =       {{text = '[{i Rhiza}]: [High Pitched Death Scream]', vid = 'X06_Rhiza_M02_05125.sfd', bank = 'X06_VO', cue = 'X06_Rhiza_M02_05125', faction = 'Aeon'},}
Rhiza_67 =       {{text = '[{i Rhiza}]: The Seraphim has destroyed much of my base. I am endeavoring to rebuild, but I am unable to launch an assault.', vid = 'X06_Rhiza_M03_03976.sfd', bank = 'X06_VO', cue = 'X06_Rhiza_M03_03976', faction = 'Aeon'},}
Rhiza_68 =       {{text = '[{i Rhiza}]: That force may have been defeated, but I will not stop!', vid = 'X06_Rhiza_T01_04505.sfd', bank = 'X06_VO', cue = 'X06_Rhiza_T01_04505', faction = 'Aeon'},}
Rhiza_69 =       {{text = '[{i Rhiza}]: It does not matter, I will continue to attack!', vid = 'X06_Rhiza_T01_04506.sfd', bank = 'X06_VO', cue = 'X06_Rhiza_T01_04506', faction = 'Aeon'},}
Rhiza_70 =       {{text = '[{i Rhiza}]: Two towers will rise to take its place!', vid = 'X06_Rhiza_T01_04507.sfd', bank = 'X06_VO', cue = 'X06_Rhiza_T01_04507', faction = 'Aeon'},}
Rhiza_71 =       {{text = '[{i Rhiza}]: Such a thing will not stop me!', vid = 'X06_Rhiza_T01_04508.sfd', bank = 'X06_VO', cue = 'X06_Rhiza_T01_04508', faction = 'Aeon'},}
Rhiza_72 =       {{text = '[{i Rhiza}]: I will rebuild twice as strong!', vid = 'X06_Rhiza_T01_04509.sfd', bank = 'X06_VO', cue = 'X06_Rhiza_T01_04509', faction = 'Aeon'},}
Rhiza_73 =       {{text = '[{i Rhiza}]: You mistake me if you think I will be cowed!', vid = 'X06_Rhiza_T01_04510.sfd', bank = 'X06_VO', cue = 'X06_Rhiza_T01_04510', faction = 'Aeon'},}
Rhiza_74 =       {{text = '[{i Rhiza}]: Glory to the Princess!', vid = 'X06_Rhiza_T01_03050.sfd', bank = 'X06_VO', cue = 'X06_Rhiza_T01_03050', faction = 'Aeon'},}
Rhiza_75 =       {{text = '[{i Rhiza}]: It is unwise to ignore me.', vid = 'X06_Rhiza_T01_03051.sfd', bank = 'X06_VO', cue = 'X06_Rhiza_T01_03051', faction = 'Aeon'},}
Rhiza_76 =       {{text = '[{i Rhiza}]: Soon you will know my wrath!', vid = 'X06_Rhiza_T01_03052.sfd', bank = 'X06_VO', cue = 'X06_Rhiza_T01_03052', faction = 'Aeon'},}
Rhiza_77 =       {{text = '[{i Rhiza}]: The will of the Princess will not be denied!', vid = 'X06_Rhiza_T01_03053.sfd', bank = 'X06_VO', cue = 'X06_Rhiza_T01_03053', faction = 'Aeon'},}
Rhiza_78 =       {{text = '[{i Rhiza}]: There is nothing here save destruction for you, Seraphim!', vid = 'X06_Rhiza_T01_03054.sfd', bank = 'X06_VO', cue = 'X06_Rhiza_T01_03054', faction = 'Aeon'},}
Rhiza_79 =       {{text = '[{i Rhiza}]: Victory to the Coalition!', vid = 'X06_Rhiza_T01_03055.sfd', bank = 'X06_VO', cue = 'X06_Rhiza_T01_03055', faction = 'Aeon'},}
Rhiza_80 =       {{text = '[{i Rhiza}]: You\'re in position. Go forth for the Illuminate.', vid = 'A01_Rhiza_M01_00746.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M01_00746', faction = 'Aeon'},}
Rhiza_81 =       {{text = '[{i Rhiza}]: I have no time to hold your hand, Knight. Build Power Generators and Mass Extractors. The Extractors can only be built over Mass Deposits. Rhiza out.', vid = 'A01_Rhiza_M01_00747.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M01_00747', faction = 'Aeon'},}
Rhiza_82 =       {{text = '[{i Rhiza}]: Build a Naval Factory. It must be positioned in the water or your Armored Command Unit will not let you construct it. Rhiza out.', vid = 'A01_Rhiza_M01_00748.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M01_00748', faction = 'Aeon'},}
Rhiza_83 =       {{text = '[{i Rhiza}]: You will require Power Generators to accomplish your mission, Knight.', vid = 'A01_Rhiza_M01_00754.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M01_00754', faction = 'Aeon'},}
Rhiza_84 =       {{text = '[{i Rhiza}]: Without Power Generators, you will soon fall to the enemy, Knight.', vid = 'A01_Rhiza_M01_01556.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M01_01556', faction = 'Aeon'},}
Rhiza_85 =       {{text = '[{i Rhiza}]: I see you have not yet built Mass Extractors, Knight. Is there a reason why?', vid = 'A01_Rhiza_M01_01558.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M01_01558', faction = 'Aeon'},}
Rhiza_86 =       {{text = '[{i Rhiza}]: You cannot spread The Way if you don\'t have sufficient Mass, Knight. I suggest you build some Mass Extractors.', vid = 'A01_Rhiza_M01_01559.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M01_01559', faction = 'Aeon'},}
Rhiza_87 =       {{text = '[{i Rhiza}]: The UEF has an air patrol in the area. Build some Light Attack Boats; they provide anti-air capabilities. Once you have a small force, engage the UEF patrol. Rhiza out.', vid = 'A01_Rhiza_M02_00749.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M02_00749', faction = 'Aeon'},}
Rhiza_88 =       {{text = '[{i Rhiza}]: You are to build a Naval Factory ASAP.  Rhiza out. ', vid = 'A01_Rhiza_M02_00756.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M02_00756', faction = 'Aeon'},}
Rhiza_89 =       {{text = '[{i Rhiza}]: Why haven\'t you built a Naval Factory? Rhiza out. ', vid = 'A01_Rhiza_M02_01560.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M02_01560', faction = 'Aeon'},}
Rhiza_90 =       {{text = '[{i Rhiza}]: The UEF is trying to blockade you with submarines! Build submarines of your own at your Naval Factory. Rhiza out.', vid = 'A01_Rhiza_M03_00750.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M03_00750', faction = 'Aeon'},}
Rhiza_91 =       {{text = '[{i Rhiza}]: I\'m still reading a UEF patrol. Destroy it. Rhiza out', vid = 'A01_Rhiza_M03_00759.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M03_00759', faction = 'Aeon'},}
Rhiza_92 =       {{text = '[{i Rhiza}]: You need to destroy that patrol, Knight. Rhiza out. ', vid = 'A01_Rhiza_M03_01561.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M03_01561', faction = 'Aeon'},}
Rhiza_93 =       {{text = '[{i Rhiza}]: Now that you have submarines, use them to destroy the UEF blockade. I will contact you once you have succeeded. Rhiza out.', vid = 'A01_Rhiza_M04_00751.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M04_00751', faction = 'Aeon'},}
Rhiza_94 =       {{text = '[{i Rhiza}]: Hey? Do you need help building those subs? Get to it. Rhiza out', vid = 'A01_Rhiza_M04_00761.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M04_00761', faction = 'Aeon'},}
Rhiza_95 =       {{text = '[{i Rhiza}]: Get your subs online ASAP. Rhiza out', vid = 'A01_Rhiza_M04_01562.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M04_01562', faction = 'Aeon'},}
Rhiza_96 =       {{text = '[{i Rhiza}]: It is time for you to attack the UEF. They have a resource base on an island to your east. Attack it and destroy all of the Power Generators and factories on the island. I am uploading the \'Beacon\' Class Frigate blueprint into your ACU. The Frigate has good range, but no anti-air capabilities, so you will need to escort it with Light Attack boats. Expect the UEF to counter with Interceptors and bombers. I want that island.', vid = 'A01_Rhiza_M05_00752.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M05_00752', faction = 'Aeon'},}
Rhiza_97 =       {{text = '[{i Rhiza}]: Sink those UEF subs, and I will give you your next objective, Knight. Rhiza out.', vid = 'A01_Rhiza_M05_00763.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M05_00763', faction = 'Aeon'},}
Rhiza_98 =       {{text = '[{i Rhiza}]: I\'m still reading subs in your sector. Rhiza out.', vid = 'A01_Rhiza_M05_01563.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M05_01563', faction = 'Aeon'},}
Rhiza_99 =       {{text = '[{i Rhiza}]: You have accomplished your objective. Perhaps you will make a fitting Sword of the Way. Rhiza out.', vid = 'A01_Rhiza_M06_00753.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M06_00753', faction = 'Aeon'},}
Rhiza_100 =      {{text = '[{i Rhiza}]: That UEF resource base must be destroyed!  Rhiza out. ', vid = 'A01_Rhiza_M06_00765.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M06_00765', faction = 'Aeon'},}
Rhiza_101 =      {{text = '[{i Rhiza}]: The longer that resource base is standing, the stronger the UEF gets. Rhiza out. ', vid = 'A01_Rhiza_M06_01564.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M06_01564', faction = 'Aeon'},}
Rhiza_102 =      {{text = '[{i Rhiza}]: I am engaging an experienced UEF Commander to the north, and he has entrusted his southern flank to a subordinate. You will attack and defeat that subordinate, thus clearing the way for my primary assault. Destroy the fleet of UEF Frigates and Cruisers. You will receive updated orders when the enemy fleet is destroyed. Rhiza out.', vid = 'A01_Rhiza_M07_00767.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M07_00767', faction = 'Aeon'},}
Rhiza_103 =      {{text = '[{i Rhiza}]: I am engaging an experienced UEF Commander to the north, and he has entrusted his southern flank to a subordinate. You will attack and defeat that subordinate, thus clearing the way for my primary assault. Attack the UEF Commander and destroy the Anti-Air Towers on his island. His position is well-fortified, so I would advise against a frontal assault. Attack his secondary bases to the north and east and draw his forces out into the open. Rhiza out.', vid = 'A01_Rhiza_M07_02484.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M07_02484', faction = 'Aeon'},}
Rhiza_104 =      {{text = '[{i Rhiza}]: I am granting you the Air Factory. Use it to construct Interceptors and bombers.', vid = 'A01_Rhiza_M07_00768.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M07_00768', faction = 'Aeon'},}
Rhiza_105 =      {{text = '[{i Rhiza}]: You have performed well on this day, Commander. You may use these Destroyers; they are sub-killers. Unleash them upon the enemy. Rhiza out.', vid = 'A01_Rhiza_M07_00769.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M07_00769', faction = 'Aeon'},}
Rhiza_106 =      {{text = '[{i Rhiza}]: Scans indicate that all enemy ships have been destroyed. Good. Continue with your mission. Rhiza out.', vid = 'A01_Rhiza_M07_00770.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M07_00770', faction = 'Aeon'},}
Rhiza_107 =      {{text = '[{i Rhiza}]: Well done. Attack the UEF Commander and destroy the Anti-Air Towers on his island. His position is well-fortified, so I would advise against a frontal assault. Attack his secondary bases to the north and east and draw his forces out into the open. Rhiza out.', vid = 'A01_Rhiza_M07_00771.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M07_00771', faction = 'Aeon'},}
Rhiza_108 =      {{text = '[{i Rhiza}]: Our ruse is working! The UEF Commander is sending air units to cover his naval base. Destroy them! Rhiza out.', vid = 'A01_Rhiza_M07_00772.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M07_00772', faction = 'Aeon'},}
Rhiza_109 =      {{text = '[{i Rhiza}]: The UEF Commander is a fool! He is moving units in the hope of supporting his air base. Bring up reinforcements and engage him. Rhiza out.', vid = 'A01_Rhiza_M07_00773.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M07_00773', faction = 'Aeon'},}
Rhiza_110 =      {{text = '[{i Rhiza}]: The UEF Commander is moving air units to cover his naval base. Annihilate his air power. Rhiza out.', vid = 'A01_Rhiza_M07_00775.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M07_00775', faction = 'Aeon'},}
Rhiza_111 =      {{text = '[{i Rhiza}]: UEF naval units are trying to support the air base. Bring up additional forces to destroy the reinforcements. Rhiza out.', vid = 'A01_Rhiza_M07_00776.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M07_00776', faction = 'Aeon'},}
Rhiza_112 =      {{text = '[{i Rhiza}]: I saw an ACU explode. I\'m pleased it wasn\'t you. Rhiza out.', vid = 'A01_Rhiza_M07_00778.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M07_00778', faction = 'Aeon'},}
Rhiza_113 =      {{text = '[{i Rhiza}]: The Anti-Air Tower is down!  Excellent. Now watch as we demonstrate the full power of the Illuminate. Rhiza out.', vid = 'A01_Rhiza_M07_01146.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M07_01146', faction = 'Aeon'},}
Rhiza_114 =      {{text = '[{i Rhiza}]: The UEF Commander is using transports to move his units out of his base. We cannot allow him to establish a position elsewhere. Attack his transports with Interceptors and Light Attack Boats as they leave the base. Rhiza out.', vid = 'A01_Rhiza_M07_01147.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M07_01147', faction = 'Aeon'},}
Rhiza_115 =      {{text = '[{i Rhiza}]: The transports are beginning to flee. Shoot them down! Rhiza out. ', vid = 'A01_Rhiza_M07_01148.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M07_01148', faction = 'Aeon'},}
Rhiza_116 =      {{text = '[{i Rhiza}]: Glorious!', vid = 'A01_Rhiza_M07_01149.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M07_01149', faction = 'Aeon'},}
Rhiza_117 =      {{text = '[{i Rhiza}]: The anti-air has been destroyed. Good. Rhiza out.', vid = 'A01_Rhiza_M07_00779.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M07_00779', faction = 'Aeon'},}
Rhiza_118 =      {{text = '[{i Rhiza}]: The naval base is no more. Well done. Perhaps you are worthy to fight by my side.', vid = 'A01_Rhiza_M07_00781.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M07_00781', faction = 'Aeon'},}
Rhiza_119 =      {{text = '[{i Rhiza}]: The UEF fleet is still sailing. Remedy that. Rhiza out. ', vid = 'A01_Rhiza_M07_00782.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M07_00782', faction = 'Aeon'},}
Rhiza_120 =      {{text = '[{i Rhiza}]: Sink the UEF fleet. I am losing my patience with you. ', vid = 'A01_Rhiza_M07_01565.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M07_01565', faction = 'Aeon'},}
Rhiza_121 =      {{text = '[{i Rhiza}]: The destruction of the anti-air installations on that island is key to my tactics, Knight. Make it happen. Rhiza out. ', vid = 'A01_Rhiza_M07_00784.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M07_00784', faction = 'Aeon'},}
Rhiza_122 =      {{text = '[{i Rhiza}]: Show me your prowess in battle, Knight. Destroy those anti-air emplacements. Rhiza out. ', vid = 'A01_Rhiza_M07_01566.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M07_01566', faction = 'Aeon'},}
Rhiza_123 =      {{text = '[{i Rhiza}]: Your last task is to defeat the UEF Commander. Rhiza out. ', vid = 'A01_Rhiza_M07_00786.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M07_00786', faction = 'Aeon'},}
Rhiza_124 =      {{text = '[{i Rhiza}]: I want to see the flash of that UEF ACU going up, Knight. Make it happen. Rhiza out. ', vid = 'A01_Rhiza_M07_01567.sfd', bank = 'A01_VO', cue = 'A01_Rhiza_M07_01567', faction = 'Aeon'},}
Rhiza_125 =      {{text = '[{i Rhiza}]: Yes, my Princess. I will gladly serve your champion. ', vid = 'A05_Rhiza_M01_00955.sfd', bank = 'A05_VO', cue = 'A05_Rhiza_M01_00955', faction = 'Aeon'},}
Rhiza_126 =      {{text = '[{i Rhiza}]: Ariel was constructing a squadron of bombers. You must protect the colonies with Heavy Shield Generators. I will upload the schematic to you. ', vid = 'A05_Rhiza_M01_00960.sfd', bank = 'A05_VO', cue = 'A05_Rhiza_M01_00960', faction = 'Aeon'},}
Rhiza_127 =      {{text = '[{i Rhiza}]: Princess!', vid = 'A05_Rhiza_M01_00980.sfd', bank = 'A05_VO', cue = 'A05_Rhiza_M01_00980', faction = 'Aeon'},}
-- No vid
Rhiza_128 =      {{text = '[{i Rhiza}]: Glory to the Princess!', bank = 'XGG', cue = 'XGG_Rhiza_MP1_04582'},}
Rhiza_129 =      {{text = '[{i Rhiza}]: Glorious!', bank = 'XGG', cue = 'XGG_Rhiza_MP1_04583'},}
Rhiza_130 =      {{text = '[{i Rhiza}]: I will not be stopped!', bank = 'XGG', cue = 'XGG_Rhiza_MP1_04584'},}
Rhiza_131 =      {{text = '[{i Rhiza}]: All enemies of the Princess will be destroyed!', bank = 'XGG', cue = 'XGG_Rhiza_MP1_04585'},}
Rhiza_132 =      {{text = '[{i Rhiza}]: I will hunt you to the ends of the galaxy!', bank = 'XGG', cue = 'XGG_Rhiza_MP1_04586'},}
Rhiza_133 =      {{text = '[{i Rhiza}]: For the Aeon!', bank = 'XGG', cue = 'XGG_Rhiza_MP1_04587'},}
Rhiza_134 =      {{text = '[{i Rhiza}]: Flee while you can!', bank = 'XGG', cue = 'XGG_Rhiza_MP1_04588'},}
Rhiza_135 =      {{text = '[{i Rhiza}]: Behold the power of the Illuminate!', bank = 'XGG', cue = 'XGG_Rhiza_MP1_04589'},}
Rhiza_136 =      {{text = '[{i Rhiza}]: Such a thing will not stop me!', bank = 'X06_VO', cue = 'X06_Rhiza_T01_04508'},}
Rhiza_137 =      {{text = '[{i Rhiza}]: You mistake me if you think I will be cowed!', bank = 'X06_VO', cue = 'X06_Rhiza_T01_04510'},}
Rhiza_138 =      {{text = '[{i Rhiza}]: Soon you will know my wrath!', bank = 'X06_VO', cue = 'X06_Rhiza_T01_03052'},}

-- Uef Riley
Riley_1 =        {{text = '[{i Riley}]: All the trucks reached Earth! Excellent job, Captain. The UEF is lucky to have soldiers like you in the field. Riley out.', vid = 'E05_Riley_M03_0390.sfd', bank = 'E05_VO', cue = 'E05_Riley_M03_0390', faction = 'UEF'},}
Riley_2 =        {{text = '[{i Riley}]: Because of your valor on the field of battle, you have been promoted to Major. With men like you in the field, I have no doubt that the UEF will triumph. Well done, Commander.', vid = 'E05_Riley_M03_0253.sfd', bank = 'E05_VO', cue = 'E05_Riley_M03_0253', faction = 'UEF'},}
Riley_3 =        {{text = '[{i Riley}]: We\'re reaching the culmination of our efforts, Major. We will not surrender! We will not yield! Victory will be ours!', vid = 'E06_Riley_M01_0309.sfd', bank = 'E06_VO', cue = 'E06_Riley_M01_0309', faction = 'UEF'},}
Riley_4 =        {{text = '[{i Riley}]: Fighting rages all across Earth. We are quickly losing ground, but if you can fire Black Sun, our losses will have been worth it. ', vid = 'E06_Riley_M02_0276.sfd', bank = 'E06_VO', cue = 'E06_Riley_M02_0276', faction = 'UEF'},}

-- Sera SethIavow
Sethiavow_1 =    {{text = '[{i SethIavow}]: You pitiful human. Do you really believe that we serve you? You are merely a means to an end. When we are finished, every human in this galaxy will be dead.', vid = 'X06_Seth-Iavow_M03_04500.sfd', bank = 'X06_VO', cue = 'X06_Seth-iavow_M03_04500', faction = 'Seraphim'},}
Sethiavow_2 =    {{text = '[{i SethIavow}]: You have no hope of standing against us: The Seraphim are eternal. Destroy the human.', vid = 'X06_Seth-Iavow_M03_03997.sfd', bank = 'X06_VO', cue = 'X06_Seth-iavow_M03_03997', faction = 'Seraphim'},}

