-- Uef Stenson
Stenson_1 =      {{text = '[{i Stenson}]: This is Director Stenson of Science Station Lima Foxtrot. Our Power Core has been breached and we are unable to repair it. If it isn\'t contained soon, the entire Facility will explode.', vid = 'E02_Stenson_M01_0100.sfd', bank = 'E02_VO', cue = 'E02_Stenson_M01_0100', faction = 'UEF'},}
Stenson_2 =      {{text = '[{i Stenson}]: The Core temperature has doubled. We can\'t last much longer!', vid = 'E02_Stenson_M01_0101.sfd', bank = 'E02_VO', cue = 'E02_Stenson_M01_0101', faction = 'UEF'},}
Stenson_3 =      {{text = '[{i Stenson}]: The Core is increasingly unstable. You\'ve got to do something! There isn\'t much time left!', vid = 'E02_Stenson_M01_0102.sfd', bank = 'E02_VO', cue = 'E02_Stenson_M01_0102', faction = 'UEF'},}
Stenson_4 =      {{text = '[{i Stenson}]: ...the thermal shield is failing! The Core will explode any minute!', vid = 'E02_Stenson_M01_0103.sfd', bank = 'E02_VO', cue = 'E02_Stenson_M01_0103', faction = 'UEF'},}
Stenson_5 =      {{text = '[{i Stenson}]: Thank goodness you\'ve made it! Repair the Core, quickly!', vid = 'E02_Stenson_M01_0104.sfd', bank = 'E02_VO', cue = 'E02_Stenson_M01_0104', faction = 'UEF'},}
Stenson_6 =      {{text = '[{i Stenson}]: They\'re coming! They\'re going to slaughter us all!', vid = 'E02_Stenson_M02_0105.sfd', bank = 'E02_VO', cue = 'E02_Stenson_M02_0105', faction = 'UEF'},}

-- Uef Sweeney
Sweeney_1 =      {{text = '[{i Sweeney}]: Captain, we\'re ready to go, we have...wait, what\'s that?', vid = 'E05_Sweeney_M02_0254.sfd', bank = 'E05_VO', cue = 'E05_Sweeney_M02_0254', faction = 'UEF'},}
Sweeney_2 =      {{text = '[{i Sweeney}]: We can\'t move the components--those guns will destroy them! Take them out!', vid = 'E05_Sweeney_M02_0255.sfd', bank = 'E05_VO', cue = 'E05_Sweeney_M02_0255', faction = 'UEF'},}
Sweeney_3 =      {{text = '[{i Sweeney}]: The convoy from Site Alpha will be ready to leave in just a moment, Captain.', vid = 'E05_Sweeney_M03_0256.sfd', bank = 'E05_VO', cue = 'E05_Sweeney_M03_0256', faction = 'UEF'},}
Sweeney_4 =      {{text = '[{i Sweeney}]: The convoy from Site Alpha has left, Captain.', vid = 'E05_Sweeney_M03_01144.sfd', bank = 'E05_VO', cue = 'E05_Sweeney_M03_01144', faction = 'UEF'},}
Sweeney_5 =      {{text = '[{i Sweeney}]: The convoy from Site Alpha has left, Captain. The trucks are now under your command.', vid = 'E05_Sweeney_M03_0257.sfd', bank = 'E05_VO', cue = 'E05_Sweeney_M03_0257', faction = 'UEF'},}
Sweeney_6 =      {{text = '[{i Sweeney}]: Another convoy from Site Alpha has been prepped and is yours to command.', vid = 'E05_Sweeney_M03_0393.sfd', bank = 'E05_VO', cue = 'E05_Sweeney_M03_0393', faction = 'UEF'},}
Sweeney_7 =      {{text = '[{i Sweeney}]: Captain, the convoy from Site Beta is prepped and ready to gate.', vid = 'E05_Sweeney_M03_0258.sfd', bank = 'E05_VO', cue = 'E05_Sweeney_M03_0258', faction = 'UEF'},}
Sweeney_8 =      {{text = '[{i Sweeney}]: The Site Beta convoy is on its way to the Gate, Captain. The convoy is now under your command.', vid = 'E05_Sweeney_M03_0259.sfd', bank = 'E05_VO', cue = 'E05_Sweeney_M03_0259', faction = 'UEF'},}
Sweeney_9 =      {{text = '[{i Sweeney}]: We\'ve prepared another convoy from Site Beta. Move it to the Gate.', vid = 'E05_Sweeney_M03_0394.sfd', bank = 'E05_VO', cue = 'E05_Sweeney_M03_0394', faction = 'UEF'},}
Sweeney_10 =     {{text = '[{i Sweeney}]: The Site Delta Convoy is fueled and ready to depart.', vid = 'E05_Sweeney_M03_0260.sfd', bank = 'E05_VO', cue = 'E05_Sweeney_M03_0260', faction = 'UEF'},}
Sweeney_11 =     {{text = '[{i Sweeney}]: Captain, the Site Delta convoy has left the facility. They are awaiting your orders.', vid = 'E05_Sweeney_M03_0261.sfd', bank = 'E05_VO', cue = 'E05_Sweeney_M03_0261', faction = 'UEF'},}
Sweeney_12 =     {{text = '[{i Sweeney}]: We\'ve got another convoy from Site Delta ready to go. It\'s under your command.', vid = 'E05_Sweeney_M03_0395.sfd', bank = 'E05_VO', cue = 'E05_Sweeney_M03_0395', faction = 'UEF'},}
Sweeney_13 =     {{text = '[{i Sweeney}]: The trucks cannot reach the Gate, Captain. Clear a path for them.', vid = 'E05_Sweeney_M03_0262.sfd', bank = 'E05_VO', cue = 'E05_Sweeney_M03_0262', faction = 'UEF'},}
Sweeney_14 =     {{text = '[{i Sweeney}]: We can\'t replace those components! Earth is finished!', vid = 'E05_Sweeney_M03_0263.sfd', bank = 'E05_VO', cue = 'E05_Sweeney_M03_0263', faction = 'UEF'},}

-- Aeon Thalia
Thalia_1 =       {{text = '[{i Thalia}]: This is Thalia. I am located on the planet Copernicus. Coalition forces sent to assist us have been destroyed. QAI is closing in on our position, and we have no chance of surviving. We can\'t --', vid = 'X02_Thalia_DB01_04021.sfd', bank = 'Briefings', cue = 'X02_Thalia_DB01_04021', faction = 'Aeon'},}
Thalia_2 =       {{text = '[{i Thalia}]: Hmpf, I didn\'t expect Rhiza to send a UEF commander. Celene\'s forces are attacking my base. Deal with them. Thalia out.', vid = 'X02_Thalia_M01_03127.sfd', bank = 'X02_VO', cue = 'X02_Thalia_M01_03127', faction = 'Aeon'},}
Thalia_3 =       {{text = '[{i Thalia}]: A Cybran? Here? Celene is attacking my base. Repel her attack. Thalia out.', vid = 'X02_Thalia_M01_03510.sfd', bank = 'X02_VO', cue = 'X02_Thalia_M01_03510', faction = 'Aeon'},}
Thalia_4 =       {{text = '[{i Thalia}]: Champion, this is a true honor. Celene is attacking my base. Please repel her attacks and defeat her. Glory to the Princess!', vid = 'X02_Thalia_M01_03128.sfd', bank = 'X02_VO', cue = 'X02_Thalia_M01_03128', faction = 'Aeon'},}
Thalia_5 =       {{text = '[{i Thalia}]: I managed to steal the schematic for the Order Sprite Striker. It is yours. Use it to bring glory to the Princess.', vid = 'X02_Thalia_M01_04264.sfd', bank = 'X02_VO', cue = 'X02_Thalia_M01_04264', faction = 'Aeon'},}
Thalia_6 =       {{text = '[{i Thalia}]: Commander, attack and destroy the Order base operating to the east. Thalia out.', vid = 'X02_Thalia_M01_04896.sfd', bank = 'X02_VO', cue = 'X02_Thalia_M01_04896', faction = 'Aeon'},}
Thalia_7 =       {{text = '[{i Thalia}]: The Order base is still operational, Commander. Please, you must destroy it so that we can free the Loyalist prisoners. Thalia out.', vid = 'X02_Thalia_M01_04898.sfd', bank = 'X02_VO', cue = 'X02_Thalia_M01_04898', faction = 'Aeon'},}
Thalia_8 =       {{text = '[{i Thalia}]: Our base is still under attack. Destroy the enemy\'s forces.', vid = 'X02_Thalia_M01_03517.sfd', bank = 'X02_VO', cue = 'X02_Thalia_M01_03517', faction = 'Aeon'},}
Thalia_9 =       {{text = '[{i Thalia}]: Celene\'s forces are closing in our location. Stop them.', vid = 'X02_Thalia_M01_03518.sfd', bank = 'X02_VO', cue = 'X02_Thalia_M01_03518', faction = 'Aeon'},}
Thalia_10 =      {{text = '[{i Thalia}]: Scans show that the area is clear of enemy forces. Thank you, Commander.', vid = 'X02_Thalia_M01_03133.sfd', bank = 'X02_VO', cue = 'X02_Thalia_M01_03133', faction = 'Aeon'},}
Thalia_11 =      {{text = '[{i Thalia}]: The Loyalists are dead because you were unable to defeat Celene\'s forces. The war is lost.', vid = 'X02_Thalia_M01_03519.sfd', bank = 'X02_VO', cue = 'X02_Thalia_M01_03519', faction = 'Aeon'},}
Thalia_12 =      {{text = '[{i Thalia}]: There is an Order resource facility to your northeast, and it is powering the shields at Celene\'s base. Attack and destroy the power generators at the resource facility. Thalia out.', vid = 'X02_Thalia_M01_03137.sfd', bank = 'X02_VO', cue = 'X02_Thalia_M01_03137', faction = 'Aeon'},}
Thalia_13 =      {{text = '[{i Thalia}]: The shields are going down at Celene\'s base! Attack her while you can! Thalia out.', vid = 'X02_Thalia_M01_04900.sfd', bank = 'X02_VO', cue = 'X02_Thalia_M01_04900', faction = 'Aeon'},}
Thalia_14 =      {{text = '[{i Thalia}]: The power generators are still online. Destroy them.', vid = 'X02_Thalia_M01_03524.sfd', bank = 'X02_VO', cue = 'X02_Thalia_M01_03524', faction = 'Aeon'},}
Thalia_15 =      {{text = '[{i Thalia}]: It is imperative that you destroy those power generators.', vid = 'X02_Thalia_M01_03525.sfd', bank = 'X02_VO', cue = 'X02_Thalia_M01_03525', faction = 'Aeon'},}
Thalia_16 =      {{text = '[{i Thalia}]: Well done, Commander.', vid = 'X02_Thalia_M01_03139.sfd', bank = 'X02_VO', cue = 'X02_Thalia_M01_03139', faction = 'Aeon'},}
Thalia_17 =      {{text = '[{i Thalia}]: You must rescue the other Loyalists! Time is of the essence!', vid = 'X02_Thalia_M02_04272.sfd', bank = 'X02_VO', cue = 'X02_Thalia_M02_04272', faction = 'Aeon'},}
Thalia_18 =      {{text = '[{i Thalia}]: If QAI succeeds in implanting its AI chips, the blame will rest on your shoulders.', vid = 'X02_Thalia_M02_04273.sfd', bank = 'X02_VO', cue = 'X02_Thalia_M02_04273', faction = 'Aeon'},}
Thalia_19 =      {{text = '[{i Thalia}]: One of our operatives is ready to plant explosives anywhere in the field, Commander. The explosion should be sufficient to destroy the targeted building, so use it wisely. Click on the Command Signal button and then click on the target to mark it.', vid = 'X02_Thalia_M02_04275.sfd', bank = 'X02_VO', cue = 'X02_Thalia_M02_04275', faction = 'Aeon'},}
Thalia_20 =      {{text = '[{i Thalia}]: My operatives are ready to strike, Commander. Please give them an attack order via the Command Signal. Thalia out.', vid = 'X02_Thalia_M02_04901.sfd', bank = 'X02_VO', cue = 'X02_Thalia_M02_04901', faction = 'Aeon'},}
Thalia_21 =      {{text = '[{i Thalia}]: There are Loyalist forces operating to the south of the prison. They will aid you if they are able. Thalia out.', vid = 'X02_Thalia_M02_04902.sfd', bank = 'X02_VO', cue = 'X02_Thalia_M02_04902', faction = 'Aeon'},}
Thalia_22 =      {{text = '[{i Thalia}]: There are Loyalists to the south of the prison. Please, you must protect them. Thalia out.', vid = 'X02_Thalia_M02_04903.sfd', bank = 'X02_VO', cue = 'X02_Thalia_M02_04903', faction = 'Aeon'},}
Thalia_23 =      {{text = '[{i Thalia}]: The Loyalists have been killed. I shall mourn their loss.', vid = 'X02_Thalia_M02_04904.sfd', bank = 'X02_VO', cue = 'X02_Thalia_M02_04904', faction = 'Aeon'},}
Thalia_24 =      {{text = '[{i Thalia}]: Detecting Quantum signature. The Seraphim is coming through.', vid = 'X02_Thalia_M03_04858.sfd', bank = 'X02_VO', cue = 'X02_Thalia_M03_04858', faction = 'Aeon'},}
Thalia_25 =      {{text = '[{i Thalia}]: The Seraphim is ours.', vid = 'X02_Thalia_M03_04859.sfd', bank = 'X02_VO', cue = 'X02_Thalia_M03_04859', faction = 'Aeon'},}

