Theluuthow_14 =  {{text = '[{i ThelUuthow}]: [Language Not Recognized]', vid = 'X03_Thel-Uuthow_T01_04346.sfd', bank = 'X03_VO', cue = 'X03_Thel-Uuthow_T01_04346', faction = 'Seraphim'},}
Theluuthow_15 =  {{text = '[{i ThelUuthow}]: [Language Not Recognized]', vid = 'X03_Thel-Uuthow_T01_04348.sfd', bank = 'X03_VO', cue = 'X03_Thel-Uuthow_T01_04348', faction = 'Seraphim'},}
Theluuthow_16 =  {{text = '[{i ThelUuthow}]: [Language Not Recognized]', vid = 'X03_Thel-Uuthow_T01_04350.sfd', bank = 'X03_VO', cue = 'X03_Thel-Uuthow_T01_04350', faction = 'Seraphim'},}
Theluuthow_17 =  {{text = '[{i ThelUuthow}]: [Language Not Recognized]', vid = 'X03_Thel-Uuthow_T01_04352.sfd', bank = 'X03_VO', cue = 'X03_Thel-Uuthow_T01_04352', faction = 'Seraphim'},}
Theluuthow_18 =  {{text = '[{i ThelUuthow}]: As you command.', vid = 'X06_Thel-Uuthow_M03_03998.sfd', bank = 'X06_VO', cue = 'X06_Thel-Uuthow_M03_03998', faction = 'Seraphim'},}
Theluuthow_19 =  {{text = '[{i ThelUuthow}]: I will not allow your experimental to interfere with my mission!', vid = 'X06_Thel-Uuthow_T01_04797.sfd', bank = 'X06_VO', cue = 'X06_Thel-Uuthow_T01_04797', faction = 'Seraphim'},}
Theluuthow_20 =  {{text = '[{i ThelUuthow}]: I will eliminate your experimental just as we will eventually eliminate your Coalition!', vid = 'X06_Thel-Uuthow_T01_04798.sfd', bank = 'X06_VO', cue = 'X06_Thel-Uuthow_T01_04798', faction = 'Seraphim'},}
Theluuthow_21 =  {{text = '[{i ThelUuthow}]: Your experimental will never activate!', vid = 'X06_Thel-Uuthow_T01_04799.sfd', bank = 'X06_VO', cue = 'X06_Thel-Uuthow_T01_04799', faction = 'Seraphim'},}
Theluuthow_22 =  {{text = '[{i ThelUuthow}]: You have accomplished nothing. You will never defeat us.', vid = 'X06_Thel-Uuthow_T01_04462.sfd', bank = 'X06_VO', cue = 'X06_Thel-Uuthow_T01_04462', faction = 'Seraphim'},}
Theluuthow_23 =  {{text = '[{i ThelUuthow}]: You think I mourn the loss of an Order commander? Then you are a fool.', vid = 'X06_Thel-Uuthow_T01_04463.sfd', bank = 'X06_VO', cue = 'X06_Thel-Uuthow_T01_04463', faction = 'Seraphim'},}
Theluuthow_24 =  {{text = '[{i ThelUuthow}]: Ha-ha-ha. Human killing human. How delicious.', vid = 'X06_Thel-Uuthow_T01_04464.sfd', bank = 'X06_VO', cue = 'X06_Thel-Uuthow_T01_04464', faction = 'Seraphim'},}
Theluuthow_25 =  {{text = '[{i ThelUuthow}]: As long as the Rift remains open, we have a door to your galaxy.', vid = 'X06_Thel-Uuthow_T01_04465.sfd', bank = 'X06_VO', cue = 'X06_Thel-Uuthow_T01_04465', faction = 'Seraphim'},}
Theluuthow_26 =  {{text = '[{i ThelUuthow}]: Your kind began this war. We are merely finishing it.', vid = 'X06_Thel-Uuthow_T01_02971.sfd', bank = 'X06_VO', cue = 'X06_Thel-Uuthow_T01_02971', faction = 'Seraphim'},}
Theluuthow_27 =  {{text = '[{i ThelUuthow}]: Your Earth fell easily. You will prove no different.', vid = 'X06_Thel-Uuthow_T01_02972.sfd', bank = 'X06_VO', cue = 'X06_Thel-Uuthow_T01_02972', faction = 'Seraphim'},}
Theluuthow_28 =  {{text = '[{i ThelUuthow}]: You Cybrans die as easily as any other human.', vid = 'X06_Thel-Uuthow_T01_02973.sfd', bank = 'X06_VO', cue = 'X06_Thel-Uuthow_T01_02973', faction = 'Seraphim'},}
Theluuthow_29 =  {{text = '[{i ThelUuthow}]: Your faith in technology will be your undoing.', vid = 'X06_Thel-Uuthow_T01_02974.sfd', bank = 'X06_VO', cue = 'X06_Thel-Uuthow_T01_02974', faction = 'Seraphim'},}
Theluuthow_30 =  {{text = '[{i ThelUuthow}]: Only one may ascend. Your Princess is a charlatan.', vid = 'X06_Thel-Uuthow_T01_02975.sfd', bank = 'X06_VO', cue = 'X06_Thel-Uuthow_T01_02975', faction = 'Seraphim'},}
Theluuthow_31 =  {{text = '[{i ThelUuthow}]: Bow down before our might, and we may spare you.', vid = 'X06_Thel-Uuthow_T01_02976.sfd', bank = 'X06_VO', cue = 'X06_Thel-Uuthow_T01_02976', faction = 'Seraphim'},}
Theluuthow_32 =  {{text = '[{i ThelUuthow}]: Perhaps you are a greater threat than I thought?', vid = 'X06_Thel-Uuthow_T01_02977.sfd', bank = 'X06_VO', cue = 'X06_Thel-Uuthow_T01_02977', faction = 'Seraphim'},}
Theluuthow_33 =  {{text = '[{i ThelUuthow}]: You will perish at my hand.', vid = 'X06_Thel-Uuthow_T01_02978.sfd', bank = 'X06_VO', cue = 'X06_Thel-Uuthow_T01_02978', faction = 'Seraphim'},}
Theluuthow_34 =  {{text = '[{i ThelUuthow}]: I serve to the end!', vid = 'X06_Thel-Uuthow_T01_02979.sfd', bank = 'X06_VO', cue = 'X06_Thel-Uuthow_T01_02979', faction = 'Seraphim'},}
