-- Uef Thompson
Thompson_1 =     {{text = '[{i Thompson}]: Commander...can you read me? This is Assistant Research Director Thompson. The Cybrans are attacking us, and I don\'t know how much longer our defenses will hold. I\'m patching you into our systems. You\'ll have access to our Long-Range Radar.', vid = 'E01_Thompson_M07_0039.sfd', bank = 'E01_VO', cue = 'E01_Thompson_M07_0039', faction = 'UEF'},}
Thompson_2 =     {{text = '[{i Thompson}]: Commander, we have a Light Artillery Installation available, but we don\'t have enough power to fire it. The Cybran has some Tech 2 Power Generators to the far west. If you can capture one of them, we can tap into your grid and fire the weapon.', vid = 'E01_Thompson_M07_0040.sfd', bank = 'E01_VO', cue = 'E01_Thompson_M07_0040', faction = 'UEF'},}
Thompson_3 =     {{text = '[{i Thompson}]: Commander, we have analyzed the Cybran\'s base. All of their Anti-Air Towers are positioned along their southern perimeter.', vid = 'E01_Thompson_M07_0041.sfd', bank = 'E01_VO', cue = 'E01_Thompson_M07_0041', faction = 'UEF'},}
Thompson_4 =     {{text = '[{i Thompson}]: We appreciate the assistance, Commander, but...oh my god...the Cybrans took Dr. Sweeney. He\'s one of our top researchers and key to Black Sun--', vid = 'E01_Thompson_M07_0042.sfd', bank = 'E01_VO', cue = 'E01_Thompson_M07_0042', faction = 'UEF'},}
Thompson_5 =     {{text = '[{i Thompson}]: The southern facility has been lost. All the researchers, all the work...it\'s all gone... ', vid = 'E01_Thompson_M07_0043.sfd', bank = 'E01_VO', cue = 'E01_Thompson_M07_0043', faction = 'UEF'},}

-- Aeon Toth
Toth_1 =         {{text = '[{i Toth}]: The Princess has expressly requested there be no unnecessary loss of life.', vid = 'A02_Toth_M02_00809.sfd', bank = 'A02_VO', cue = 'A02_Toth_M02_00809', faction = 'Aeon'},}
Toth_2 =         {{text = '[{i Toth}]: The UEF is coming.', vid = 'A03_Toth_M01_00857.sfd', bank = 'A03_VO', cue = 'A03_Toth_M01_00857', faction = 'Aeon'},}
Toth_3 =         {{text = '[{i Toth}]: Harmony to you, Knight. I apologize for this interruption, but the Princess wishes to speak to you. It is a matter of great importance.', vid = 'A03_Toth_M03_00876.sfd', bank = 'A03_VO', cue = 'A03_Toth_M03_00876', faction = 'Aeon'},}

-- Aeon Vendetta
Vendetta_1 =     {{text = '[{i Vendetta}]: The Seraphim are eternal. You will never defeat them.', vid = 'X06_Vedetta_T01_04466.sfd', bank = 'X06_VO', cue = 'X06_Vedetta_T01_04466', faction = 'Aeon'},}
Vendetta_2 =     {{text = '[{i Vendetta}]: The Coalition is collapsing. It\'s only a matter of time before you all turn on each other.', vid = 'X06_Vedetta_T01_04467.sfd', bank = 'X06_VO', cue = 'X06_Vedetta_T01_04467', faction = 'Aeon'},}
Vendetta_3 =     {{text = '[{i Vendetta}]: Your experimental could cause me problems, so I think I will eliminate it.', vid = 'X06_Vedetta_T01_04800.sfd', bank = 'X06_VO', cue = 'X06_Vedetta_T01_04800', faction = 'Aeon'},}
Vendetta_4 =     {{text = '[{i Vendetta}]: No, you will not finish that experimental!', vid = 'X06_Vedetta_T01_04801.sfd', bank = 'X06_VO', cue = 'X06_Vedetta_T01_04801', faction = 'Aeon'},}
Vendetta_5 =     {{text = '[{i Vendetta}]: Do not think you will complete that experimental!', vid = 'X06_Vedetta_T01_04802.sfd', bank = 'X06_VO', cue = 'X06_Vedetta_T01_04802', faction = 'Aeon'},}
Vendetta_6 =     {{text = '[{i Vendetta}]: Why are you still fighting us?', vid = 'X06_Vedetta_T01_03012.sfd', bank = 'X06_VO', cue = 'X06_Vedetta_T01_03012', faction = 'Aeon'},}
Vendetta_7 =     {{text = '[{i Vendetta}]: You could not even defend Earth. How can you hope to stop us?', vid = 'X06_Vedetta_T01_03013.sfd', bank = 'X06_VO', cue = 'X06_Vedetta_T01_03013', faction = 'Aeon'},}
Vendetta_8 =     {{text = '[{i Vendetta}]: You are an abomination.', vid = 'X06_Vedetta_T01_03014.sfd', bank = 'X06_VO', cue = 'X06_Vedetta_T01_03014', faction = 'Aeon'},}
Vendetta_9 =     {{text = '[{i Vendetta}]: Your reliance on technology shall be your undoing.', vid = 'X06_Vedetta_T01_03015.sfd', bank = 'X06_VO', cue = 'X06_Vedetta_T01_03015', faction = 'Aeon'},}
Vendetta_10 =    {{text = '[{i Vendetta}]: You will die by my hand, traitor.', vid = 'X06_Vedetta_T01_03016.sfd', bank = 'X06_VO', cue = 'X06_Vedetta_T01_03016', faction = 'Aeon'},}
Vendetta_11 =    {{text = '[{i Vendetta}]: The Princess\' time has passed. Kael now leads us.', vid = 'X06_Vedetta_T01_03017.sfd', bank = 'X06_VO', cue = 'X06_Vedetta_T01_03017', faction = 'Aeon'},}
Vendetta_12 =    {{text = '[{i Vendetta}]: Nice try.', vid = 'X06_Vedetta_T01_03018.sfd', bank = 'X06_VO', cue = 'X06_Vedetta_T01_03018', faction = 'Aeon'},}
Vendetta_13 =    {{text = '[{i Vendetta}]: I am not defeated yet!', vid = 'X06_Vedetta_T01_03019.sfd', bank = 'X06_VO', cue = 'X06_Vedetta_T01_03019', faction = 'Aeon'},}
Vendetta_14 =    {{text = '[{i Vendetta}]: Aaaaaaaaah!', vid = 'X06_Vedetta_T01_03020.sfd', bank = 'X06_VO', cue = 'X06_Vedetta_T01_03020', faction = 'Aeon'},}
